--
-- Cấu trúc bảng cho bảng `pages`
--

CREATE TABLE `pages` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `pages`
--

INSERT INTO `pages` (`id`, `title`, `content`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Liên Hệ', '<p>Th&ocirc;ng tin li&ecirc;n hệ cửa h&agrave;ng:</p>\r\n<p><img src=\"/unitop.vn/laravel-pro/project/ismart/public//storage/photos/1/pages/ismart.PNG\" alt=\"http://localhost:8080/unitop.vn/laravel-pro/project/ismart/public//storage/photos/1/pages/ismart.PNG\" width=\"948\" height=\"435\" /></p>\r\n<p>Email: <a href=\"mailto:lienhe@ismart.com\">lienhe@ismart.com</a></p>\r\n<p>Sđt: 0123456789</p>\r\n<p>Địa chỉ: 22 Quang Trung, Quận 3, TP HCM.</p>', 2, '2021-07-15 01:34:52', '2021-07-15 03:37:55', NULL),
(2, 'Giới Thiệu', '<p id=\"lsht\"><strong>I. Lịch sử h&igrave;nh th&agrave;nh&nbsp;</strong></p>\r\n<p><strong>Di Động Th&ocirc;ng Minh</strong>&nbsp;l&agrave; hệ thống kinh doanh nhượng quyền b&aacute;n lẻ Smartphone, Phụ kiện ch&iacute;nh h&atilde;ng v&agrave; sửa chữa dịch vụ, l&agrave; đối t&aacute;c của c&aacute;c h&atilde;ng điện thoại ch&iacute;nh h&atilde;ng lớn tại Việt Nam như: Apple, Samsung, Oppo, Realme,&nbsp;Xiaomi,&hellip;</p>\r\n<p>Hệ thống b&aacute;n lẻ Di Động Th&ocirc;ng Minh hiện c&oacute; trụ sở ch&iacute;nh tại địa chỉ:&nbsp;<strong>119 Th&aacute;i Thịnh, Phường Thịnh Quang, Quận Đống Đa, tp H&agrave; Nội</strong>&nbsp;v&agrave; chi nh&aacute;nh tại&nbsp;<strong>701 L&ecirc; Hồng Phong, P10, Q10, tp Hồ Ch&iacute; Minh</strong></p>\r\n<p><strong>Th&agrave;nh lập từ năm 2012</strong>, trải qua gần&nbsp;<strong>10&nbsp;năm ph&aacute;t triển</strong>, Di Động Th&ocirc;ng Minh đ&atilde; trở th&agrave;nh điểm đến quen thuộc của kh&aacute;ch h&agrave;ng y&ecirc;u c&ocirc;ng nghệ tr&ecirc;n to&agrave;n quốc với c&aacute;c sản phẩm điện thoại, m&aacute;y t&iacute;nh bảng, Smartphone ch&iacute;nh h&atilde;ng uy t&iacute;n chất lượng. Thời điểm hiện tại hệ thống đ&atilde; c&oacute;&nbsp;<strong>17 cửa h&agrave;ng nhượng quyền</strong>&nbsp;+&nbsp;<strong>4 trung t&acirc;m bảo h&agrave;nh</strong>&nbsp;trải d&agrave;i khắp to&agrave;n quốc tạo sự thuận tiện cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><em><strong>Quyền lợi khi được tham gia nhượng quyền:</strong></em></p>\r\n<ul>\r\n<li>&Iacute;t rủi ro bởi c&aacute;c sản phẩm, dịch vụ của Di Động Th&ocirc;ng Minh đều đ&atilde; khẳng định được chất lượng, uy t&iacute;n</li>\r\n<li>Được chuyển giao v&agrave; đảm bảo c&aacute;c vấn đề x&acirc;y dựng chiến lược quảng b&aacute;, tiếp thị, đ&agrave;o tạo, tận dụng nguồn nh&acirc;n lực c&oacute; sẵn 1 c&aacute;ch tối đa. C&aacute;c quy tr&igrave;nh vận h&agrave;nh đều sẽ được Di Động Th&ocirc;ng Minh hỗ trợ v&agrave; chuyển giao. Cửa h&agrave;ng nhượng quyền chỉ cần tập trung cho việc b&aacute;n h&agrave;ng.</li>\r\n<li>Được sử dụng thương hiệu uy t&iacute;n của Di Động Th&ocirc;ng Minh trong lĩnh vực b&aacute;n lẻ điện thoại ch&iacute;nh h&atilde;ng đ&atilde; được khẳng định qua hơn 7 năm ph&aacute;t triển v&agrave; được kh&aacute;ch h&agrave;ng tin tưởng.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><em><strong>Một&nbsp;v&agrave;i th&ocirc;ng tin về c&ocirc;ng ty:</strong></em></p>\r\n<ul>\r\n<li>T&ecirc;n c&ocirc;ng ty:&nbsp;C&ocirc;ng Ty Cổ Phần Viễn Th&ocirc;ng Di Động Th&ocirc;ng Minh</li>\r\n<li>Địa chỉ: 119 phố Th&aacute;i Thịnh, phường Thịnh Quang, quận Đống Đa, th&agrave;nh phố&nbsp;H&agrave; Nội, Việt Nam</li>\r\n<li>MST: 0105815899&nbsp;(Số giấy chứng nhận đăng k&yacute; kinh doanh thương nh&acirc;n</li>\r\n<li>Ng&agrave;y cấp lần đầu: 12/03/2012</li>\r\n<li>Đăng k&yacute; thay đổi lần 2: 04/11/2013</li>\r\n<li>Nơi cấp: Sở kế hoạch v&agrave; đầu tư th&agrave;nh phố H&agrave; Nội</li>\r\n<li>Số điện thoại : 04.62757392 / 04.62757393</li>\r\n</ul>\r\n<p><strong>II. Tầm nh&igrave;n &ndash; sứ mệnh</strong></p>\r\n<p id=\"sm\"><em><strong>&gt;&gt; Sứ mệnh:</strong></em></p>\r\n<p>Ch&uacute;ng t&ocirc;i lấy kh&aacute;ch h&agrave;ng l&agrave;m trung t&acirc;m, lấy tổ chức chức chuy&ecirc;n nghiệp l&agrave;m sức mạnh cạnh tranh, kh&ocirc;ng ngừng nỗ lực cung cấp những sản phẩm, dịch vụ gi&aacute; trị mới, chất lượng cao với gi&aacute; hợp l&yacute; để đ&oacute;ng g&oacute;p v&agrave;o mục ti&ecirc;u n&acirc;ng cao chất lượng cuộc sống th&ocirc;ng tin.</p>\r\n<p id=\"tn\"><em><strong>&gt;&gt; Tầm nh&igrave;n:</strong></em></p>\r\n<p>Trở th&agrave;nh một c&ocirc;ng ty h&agrave;ng đầu cung cấp sản phẩm, dịch vụ c&aacute;c thế hệ Di Động Th&ocirc;ng Minh trong đ&oacute; lấy chất lượng phục vụ kh&aacute;ch h&agrave;ng l&agrave;m sự ph&aacute;t triển bền vững.</p>\r\n<p id=\"gtcl\"><strong>III. Những Gi&aacute; Trị Cốt L&otilde;i&nbsp;</strong></p>\r\n<p>Gi&aacute; trị cốt l&otilde;i của Di Động Th&ocirc;ng Minh&nbsp; l&agrave; c&aacute;c gi&aacute; trị bền vững l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng,&nbsp; l&agrave; t&ocirc;n chỉ to&agrave;n bộ h&agrave;nh động của c&ocirc;ng ty , được h&igrave;nh th&agrave;nh qua những ng&agrave;y th&aacute;ng gian khổ đầu ti&ecirc;n của c&ocirc;ng ty, được x&acirc;y dựng từ những kinh nghiệm v&agrave; học hỏi, t&ocirc;i luyện qua những thử th&aacute;ch trong suốt qu&aacute; tr&igrave;nh ph&aacute;t triển.</p>\r\n<p>&nbsp;</p>\r\n<p><em><strong>1. Tư duy hệ thống x&atilde; hội h&oacute;a:&nbsp;</strong></em></p>\r\n<p>C&ocirc;ng ty được tổ chức như một hệ thống với những nhiệm vụ sứ mệnh r&otilde; r&agrave;ng, được tổ chức to&agrave;n vẹn v&agrave; hiệu quả, kh&ocirc;ng ngừng n&acirc;ng cấp để thay đổi về tầm ảnh hưởng, hệ thống sẽ được X&atilde; Hội H&oacute;a theo hướng l&agrave; t&agrave;i sản chung của to&agrave;n bộ c&aacute;n bộ, nh&acirc;n vi&ecirc;n Di Động Th&ocirc;ng Minh v&agrave; đ&oacute;ng g&oacute;p để trở th&agrave;nh 1 phần hữu cơ của X&atilde; Hội.</p>\r\n<p><em><strong>2. Th&agrave;nh c&ocirc;ng của con người l&agrave; th&agrave;nh c&ocirc;ng của tổ chức.&nbsp;</strong></em></p>\r\n<p>C&ocirc;ng ty Di Động Th&ocirc;ng Minh x&acirc;y dựng đội ngũ nh&acirc;n vi&ecirc;n như những người đồng đội tr&ecirc;n c&ugrave;ng một chiến thuyền, mỗi c&aacute; nh&acirc;n được đ&aacute;nh thức nh&acirc;n sinh quan v&agrave; hướng dẫn c&aacute;ch nhận định, phấn đấu v&igrave; mục ti&ecirc;u sống, th&agrave;nh c&ocirc;ng của mỗi nh&acirc;n vi&ecirc;n l&agrave; th&agrave;nh c&ocirc;ng của tổ chức. Hội tụ c&agrave;ng nhiều th&agrave;nh c&ocirc;ng n&agrave;y, tổ chức đạt được mục ti&ecirc;u. Tr&ecirc;n c&ugrave;ng một chiến thuyền, giữa biển khơi bao la&hellip;đ&ograve;i hỏi một tập thể phải biết tối ưu ở mọi cấp độ, đo&agrave;n kết v&agrave; tin tưởng v&agrave;o mục ti&ecirc;u của tổ chức. Hệ thống đ&ograve;i hỏi những con người phải biết th&agrave;nh c&ocirc;ng v&agrave; d&aacute;m th&agrave;nh c&ocirc;ng.</p>\r\n<p><em><strong>3. Gi&aacute; trị doanh nghiệp nằm ở kh&aacute;ch h&agrave;ng&nbsp;</strong></em></p>\r\n<p>Gi&aacute; trị doanh nghiệp nằm ở kh&aacute;ch h&agrave;ng, kết nối kh&aacute;ch h&agrave;ng th&agrave;nh một x&atilde; hội v&agrave; phục vụ x&atilde; hội n&agrave;y với những năng lực, tư duy lu&ocirc;n đổi mới. Mỗi kh&aacute;ch h&agrave;ng l&agrave; một hạt nh&acirc;n quan trọng tr&ecirc;n bước đường c&oacute;p nhặt th&agrave;nh c&ocirc;ng của c&ocirc;ng ty. To&agrave;n thể từ l&atilde;nh đạo đến nh&acirc;n vi&ecirc;n trước kh&aacute;ch h&agrave;ng lu&ocirc;n phải thể hiện trong vai tr&ograve; người phục vụ: tận tụy, lắng nghe v&agrave; đưa ra giải ph&aacute;p tốt nhất.</p>', 2, '2021-07-15 03:19:42', '2021-07-15 06:38:02', NULL),
(4, 'bloggggggg', '<p>&aacute;dasdasdasdas</p>', 1, '2021-08-03 06:19:13', '2021-08-12 22:42:59', '2021-08-12 22:42:59');

