--
-- Cấu trúc bảng cho bảng `posts`
--

CREATE TABLE `posts` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image_post` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image_post_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1,
  `category_post_id` bigint(20) UNSIGNED NOT NULL,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `posts`
--

INSERT INTO `posts` (`id`, `title`, `slug`, `image_post`, `image_post_name`, `content`, `status`, `category_post_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(2, 'Cách tham gia khuyến mãi của trương tình săn sale điện thoại', 'cach-tham-gia-khuyen-mai-cua-truong-tinh-san-sale-dien-thoai', 'public/uploads/post/1\\dequy.PNG', 'dequy.PNG', '<p>C&aacute;ch tham gia khuyến m&atilde;i của trương t&igrave;nh săn sale điện thoại ch&uacute;c c&aacute;c bạn tham gia m&aacute;y mắn</p>', 1, 3, 1, '2021-08-01 07:06:23', '2021-08-15 07:07:59', '2021-08-15 07:07:59'),
(3, 'Cách hiển thị tệp và thư mục ẩn trên Windows 11', 'cach-hien-thi-tep-va-thu-muc-an-tren-windows-11', 'public/uploads/post/1\\show-hidden-files-in-windows-11-17.jpg', 'show-hidden-files-in-windows-11-17.jpg', '<p>Tệp ẩn l&agrave; những tệp kh&ocirc;ng được liệt k&ecirc; trong <a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-sua-loi-file-explorer-tren-windows-10-khong-phan-hoi-133347\">File Explorer</a>&nbsp;ở chế độ hiển thị b&igrave;nh thường tr&ecirc;n m&aacute;y t&iacute;nh. Đ&acirc;y c&oacute; thể l&agrave; c&aacute;c tệp đơn giản hoặc tệp hệ thống cốt l&otilde;i. C&aacute;c tệp hệ thống được ẩn để đảm bảo rằng kh&ocirc;ng c&oacute; thay đổi n&agrave;o được thực hiện từ ph&iacute;a người d&ugrave;ng. Di chuyển hoặc x&oacute;a một tệp hệ thống c&oacute; thể ảnh hưởng đến c&aacute;c chức năng quan trọng của hệ thống v&agrave; trong trường hợp xấu nhất, khiến n&oacute; kh&ocirc;ng thể sử dụng được.</p>\r\n<p>Tuy nhi&ecirc;n, đ&ocirc;i khi bạn c&oacute; thể cần phải xem hoặc truy cập c&aacute;c tệp ẩn, trong trường hợp đ&oacute; h&atilde;y thực hiện theo hướng dẫn dưới đ&acirc;y.&nbsp;</p>\r\n<h2><strong>Hiển thị c&aacute;c mục bị ẩn từ menu View trong File Explorer</strong></h2>\r\n<p>Để hiển thị c&aacute;c tệp ẩn trong File Explorer, truy cập v&agrave;o menu&nbsp;<em><strong>View</strong></em>&nbsp;tr&ecirc;n thanh lệnh.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-image-1.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (1)\" /></p>\r\n<p>Trong menu xổ xuống chọn t&ugrave;y chọn&nbsp;<em><strong>Show</strong></em>, sau đ&oacute; chọn tiếp mục&nbsp;<em><strong>Hidden items.</strong></em></p>\r\n<p><em><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-4.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (2)\" /></strong></em></p>\r\n<p>C&aacute;c tệp ẩn tr&ecirc;n to&agrave;n hệ thống b&acirc;y giờ sẽ được hiển thị. Bạn sẽ thấy rằng biểu tượng c&aacute;c tệp ẩn hơi mờ hoặc mờ, do đ&oacute; c&oacute; thể ph&acirc;n biệt với c&aacute;c tệp v&agrave; thư mục kh&aacute;c hiện c&oacute;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-3.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (3)\" /></p>\r\n<h2><strong>Hiển thị c&aacute;c tệp v&agrave; thư mục ẩn từ t&ugrave;y chọn File Explorer</strong></h2>\r\n<p>Để hiển thị c&aacute;c tệp ẩn trong File Explorer, h&atilde;y nhấp v&agrave;o biểu tượng&nbsp;<em><strong>More (biểu tượng ba chấm)</strong></em>&nbsp;b&ecirc;n phải menu View v&agrave; chọn&nbsp;<em><strong>Options</strong></em>&nbsp;từ tr&igrave;nh đơn thả xuống.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-4.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (4)\" /></p>\r\n<p>Trong hộp thoại hiển thị, chọn thẻ&nbsp;<em><strong>View</strong></em>, tiếp theo k&iacute;ch chọn mục&nbsp;<em><strong>Hidden files and folders</strong></em>, trong menu con xổ xuống k&iacute;ch t&ugrave;y chọn&nbsp;<em><strong>Show hidden files, folders, and drive</strong></em>s, sau đ&oacute; bấm n&uacute;t&nbsp;<em><strong>OK</strong></em>&nbsp;để lưu c&aacute;c thay đổi v&agrave; đ&oacute;ng cửa sổ.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-5.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (5)\" /></p>\r\n<p>Thao t&aacute;c n&agrave;y sẽ hiển thị c&aacute;c tệp, thư mục v&agrave; ổ đĩa ẩn trong File Explorer. Tuy nhi&ecirc;n, nếu muốn xem c&aacute;c tệp hệ thống ẩn bạn cũng c&oacute; thể bật t&ugrave;y chọn n&agrave;y từ c&aacute;c t&ugrave;y chọn trong File Explorer.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-6.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (6)\" /></p>\r\n<p><strong>Để xem c&aacute;c tệp hệ thống ẩn</strong>, vẫn tr&ecirc;n thẻ&nbsp;<em>View</em>&nbsp;của cửa sổ&nbsp;<em>Folder Option</em>s, k&iacute;ch bỏ t&ugrave;y chọn<em><strong>&nbsp;Hide protected operating system files (Recommended)</strong></em>&nbsp;dưới khung&nbsp;<em>Advanced settings.</em></p>\r\n<p><em><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-7.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (7)\" /></em></p>\r\n<p>Một hộp thoại x&aacute;c nhận hiển thị cho biết c&aacute;c tệp n&agrave;y rất quan trọng đối với hoạt động của Windows v&agrave; việc chỉnh sửa hoặc x&oacute;a ch&uacute;ng c&oacute; thể khiến hệ thống kh&ocirc;ng sử dụng được. Bấm n&uacute;t&nbsp;<em><strong>Yes</strong></em>&nbsp;để tiếp tục.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-8.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (8)\" /></p>\r\n<p>Cuối c&ugrave;ng, bấm n&uacute;t&nbsp;<em><strong>OK</strong></em>&nbsp;để lưu c&aacute;c thay đổi. B&acirc;y giờ tất cả c&aacute;c tệp ẩn của hệ điều h&agrave;nh sẽ được hiển thị.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-9.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (9)\" /></p>\r\n<p><em>Lưu &yacute;:&nbsp;</em>Ch&uacute;ng t&ocirc;i khuy&ecirc;n bạn kh&ocirc;ng n&ecirc;n thực hiện bất kỳ thay đổi n&agrave;o đối với c&aacute;c tệp hệ thống quan trọng hoặc x&oacute;a ch&uacute;ng trừ khi hiểu r&otilde;. Ngo&agrave;i ra, n&ecirc;n ẩn ch&uacute;ng trở lại sau khi bạn đ&atilde; ho&agrave;n th&agrave;nh c&ocirc;ng việc để tr&aacute;nh người kh&aacute;c x&oacute;a nhầm.</p>\r\n<h2><strong>Hiển thị c&aacute;c tệp v&agrave; thư mục ẩn từ Registry Editor</strong></h2>\r\n<p>Như trường hợp của c&aacute;c c&agrave;i đặt kh&aacute;c, bạn cũng c&oacute; thể hiển thị c&aacute;c tệp v&agrave; thư mục ẩn bằng c&aacute;ch sửa đổi một v&agrave;i gi&aacute; trị dữ liệu trong&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/gioi-thieu-ve-registry-tren-windows-10-125617\">Registry Editor</a>.&nbsp;</p>\r\n<p><strong>Bước 1:</strong>&nbsp;Bấm&nbsp;<em><strong>Windows + R</strong></em>&nbsp;để k&iacute;ch hoạt&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/tat-tan-tat-cac-cau-lenh-tu-hop-thoai-run-trong-may-tinh-windows-47137\">hộp thoại Run</a>, sau đ&oacute; nhập&nbsp;<em><strong>regedit&nbsp;</strong></em>v&agrave;o khung trống v&agrave; bấm n&uacute;t&nbsp;<em><strong>OK.</strong></em></p>\r\n<p><em><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-10.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (10)\" /></strong></em></p>\r\n<p><strong>Bước 2:</strong>&nbsp;Trong cửa sổ Registry Editor hiển thị, truy cập v&agrave;o đường dẫn giống như tr&ecirc;n thanh địa chỉ của h&igrave;nh dưới đ&acirc;y.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-11.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (11)\" /></p>\r\n<p>Ở khung b&ecirc;n phải, bạn t&igrave;m kh&oacute;a&nbsp;<em><strong>Hidden</strong></em>&nbsp;v&agrave; nhấp đ&uacute;p v&agrave;o n&oacute;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-12.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (12)\" /></p>\r\n<p><em>Lưu &yacute;:&nbsp;</em>Nếu bạn kh&ocirc;ng t&igrave;m thấy kh&oacute;a Hidden, h&atilde;y k&iacute;ch chuột phải v&agrave;o bất kỳ đ&acirc;u tr&ecirc;n kh&ocirc;ng gian trống, trong menu hiển thị chọn&nbsp;<strong><em>New &gt; DWORD (32-bit) Value</em></strong>&nbsp;v&agrave; đặt t&ecirc;n kh&oacute;a mới vừa tạo l&agrave; Hidden.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-13.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (13)\" /></p>\r\n<p>Trong hộp thoại hiển thị, thay đổi gi&aacute; trị dưới khung Value data&nbsp;<em><strong>từ 2 th&agrave;nh 1</strong></em>. Mặc định gi&aacute; trị l&agrave; 2 c&oacute; nghĩa c&aacute;c tệp ẩn sẽ kh&ocirc;ng được hiển thị, trong khi thay đổi n&oacute; th&agrave;nh 1 sẽ hiển thị c&aacute;c tệp ẩn. Bấm n&uacute;t OK để lưu c&aacute;c thay đổi.</p>\r\n<p>Để xem c&aacute;c tệp hệ thống ẩn, h&atilde;y t&igrave;m kh&oacute;a&nbsp;<em><strong>ShowSuperHidden&nbsp;</strong></em>ở c&ugrave;ng một vị tr&iacute; v&agrave; nhấp đ&uacute;p v&agrave;o n&oacute;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-14.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (14)\" /></p>\r\n<p><em>Lưu &yacute;:&nbsp;</em>Nếu kh&ocirc;ng t&igrave;m thấy kh&oacute;a ShowSuperHidden, bạn c&oacute; thể tạo mới theo c&aacute;ch tương tự, k&iacute;ch chuột phải v&agrave; chọn&nbsp;<em><strong>New &gt; DWORD (32-bit) Value</strong></em>&nbsp;để tạo kh&oacute;a mới v&agrave; đặt t&ecirc;n l&agrave; ShowSuperHidden.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137972/Originals/show-hidden-files-in-windows-11-15.jpg\" alt=\"C&aacute;ch hiển thị tệp v&agrave; thư mục ẩn tr&ecirc;n Windows 11 (15)\" /></p>\r\n<p>B&acirc;y giờ thay đổi gi&aacute; trị trong khung Value data&nbsp;<em><strong>từ 0 th&agrave;nh 1</strong></em>. Khi gi&aacute; trị l&agrave; 0 th&igrave; c&aacute;c tệp hệ thống vẫn bị ẩn, trong khi thay đổi th&agrave;nh 1 sẽ hiển thị c&aacute;c tệp hệ thống. Bấm n&uacute;t OK ở dưới c&ugrave;ng để lưu c&aacute;c thay đổi. C&aacute;c thay đổi sẽ được thực hiện ngay lập tức trong File Explorer. Trong trường hợp bạn kh&ocirc;ng thấy hiển thị, h&atilde;y đ&oacute;ng v&agrave; khởi chạy lại File Explorer.</p>\r\n<p>Với c&aacute;c phương ph&aacute;p tr&ecirc;n, bạn c&oacute; thể dễ d&agrave;ng hiển thị kh&ocirc;ng chỉ c&aacute;c tệp v&agrave; thư mục th&ocirc;ng thường m&agrave; cả c&aacute;c tệp hệ thống trong File Explorer tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-nang-cap-len-windows-11-tu-windows-10-136464\">Windows 11</a>.&nbsp;</p>', 1, 2, 1, '2021-08-01 20:19:01', '2021-08-15 02:59:19', NULL),
(6, 'FPT Shop giảm thêm 3% cho khu vực Đà Nẵng và Khánh Hòa', 'fpt-shop-giam-them-3-cho-khu-vuc-da-nang-va-khanh-hoa', 'public/uploads/post/1\\nf_1200x628-2x.jpg', 'nf_1200x628-2x.jpg', '<p>Theo đ&oacute;, kh&aacute;ch h&agrave;ng tại TP. Đ&agrave; Nẵng v&agrave; tỉnh Kh&aacute;nh H&ograve;a sẽ được giảm ngay 3% khi chọn mua điện thoại, sản phẩm Apple, laptop,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>, smartwatch tại FPT Shop. Kh&ocirc;ng dừng lại ở đ&oacute;, bạn vẫn được hưởng những ưu đ&atilde;i hiện c&oacute; tại FPT Shop như: c&aacute;c chương tr&igrave;nh hotsale, chương tr&igrave;nh khuyến mại &ldquo;Vượt m&ugrave;a dịch, giảm kịch s&agrave;n&rdquo;, gi&aacute; đặc biệt cho c&aacute;c khu vực đang &aacute;p dụng Chỉ thị 16, ưu đ&atilde;i Back To School d&agrave;nh cho laptop,&hellip; B&ecirc;n cạnh đ&oacute;, FPT Shop sẽ giao h&agrave;ng miễn ph&iacute; để bạn c&oacute; thể dễ d&agrave;ng sở hữu ngay c&aacute;c thiết bị c&ocirc;ng nghệ phục vụ cho việc học tập, l&agrave;m việc tại nh&agrave;, cập nhật tin tức, duy tr&igrave; li&ecirc;n lạc với người th&acirc;n ở khắp mọi nơi. Để chọn mua sản phẩm, bạn c&oacute; thể đặt h&agrave;ng online tr&ecirc;n<a href=\"https://fptshop.com.vn/\" target=\"_blank\" rel=\"noopener\">&nbsp;website FPT Shop</a>, tr&ecirc;n&nbsp;<a href=\"https://www.facebook.com/FPTShopOnline\" rel=\"nofollow\">fanpage</a>&nbsp;v&agrave; đặt cọc 500.000 đồng hoặc gọi hotline miễn ph&iacute; cước 1800 6601 để được tư vấn nhanh nhất.&nbsp;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/138013/Originals/NF_1200x628%402x.png\" alt=\"Giam 3 DN KH\" width=\"600\" height=\"314\" /></p>\r\n<p><em>Từ ng&agrave;y 16 - 22/8, kh&aacute;ch h&agrave;ng tại Đ&agrave; Nẵng v&agrave; Kh&aacute;nh H&ograve;a sẽ được giảm th&ecirc;m 3% khi chọn mua c&aacute;c thiết bị c&ocirc;ng nghệ tại FPT Shop</em></p>\r\n<p>Kh&ocirc;ng chỉ được giao h&agrave;ng tận nh&agrave; miễn ph&iacute;, c&aacute;ch thức thanh to&aacute;n rất đa dạng, bạn c&oacute; thể lựa chọn thanh to&aacute;n trực tiếp khi nhận h&agrave;ng hoặc thanh to&aacute;n online qua thẻ, hạn chế tiếp x&uacute;c với tiền mặt để tr&aacute;nh l&acirc;y nhiễm bệnh.&nbsp;</p>\r\n<p>Ngo&agrave;i ra, chọn mua sản phẩm c&ocirc;ng nghệ tại FPT Shop, bạn c&ograve;n được hưởng c&aacute;c ưu đ&atilde;i thiết thực kh&aacute;c như: giảm đến 300.000 đồng khi chọn thanh to&aacute;n to&agrave;n bộ đơn h&agrave;ng qua VNPAY-QR, v&iacute; Moca, bảo h&agrave;nh 2 năm khi mua&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12\">iPhone 12</a>&nbsp;Series, trả g&oacute;p 0% l&atilde;i suất m&agrave; kh&ocirc;ng cần trả trước bằng thẻ t&iacute;n dụng v&agrave; trả g&oacute;p 0% l&atilde;i suất qua c&ocirc;ng ty t&agrave;i ch&iacute;nh với khoản trả trước chỉ từ 30% gi&aacute; m&aacute;y,&hellip; Hơn nữa, bạn c&ograve;n được tham gia chương tr&igrave;nh &ldquo;Thu cũ đổi mới&rdquo; với mức trợ gi&aacute; đến 15% d&agrave;nh cho một số thương hiệu smartphone như&nbsp;iPhone, Samsung, OPPO v&agrave; vivo.</p>\r\n<p>Kh&ocirc;ng chỉ nhận những ưu đ&atilde;i hấp dẫn nhất, kh&aacute;ch h&agrave;ng chọn mua sản phẩm c&ocirc;ng nghệ tại FPT Shop c&ograve;n c&oacute; thể y&ecirc;n t&acirc;m về chất lượng cũng như ch&iacute;nh s&aacute;ch bảo h&agrave;nh tại đ&acirc;y. Tất cả sản phẩm tại FPT Shop sẽ được bảo h&agrave;nh theo IMEI n&ecirc;n rất ch&iacute;nh x&aacute;c v&agrave; thuận tiện, bạn sẽ kh&ocirc;ng phải lo bị mất giấy tờ, h&oacute;a đơn. Bạn sẽ được miễn ph&iacute; một đổi một trong 30 ng&agrave;y nếu sản phẩm bạn mua ph&aacute;t sinh lỗi do nh&agrave; sản xuất.</p>', 1, 1, 1, '2021-08-15 02:33:25', '2021-08-15 02:33:25', NULL),
(7, 'FPT Shop tung 1000 suất giảm 2 triệu đồng cho khách hàng đặt mua sớm Galaxy Z Fold3 | Flip3 5G', 'fpt-shop-tung-1000-suat-giam-2-trieu-dong-cho-khach-hang-dat-mua-som-galaxy-z-fold3-flip3-5g', 'public/uploads/post/1\\z-fold-flip-3-1000-suat-thumb.jpg', 'z-fold-flip-3-1000-suat-thumb.jpg', '<p>Theo đ&oacute;, 1000 suất qu&agrave; đặc biệt n&agrave;y bao gồm: phiếu mua h&agrave;ng phụ kiện trị gi&aacute; 3 triệu đồng, giảm 2 triệu đồng khi thanh to&aacute;n to&agrave;n bộ gi&aacute; m&aacute;y qua VNPAY-QR v&agrave;o ng&agrave;y nhận h&agrave;ng, bao da k&egrave;m S Pen cho Z Fold3.</p>\r\n<p><br />Để được hưởng ưu đ&atilde;i giảm 2 triệu đồng, bạn chỉ cần thao t&aacute;c theo c&aacute;c bước sau:</p>\r\n<ul>\r\n<li>Bước 1: Truy cập đường link đặt trước&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-flip-3\" target=\"_blank\" rel=\"noopener\">tại đ&acirc;y</a></li>\r\n<li>Bước 2: Chọn \"ĐẶT GẠCH NGAY\"</li>\r\n<li>Bước 3: Chọn sản phẩm</li>\r\n<li>Bước 4: Nhập th&ocirc;ng tin v&agrave; chọn \"Đồng &yacute; nhận khuyến mại giảm th&ecirc;m 2 triệu\". Số tiền cọc 3 triệu, khi nhận m&aacute;y thanh to&aacute;n 100% qua VNPAY-QR.</li>\r\n<li>Bước 5: Thanh to&aacute;n tiền cọc&nbsp;3 triệu đồng&nbsp;bằng h&igrave;nh thức online v&agrave; ho&agrave;n tất đặt h&agrave;ng.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/138008/Originals/VNPAY-Datcoc.jpg\" alt=\"Man hinh dat coc Z 2021\" width=\"600\" height=\"337\" /></p>\r\n<ul>\r\n<li>Bước 6: Đến ng&agrave;y nhận m&aacute;y Qu&yacute; kh&aacute;ch thanh to&aacute;n 100% gi&aacute; trị m&aacute;y qua VNPAY-QR v&agrave; nhập m&atilde; khuyến mại do nh&acirc;n vi&ecirc;n cửa h&agrave;ng cung cấp sẽ nhận ngay ưu đ&atilde;i giảm th&ecirc;m 2 triệu.</li>\r\n</ul>\r\n<h3><strong>Một v&agrave;i ưu &yacute; nhỏ d&agrave;nh cho bạn:</strong></h3>\r\n<ul>\r\n<li>Ưu đ&atilde;i giảm gi&aacute; khi thanh to&aacute;n qua VNPAY-QR &aacute;p dụng đối với c&aacute;c ng&acirc;n h&agrave;ng: Agribank, BIDV, VietinBank, Vietcombank, SCB, IVB, VPBank, ABBank, Eximbank, HDBank, NCB, Nam A Bank, Vietbank, Shinhan bank, BIDC bank. Số lượng Ng&acirc;n h&agrave;ng cập nhật mới&nbsp;<a href=\"https://coupons.vnpay.vn/\" rel=\"nofollow\">tại đ&acirc;y</a>.</li>\r\n<li>Chương tr&igrave;nh được nhập m&atilde; khuyến mại tối đa 1 lần/th&aacute;ng.</li>\r\n<li>Số tiền khuyến mại sẽ được l&agrave;m tr&ograve;n theo đơn vị: ngh&igrave;n đồng. Cụ thể, &lt;500 đồng l&agrave;m tr&ograve;n xuống 0 đồng; &gt;=500 đồng l&agrave;m tr&ograve;n l&ecirc;n 1.000 đồng.</li>\r\n<li>Kh&ocirc;ng giới hạn số lần kh&aacute;ch h&agrave;ng được khuyến mại trong suốt thời gian diễn ra chương tr&igrave;nh.</li>\r\n<li>Kh&ocirc;ng &aacute;p dụng t&aacute;ch h&oacute;a đơn dưới mọi h&igrave;nh thức hoặc ho&agrave;n tiền một phần với c&aacute;c giao dịch đ&atilde; hưởng khuyến mại (kh&ocirc;ng &aacute;p dụng trả h&agrave;ng theo nhu cầu).</li>\r\n<li>Chỉ &aacute;p dụng cho kh&aacute;ch lẻ, kh&ocirc;ng &aacute;p dụng cho kh&aacute;ch mua số lượng.</li>\r\n<li>Kh&aacute;ch h&agrave;ng cần thanh to&aacute;n 100% gi&aacute; trị đơn h&agrave;ng qua VNPAY-QR (kh&ocirc;ng &aacute;p dụng được cho c&aacute;c h&igrave;nh thức trả g&oacute;p, F-Friend, hay c&aacute;c h&igrave;nh thức trả trước một phần). Kh&aacute;ch h&agrave;ng tham gia thu cũ đổi mới nếu số tiền thanh to&aacute;n 100% từ 15.000.000 đồng trở l&ecirc;n vẫn được tham gia chương tr&igrave;nh.</li>\r\n</ul>', 1, 1, 1, '2021-08-15 02:34:43', '2021-08-15 02:34:43', NULL),
(8, 'Cách thức nhận ưu đãi giảm đến 6,4 triệu đồng khi lên đời Galaxy Z Fold3 | Flip3 5G tại FPT Shop', 'cach-thuc-nhan-uu-dai-giam-den-64-trieu-dong-khi-len-doi-galaxy-z-fold3-flip3-5g-tai-fpt-shop', 'public/uploads/post/1\\z-fold-flip-3-uudai-6.jpg', 'z-fold-flip-3-uudai-6.jpg', '<h3><img style=\"font-size: 14px; display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137935/Originals/Z-Fold-Flip-3-UuDai-6.jpg\" alt=\"VC Note20 Series\" width=\"600\" height=\"312\" /></h3>\r\n<div class=\"post__content\">\r\n<p>Theo đ&oacute;, những kh&aacute;ch h&agrave;ng đang sở hữu&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-note-20\">Galaxy Note 20</a>&nbsp;Series c&oacute; ng&agrave;y bảo h&agrave;nh cuối c&ugrave;ng nằm trong khoảng từ ng&agrave;y 20/8/2021 đến 29/9/2021 sẽ được nhận voucher trừ thẳng v&agrave;o gi&aacute; b&aacute;n khi l&ecirc;n đời&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-fold3\">Galaxy Z Fold 3 5G</a>&nbsp;|&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-flip-3\">Z Flip 3 5G</a>&nbsp;tại FPT Shop.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137935/Originals/Frame%20286.png\" alt=\"Dac quyen nang doi Note20\" width=\"600\" height=\"331\" /></p>\r\n<h3><strong>Để nhận ưu đ&atilde;i n&agrave;y, bạn c&oacute; thể thực hiện theo 1 trong 3 c&aacute;ch thức sau:</strong></h3>\r\n<ol>\r\n<li>Gọi ngay đến hotline miễn ph&iacute; cước 18006601 để được hướng dẫn cụ thể.</li>\r\n<li>Để lại th&ocirc;ng tin của bạn bao gồm: Họ t&ecirc;n - SĐT - Email (Nếu c&oacute;), FPT Shop sẽ li&ecirc;n hệ ngay.<a class=\"btn-upgrade btn-booking js--open-popup\">ĐĂNG K&Yacute; NGAY</a></li>\r\n<li>Đến trực tiếp tại hơn 600 cửa h&agrave;ng FPT Shop tr&ecirc;n to&agrave;n quốc để được tư vấn chi tiết.</li>\r\n</ol>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137935/Originals/Z-Fold-Flip-3-GiaDuKien.jpg\" alt=\"Bang gia du kien Z 2021\" width=\"600\" height=\"750\" /></p>\r\n<div class=\"popup\">\r\n<div class=\"popup-overlay js--close-popup\">&nbsp;</div>\r\n<div class=\"popup-wrapper\">\r\n<div class=\"popup-box popup-signup\">\r\n<div class=\"popup-heading\">&nbsp;</div>\r\n<div class=\"popup-body\">\r\n<div class=\"product-dropdown product-have\">\r\n<div class=\"dropdown\">\r\n<div class=\"dropdown-button\">&nbsp;</div>\r\n<div class=\"dropdown-menu\">\r\n<div class=\"dropdown-menu-wrapper\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"new-product\">\r\n<div class=\"product-list js--popup-wrapper\">&nbsp;</div>\r\n</div>\r\n<div class=\"line\">&nbsp;</div>\r\n<ul class=\"gender js--popup-wrapper\">\r\n<li class=\"active gender1\" data-gender=\"true\">&nbsp;</li>\r\n<li class=\"gender2\" data-gender=\"false\">&nbsp;</li>\r\n</ul>\r\n<form id=\"checkValidate\" action=\"https://fptshop.com.vn/tin-tuc/tin-khuyen-mai/cach-thuc-nhan-uu-dai-giam-den-6-4-trieu-dong-khi-len-doi-galaxy-z-fold3-or-flip3-5g-tai-fpt-shop-137935\" novalidate=\"novalidate\">\r\n<div class=\"input-box\">\r\n<div class=\"box-name\"><input id=\"name\" class=\"customer-name\" name=\"name\" type=\"text\" placeholder=\"Nhập họ v&agrave; t&ecirc;n*\" /></div>\r\n<div class=\"box-phone\"><input id=\"phone\" class=\"customer-phone\" name=\"phone\" type=\"text\" placeholder=\"Nhập số điện thoại*\" /></div>\r\n<div class=\"box-mail\"><input id=\"email\" class=\"customer-mail\" name=\"email\" type=\"text\" placeholder=\"Nhập email\" /></div>\r\n</div>\r\n<div class=\"btn-box\"><input class=\"btn-booking\" type=\"submit\" value=\"ĐĂNG K&Yacute; TH&Ocirc;NG TIN\" /></div>\r\n</form></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 1, 1, 1, '2021-08-15 02:37:25', '2021-08-15 02:37:25', NULL),
(9, 'Chọn mua laptop, Macbook, FPT Shop tặng ưu đãi 100.000 đồng dùng để mua phần mềm Office', 'chon-mua-laptop-macbook-fpt-shop-tang-uu-dai-100000-dong-dung-de-mua-phan-mem-office', 'public/uploads/post/1\\a-nh_viber_2021-08-12_10-54-13-679.jpg', 'a-nh_viber_2021-08-12_10-54-13-679.jpg', '<p>Theo đ&oacute;, từ h&ocirc;m nay đến hết ng&agrave;y 30/9/2021, FPT Shop tặng phiếu mua h&agrave;ng trị gi&aacute; 100.000 đồng d&ugrave;ng để mua phần mềm Microsoft 365 Personal/Family/Home &amp; Student cho kh&aacute;ch h&agrave;ng mua sản phẩm&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/apple-macbook\">Macbook</a>.&nbsp;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137914/Originals/a%CC%89nh_Viber_2021-08-12_10-54-13-679.jpg\" alt=\"\" width=\"600\" height=\"337\" /></p>\r\n<p><em>FPT Shop tặng phiếu mua h&agrave;ng trị gi&aacute; 100.000 đồng d&ugrave;ng để mua phần mềm Microsoft 365 Personal/Family/Home &amp; Student cho kh&aacute;ch h&agrave;ng mua sản phẩm laptop, Macbook.&nbsp;</em></p>\r\n<p>Sở hữu phần mềm Microsoft Office bản quyền tại FPT Shop sẽ gi&uacute;p bạn v&agrave; gia đ&igrave;nh y&ecirc;n t&acirc;m hơn khi sử dụng. Đồng thời, trong qu&aacute; tr&igrave;nh sử dụng, bạn sẽ được cập nhật trực tiếp từ Microsoft, phần mềm n&agrave;y khả dụng tr&ecirc;n tất cả c&aacute;c thiết bị như&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>, PC, laptop.</p>\r\n<p>Hơn thế nữa, khi đăng k&yacute;, bạn sẽ được tặng dung lượng lưu trữ đ&aacute;m m&acirc;y OneDrive, một dịch vụ lưu trữ đ&aacute;m m&acirc;y của Microsoft. OneDrive c&oacute; thể d&ugrave;ng để lưu trữ c&aacute;c file, folders, ảnh, video&hellip; trực tuyến thay v&igrave; lưu trữ tr&ecirc;n m&aacute;y t&iacute;nh. Do đ&oacute;, bạn c&oacute; thể dễ d&agrave;ng truy cập v&agrave; chia sẻ dữ liệu được lưu trữ tr&ecirc;n OneDrive ở thiết bị bất kỳ n&agrave;o như&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>, m&aacute;y t&iacute;nh bảng hay m&aacute;y t&iacute;nh kh&aacute;c.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, việc sử dụng phần mềm ch&iacute;nh h&atilde;ng từ FPT Shop sẽ gi&uacute;p kh&aacute;ch h&agrave;ng hạn chế rủi ro mất dữ liệu, tập tin hoặc th&ocirc;ng tin c&aacute;c nh&acirc;n quan trọng. Th&ecirc;m v&agrave;o đ&oacute;, sản phẩm c&ograve;n mang lại nhiều tiện &iacute;ch hơn nhiều so với với t&agrave;i khoản Microsoft miễn ph&iacute;. B&ecirc;n cạnh phi&ecirc;n bản web, Microsoft ch&iacute;nh h&atilde;ng cho ph&eacute;p người d&ugrave;ng sẽ được c&agrave;i đặt đầy đủ ứng dụng cao cấp Word, Excel, PowerPoint v&agrave; Outlook tr&ecirc;n MacOS hoặc Windows 10,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang/apple-ipad\">iPad</a>&nbsp;v&agrave; Android.&nbsp;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137914/Originals/Branding-SyniTrang-%20(59).jpeg\" alt=\"\" width=\"600\" height=\"400\" /></p>\r\n<p><em>FPT Shop tặng ngay bảo h&agrave;nh đến 3 năm, giảm gi&aacute; đến 15% khi trả thẳng, trả g&oacute;p 0% cho học sinh, sinh vi&ecirc;n mua laptop, Macbook.</em></p>\r\n<p>Ngo&agrave;i ra, đối với kh&aacute;ch h&agrave;ng l&agrave; học sinh, t&acirc;n sinh vi&ecirc;n, sinh vi&ecirc;n, FPT Shop c&ograve;n tặng th&ecirc;m nhiều ưu đ&atilde;i hấp dẫn nhất th&ocirc;ng qua chuỗi hoạt động thường ni&ecirc;n \"Back to school\". Cụ thể, chọn mua laptop v&agrave; MacBook c&oacute; gi&aacute; từ 10.000.000 đồng trở l&ecirc;n, FPT Shop tặng ngay đặc quyền bảo h&agrave;nh đến 3 năm. Đồng thời, kh&aacute;ch h&agrave;ng c&ograve;n được giảm gi&aacute; đến 15% khi trả thẳng hoặc t&ugrave;y chọn trả g&oacute;p 0% l&atilde;i suất.</p>\r\n<p>Hơn nữa, FPT Shop c&ograve;n tặng th&ecirc;m nhiều phần qu&agrave; thiết thực d&agrave;nh cho kh&aacute;ch h&agrave;ng mua Laptop tại hệ thống như:&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/balo-tui-xach\">balo Laptop thời trang</a>, giảm đến 35% m&agrave;n h&igrave;nh LCD Samsung khi mua k&egrave;m Laptop hoặc MacBook, giảm th&ecirc;m 200.000 đồng khi mua&nbsp;<a href=\"https://fptshop.com.vn/may-in\">m&aacute;y in</a>&nbsp;Brother, giảm đến 300.000 đồng khi mua g&oacute;i bảo h&agrave;nh rơi vỡ, v&agrave;o nước&hellip; Th&ocirc;ng tin tiết chương tr&igrave;nh c&oacute;&nbsp;<a href=\"https://fptshop.com.vn/tiep-suc-mua-thi\">tại đ&acirc;y</a>.&nbsp;</p>\r\n<p>Để chọn mua sản phẩm với nhiều ưu đ&atilde;i nhất, bạn c&oacute; thể đến trực tiếp 700 cửa h&agrave;ng FPT Shop tr&ecirc;n to&agrave;n quốc, mua online tại website FPT Shop hoặc gọi hotline miễn cước 1800 6601 để được tư vấn nhanh. FPT Shop lu&ocirc;n c&oacute; chương tr&igrave;nh giao h&agrave;ng v&agrave; c&agrave;i đặt miễn ph&iacute; tại nh&agrave;, hỗ trợ online từ xa, gi&uacute;p bạn khắc phục những vấn đề kỹ thuật một c&aacute;ch nhanh ch&oacute;ng nhất.</p>', 1, 1, 1, '2021-08-15 02:40:56', '2021-08-15 02:40:56', NULL),
(10, 'FPT Shop giảm thêm đến 100.000 đồng cho khách hàng thanh toán qua cổng VNPAY-QR', 'fpt-shop-giam-them-den-100000-dong-cho-khach-hang-thanh-toan-qua-cong-vnpay-qr', 'public/uploads/post/1\\vnpay-thang-8-1.jpg', 'vnpay-thang-8-1.jpg', '<div class=\"post__content\">\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/104966/Originals/VNPAY%20THANG%208%201.png\" alt=\"\" width=\"600\" height=\"315\" /></p>\r\n<p><strong>1. Đối tượng tham gia:&nbsp;</strong>Tất cả kh&aacute;ch h&agrave;ng chọn mua sản phẩm tại FPT Shop v&agrave; thanh to&aacute;n qua cổng VNPAY-QR</p>\r\n<p><strong>2. Thời gian:&nbsp;</strong>Từ 05/8 đến 31/08/2021</p>\r\n<p><strong>3. Ưu đ&atilde;i:&nbsp;</strong>Được ho&agrave;n tiền 3%, tối đa 100.000 đồng cho kh&aacute;ch h&agrave;ng thanh to&aacute;n qua VNPAY-QR, &aacute;p dụng với c&aacute;c sản phẩm:&nbsp;điện thoại,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>, laptop,&nbsp;<a href=\"https://fptshop.com.vn/phu-kien\">Apple</a>,&nbsp;<a href=\"https://fptshop.com.vn/phu-kien\">phụ kiện</a>, dịch vụ như SIM/phần mềm/FPT Play Box... (trừ thẻ nạp c&aacute;c loại)</p>\r\n<p><strong>4. C&aacute;ch thức tham gia:</strong></p>\r\n<p>Bước 1: Đăng nhập ứng dụng Mobile Banking của ng&acirc;n h&agrave;ng, chọn t&iacute;nh năng QR Pay</p>\r\n<p>Bước 2: Qu&eacute;t m&atilde; QR tr&ecirc;n h&oacute;a đơn thanh to&aacute;n của bạn</p>\r\n<p>Bước 3: Nhập m&atilde; khuyến mạị&nbsp;<strong>QR100</strong>, x&aacute;c minh v&agrave; ho&agrave;n tất giao dịch</p>\r\n<p><strong>5. Lưu &yacute;:</strong></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh &aacute;p dụng đối với c&aacute;c ng&acirc;n h&agrave;ng: Agribank, BIDV, VietinBank, Vietcombank, SCB, IVB, VPBank, ABBank, Eximbank, HDBank, NCB, Nam A Bank, Vietbank, Shinhan bank, BIDC bank. Số lượng ng&acirc;n h&agrave;ng cập nhật mới&nbsp;<a href=\"https://vnpay.vn/khuyen-mai-vnpayqr/\" rel=\"nofollow\">tại đ&acirc;y</a>.</li>\r\n<li>Chương tr&igrave;nh được nhập m&atilde; khuyến mại tối đa 1 lần/th&aacute;ng (&aacute;p dụng từ ng&agrave;y 05/8).&nbsp;</li>\r\n<li>Số tiền khuyến mại sẽ được l&agrave;m tr&ograve;n theo đơn vị: ngh&igrave;n đồng. Cụ thể, &lt;500đ l&agrave;m tr&ograve;n xuống 0đ; &gt;=500đ l&agrave;m tr&ograve;n l&ecirc;n 1.000đ.</li>\r\n<li>Kh&ocirc;ng giới hạn số lần kh&aacute;ch h&agrave;ng được khuyến mại trong suốt thời gian diễn ra chương tr&igrave;nh.&nbsp;</li>\r\n<li>Kh&ocirc;ng &aacute;p dụng t&aacute;ch h&oacute;a đơn dưới mọi h&igrave;nh thức hoặc ho&agrave;n tiền một phần với c&aacute;c giao dịch đ&atilde; hưởng khuyến mại (kh&ocirc;ng &aacute;p dụng trả h&agrave;ng theo nhu cầu).&nbsp;</li>\r\n<li>Chỉ &aacute;p dụng cho kh&aacute;ch lẻ, kh&ocirc;ng &aacute;p dụng cho kh&aacute;ch mua bu&ocirc;n.&nbsp;</li>\r\n<li>Kh&aacute;ch h&agrave;ng cần thanh to&aacute;n 100% gi&aacute; trị đơn h&agrave;ng qua VNPAY-QR (kh&ocirc;ng &aacute;p dụng được cho c&aacute;c h&igrave;nh thức trả g&oacute;p, F-Friend, hay c&aacute;c h&igrave;nh thức trả trước một phần).</li>\r\n</ul>\r\n</div>', 1, 1, 1, '2021-08-15 02:45:17', '2021-08-15 02:45:17', NULL),
(11, 'Cách tạo ghi chú cho nhóm chat Zalo không phải ai cũng biết', 'cach-tao-ghi-chu-cho-nhom-chat-zalo-khong-phai-ai-cung-biet', 'public/uploads/post/1\\cach-tao-ghi-chu-nhom-zalo.jpg', 'cach-tao-ghi-chu-nhom-zalo.jpg', '<h2><strong>C&aacute;ch tạo ghi ch&uacute; nh&oacute;m Zalo tr&ecirc;n điện thoại</strong></h2>\r\n<p><strong>Bước 1:&nbsp;</strong>K&iacute;ch hoạt&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tu-van/cach-tai-zalo-va-huong-dan-cai-dat-tren-dien-thoai-5798\">ứng dụng Zalo</a>, t&igrave;m v&agrave; truy cập v&agrave;o nh&oacute;m Zalo cần tạo ghi ch&uacute;.</p>\r\n<p><strong>Bước 2:&nbsp;</strong>Bấm v&agrave;o&nbsp;<em><strong>biểu tượng dấu 3 dấu gạch ngang&nbsp;</strong></em>ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n phải để truy cập cửa sổ T&ugrave;y chọn, sau đ&oacute; bấm chọn mục&nbsp;<em><strong>Tin nhắn đ&atilde; ghim</strong></em>.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-dien-thoai-1.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (1)\" /></p>\r\n<p><strong>&nbsp;Bước 3:&nbsp;</strong>Trong cửa sổ Bảng tin nh&oacute;m, bạn&nbsp;<em><strong>bấm n&uacute;t c&oacute; biểu tượng dấu cộng</strong></em>&nbsp;ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n phải, rồi chọn t&ugrave;y chọn&nbsp;<em><strong>Ghi ch&uacute;&nbsp;</strong></em>trong menu hiển thị.</p>\r\n<p><strong>Bước 4:</strong>&nbsp;Cửa sổ Tạo mới hiển thị, bạn nhập nội dung cần th&ocirc;ng b&aacute;o cho nh&oacute;m v&agrave;o khung trống b&ecirc;n dưới. Nếu cần đ&iacute;nh k&egrave;m dữ liệu bất kỳ v&agrave;o th&ocirc;ng b&aacute;o của nh&oacute;m, bạn bấm v&agrave;o biểu tượng đ&iacute;nh k&egrave;m ở g&oacute;c ngo&agrave;i b&ecirc;n phải của mục Th&ecirc;m đ&iacute;nh k&egrave;m, ph&iacute;a tr&ecirc;n b&agrave;n ph&iacute;m.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-dien-thoai-3.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (2)\" /></p>\r\n<p>Sau đ&oacute;, quay trở ra ngo&agrave;i bạn k&iacute;ch hoạt tr&igrave;nh duyệt web, truy cập v&agrave;o trang web c&oacute; chứa t&agrave;i liệu, nhạc, ảnh&hellip; cần đ&iacute;nh k&egrave;m ghi ch&uacute;, copy đường link của dữ liệu đ&oacute;. Quay trở lại Zalo rồi bấm n&uacute;t Link t&agrave;i liệu trực tuyến,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/huong-dan-tai-nhac-ban-quyen-tren-zing-mp3-ve-may-android-42469\">Zing MP3</a>&hellip; Lập tức một hộp thoại nhỏ hiển thị với li&ecirc;n kết bạn đ&atilde; sao ch&eacute;p ở tr&ecirc;n. Nếu đ&uacute;ng địa chỉ bạn đ&atilde; sao ch&eacute;p, bấm n&uacute;t&nbsp;<em><strong>Đồng &yacute;.</strong></em></p>\r\n<p><strong>Bước 5:&nbsp;</strong>Quay trở lại cửa sổ nhập nội dung cho th&ocirc;ng b&aacute;o nh&oacute;m bạn sẽ thấy li&ecirc;n kết đ&atilde; được ch&egrave;n v&agrave;o, bấm t&ugrave;y chọn&nbsp;<em><strong>Ghim l&ecirc;n đầu tr&ograve; chuyện nh&oacute;m</strong></em>, sau đ&oacute; bấm n&uacute;t&nbsp;<em><strong>Tạo</strong></em>&nbsp;ở g&oacute;c tr&ecirc;n b&ecirc;n phải.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-dien-thoai-4.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (3)\" width=\"600\" height=\"657\" /></p>\r\n<p><strong>Bước 6:</strong>&nbsp;Bạn sẽ nhận được th&ocirc;ng b&aacute;o cho biết ghi ch&uacute; nh&oacute;m đ&atilde; được tạo. B&acirc;y giờ quay trở ra cửa sổ chat của nh&oacute;m, bạn sẽ thấy th&ocirc;ng b&aacute;o đ&atilde; được th&ecirc;m v&agrave;o ngay tr&ecirc;n đầu nh&oacute;m.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-dien-thoai-5.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (4)\" width=\"600\" height=\"496\" /></p>\r\n<p><strong>Bước 7:</strong>&nbsp;Để thay đổi t&ugrave;y chọn của ghi ch&uacute; nh&oacute;m, bạn bấm v&agrave; biểu tượng mũi t&ecirc;n xổ xuống của ghi ch&uacute; đ&oacute;.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-dien-thoai-6.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (5)\" /></p>\r\n<p>Trong menu xổ xuống c&oacute; thể chọn&nbsp;<em><strong>Chỉnh sửa</strong></em>&nbsp;để x&oacute;a hoặc tạo một ghi ch&uacute; mới. Hoặc c&oacute; thể bấm trực tiếp v&agrave;o nội dung ghi ch&uacute; để chuyển sang cửa sổ Chi tiết ghi ch&uacute;, sau đ&oacute; bấm v&agrave;o n&uacute;t c&oacute; biểu tượng ba chấm ngang ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n phải. Một menu hiển thị, bạn c&oacute; thể chọn một trong ba t&ugrave;y chọn c&oacute; sẵn gồm: Sửa để thay đổi nội dung ghi ch&uacute; hiện tại, Ghi l&ecirc;n đầu tr&ograve; chuyện nh&oacute;m hoặc X&oacute;a.</p>\r\n<h2><strong>C&aacute;ch tạo ghi ch&uacute; nh&oacute;m Zalo tr&ecirc;n m&aacute;y t&iacute;nh</strong></h2>\r\n<p>Trong trường hợp bạn&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/huong-dan-dung-zalo-bang-may-tinh-don-gian-nhat-58709\">sử dụng Zalo tr&ecirc;n m&aacute;y t&iacute;nh</a>&nbsp;l&agrave;m phương tiện li&ecirc;n lạc th&igrave; c&oacute; thể tạo ghi ch&uacute; nh&oacute;m bằng c&aacute;ch sau:</p>\r\n<p><strong>Bước 1:&nbsp;</strong>Truy cập nh&oacute;m Zalo bạn cần tạo ghi ch&uacute;, bấm n&uacute;t ba chấm ngang ngay ph&iacute;a tr&ecirc;n khung nhập nội dung chat, sau đ&oacute; chọn t&ugrave;y chọn&nbsp;<em><strong>Tạo ghi ch&uacute;</strong></em>&nbsp;trong menu hiển thị.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-may-tinh-1.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (6)\" /></p>\r\n<p><strong>Bước 2:&nbsp;</strong>Trong cửa sổ Tạo ghi ch&uacute; hiển thị, bạn nhập nội dung ghi ch&uacute; v&agrave;o khung trống b&ecirc;n dưới hoặc cũng c&oacute; thể copy nội dung từ nơi n&agrave;o đ&oacute; v&agrave; d&aacute;n v&agrave;o đ&acirc;y. Giống như tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>, bạn cũng c&oacute; thể copy v&agrave; paste c&aacute;c li&ecirc;n kết chứa t&agrave;i liệu, địa chỉ trang web, nhạc, ảnh &hellip; v&agrave;o khung nội dung ghi ch&uacute;.&nbsp;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-may-tinh-2.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (7)\" /></p>\r\n<p><strong>Bước 3:</strong>&nbsp;Cuối c&ugrave;ng bấm n&uacute;t&nbsp;<em><strong>Tạo ghi ch&uacute;</strong></em>. Quay trở ra cửa sổ chat của nh&oacute;m bạn sẽ thấy ngay ghi ch&uacute; vừa tạo được ghim l&ecirc;n đầu nh&oacute;m.</p>\r\n<p><strong>Bước 4:&nbsp;</strong>Để t&ugrave;y chỉnh ghi ch&uacute; đ&atilde; tạo, bạn bấm n&uacute;t c&oacute; biểu tượng ba chấm ngang ở ph&iacute;a ngo&agrave;i c&ugrave;ng b&ecirc;n phải của ghi ch&uacute; đ&atilde; ghim.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-may-tinh-3.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (8)\" /></p>\r\n<p>Trong menu xổ xuống cung cấp 4 t&ugrave;y chọn gồm:&nbsp;</p>\r\n<p><strong>Copy&nbsp;</strong>&ndash; Để sao ch&eacute;p lại to&agrave;n bộ nội dung của ghi ch&uacute;.</p>\r\n<p><strong>Sửa ghi ch&uacute;&nbsp;</strong>&ndash; Để quay trở lại cửa sổ ghi ch&uacute;, thay đổi nội dung ghi ch&uacute; của nh&oacute;m.</p>\r\n<p><strong>Mở bảng tin nh&oacute;m</strong>&nbsp;&ndash; T&ugrave;y chọn n&agrave;y cho ph&eacute;p xem lại tất cả c&aacute;c nội dung ghi ch&uacute; hoặc những th&ocirc;ng b&aacute;o kh&aacute;c đ&atilde; tạo tr&ecirc;n nh&oacute;m ở khung b&ecirc;n phải.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137941/Originals/cach-tao-ghi-chu-nhom-zalo-tren-may-tinh-5.jpg\" alt=\"C&aacute;ch tạo ghi ch&uacute; cho nh&oacute;m chat Zalo kh&ocirc;ng phải ai cũng biết (9)\" /></p>\r\n<p>B&acirc;y giờ, chỉ cần k&iacute;ch chuột phải l&ecirc;n ghi ch&uacute; bất kỳ trong nh&oacute;m, sau đ&oacute; chọn Ghim ghi ch&uacute; th&agrave;nh th&ocirc;ng b&aacute;o; Sửa ghi ch&uacute; hoặc X&oacute;a ghi ch&uacute; nếu muốn.</p>\r\n<p><strong>Bỏ ghim&nbsp;</strong>&ndash; T&ugrave;y chọn n&agrave;y sẽ th&ocirc;i kh&ocirc;ng ghim nội dung ghi ch&uacute; của nh&oacute;m l&ecirc;n đầu nh&oacute;m.</p>\r\n<p>Tr&ecirc;n đ&acirc;y FPTShop vừa c&ugrave;ng c&aacute;c bạn t&igrave;m hiểu c&aacute;c c&aacute;ch để tạo ghi ch&uacute; nh&oacute;m Zalo tr&ecirc;n điện thoại hoặc m&aacute;y t&iacute;nh. Điều n&agrave;y gi&uacute;p cho mọi th&agrave;nh vi&ecirc;n trong nh&oacute;m kh&ocirc;ng bỏ qua những nội dung quan trọng cần lưu &yacute;.</p>', 1, 2, 1, '2021-08-15 03:00:54', '2021-08-15 03:00:54', NULL),
(12, 'Bỏ túi ngay cách tìm điện thoại bị mất qua Facebook đơn giản, nhanh chóng', 'bo-tui-ngay-cach-tim-dien-thoai-bi-mat-qua-facebook-don-gian-nhanh-chong', 'public/uploads/post/1\\tim-dien-thoai-bi-mat-qua-fb.jpg', 'tim-dien-thoai-bi-mat-qua-fb.jpg', '<p>T&iacute;nh năng định vị tr&ecirc;n Facebook sẽ gi&uacute;p bạn thấy được vị tr&iacute; bạn truy cập v&agrave;o ứng dụng Facebook tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>, n&oacute; c&oacute; thể gi&uacute;p bạn t&igrave;m được vị tr&iacute; sau c&ugrave;ng hoặc bất cứ thời gian n&agrave;o trước đ&oacute; bạn sử dụng ứng dụng. Điều n&agrave;y sẽ gi&uacute;p cho bạn trong v&agrave;i trường hợp, trong đ&oacute; l&agrave; t&igrave;m điện thoại bị mất. B&agrave;i viết dưới đ&acirc;y, FPTShop sẽ m&aacute;ch bạn c&aacute;ch t&igrave;m điện thoại bị mất qua Facebook tr&ecirc;n điện thoại v&agrave; m&aacute;y t&iacute;nh chỉ với v&agrave;i bước đơn giản.&nbsp;</p>\r\n<h2><strong>C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook tr&ecirc;n m&aacute;y t&iacute;nh</strong></h2>\r\n<p>Tr&ecirc;n m&aacute;y t&iacute;nh,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tags/facebook\">Facebook</a>&nbsp;cũng t&iacute;ch hợp t&iacute;nh năng định vị v&agrave; cho ph&eacute;p người d&ugrave;ng xem lịch sử vị tr&iacute; của m&igrave;nh. Lịch sử vị tr&iacute; l&agrave; ri&ecirc;ng tư của mỗi t&agrave;i khoản Facebook v&agrave; bạn c&oacute; thể x&oacute;a th&ocirc;ng tin n&agrave;y bất cứ l&uacute;c n&agrave;o. Nhờ t&iacute;nh năng n&agrave;y, bạn c&oacute; thể xem lại lịch sử lần cuối sử dụng ứng dụng, c&oacute; thể gi&uacute;p bạn t&igrave;m điện thoại nếu v&ocirc; t&igrave;nh bị mất, nhưng bạn cần nhớ lần cuối truy cập để chọn mốc thời gian v&agrave; việc t&igrave;m kiếm trở n&ecirc;n dễ d&agrave;ng hơn.&nbsp;</p>\r\n<p><strong>Bước 1:&nbsp;</strong>Đăng nhập v&agrave;o Facebook của bạn bằng tr&igrave;nh duyệt bất kỳ tr&ecirc;n m&aacute;y t&iacute;nh, sau đ&oacute; ở biểu tượng&nbsp;<em><strong>h&igrave;nh tam gi&aacute;c</strong></em>, chọn&nbsp;<em><strong>C&agrave;i đặt &amp; Quyền ri&ecirc;ng tư &gt; C&agrave;i đặt.&nbsp;</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-1.PNG\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (1)\" /></strong></em></p>\r\n<p><strong>Bước 2:&nbsp;</strong>Chọn mục&nbsp;<em><strong>Vị tr&iacute;</strong></em>, sau đ&oacute; chọn&nbsp;<em><strong>Xem lịch sử vị tr&iacute;&nbsp;</strong></em>ở g&oacute;c tr&ecirc;n b&ecirc;n phải m&agrave;n h&igrave;nh.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-2.PNG\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (2)\" /></p>\r\n<p><strong>Bước 3:&nbsp;</strong>L&uacute;c n&agrave;y, chế độ xem&nbsp;<em>Lịch sử vị tr&iacute;&nbsp;</em>sẽ hiện ra, bạn c&oacute; thể chọn ng&agrave;y ở c&ocirc;ng cụ lịch, trong khi Bản đồ lớn sẽ hiển thị vị tr&iacute;.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-3.PNG\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (3)\" /></p>\r\n<p>Tại đ&acirc;y, bạn c&oacute; thể chọn v&agrave; xem lịch sử vị tr&iacute; sau c&ugrave;ng sử dụng ứng dụng Facebook bằng điện thoại, nhờ đ&oacute; việc t&igrave;m điện thoại bị mất sẽ dễ d&agrave;ng hơn bao giờ hết.&nbsp;</p>\r\n<h2><strong>C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook tr&ecirc;n điện thoại</strong></h2>\r\n<p>T&iacute;nh năng vị tr&iacute; tr&ecirc;n Facebook thực chất l&agrave; dịch vụ vị tr&iacute; tr&ecirc;n thiết bị di động, n&oacute; tạo lịch sử về c&aacute;c vị tr&iacute; ch&iacute;nh x&aacute;c v&agrave; bạn c&oacute; thể quản l&yacute; n&oacute; tr&ecirc;n điện thoại v&agrave; m&aacute;y t&iacute;nh. C&aacute;c thao t&aacute;c t&igrave;m điện thoại bị mất cũng tương tự như tr&ecirc;n m&aacute;y t&iacute;nh, nhưng với việc v&ocirc; t&igrave;nh l&agrave;m mất điện thoại, bạn phải thực hiện tr&ecirc;n một thiết bị kh&aacute;c.</p>\r\n<p><strong>Bước 1:</strong>&nbsp;Đăng nhập t&agrave;i khoản Facebook tr&ecirc;n điện thoại bị mất v&agrave;o thiết bị kh&aacute;c.&nbsp;</p>\r\n<p><strong>Bước 2:</strong>&nbsp;V&agrave;o&nbsp;<em><strong>Menu</strong></em>, chọn&nbsp;<em><strong>C&agrave;i đặt &amp; Quyền ri&ecirc;ng tư</strong></em>, sau đ&oacute; chọn&nbsp;<em><strong>Vị tr&iacute;&nbsp;</strong></em>ở mục&nbsp;<em><strong>Quyền.&nbsp;</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-4.jpg\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (4)\" /></strong></em></p>\r\n<p>Bước 3: Ở&nbsp;<em><strong>C&agrave;i đặt vị tr&iacute;</strong></em>, bạn chọn mục&nbsp;<em><strong>Lịch sử vị tr&iacute; &gt; Xem lịch sử vị tr&iacute;.</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-5.jpg\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (5)\" /></strong></em></p>\r\n<p><strong>Bước 4:&nbsp;</strong>Giao diện định vị sẽ xuất hiện, bạn chọn thời gian bằng c&aacute;ch nhấn v&agrave;o h&igrave;nh tam gi&aacute;c nhỏ gần ng&agrave;y th&aacute;ng. Tr&ecirc;n đ&oacute; sẽ l&agrave; vị tr&iacute; trong khi b&ecirc;n dưới sẽ l&agrave; lịch tr&igrave;nh vị tr&iacute; di chuyển trong ng&agrave;y.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137981/Originals/tim-dien-thoai-bi-mat-qua-fb-6.jpg\" alt=\"C&aacute;ch t&igrave;m điện thoại bị mất qua Facebook (6)\" /></p>\r\n<p>Với t&iacute;nh năng n&agrave;y, bạn c&oacute; thể t&igrave;m xem vị tr&iacute; chiếc điện thoại bị mất của m&igrave;nh đơn giản hơn, hy vọng c&aacute;c bước hướng dẫn tr&ecirc;n đ&acirc;y sẽ gi&uacute;p bạn t&igrave;m điện thoại bị mất qua Facebook đơn giản v&agrave; nhanh ch&oacute;ng nhất. Ngo&agrave;i ra, bạn cũng c&oacute; thể thử nhiều&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/lam-sao-bao-iphone-bi-mat-trong-find-my-128634\">c&aacute;ch t&igrave;m điện thoại bị mất bằng Find My</a>&nbsp;đối với iPhone hoặc th&ocirc;ng qua Find My Device đối với điện thoại Android.</p>', 1, 2, 1, '2021-08-15 03:01:38', '2021-08-15 03:01:38', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image_post`, `image_post_name`, `content`, `status`, `category_post_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(13, 'Cách lấy lại mật khẩu Facebook khi đăng ký 2 nick cùng 1 số điện thoại', 'cach-lay-lai-mat-khau-facebook-khi-dang-ky-2-nick-cung-1-so-dien-thoai', 'public/uploads/post/1\\lay-mat-khau-2-nick-cung-so-dien-thoai.jpg', 'lay-mat-khau-2-nick-cung-so-dien-thoai.jpg', '<p>Với người d&ugrave;ng đăng k&yacute; 2 nick c&ugrave;ng một số&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>, việc lấy mật khẩu khi bị mất sẽ trở n&ecirc;n kh&oacute; khăn hơn bao giờ hết. Tuy nhi&ecirc;n, với bảo mật bằng số điện thoại, bạn sẽ dễ d&agrave;ng thực hiện được n&oacute; th&ocirc;ng qua&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/id-facebook-la-gi-meo-check-id-facebook-cuc-ky-nhanh-chong-137499\">ID Facebook</a>. Dưới đ&acirc;y, FPTShop sẽ hướng dẫn bạn c&aacute;ch lấy lại mật khẩu&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tags/facebook\">Facebook</a>&nbsp;khi đăng k&yacute; 2 nick c&ugrave;ng một số điện thoại tr&ecirc;n cả hai nền tảng m&aacute;y t&iacute;nh v&agrave; điện thoại, c&ugrave;ng theo d&otilde;i nh&eacute;</p>\r\n<h2><strong>C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại</strong></h2>\r\n<h3><strong>Tr&ecirc;n m&aacute;y t&iacute;nh</strong></h3>\r\n<p>Trước khi tiến h&agrave;nh lấy lại mật khẩu Facebook cho một trong hai t&agrave;i khoản của bạn, bạn h&atilde;y đăng nhập v&agrave;o t&agrave;i khoản đang sử dụng, sau đ&oacute; t&igrave;m đến trang c&aacute; nh&acirc;n của t&agrave;i khoản cần lấy lại mật khẩu để thực hiện. Sau đ&oacute;, h&atilde;y l&agrave;m theo c&aacute;c bước dưới đ&acirc;y:&nbsp;</p>\r\n<p><strong>Bước 1:&nbsp;</strong>Lấy ID Facebook của t&agrave;i khoản cũ (ID n&agrave;y sẽ nằm ở khung URL, bạn chỉ lấy phần ID sau dấu / v&agrave; copy n&oacute; lại).</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-1.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (1)\" /></p>\r\n<p><strong>Bước 2:</strong>&nbsp;Sau đ&oacute; đăng xuất ra lại menu đăng nhập, d&aacute;n ID vừa lấy v&agrave;o &ocirc; t&ecirc;n đăng nhập v&agrave; điền một mật khẩu bất kỳ v&agrave;o &ocirc; mật khẩu, sau đ&oacute; tiến h&agrave;nh&nbsp;<em><strong>Đăng nhập.</strong></em></p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-2.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (2)\" /></p>\r\n<p><strong>Bước 3:</strong>&nbsp;Một th&ocirc;ng b&aacute;o sai mật khẩu sẽ hiện l&ecirc;n, bạn chọn&nbsp;<em><strong>Qu&ecirc;n mật khẩu.</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-3.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (3)\" /></strong></em></p>\r\n<p><strong>Bước 4:&nbsp;</strong>Menu&nbsp;<em><strong>Đặt lại mật khẩu&nbsp;</strong></em>sẽ xuất hiện, bạn chắc chắn rằng số điện thoại d&ugrave;ng để đăng k&yacute; t&agrave;i khoản n&agrave;y đ&uacute;ng v&agrave; bấm v&agrave;o&nbsp;<em><strong>Tiếp tục&nbsp;</strong></em>để lấy m&atilde;.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-4.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (4)\" /></p>\r\n<p><strong>Bước 5:&nbsp;</strong>Một m&atilde; sẽ được gửi về Số điện thoại đăng k&yacute;, bạn nhập m&atilde; v&agrave; nhấn&nbsp;<em><strong>Tiếp tục.</strong></em></p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-5.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (5)\" /></p>\r\n<p><strong>Bước 6:&nbsp;</strong>Nhập&nbsp;<em><strong>Mật khẩu mới &gt; Tiếp tục</strong></em>&nbsp;để ho&agrave;n tất.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-6.PNG\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (6)\" /></p>\r\n<p>Như vậy, chỉ với v&agrave;i bước đơn giản, bạn đ&atilde; c&oacute; thể lấy lại mật khẩu cho một t&agrave;i khoản kh&aacute;c c&oacute; c&ugrave;ng số điện thoại đăng k&yacute;. Sau đ&oacute;, bạn chỉ cần đăng nhập lại theo ID v&agrave; mật khẩu mới tạo l&agrave; xong.&nbsp;</p>\r\n<h3><strong>Tr&ecirc;n điện thoại</strong></h3>\r\n<p>Cũng giống như tr&ecirc;n m&aacute;y t&iacute;nh, đầu ti&ecirc;n bạn cần phải đăng nhập t&agrave;i khoản ch&iacute;nh v&agrave;o ứng dụng Facebook, sau đ&oacute; t&igrave;m kiếm v&agrave; truy cập v&agrave;o trang c&aacute; nh&acirc;n t&agrave;i khoản cần lấy lại mật khẩu để lấy ID Facebook.&nbsp;</p>\r\n<p><strong>Bước 1:&nbsp;</strong>Ở trang c&aacute; nh&acirc;n t&agrave;i khoản cần lấy lại mật khẩu, bạn chọn v&agrave;o biểu tượng&nbsp;<em><strong>3 chấm</strong></em>, sau đ&oacute; sẽ thấy đường li&ecirc;n kết trang c&aacute; nh&acirc;n, copy đoạn ID sau dấu /.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-7.jpg\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (7)\" /></p>\r\n<p><strong>Bước 2:</strong>&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/huong-dan-dang-xuat-facebook-tu-xa-khi-ban-dang-tri-52892\">Đăng xuất khỏi Facebook</a>, sau đ&oacute; điền ID vừa lấy ở mục t&ecirc;n đăng nhập v&agrave; nhập mật khẩu bất kỳ, chọn&nbsp;<em><strong>Đăng nhập.&nbsp;</strong></em>L&uacute;c n&agrave;y, một th&ocirc;ng b&aacute;o sẽ hiện ra, bạn chọn&nbsp;<em><strong>Qu&ecirc;n mật khẩu.&nbsp;</strong></em></p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-8.jpg\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (8)\" /></p>\r\n<p><strong>Bước 3:</strong>&nbsp;Bạn sẽ được chuyển sang qu&aacute; tr&igrave;nh&nbsp;<em>X&aacute;c nhận t&agrave;i khoản</em>, chọn&nbsp;<strong><em>X&aacute;c nhận qua m&atilde; SMS&nbsp;</em></strong>v&agrave; kiểm tra lại số điện thoại đ&oacute; c&oacute; đ&uacute;ng kh&ocirc;ng, sau đ&oacute; chọn&nbsp;<em><strong>Tiếp tục.&nbsp;</strong></em>Một m&atilde; đ&atilde; được gửi đến số điện thoại của bạn, điền v&agrave;o v&agrave; chọn&nbsp;<em><strong>Tiếp tục.</strong></em></p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-9.jpg\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (9)\" /></p>\r\n<p><strong>Bước 4:&nbsp;</strong>Chọn&nbsp;<em><strong>Duy tr&igrave; đăng nhập&nbsp;</strong></em>ở mục&nbsp;<em>Đăng xuất tất cả thiết bị</em>, sau đ&oacute; chọn&nbsp;<em><strong>Tiếp tục.&nbsp;</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-10.jpg\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (10)\" /></strong></em></p>\r\n<p><strong>Bước 5:&nbsp;</strong>Bạn tạo một mật khẩu mới v&agrave; nhấn&nbsp;<em><strong>Tiếp tục&nbsp;</strong></em>để ho&agrave;n tất.&nbsp;</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137966/Originals/lay-mat-khau-2-nick-cung-so-dien-thoai-11.jpg\" alt=\"C&aacute;ch lấy lại mật khẩu Facebook khi đăng k&yacute; 2 nick c&ugrave;ng 1 số điện thoại (11)\" /></p>\r\n<p>Sau khi ho&agrave;n tất lấy lại mật khẩu, bạn c&oacute; thể dễ d&agrave;ng đăng nhập t&agrave;i khoản đ&oacute; một c&aacute;ch dễ d&agrave;ng.&nbsp;</p>\r\n<p><em>Lưu &yacute;:&nbsp;</em></p>\r\n<ul>\r\n<li>C&aacute;ch&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-lay-lai-tai-khoan-facebook-khi-bi-hack-hoac-quen-mat-khau-48369\">lấy lại mật khẩu Facebook</a>&nbsp;khi đăng k&yacute; 2 nick c&ugrave;ng một số điện thoại kh&ocirc;ng thực hiện được với ID c&oacute; dạng một d&atilde;y số, m&agrave; n&oacute; chỉ thực hiện được với dạng ID người d&ugrave;ng.&nbsp;</li>\r\n<li>Trong một v&agrave;i trường hợp, Facebook sẽ y&ecirc;u cầu bạn nhận m&atilde; x&aacute;c thực th&ocirc;ng qua email đ&atilde; li&ecirc;n kết thay v&igrave; gửi m&atilde; qua số điện thoại đăng k&yacute;.&nbsp;</li>\r\n</ul>\r\n<p>Hy vọng với c&aacute;c bước tr&ecirc;n đ&acirc;y, bạn sẽ biết c&aacute;ch lấy lại mật khẩu cho t&agrave;i khoản c&oacute; chung số điện thoại đăng k&yacute;. Tuy nhi&ecirc;n, hiện nay ch&iacute;nh s&aacute;ch bảo mật của Facebook đ&atilde; thay đổi, bạn sẽ kh&ocirc;ng được đăng k&yacute; nhiều t&agrave;i khoản tr&ecirc;n một số điện thoại nữa, do đ&oacute; h&atilde;y c&acirc;n nhắc đổi số điện thoại nh&eacute;. Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</p>', 1, 2, 1, '2021-08-15 03:02:18', '2021-08-15 03:02:18', NULL),
(14, 'Hướng dẫn cách tắt thông báo live stream trên Facebook', 'huong-dan-cach-tat-thong-bao-live-stream-tren-facebook', 'public/uploads/post/1\\cach-tat-thong-bao-live-stream-tren-facebook.jpg', 'cach-tat-thong-bao-live-stream-tren-facebook.jpg', '<p>Mặc định khi c&oacute; bất kỳ người bạn n&agrave;o thực hiện&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-live-stream-man-hinh-dien-thoai-ios-136158\">live stream</a>,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tags/facebook\">Facebook</a>&nbsp;sẽ gửi th&ocirc;ng b&aacute;o đến cho bạn biết. Nhưng nếu h&agrave;ng ng&agrave;y bạn phải nhận rất nhiều th&ocirc;ng b&aacute;o kiểu như thế n&agrave;y sẽ rất phiền phức, bởi thực tế c&oacute; rất nhiều live stream b&aacute;n h&agrave;ng rất phiền phức.</p>\r\n<p>Trong trường hợp n&agrave;y, việc tắt v&agrave; chặn ho&agrave;n to&agrave;n c&aacute;c th&ocirc;ng b&aacute;o c&oacute; li&ecirc;n quan đến t&iacute;nh năng live stream tr&ecirc;n Facebook l&agrave; cần thiết. C&aacute;ch thực hiện như sau:</p>\r\n<h2><strong>C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream Facebook tr&ecirc;n m&aacute;y t&iacute;nh&nbsp;</strong></h2>\r\n<p>Nếu bạn muốn tắt tất cả th&ocirc;ng b&aacute;o live stream Facebook khi sử dụng Facebook tr&ecirc;n m&aacute;y t&iacute;nh, h&atilde;y thực hiện theo hướng dẫn sau:&nbsp;</p>\r\n<p><strong>Bước 1:&nbsp;</strong>K&iacute;ch hoạt tr&igrave;nh duyệt web, truy cập v&agrave;o địa chỉ&nbsp;<a href=\"http://www.facebook.com/\" rel=\"nofollow\">www.facebook.com</a>&nbsp;v&agrave; đăng nhập v&agrave;o t&agrave;i khoản của m&igrave;nh.</p>\r\n<p><strong>Bước 2:</strong>&nbsp;Từ trang Facebook ch&iacute;nh, bạn bấm v&agrave;o&nbsp;<em><strong>biểu tượng mũi t&ecirc;n xổ xuống</strong></em>&nbsp;ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n phải. Từ menu thả xuống, chọn t&ugrave;y chọn&nbsp;<em><strong>C&agrave;i đặt &amp; quyền ri&ecirc;ng tư &gt; C&agrave;i đặt.</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137771/Originals/Cach-tat-thong-bao-live-stream-tren-Facebook.jpg\" alt=\"C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook (1)\" /></strong></em></p>\r\n<p><strong>Bước 3:&nbsp;</strong>Ở khung b&ecirc;n tr&aacute;i bạn sẽ thấy menu tất cả c&aacute;c c&agrave;i đặt c&oacute; li&ecirc;n quan đến trang Facebook của bạn. B&acirc;y giờ h&atilde;y k&iacute;ch t&ugrave;y chọn&nbsp;<em><strong>Th&ocirc;ng b&aacute;o (1)</strong></em>&nbsp;ở ph&iacute;a dưới.</p>\r\n<p><strong>Bước 4:</strong>&nbsp;Quan s&aacute;t khung b&ecirc;n phải, di chuyển xuống ph&iacute;a dưới v&agrave; k&iacute;ch t&ugrave;y chọn&nbsp;<em><strong>Video (2)</strong></em>.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137771/Originals/Cach-tat-thong-bao-live-stream-tren-Facebook-2.jpg\" alt=\"C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook (2)\" /></p>\r\n<p><strong>Bước 5:</strong>&nbsp;B&acirc;y giờ bạn sẽ thấy tất cả c&aacute;c thiết lập c&oacute; li&ecirc;n quan đến t&iacute;nh năng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/huong-dan-cach-tai-video-tu-facebook-ve-iphone-sieu-don-gian-132854\">video tr&ecirc;n Facebook</a>&nbsp;ở ngay ph&iacute;a dưới (3), bao gồm cả t&iacute;nh năng th&ocirc;ng b&aacute;o live stream.</p>\r\n<p>T&ugrave;y theo nhu cầu của m&igrave;nh, bạn c&oacute; thể tắt một trong c&aacute;c t&ugrave;y chọn c&oacute; li&ecirc;n quan như: Th&ocirc;ng b&aacute;o đẩy; Email v&agrave; SMS. Tương ứng với mỗi thiết lập được Bật hoặc Tắt, Facebook sẽ kh&ocirc;ng gửi c&aacute;c th&ocirc;ng b&aacute;o c&oacute; li&ecirc;n quan đến t&iacute;nh năng live stream đến Facebook của bạn nữa.</p>\r\n<p>Tuy nhi&ecirc;n nếu chuyển thiết lập&nbsp;<em><strong>Cho ph&eacute;p th&ocirc;ng b&aacute;o tr&ecirc;n Facebook</strong></em>&nbsp;ở ph&iacute;a tr&ecirc;n từ&nbsp;<em><strong>Bật</strong></em>&nbsp;về&nbsp;<em><strong>Tắt</strong></em>&nbsp;cũng c&oacute; nghĩa l&agrave; mọi thiết lập ở ph&iacute;a dưới tự động tắt. Như vậy mọi th&ocirc;ng b&aacute;o c&oacute; li&ecirc;n quan đến live stream sẽ được chặn ho&agrave;n to&agrave;n, thay v&igrave; chặn c&oacute; chọn lọc như việc chọn từng t&ugrave;y chọn ri&ecirc;ng.&nbsp;</p>\r\n<h2><strong>C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream Facebook tr&ecirc;n điện thoại&nbsp;&nbsp;</strong></h2>\r\n<p>C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream Facebook tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;cũng được thực hiện tương tự như c&aacute;ch&nbsp; thực hiện tr&ecirc;n m&aacute;y t&iacute;nh ở tr&ecirc;n.</p>\r\n<p><strong>Bước 1:&nbsp;</strong>K&iacute;ch hoạt ứng dụng Facebook tr&ecirc;n smartphone hoặc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>&nbsp;v&agrave; đăng nhập (nếu bạn chưa đăng nhập) v&agrave;o t&agrave;i khoản của m&igrave;nh.&nbsp;</p>\r\n<p><strong>Bước 2:&nbsp;</strong>Nhấn v&agrave;o biểu tượng&nbsp;<em><strong>ba dấu gạch ngang&nbsp;</strong></em>ở g&oacute;c tr&ecirc;n c&ugrave;ng b&ecirc;n phải, nằm b&ecirc;n dưới biểu tượng Facebook Messenger.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137771/Originals/Cach-tat-thong-bao-live-stream-tren-Facebook-dien-thoai-1.jpg\" alt=\"C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook (3)\" /></p>\r\n<p><strong>Bước 3:&nbsp;</strong>Trong menu hiển thị, di chuyển xuống dưới c&ugrave;ng, sau đ&oacute; nhấn v&agrave;o&nbsp;<em><strong>C&agrave;i đặt &amp; quyền ri&ecirc;ng tư &gt; C&agrave;i đặt.</strong></em></p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137771/Originals/Cach-tat-thong-bao-live-stream-tren-Facebook-dien-thoai-2.jpg\" alt=\"C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook (4)\" /></p>\r\n<p><strong>Bước 4:&nbsp;</strong>Tiếp theo chọn t&ugrave;y chọn&nbsp;<em><strong>Th&ocirc;ng b&aacute;o</strong></em>, sang cửa sổ mới di chuyển xuống ph&iacute;a dưới v&agrave; bấm t&ugrave;y chọn&nbsp;<em><strong>Video.&nbsp;</strong></em></p>\r\n<p><em><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137771/Originals/Cach-tat-thong-bao-live-stream-tren-Facebook-dien-thoai-4.jpg\" alt=\"C&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook (5)\" /></strong></em></p>\r\n<p><strong>Bước 5:</strong>&nbsp;Tại đ&acirc;y, bạn sẽ c&oacute; rất nhiều t&ugrave;y chọn li&ecirc;n quan đến th&ocirc;ng b&aacute;o Video. C&aacute;c t&ugrave;y chọn n&agrave;y giống hệt khi thực hiện tr&ecirc;n trang Facebook khi sử dụng với m&aacute;y t&iacute;nh ở tr&ecirc;n. T&ugrave;y v&agrave;o nhu cầu sử dụng, bạn c&oacute; thể chọn t&ugrave;y chọn ph&ugrave; hợp với nhu cầu sử dụng của m&igrave;nh.&nbsp;</p>\r\n<p>Trường hợp, muốn tắt tất cả th&ocirc;ng b&aacute;o live stream Facebook, h&atilde;y nhấn v&agrave;o thanh trượt&nbsp;<em><strong>Cho ph&eacute;p th&ocirc;ng b&aacute;o tr&ecirc;n Facebook</strong></em>. Một hộp thoại hiển thị, bấm n&uacute;t&nbsp;<em><strong>Tắt&nbsp;</strong></em>để ho&agrave;n tất v&agrave; mọi th&ocirc;ng b&aacute;o live stream Facebook g&acirc;y phiền phức sẽ bị chặn lại ho&agrave;n to&agrave;n.</p>\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; hướng dẫn chi tiết c&aacute;ch tắt th&ocirc;ng b&aacute;o live stream tr&ecirc;n Facebook, hy vọng n&oacute; sẽ l&agrave; giải ph&aacute;p gi&uacute;p bạn giải quyết được vấn đề kh&oacute; chịu n&agrave;y. H&atilde;y thử v&agrave; đừng qu&ecirc;n cho biết &yacute; kiến b&ecirc;n dưới bạn nh&eacute;, ch&uacute;c bạn th&agrave;nh c&ocirc;ng!</p>', 1, 2, 1, '2021-08-15 03:02:59', '2021-08-15 03:02:59', NULL),
(15, 'S Pen Fold Edition và S Pen Pro: Sự khác biệt là gì?', 's-pen-fold-edition-va-s-pen-pro-su-khac-biet-la-gi', 'public/uploads/post/1\\z-fold3-with-s-pen-pro-and-s-pen-fold-edition.jpg', 'z-fold3-with-s-pen-pro-and-s-pen-fold-edition.jpg', '<p>Năm nay,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;đ&atilde; kh&ocirc;ng c&oacute; chiếc Note mới để giới thiệu tới người d&ugrave;ng. C&ocirc;ng ty H&agrave;n Quốc chưa cho biết th&ocirc;ng tin n&agrave;o li&ecirc;n quan tới tương lai của d&ograve;ng Note nhưng điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; n&oacute; đ&atilde; bị l&atilde;ng qu&ecirc;n. Đối với những người y&ecirc;u th&iacute;ch S Pen để viết v&agrave; vẽ, họ cũng kh&ocirc;ng n&ecirc;n cảm thấy hụt hẫng v&igrave;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-fold3?dung-luong=512-gb\">Galaxy Z Fold 3</a>&nbsp;mới cũng c&oacute; thể được sử dụng với một c&acirc;y b&uacute;t mặc d&ugrave; n&oacute; kh&ocirc;ng được tặng k&egrave;m trong hộp b&aacute;n lẻ.</p>\r\n<p><img id=\"S Pen Fold Edition v&agrave; S Pen Pro\" title=\"S Pen Fold Edition v&agrave; S Pen Pro\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/Z-Fold3-with-S-Pen-Pro-and-S-Pen-Fold-Edition-01.jpg\" alt=\"S Pen Fold Edition v&agrave; S Pen Pro\" /></p>\r\n<p><em>S Pen Fold Edition (b&ecirc;n dưới) v&agrave; S Pen Pro (b&ecirc;n tr&ecirc;n)</em></p>\r\n<p>Tại sự kiện&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-ca-san-pham-moi-ra-mat-tai-samsung-galaxy-unpacked-2021-137909\">Galaxy Unpacked 2021</a>&nbsp;vừa diễn ra, Samsung đ&atilde; giới thiệu th&ecirc;m hai chiếc b&uacute;t S Pen mới l&agrave; S Pen Fold Edition v&agrave; S Pen Pro. Liệu rằng hai chiếc S Pen n&agrave;y c&oacute; g&igrave; kh&aacute;c so với những S Pen trước đ&oacute;? Ch&uacute;ng c&oacute; điểm g&igrave; kh&aacute;c biệt với nhau hay kh&ocirc;ng? H&atilde;y để b&agrave;i viết dưới đ&acirc;y giải th&iacute;ch cho c&aacute;c bạn nh&eacute;.</p>\r\n<h3><strong>Sự kh&aacute;c biệt giữa S Pen Fold Edition v&agrave; S Pen Pro</strong></h3>\r\n<p><strong><img id=\"S Pen Pro v&agrave; S Pen Fold Edition\" title=\"S Pen Pro v&agrave; S Pen Fold Edition\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/Z-Fold3-with-S-Pen-Pro-and-S-Pen-Fold-Edition-02.jpg\" alt=\"S Pen Pro v&agrave; S Pen Fold Edition\" /></strong></p>\r\n<p><em>T&ecirc;n của S Pen được in l&ecirc;n th&acirc;n của ch&uacute;ng để bạn c&oacute; thể dễ d&agrave;ng ph&acirc;n biệt</em></p>\r\n<p>Hai chiếc b&uacute;t S Pen n&agrave;y c&oacute; rất nhiều đặc điểm giống nhau. Bạn đều c&oacute; thể mua ch&uacute;ng ở c&aacute;c cửa h&agrave;ng được ủy quyền hoặc ch&iacute;nh thức của Samsung. Những b&uacute;t S Pen n&agrave;y sẽ kh&ocirc;ng hoạt động tr&ecirc;n Galaxy Z Flip 3 v&igrave; ch&uacute;ng được thiết kế đặc biệt để ph&ugrave; hợp với m&agrave;n h&igrave;nh gập lại rất \"nhạy cảm\" của Galaxy Z Fold 3.</p>\r\n<p><img id=\"S Pen Pro v&agrave; S Pen Fold Edition c&oacute; gi&aacute; b&aacute;n ch&ecirc;nh lệch nhau kh&aacute; nhiều\" title=\"S Pen Pro v&agrave; S Pen Fold Edition c&oacute; gi&aacute; b&aacute;n ch&ecirc;nh lệch nhau kh&aacute; nhiều\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/Z-Fold3-with-S-Pen-Pro-and-S-Pen-Fold-Edition-05.jpg\" alt=\"S Pen Pro v&agrave; S Pen Fold Edition c&oacute; gi&aacute; b&aacute;n ch&ecirc;nh lệch nhau kh&aacute; nhiều\" /></p>\r\n<p><em>S Pen Pro v&agrave; S Pen Fold Edition c&oacute; gi&aacute; b&aacute;n ch&ecirc;nh lệch nhau kh&aacute; nhiều</em></p>\r\n<p>Về thiết kế, S Pen Fold Edition v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/but-cam-ung-s-pen-pro\">S Pen Pro</a>&nbsp;c&oacute; c&aacute;c đầu bo tr&ograve;n kh&aacute;c biệt so với những S Pen truyền thống. Điều đ&oacute; đảm bảo rằng b&uacute;t sẽ kh&ocirc;ng c&oacute; khả năng l&agrave;m xước mặt k&iacute;nh mỏng manh của Fold 3 trong qu&aacute; tr&igrave;nh viết hoặc vẽ. Dưới đ&acirc;y l&agrave; bảng so s&aacute;nh nhanh giữa c&aacute;c th&ocirc;ng số kỹ thuật giữa S Pen Fold Edition v&agrave; S Pen Pro.</p>\r\n<p><img id=\"So s&aacute;nh S Pen Fold Edition v&agrave; S Pen Pro\" title=\"So s&aacute;nh S Pen Fold Edition v&agrave; S Pen Pro\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/so-sanh-S-Pen-01.jpg\" alt=\"So s&aacute;nh S Pen Fold Edition v&agrave; S Pen Pro\" /></p>\r\n<p><em>Bảng so s&aacute;nh nhanh S Pen Fold Edition v&agrave; S Pen Pro</em></p>\r\n<p>Tuy nhi&ecirc;n, những điểm tương đồng giữa hai chiếc b&uacute;t S Pen mới sẽ dừng lại ở đ&acirc;y. Về mặt vật chất, những chiếc b&uacute;t n&agrave;y ho&agrave;n to&agrave;n kh&aacute;c nhau. Phi&ecirc;n bản Fold Edition mỏng hơn, ngắn hơn v&agrave; chỉ hoạt động tr&ecirc;n m&agrave;n h&igrave;nh của Galaxy Z Fold 3 ở thời điểm hiện tại. Mặc d&ugrave; n&oacute; kh&ocirc;ng được trang bị sẵn cho Fold 3 nhưng Samsung đ&atilde; tạo ra một case ri&ecirc;ng cho ph&eacute;p S Pen được cất gọn g&agrave;ng b&ecirc;n trong v&agrave; tr&aacute;nh t&igrave;nh trạng mất b&uacute;t trong qu&aacute; tr&igrave;nh sử dụng. C&aacute;c t&iacute;nh năng tr&ecirc;n S Pen Fold Edition hạn chế hơn so với b&uacute;t S Pen c&oacute; tr&ecirc;n Galaxy Note 9 v&agrave; c&aacute;c mẫu Note mới hơn. Bạn kh&ocirc;ng nhận được điều khiển bằng cử chỉ hoặc kết nối Bluetooth n&ecirc;n c&aacute;c t&iacute;nh năng tr&ecirc;n chiếc b&uacute;t n&agrave;y cơ bản l&agrave; giống c&aacute;c mẫu b&uacute;t S Pen ra mắt từ năm 2017 trở về trước.</p>\r\n<p><img id=\"S Pen Fold Edition\" title=\"S Pen Fold Edition\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/Z-Fold3-with-S-Pen-Pro-and-S-Pen-Fold-Edition-03.jpg\" alt=\"S Pen Fold Edition\" /></p>\r\n<p><em>S Pen Fold Edition nhỏ hơn v&agrave; một số mẫu case d&agrave;nh cho Galaxy Z Fold 3 sẽ c&oacute; khe cắm ri&ecirc;ng cho n&oacute;</em></p>\r\n<p>Phi&ecirc;n bản b&uacute;t S Pen Pro cao hơn, d&agrave;y hơn v&agrave; c&oacute; thể kết nối với nhiều thiết bị. Một n&uacute;t chuyển đổi cho ph&eacute;p bạn chuyển đổi giữa Fold 3 với c&aacute;c điện thoại v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>&nbsp;tương th&iacute;ch với S Pen kh&aacute;c. N&oacute; cũng c&oacute; thể gh&eacute;p nối với c&aacute;c thiết bị qua Bluetooth để cho ph&eacute;p thực hiện c&aacute;c lệnh cử chỉ v&agrave; chức năng Bluetooth từ xa. Điều đ&oacute; cũng c&oacute; nghĩa l&agrave; chiếc b&uacute;t n&agrave;y c&oacute; t&iacute;ch hợp pin b&ecirc;n trong. Bạn c&oacute; thể sạc lại n&oacute; qua cổng USB-C ở tr&ecirc;n c&ugrave;ng. S Pen Pro cũng c&oacute; thể được theo d&otilde;i vị tr&iacute; th&ocirc;ng qua ứng dụng SmartThings để bạn c&oacute; thể x&aacute;c định vị tr&iacute; của n&oacute; nếu bị mất. Ngo&agrave;i ra, b&uacute;t c&ograve;n c&oacute; thể sao ch&eacute;p v&agrave; d&aacute;n dữ liệu giữa c&aacute;c thiết bị kh&aacute;c nhau.</p>\r\n<p><img id=\"Cổng sạc USB-C của S Pen Pro\" title=\"Cổng sạc USB-C của S Pen Pro\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137975/Originals/Z-Fold3-with-S-Pen-Pro-and-S-Pen-Fold-Edition-04.jpg\" alt=\"Cổng sạc USB-C của S Pen Pro\" /></p>\r\n<p><em>Cổng sạc USB-C của S Pen Pro</em></p>\r\n<p>Vẫn c&ograve;n một số chi tiết cụ thể m&agrave; ch&uacute;ng ta chưa biết về S Pen Fold Edition v&agrave; S Pen Pro mới như k&iacute;ch thước ch&iacute;nh x&aacute;c của ch&uacute;ng cũng như những kh&aacute;c biệt phức tạp về mặt chức năng. FPT Shop sẽ cập nhật b&agrave;i viết n&agrave;y khi c&oacute; th&ecirc;m th&ocirc;ng tin từ Samsung.</p>', 1, 3, 1, '2021-08-15 03:05:59', '2021-08-15 03:05:59', NULL),
(16, 'Trên tay Honor Magic 3 Pro: Giống Mate 4 Pro, Snapdragon 888 Plus và camera zoom 100X', 'tren-tay-honor-magic-3-pro-giong-mate-4-pro-snapdragon-888-plus-va-camera-zoom-100x', 'public/uploads/post/1\\tren-tay-honor-magic-3-pro.jpg', 'tren-tay-honor-magic-3-pro.jpg', '<p>H&ocirc;m nay (13/08), Honor đ&atilde; ch&iacute;nh thức c&ocirc;ng bố d&ograve;ng sản phẩm Magic 3 theo đ&uacute;ng lịch tr&igrave;nh. D&ograve;ng sản phẩm n&agrave;y bao gồm những điện thoại cao cấp đầu ti&ecirc;n của thương hiệu sau khi t&aacute;ch hẳn khỏi Huawei th&ocirc;ng qua một thương vụ chuyển nhượng. Đ&oacute; l&agrave; Honor Magic 3, Magic 3 Pro v&agrave; Magic 3 Pro+ (hay c&ograve;n gọi l&agrave; Honor Magic 3 Ultimate Edition). Ở b&agrave;i viết n&agrave;y, ch&uacute;ng ta h&atilde;y c&ugrave;ng xem Honor Magic 3 Pro c&oacute; g&igrave; th&uacute; vị nh&eacute;.</p>\r\n<p><img id=\"Honor Magic 3 Pro\" title=\"Honor Magic 3 Pro\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-01.jpg\" alt=\"Honor Magic 3 Pro\" /></p>\r\n<p>Về mặt thiết kế, bạn chắc chắn sẽ thấy Honor Magic 3 Pro rất quen thuộc. Tr&ecirc;n thực tế, Honor đang sử dụng ch&iacute;nh thiết kế của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/huawei-mate-40-pro\">Mate 40 Pro</a>&nbsp;để tạo ra sản phẩm của ri&ecirc;ng m&igrave;nh. Thiết kế đ&oacute; kh&ocirc;ng lỗi thời nhưng n&oacute; vẫn khiến mọi người nghĩ rằng Honor vẫn chưa t&aacute;ch ra khỏi c&aacute;i b&oacute;ng qu&aacute; lớn của Huawei.</p>\r\n<p><img id=\"Mặt sau Honor Magic 3 Pro c&oacute; camera tr&ograve;n độc đ&aacute;o\" title=\"Mặt sau Honor Magic 3 Pro c&oacute; camera tr&ograve;n độc đ&aacute;o\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-02.jpg\" alt=\"Mặt sau Honor Magic 3 Pro c&oacute; camera tr&ograve;n độc đ&aacute;o\" /></p>\r\n<p>Ở ph&iacute;a sau, Honor Magic 3 Pro nổi bật với cụm camera h&igrave;nh tr&ograve;n đa tầng c&ugrave;ng rất nhiều ống k&iacute;nh ở b&ecirc;n trong. Mặt lưng của m&aacute;y được ho&agrave;n thiện từ chất liệu k&iacute;nh v&agrave; c&aacute;c cạnh được v&aacute;t cong nhẹ nh&agrave;ng n&ecirc;n mang lại cảm gi&aacute;c rất cao cấp v&agrave; sang trọng.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">Điện thoại</a>&nbsp;c&oacute; tổng cộng 3 t&ugrave;y chọn m&agrave;u cho người d&ugrave;ng lựa chọn l&agrave; Golden Hour, Black v&agrave; White.</p>\r\n<p><img id=\"Mặt trước của Honor Magic 3 Pro\" title=\"Mặt trước của Honor Magic 3 Pro\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-07.jpg\" alt=\"Mặt trước của Honor Magic 3 Pro\" /></p>\r\n<p>Mặt trước của Honor Magic 3 Pro cũng kh&ocirc;ng kh&aacute;c biệt so với Mate 40 Pro của Huawei. Đ&oacute; l&agrave; một m&agrave;n h&igrave;nh cong về hai b&ecirc;n với g&oacute;c cong l&ecirc;n tới 89 độ, viền bezel mỏng xung quanh v&agrave; camera selfie đục lỗ với h&igrave;nh dạng vi&ecirc;n thuốc. Honor đ&atilde; sử dụng một thuật to&aacute;n đặc biệt để ngăn chặn những t&igrave;nh huống cảm ứng nhầm v&agrave;o phần cạnh cong của m&aacute;y.</p>\r\n<p><img id=\"Honor Magic 3 Pro kh&aacute; nặng v&agrave; to\" title=\"Honor Magic 3 Pro kh&aacute; nặng v&agrave; to\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-06.jpg\" alt=\"Honor Magic 3 Pro kh&aacute; nặng v&agrave; to\" /></p>\r\n<p>Honor Magic 3 Pro kh&aacute; lớn v&agrave; nặng. M&aacute;y c&oacute; trọng lượng tr&ecirc;n 200 gram v&agrave; độ d&agrave;y trung b&igrave;nh khoảng 9mm n&ecirc;n việc cầm l&acirc;u tr&ecirc;n tay c&oacute; thể tạo ra cảm gi&aacute;c kh&oacute; chịu. Đối với phi&ecirc;n bản Golden Hour c&oacute; mặt lưng bằng da, điện thoại c&ograve;n d&agrave;y hơn nữa. Do đ&oacute;, bạn kh&ocirc;ng n&ecirc;n hy vọng v&agrave;o việc sử dụng hoặc cầm nắm Magic 3 Pro bằng một tay.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; m&agrave;n h&igrave;nh chất lượng\" title=\"Honor Magic 3 Pro c&oacute; m&agrave;n h&igrave;nh chất lượng\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-09.jpg\" alt=\"Honor Magic 3 Pro c&oacute; m&agrave;n h&igrave;nh chất lượng\" /></p>\r\n<p>Th&ocirc;ng số kỹ thuật m&agrave;n h&igrave;nh của Honor Magic 3 Pro rất ấn tượng với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-oled-la-gi-cong-nghe-man-hinh-oled-uu-va-nhuoc-diem-57402\">m&agrave;n h&igrave;nh OLED</a>&nbsp;dẻo c&oacute; k&iacute;ch thước 6,76 inch, độ ph&acirc;n giải Full HD+ (2772 &times; 1344 pixel), hỗ trợ tốc độ l&agrave;m tươi 120Hz, hiển thị 1,07 tỷ m&agrave;u, điều chỉnh độ s&aacute;ng 4096 mức v&agrave; gam m&agrave;u rộng DCI-P3. M&agrave;n h&igrave;nh n&agrave;y cũng hỗ trợ HDR10 + v&agrave; đạt chứng nhận bảo vệ mắt của T&Uuml;V Rheinland.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; chip khủng\" title=\"Honor Magic 3 Pro c&oacute; chip khủng\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-10.jpg\" alt=\"Honor Magic 3 Pro c&oacute; chip khủng\" /></p>\r\n<p>Honor Magic 3 Pro được cung cấp sức mạnh bởi chip xử l&yacute; Snapdragon 888+ h&agrave;ng đầu của Qualcomm. Điều đ&oacute; khiến cho chiếc m&aacute;y n&agrave;y trở th&agrave;nh thiết bị thứ hai sau Mi Mix 4 của Xiaomi sở hữu con chip khủng n&agrave;y. Chip sẽ được kết hợp với tối đa 12GB RAM v&agrave; 512GB dung lượng lưu trữ. Tuy nhi&ecirc;n, điện thoại kh&ocirc;ng c&oacute; khe cắm mở rộng bộ nhớ trong.</p>\r\n<p><img id=\"Honor Magic 3 Pro chạy giao diện t&ugrave;y chỉnh của h&atilde;ng\" title=\"Honor Magic 3 Pro chạy giao diện t&ugrave;y chỉnh của h&atilde;ng\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-03.jpg\" alt=\"Honor Magic 3 Pro chạy giao diện t&ugrave;y chỉnh của h&atilde;ng\" /></p>\r\n<p>Ở b&ecirc;n trong, Honor Magic 3 Pro chạy giao diện người d&ugrave;ng Magic UI 5.0 dựa tr&ecirc;n Android 11. Do kh&ocirc;ng c&ograve;n li&ecirc;n quan tới Huawei, điện thoại vẫn hỗ trợ c&aacute;c dịch vụ GMS (dịch vụ di động của Google). Ch&iacute;nh v&igrave; thế, người mua kh&ocirc;ng phải lo lắng về t&iacute;nh khả dụng của c&aacute;c ứng dụng v&agrave; dịch vụ của Google tr&ecirc;n chiếc m&aacute;y n&agrave;y.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; 4 camera ở ph&iacute;a sau\" title=\"Honor Magic 3 Pro c&oacute; 4 camera ở ph&iacute;a sau\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-12.jpg\" alt=\"Honor Magic 3 Pro c&oacute; 4 camera ở ph&iacute;a sau\" /></p>\r\n<p>Để chụp ảnh, Honor Magic 3 Pro c&oacute; tổng cộng 6 camera tr&ecirc;n to&agrave;n bộ th&acirc;n m&aacute;y. Trong đ&oacute;, hệ thống camera sau sẽ c&oacute; 4 camera kh&aacute;c nhau bao gồm camera ch&iacute;nh g&oacute;c rộng 50MP, camera g&oacute;c si&ecirc;u rộng 13MP với trường nh&igrave;n 120 độ, camera đơn sắc 64MP v&agrave; camera tele 64MP với zoom quang 3,5x. Sự kết hợp đ&oacute; mang lại cho điện thoại khả năng chụp ảnh zoom lai 10x v&agrave; zoom kỹ thuật số 100x.</p>\r\n<p><img id=\"Honor Magic 3 Pro sở hữu hệ thống cảm biến chất lượng\" title=\"Honor Magic 3 Pro sở hữu hệ thống cảm biến chất lượng\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-13.jpg\" alt=\"Honor Magic 3 Pro sở hữu hệ thống cảm biến chất lượng\" /></p>\r\n<p>Ngo&agrave;i c&aacute;c camera được liệt k&ecirc; ở tr&ecirc;n, m&aacute;y ảnh ph&iacute;a sau của Honor Magic 3 Pro c&ograve;n c&oacute; hệ thống lấy n&eacute;t laser 8 &times; 8 dTOF, cảm biến nhiệt độ m&agrave;u đa k&iacute;nh v&agrave; cảm biến nhấp nh&aacute;y. Nhờ đ&oacute;, camera n&agrave;y c&oacute; thể lấy n&eacute;t ch&iacute;nh x&aacute;c v&agrave; si&ecirc;u nhanh trong bất kỳ ho&agrave;n cảnh n&agrave;o.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; camera k&eacute;p ở ph&iacute;a trước\" title=\"Honor Magic 3 Pro c&oacute; camera k&eacute;p ở ph&iacute;a trước\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-11.jpg\" alt=\"Honor Magic 3 Pro c&oacute; camera k&eacute;p ở ph&iacute;a trước\" /></p>\r\n<p>Ở ph&iacute;a trước, lỗ đục h&igrave;nh dạng vi&ecirc;n thuốc của Honor Magic 3 Pro c&oacute; chứa hai camera gồm camera ch&iacute;nh g&oacute;c rộng 13MP với trường nh&igrave;n 100 độ v&agrave; camera TOF 3D. C&aacute;c bức ảnh tự sướng ch&acirc;n dung được chụp từ điện thoại hứa hẹn cung cấp chất lượng tốt nhờ khả năng t&aacute;ch chủ thể v&agrave; l&agrave;m mờ nền ch&iacute;nh x&aacute;c.</p>\r\n<p><img id=\"Honor Magic 3 Pro sở hữu nhiều t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute;\" title=\"Honor Magic 3 Pro sở hữu nhiều t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute;\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-08.jpg\" alt=\"Honor Magic 3 Pro sở hữu nhiều t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute;\" /></p>\r\n<p>Honor Magic 3 Pro v&agrave; hai người anh em của n&oacute; l&agrave; những điện thoại th&ocirc;ng minh được chứng nhận IMAX Enhanced đầu ti&ecirc;n tr&ecirc;n thế giới. Ch&uacute;ng kh&ocirc;ng chỉ hỗ trợ ghi h&igrave;nh với định dạng LOG m&agrave; c&ograve;n hỗ trợ t&aacute;m cấu h&igrave;nh ph&acirc;n loại m&agrave;u 3D LUT chuy&ecirc;n nghiệp.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; nhiều c&ocirc;ng nghệ kết nối ti&ecirc;n tiến\" title=\"Honor Magic 3 Pro c&oacute; nhiều c&ocirc;ng nghệ kết nối ti&ecirc;n tiến\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-14.jpg\" alt=\"Honor Magic 3 Pro c&oacute; nhiều c&ocirc;ng nghệ kết nối ti&ecirc;n tiến\" /></p>\r\n<p>Về mặt kết nối, Honor Magic 3 Pro hỗ trợ hai SIM, 5G, WiFi 6, Bluetooth 5.2, NFC v&agrave; cổng USB Type-C (USB 3.1 Gen1, DP1.2). Một số t&iacute;nh năng phần mềm nổi bật của m&aacute;y bao gồm OS Turbo X, GPU Turbo X v&agrave; Link Turbo X. Điện thoại c&ograve;n c&oacute; cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh v&agrave; hỗ trợ nhận diện bằng khu&ocirc;n mặt.</p>\r\n<p><img id=\"Honor Magic 3 Pro c&oacute; chống nước IP68\" title=\"Honor Magic 3 Pro c&oacute; chống nước IP68\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-04.jpg\" alt=\"Honor Magic 3 Pro c&oacute; chống nước IP68\" /></p>\r\n<p>C&aacute;c t&iacute;nh năng kh&aacute;c của Honor Magic 3 Pro bao gồm ba micro, loa k&eacute;p &acirc;m thanh nổi, pin 4.600mAh, sạc nhanh c&oacute; d&acirc;y 66W v&agrave; sạc nhanh kh&ocirc;ng d&acirc;y 50W. Điện thoại cũng đạt được chứng nhận IP68 về khả năng chống bụi v&agrave; nước.</p>\r\n<p><img id=\"Honor Magic 3 Pro+\" title=\"Honor Magic 3 Pro+\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137974/Originals/tren-tay-honor-magic-3-pro-05.jpg\" alt=\"Honor Magic 3 Pro+\" /></p>\r\n<p><em>Honor Magic 3 Pro+ c&ograve;n c&oacute; nhiều t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute; hơn cả phi&ecirc;n bản Pro trong b&agrave;i viết</em></p>\r\n<p>Honor Magic 3 Pro sẽ l&ecirc;n kệ từ ng&agrave;y 20 th&aacute;ng 8. Mức gi&aacute; c&aacute;c phi&ecirc;n bản bộ nhớ của điện thoại như sau:</p>\r\n<ul>\r\n<li>Phi&ecirc;n bản RAM 8GB v&agrave; 256GB dung lượng lưu trữ: 5999 CNY (khoảng 21,3 triệu đồng).</li>\r\n<li>Phi&ecirc;n bản RAM 12GB v&agrave; 512GB dung lượng lưu trữ: 6799 CNY (khoảng 24 triệu đồng).</li>\r\n</ul>', 1, 3, 1, '2021-08-15 03:06:32', '2021-08-15 03:06:32', NULL),
(17, 'Điểm mặt loạt tính năng đáng tiền trên Lenovo Tab M8 Gen 2', 'diem-mat-loat-tinh-nang-dang-tien-tren-lenovo-tab-m8-gen-2', 'public/uploads/post/1\\lenovo-tab-m8-gen-2-2.jpg', 'lenovo-tab-m8-gen-2-2.jpg', '<p>Quay ngược thời gian trở về v&agrave;i năm trước, việc sở hữu một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">m&aacute;y t&iacute;nh bảng</a>&nbsp;l&agrave; niềm mơ ước của rất nhiều người. Tuy nhi&ecirc;n, ở thời điểm hiện tại, bạn c&oacute; thể mua rất dễ d&agrave;ng loại thiết bị n&agrave;y v&igrave; mức gi&aacute; của ch&uacute;ng đ&atilde; rất phải chăng. Trong ph&acirc;n kh&uacute;c dưới 4 triệu đồng,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang/lenovo-tab-m8-gen-2\">Lenovo Tab M8 Gen 2</a>&nbsp;l&agrave; một trong những lựa chọn rất đ&aacute;ng để quan t&acirc;m. Ở trong b&agrave;i viết n&agrave;y, ch&uacute;ng ta h&atilde;y c&ugrave;ng điểm qua những t&iacute;nh năng nổi trội khiến chiếc m&aacute;y t&iacute;nh bảng n&agrave;y đ&aacute;ng mua nh&eacute;.</p>\r\n<h3><strong>T&iacute;nh di động cao</strong></h3>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/2015/Tin-Tuc/BaoPK/Lumia950XL/Lenovo-tab-m8-gen-2-1.JPG\" alt=\"Lenovo Tab M8-Gen 2\" /></p>\r\n<p>Mang theo những thiết bị cồng kềnh lu&ocirc;n l&agrave; một &yacute; tưởng tồi, đặc biệt đối với những người thường xuy&ecirc;n di chuyển. Tuy nhi&ecirc;n, bạn sẽ kh&ocirc;ng gặp phải vấn đề đ&oacute; khi sử dụng Lenovo Tab M8 Gen 2. Với độ d&agrave;y chỉ 8,15mm v&agrave; trọng lượng 300 gram, bạn ho&agrave;n to&agrave;n c&oacute; thể dễ d&agrave;ng mang theo chiếc m&aacute;y t&iacute;nh bảng n&agrave;y tới bất kỳ nơi n&agrave;o. Chắc chắn Tab M8 Gen 2 sẽ l&agrave; một người bạn đồng h&agrave;nh tuyệt vời.</p>\r\n<p>B&ecirc;n cạnh sự nhỏ gọn cần thiết, Lenovo Tab M8 Gen 2 c&ograve;n sở hữu một d&aacute;ng vẻ cao cấp đến từ mặt lưng l&agrave;m từ kim loại v&agrave; c&aacute;c đường cong nhẹ nh&agrave;ng tr&ecirc;n th&acirc;n m&aacute;y. Ở trong tầm gi&aacute; 3 triệu đồng, bạn thường chỉ t&igrave;m thấy c&aacute;c m&aacute;y t&iacute;nh bảng được ho&agrave;n thiện từ nhựa. Sự tiện dụng, t&iacute;nh linh hoạt cao v&agrave; chất lượng ho&agrave;n thiện tốt l&agrave; một điểm cộng cho tablet của&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang/lenovo\">Lenovo</a>.</p>\r\n<h3><strong>Giao diện th&acirc;n thiện</strong></h3>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/2015/Tin-Tuc/BaoPK/Lumia950XL/Lenovo-tab-m8-gen-2-2.JPG\" alt=\"Thiết kế sang trọng mang n&eacute;t cao cấp\" /></p>\r\n<p>Lenovo cũng c&oacute; kinh doanh mảng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;th&ocirc;ng minh n&ecirc;n họ c&oacute; rất nhiều kinh nghiệm trong việc t&ugrave;y biến giao diện người d&ugrave;ng dựa tr&ecirc;n Android. Đối với Lenovo Tab M8 Gen 2, bạn c&oacute; thể coi n&oacute; như một chiếc điện thoại c&ugrave;ng thương hiệu được ph&oacute;ng to để cung cấp trải nghiệm giải tr&iacute; tốt hơn. Ch&iacute;nh v&igrave; thế, bạn cũng sẽ t&igrave;m thấy một giao diện người d&ugrave;ng khoa học v&agrave; cực kỳ dễ sử dụng tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng n&agrave;y. C&aacute;c ứng dụng v&agrave; t&iacute;nh năng c&oacute; thể dễ d&agrave;ng được t&igrave;m kiếm ngay tr&ecirc;n m&agrave;n h&igrave;nh rộng 8 inch độ ph&acirc;n giải 1280 x 800 pixel th&ocirc;ng qua c&aacute;c cử chỉ vuốt chạm hết sức đơn giản. Điều đ&oacute; thực sự rất c&oacute; &yacute; nghĩa đối với nhiều người d&ugrave;ng.</p>\r\n<h3><strong>Kho ứng dụng dồi d&agrave;o</strong></h3>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/2015/Tin-Tuc/BaoPK/Lumia950XL/Lenovo-tab-m8-gen-2-4.JPG\" alt=\"Trải nghiệm xuy&ecirc;n suốt 18 giờ kh&ocirc;ng nghỉ\" /></p>\r\n<p>Với việc sử dụng nền tảng Android, Lenovo Tab M8 Gen 2 được hưởng lợi rất nhiều từ sự đa dạng của hệ sinh th&aacute;i Google. Bạn c&oacute; thể dễ d&agrave;ng t&igrave;m kiếm v&agrave; c&agrave;i đặt c&aacute;c ứng dụng hoặc tr&ograve; chơi y&ecirc;u th&iacute;ch th&ocirc;ng qua cửa h&agrave;ng Google Play. Nhờ c&oacute; bộ nhớ trong l&ecirc;n tới 32GB, m&aacute;y t&iacute;nh bảng ho&agrave;n to&agrave;n đ&aacute;p ứng được nhu cầu sử dụng cơ bản của hầu hết mọi người. Bạn sẽ kh&ocirc;ng phải c&acirc;n nhắc việc n&ecirc;n x&oacute;a ứng dụng hay tr&ograve; chơi n&agrave;o trong suốt qu&aacute; tr&igrave;nh trải nghiệm Tab M8 Gen 2.</p>\r\n<h3><strong>Kết nối dễ d&agrave;ng</strong></h3>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/2015/Tin-Tuc/BaoPK/Lumia950XL/Lenovo-tab-m8-gen-2-8.JPG\" alt=\"Giải ph&aacute;p li&ecirc;n lạc tiện dụng cho mọi người\" /></p>\r\n<p>Lenovo Tab M8 Gen 2 sở hữu khe cắm thẻ SIM vật l&yacute;. Do đ&oacute;, bạn chỉ việc bỏ th&ecirc;m một số tiền nhất định để c&oacute; thể kết nối mạng mọi l&uacute;c mọi nơi. Đ&oacute; thực sự l&agrave; một điều tuyệt vời cho những ai lu&ocirc;n lu&ocirc;n phải giữ kết nối với mạng x&atilde; hội, email... Hơn nữa, m&aacute;y t&iacute;nh bảng n&agrave;y c&ograve;n c&oacute; khả năng nghe v&agrave; gọi độc lập th&ocirc;ng qua tr&igrave;nh gọi điện t&iacute;ch hợp trong phần mềm. Việc t&iacute;ch hợp camera đầy đủ ở cả ph&iacute;a trước v&agrave; sau c&ograve;n cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng thực hiện c&aacute;c cuộc gọi video qua nhiều nền tảng như Zalo, Viber, Skype,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tags/facebook\">Facebook</a>...</p>\r\n<h3><strong>Thời gian sử dụng d&agrave;i l&acirc;u</strong></h3>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/2015/Tin-Tuc/BaoPK/Lumia950XL/Lenovo-tab-m8-gen-2-5.JPG\" alt=\"Giải tr&iacute; đỉnh cao c&ugrave;ng Dolby Audio\" /></p>\r\n<p>Lenovo Tab M8 Gen 2 được trang bị vi&ecirc;n pin dung lượng 5000 mAh. Nhờ hệ điều h&agrave;nh tối ưu v&agrave; chip xử l&yacute; tiết kiệm năng lượng, m&aacute;y t&iacute;nh bảng ho&agrave;n to&agrave;n c&oacute; thể cung cấp thời gian sử dụng trong cả ng&agrave;y d&agrave;i chỉ với một lần sạc duy nhất. Mọi t&aacute;c vụ như gọi điện, chơi game, xem video... đều c&oacute; thể hoạt động liền mạch m&agrave; kh&ocirc;ng phải lo lắng về sự cố pin hết giữa chừng.</p>', 1, 3, 1, '2021-08-15 03:07:29', '2021-08-15 03:07:29', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image_post`, `image_post_name`, `content`, `status`, `category_post_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(18, 'Samsung mang đến những nâng cấp gì cho Galaxy Watch 4 so với Galaxy Watch 3?', 'samsung-mang-den-nhung-nang-cap-gi-cho-galaxy-watch-4-so-voi-galaxy-watch-3', 'public/uploads/post/1\\samsung-galaxy-watch-4-vs-galaxy-watch-3-cover.jpg', 'samsung-galaxy-watch-4-vs-galaxy-watch-3-cover.jpg', '<p dir=\"ltr\">Vậy so với thế hệ trước l&agrave; chiếc&nbsp;<a title=\"Galaxy Watch 3\" href=\"https://fptshop.com.vn/smartwatch/samsung-galaxy-watch-3-45mm-vien-thep-den-day-da\" type=\"Galaxy Watch 3\">Galaxy Watch 3</a>&nbsp;th&igrave; c&ocirc;ng ty H&agrave;n Quốc đ&atilde; mang đến những cải tiến v&agrave; n&acirc;ng cấp g&igrave; cho chiếc&nbsp;<a title=\"smartwatch\" href=\"https://fptshop.com.vn/smartwatch\" type=\"smartwatch\">smartwatch</a>&nbsp;mới nhất của họ để thu h&uacute;t người d&ugrave;ng? H&atilde;y c&ugrave;ng FPTShop t&igrave;m c&acirc;u trả lời qua b&agrave;i viết dưới đ&acirc;y.</p>\r\n<h2 dir=\"ltr\">Tạm biệt Tizen, xin ch&agrave;o Wear OS</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 1)\" title=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 1)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137978/Originals/Samsung-Galaxy-Watch-4-vs-Galaxy-Watch-3-1.jpg\" alt=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 1)\" /></p>\r\n<p dir=\"ltr\">Sự kh&aacute;c biệt lớn nhất v&agrave; r&otilde; r&agrave;ng nhất giữa hai&nbsp;<a href=\"https://fptshop.com.vn/smartwatch\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y l&agrave; việc&nbsp;<a title=\"Galaxy Watch 4\" href=\"https://fptshop.com.vn/smartwatch/dong-ho-samsung-galaxy-watch-4\" type=\"Galaxy Watch 4\">Galaxy Watch 4</a>&nbsp;chạy tr&ecirc;n phi&ecirc;n bản cải tiến hệ điều h&agrave;nh Wear OS của Google, với lớp giao diện người d&ugrave;ng độc quyền ở tr&ecirc;n c&ugrave;ng được gọi l&agrave; One UI Watch. Hệ thống n&agrave;y c&oacute; kh&aacute; nhiều t&iacute;nh năng mới lạ, hứa hẹn mang đến một trải nghiệm mới cho người d&ugrave;ng.</p>\r\n<p dir=\"ltr\">Theo đ&oacute;, ngo&agrave;i c&aacute;c ứng dụng quen thuộc với d&ograve;ng Galaxy Watch như Samsung Pay v&agrave; Spotify hoạt động ngoại tuyến th&igrave; giờ đ&acirc;y, người d&ugrave;ng Galaxy Watch 4 cũng c&oacute; thể sử dụng Google Pay, Google Maps. C&aacute;c ứng dụng n&agrave;y sẽ tự động đồng bộ với thư viện tr&ecirc;n smartphone, gi&uacute;p trải nghiệm tốt hơn, v&agrave; đ&acirc;y l&agrave; t&iacute;nh năng m&agrave; người d&ugrave;ng Galaxy Watch 3 kh&ocirc;ng thể c&oacute;.</p>\r\n<h2 dir=\"ltr\">Tốc độ nhanh hơn nhờ vi xử l&yacute; 5nm</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 2)\" title=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 2)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137978/Originals/Samsung-Galaxy-Watch-4-vs-Galaxy-Watch-3-4.jpg\" alt=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 2)\" /></p>\r\n<p dir=\"ltr\">B&ecirc;n cạnh chạy tr&ecirc;n hệ điều h&agrave;nh mới, Galaxy Watch 4 cũng c&oacute; hiệu năng cải tiến kh&aacute; nhiều so với thế hệ trước. Đ&oacute; l&agrave; bởi v&igrave; chiếc smartwatch mới của Samsung được trang bị bộ xử l&yacute; Exynos W920 mới, x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh 5nm ti&ecirc;n tiến. Chipset mới nhanh hơn 20% v&agrave; GPU nhanh hơn gấp 10 lần so với bộ xử l&yacute; c&oacute; mặt trong Galaxy Watch 3.</p>\r\n<p dir=\"ltr\">Với n&acirc;ng cấp n&agrave;y, khi sử dụng thực tế th&igrave; Galaxy Watch 4 sẽ cho ph&eacute;p người d&ugrave;ng sử dụng nhiều ứng dụng m&agrave; kh&ocirc;ng gặp hiện tượng giật lag. Điều n&agrave;y sẽ kh&aacute; hữu &iacute;ch nếu bạn đang nghe Spotify trong khi theo d&otilde;i qu&aacute; tr&igrave;nh chạy bộ của m&igrave;nh.</p>\r\n<h2 dir=\"ltr\">Một c&aacute;ch tiếp cận ho&agrave;n to&agrave;n mới để theo d&otilde;i hoạt động thể chất</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 3)\" title=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 3)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137978/Originals/Samsung-Galaxy-Watch-4-vs-Galaxy-Watch-3-6.jpg\" alt=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 3)\" /></p>\r\n<p dir=\"ltr\">Đối với Galaxy Watch 4, Samsung đ&atilde; học theo c&aacute;c c&ocirc;ng ty kh&aacute;c như&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/garmin\">Fitbit</a>&nbsp;để vượt ra ngo&agrave;i t&iacute;nh năng theo d&otilde;i thể dục v&agrave; bước v&agrave;o thế giới theo d&otilde;i sức khỏe. Điều n&agrave;y được chứng minh bởi cảm biến BioActive mới c&oacute; khả năng ph&acirc;n t&iacute;ch nhịp tim quang học, nhịp tim điện v&agrave; trở kh&aacute;ng điện sinh học của bạn.</p>\r\n<p dir=\"ltr\">Ngo&agrave;i c&aacute;c số liệu thống k&ecirc; ch&iacute;nh x&aacute;c hơn về huyết &aacute;p v&agrave; oxy trong m&aacute;u, cảm biến mới n&agrave;y cũng cho ph&eacute;p Galaxy Watch 4 tiến h&agrave;nh ph&eacute;p đo th&agrave;nh phần cơ thể người d&ugrave;ng để c&oacute; thể đ&aacute;nh gi&aacute; tỷ lệ phần trăm chất b&eacute;o, khối lượng cơ v&agrave; mức nước trong cơ thể của bạn. Tất cả điều n&agrave;y đều đ&oacute;ng vai tr&ograve; quan trọng trong việc x&aacute;c định sức khỏe tổng thể.</p>\r\n<h2 dir=\"ltr\">Độ ph&acirc;n giải m&agrave;n h&igrave;nh tốt hơn</h2>\r\n<p dir=\"ltr\">Để l&agrave;m cho One UI thực sự nổi bật, Samsung đ&atilde; sử dụng cả hai phi&ecirc;n bản 40mm v&agrave; 44mm của Galaxy Watch 4 c&ugrave;ng độ ph&acirc;n giải m&agrave;n h&igrave;nh. Điều n&agrave;y c&oacute; nghĩa l&agrave; phi&ecirc;n bản 40mm sẽ c&oacute; độ ph&acirc;n giải 396 x 396 pixel, cao hơn kh&aacute; nhiều so với mức 360 x 360 pixel của Galaxy Watch 3.</p>\r\n<h2 dir=\"ltr\">V&ograve;ng bezel xoay đ&atilde; chuyển sang dạng kỹ thuật số</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 4)\" title=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 4)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137978/Originals/Samsung-Galaxy-Watch-4-vs-Galaxy-Watch-3-3.jpg\" alt=\"So s&aacute;nh Galaxy Watch 4 v&agrave; Galaxy Watch 4 (ảnh 4)\" /></p>\r\n<p dir=\"ltr\">Một trong những t&iacute;nh năng nổi bật nhất của Galaxy Watch 3 l&agrave; khung viền bezel xoay, gi&uacute;p người d&ugrave;ng điều hướng dễ d&agrave;ng.</p>\r\n<p dir=\"ltr\">Đối với Galaxy Watch 4, Samsung đ&atilde; chọn một giải ph&aacute;p thay thế dưới dạng một khung bezel xoay kỹ thuật số theo d&otilde;i chuyển động của ng&oacute;n tay người d&ugrave;ng. Sự thay đổi c&oacute; thể kh&ocirc;ng ph&ugrave; hợp với thị hiếu của mọi người, nhưng &iacute;t nhất th&igrave; th&agrave;nh phần vật l&yacute; kh&ocirc;ng ho&agrave;n to&agrave;n biến mất khỏi c&aacute;c thiết bị đeo được của năm nay.</p>', 1, 3, 1, '2021-08-15 03:07:59', '2021-08-15 03:07:59', NULL),
(19, 'So sánh Samsung Galaxy Z Flip3 5G và Galaxy Z Flip 5G: Có đáng để nâng cấp?', 'so-sanh-samsung-galaxy-z-flip3-5g-va-galaxy-z-flip-5g-co-dang-de-nang-cap', 'public/uploads/post/1\\galaxy-z-flip3-5g-vs-galaxy-z-flip-5g-cover.jpg', 'galaxy-z-flip3-5g-vs-galaxy-z-flip-5g-cover.jpg', '<p dir=\"ltr\">Điều đ&oacute; c&oacute; nghĩa l&agrave;&nbsp;<a title=\"Galaxy Z Flip3 5G\" href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-flip-3?dung-luong=128-gb\" type=\"Galaxy Z Flip3 5G\">Galaxy Z Flip3 5G</a>&nbsp;l&agrave; phi&ecirc;n bản kế nhiệm của&nbsp;<a title=\"Galaxy Z Flip 5G\" href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-z-flip\" type=\"Galaxy Z Flip 5G\">Galaxy Z Flip 5G</a>, smartphone đ&atilde; ra mắt v&agrave;o th&aacute;ng 8 năm ngo&aacute;i. Vậy sau một năm,&nbsp;<a title=\"Samsung\" href=\"https://fptshop.com.vn/dien-thoai/samsung\" type=\"Samsung\">Samsung</a>&nbsp;đ&atilde; mang đến những cải tiến g&igrave; cho chiếc smartphone m&agrave;n h&igrave;nh gập mới của m&igrave;nh v&agrave; liệu c&oacute; đ&aacute;ng để n&acirc;ng cấp? H&atilde;y c&ugrave;ng FPTShop t&igrave;m c&acirc;u trả lời qua b&agrave;i viết dưới đ&acirc;y.</p>\r\n<h2 dir=\"ltr\">Thiết kế</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 1)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 1)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-1.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 1)\" /></p>\r\n<p dir=\"ltr\">Samsung cho biết họ đ&atilde; tập trung v&agrave;o những chi tiết v&agrave; h&igrave;nh dạng nhỏ nhất khi thiết kế Galaxy Z Flip3 5G. Nh&oacute;m kỹ sư của c&ocirc;ng ty H&agrave;n Quốc thậm ch&iacute; c&ograve;n t&iacute;nh đến đường đi của &aacute;nh s&aacute;ng phản chiếu khi x&aacute;c định c&aacute;c đường cong của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>. Họ đ&atilde; trải qua nhiều nguy&ecirc;n mẫu để tối ưu h&oacute;a h&igrave;nh dạng của smartphone n&agrave;y nhằm mang lại sự thoải m&aacute;i cho người d&ugrave;ng khi cầm nắm m&aacute;y sử dụng.</p>\r\n<p dir=\"ltr\">Ch&iacute;nh v&igrave; vậy m&agrave; Galaxy Z Flip3 5G mỏng, hẹp v&agrave; ngắn hơn một ch&uacute;t so với Galaxy Z Flip 5G. Ngo&agrave;i ra, model mới c&ograve;n sở hữu khung viền phẳng hơn v&agrave; viền bezel bao quanh m&agrave;n h&igrave;nh c&oacute; thể gập lại cũng hẹp hơn. Z Flip3 5G cũng c&oacute; xếp hạng IPX8, khiến n&oacute; trở th&agrave;nh chiếc Flip đầu ti&ecirc;n trong d&ograve;ng sản phẩm n&agrave;y c&oacute; khả năng chống nước.</p>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 2)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 2)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-3.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 2)\" /></p>\r\n<p dir=\"ltr\">Thay đổi lớn cuối c&ugrave;ng m&agrave; Samsung mang đến cho Galaxy Z Flip3 5G ch&iacute;nh l&agrave; mặt sau được thiết kế hai t&ocirc;ng m&agrave;u. N&oacute; tr&ocirc;ng nổi bật v&agrave; thu h&uacute;t hơn nhiều so với Galaxy Z Flip 5G.</p>\r\n<h2 dir=\"ltr\">C&aacute; nh&acirc;n h&oacute;a v&agrave; phụ kiện</h2>\r\n<p dir=\"ltr\">Galaxy Z Flip3 5G tr&ocirc;ng hiện đại hơn v&agrave; c&oacute; lớp ho&agrave;n thiện chống b&aacute;m dấu v&acirc;n tay tốt hơn so với Galaxy Z Flip 5G. Chiếc smartphone mới của Samsung cũng sở hữu nhiều m&agrave;u sắc hơn, l&ecirc;n tới con số 7 để người d&ugrave;ng lựa chọn cho ph&ugrave; hợp với sở th&iacute;ch c&aacute; nh&acirc;n của m&igrave;nh. Chưa hết, Samsung cũng đ&atilde; ph&aacute;t triển ba mẫu&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/bao-da-op-lung\">ốp lưng</a>&nbsp;kh&aacute;c nhau cho Galaxy Z Flip3 5G, v&agrave; tất cả đều c&oacute; nhiều m&agrave;u sắc, kiểu d&aacute;ng kh&aacute;c nhau, mang đến hơn một t&aacute; sự lựa chọn cho người d&ugrave;ng.</p>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 3)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 3)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-5.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 3)\" /></p>\r\n<p dir=\"ltr\">Cuối c&ugrave;ng, Samsung muốn cung cấp cho người d&ugrave;ng Galaxy Z Flip3 5G một c&aacute;ch để thể hiện bản th&acirc;n th&ocirc;ng qua m&agrave;n h&igrave;nh phụ lớn hơn ở ph&iacute;a sau. Cải tiến n&agrave;y gi&uacute;p mang đến nhiều kiểu&nbsp;<a href=\"https://fptshop.com.vn/dong-ho\">đồng hồ</a>&nbsp;kh&aacute;c nhau, đồng bộ h&oacute;a với mặt đồng hồ Galaxy Watch 4, th&ocirc;ng b&aacute;o chi tiết hơn,...</p>\r\n<h2 dir=\"ltr\">M&agrave;n h&igrave;nh</h2>\r\n<p dir=\"ltr\">Galaxy Z Flip3 5G v&agrave; Z Flip 5G dường như sử dụng chung tấm nền hiển thị của m&agrave;n h&igrave;nh ch&iacute;nh, nhưng model mới c&oacute; một v&agrave;i t&iacute;nh năng bổ sung thực sự quan trọng. Mặc d&ugrave; cả hai đều được m&ocirc; tả l&agrave; m&agrave;n h&igrave;nh Dynamic AMOLED 2X Infinity Flex 6.7 inch với độ ph&acirc;n giải 2,640 x 1,080 pixel, cho mật độ điểm ảnh 425ppi, nhưng Galaxy Z Flip3 5G hỗ trợ tần số qu&eacute;t 120Hz mượt m&agrave; hơn. Tấm nền mới cũng s&aacute;ng hơn 29% v&agrave; được phủ một lớp m&agrave;ng bảo vệ với độ bền cao hơn 80% so với thế hệ trước.</p>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 4)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 4)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-2.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 4)\" /></p>\r\n<p dir=\"ltr\">Samsung cũng đ&atilde; l&agrave;m việc để cải thiện t&igrave;nh trạng m&agrave;n h&igrave;nh bị che. M&agrave;n h&igrave;nh phụ mới c&oacute; k&iacute;ch thước 1.9 inch, tăng từ 1.1 inch của Z Flip 5G v&agrave; độ ph&acirc;n giải cao hơn l&agrave; 512 x 260 pixel. Về khả năng sử dụng, n&oacute; cung cấp c&aacute;c th&ocirc;ng b&aacute;o chi tiết hơn v&agrave; t&iacute;ch hợp với c&aacute;c ứng dụng của Samsung như Weather, Schedule, Alarm, Samsung Health, Buds Controller, Voice Recorder v&agrave; Timer.</p>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 5)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 5)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-4.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 5)\" /></p>\r\n<p dir=\"ltr\">Người d&ugrave;ng sẽ c&oacute; thể vuốt sang phải tr&ecirc;n m&agrave;n h&igrave;nh phụ để truy cập th&ocirc;ng b&aacute;o, sang tr&aacute;i để truy cập widget, vuốt xuống để c&agrave;i đặt nhanh v&agrave; l&ecirc;n tr&ecirc;n để truy cập Samsung Pay. Th&ocirc;ng b&aacute;o đi k&egrave;m với n&uacute;t &ldquo;Open App&rdquo;, đ&acirc;y l&agrave; c&aacute;ch Galaxy Z Flip3 5G sử dụng kh&aacute;i niệm App Continuity của ứng dụng. Sau khi chạm v&agrave;o, người d&ugrave;ng c&oacute; thể lật mở điện thoại để truy cập ngay v&agrave;o phi&ecirc;n bản đầy đủ của ứng dụng v&agrave; th&ocirc;ng b&aacute;o.</p>\r\n<h2 dir=\"ltr\">Cấu h&igrave;nh v&agrave; camera</h2>\r\n<p dir=\"ltr\">Giống như Z Fold3 5G, Galaxy Z Flip3 5G cũng được Samsung trang bị con chip Snapdragon 888 mạnh mẽ, nhưng chỉ với 8GB RAM c&ugrave;ng hai lựa chọn bộ nhớ trong 128GB v&agrave; 256GB. Do đ&oacute; m&agrave; hiệu năng của n&oacute; sẽ vượt trội Galaxy Z Flip 5G vốn chỉ sử dụng con chip Snapdragon 865+.</p>\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, camera của điện thoại n&agrave;y lại giống hệt so với thế hệ trước. Trong đ&oacute;, cụm hai camera sẽ bao gồm cảm biến ch&iacute;nh 12MP Dual Pixel OIS v&agrave; ống k&iacute;nh g&oacute;c si&ecirc;u rộng 12MP. Điện thoại n&agrave;y c&ograve;n sở hữu một camera selfie 10MP nằm trong phần đục lỗ của m&agrave;n h&igrave;nh ch&iacute;nh.</p>\r\n<p><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 6)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 6)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-91.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 6)\" /></p>\r\n<p dir=\"ltr\">Một n&acirc;ng cấp nhỏ về phần cứng m&agrave; Samsung mang đến cho Z Flip3 5G l&agrave; việc bổ sung loa stereo. Kết hợp với hiệu năng được n&acirc;ng cấp v&agrave; m&agrave;n h&igrave;nh 120Hz, ch&uacute;ng sẽ khiến chiếc smartphone m&agrave;n h&igrave;nh gập dọc mới của Samsung trở th&agrave;nh một chiếc điện thoại ph&ugrave; hợp hơn để thưởng thức nội dung đa phương tiện.</p>\r\n<h2 dir=\"ltr\">Gi&aacute; b&aacute;n</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 7)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 7)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-6.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 7)\" /></p>\r\n<p dir=\"ltr\">Năm ngo&aacute;i, Samsung đ&atilde; c&ocirc;ng bố chiếc Galaxy Z Flip 5G với mức gi&aacute; kh&aacute; cao l&agrave; 1,499 USD. Một năm sau, Galaxy Z Flip3 5G c&oacute; gi&aacute; khởi điểm rẻ hơn rất nhiều l&agrave; 999 USD, đ&aacute;nh dấu một cột mốc quan trọng trong d&ograve;ng thời gian của d&ograve;ng smartphone m&agrave;n h&igrave;nh gập của Samsung. Đ&acirc;y l&agrave; điện thoại c&oacute; thể gập lại đầu ti&ecirc;n của c&ocirc;ng ty c&oacute; gi&aacute; ra mắt dưới 1,000 USD.</p>\r\n<h2 dir=\"ltr\">Tạm kết</h2>\r\n<p dir=\"ltr\"><img id=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 8)\" title=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 8)\" src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137940/Originals/Galaxy-Z-Flip3-5G-vs-Galaxy-Z-Flip-5G-8.jpg\" alt=\"So s&aacute;nh Galaxy Z Flip3 5G v&agrave; Galaxy Z Flip 5G (ảnh 8)\" /></p>\r\n<p dir=\"ltr\">Qua b&agrave;i so s&aacute;nh tr&ecirc;n, c&oacute; thể khẳng định Galaxy Z Flip3 5G l&agrave; một bản n&acirc;ng cấp ho&agrave;n to&agrave;n xứng đ&aacute;ng so với Z Flip 5G. Mặc d&ugrave; c&oacute; gi&aacute; b&aacute;n rẻ hơn 500 USD nhưng điện thoại n&agrave;y nhận được rất nhiều cải tiến đ&aacute;ng gi&aacute; với thiết kế tinh tế hơn, khả năng kh&aacute;ng nước IPX8, hiệu năng mạnh mẽ v&agrave; m&agrave;n h&igrave;nh 120Hz cực kỳ chất lượng.</p>', 1, 3, 1, '2021-08-15 03:08:33', '2021-08-15 03:08:33', NULL),
(20, 'Top mini game offline cực hay cho PC cấu hình yếu', 'top-mini-game-offline-cuc-hay-cho-pc-cau-hinh-yeu', 'public/uploads/post/1\\15860850316052013074651.jpg', '15860850316052013074651.jpg', '<h2><strong>Dynomite&nbsp;</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/maxresdefault%20(1).jpg\" alt=\"Dynomite\" /></strong></p>\r\n<p>Dynomite,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/giai-tri\">game</a>&nbsp;bắn trứng khủng long quen thuộc v&agrave; nổi tiếng của h&atilde;ng Popcap. C&oacute; lẽ c&aacute;i t&ecirc;n Dynomite kh&ocirc;ng quen thuộc so với c&aacute;i t&ecirc;n game bắn trứng khủng long đ&atilde; gắn liền với tuổi thơ nhiều người.</p>\r\n<p>C&aacute;ch chơi đơn giản của Dynomite sẽ gi&uacute;p người chơi xả stress hiệu quả, bạn chỉ cần ngắm bắn tối thiểu 3 quả trứng c&ugrave;ng m&agrave;u để ch&uacute;ng nổ tung. Đ&ocirc;i khi bạn sẽ thấy những quả trứng đặc biệt v&agrave; nếu l&agrave;m nổ th&igrave; sẽ nhận được một số điểm lớn. Đồ họa trong game tuy đơn giản nhưng rất nhiều m&agrave;u sắc, c&aacute;c nh&acirc;n vật dễ thương v&agrave; vui nhộn</p>\r\n<p>C&oacute; nhiều chế độ chơi trong game bắn trứng khủng long gi&uacute;p người chơi kh&ocirc;ng cảm thấy nh&agrave;m ch&aacute;n. Để trải nghiệm tr&ograve; chơi với nhịp độ nhanh hơn bạn h&atilde;y chọn chế độ chơi Endless. Chế độ chơi Time Trial sẽ cho bạn thư gi&atilde;n với nhịp độ nhẹ nh&agrave;ng hơn. H&atilde;y tải ngay Dynamite để thư gi&atilde;n trong m&ugrave;a dịch n&agrave;y nếu bạn sở hữu một chiếc m&aacute;y t&iacute;nh kh&ocirc;ng qu&aacute; xịn x&ograve; nh&eacute;!</p>\r\n<h2><strong>Stardew Valley</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/stardewvalley-farm.png\" alt=\"Stardew Valley\" /></strong></p>\r\n<p>Nếu đ&atilde; từng chơi tựa game m&ocirc; phỏng nổi tiếng Harvest Moon, bạn sẽ c&oacute; cảm nhận rằng Stardew Valley giống như một hậu duệ của n&oacute;, bởi 2 game đều c&oacute; rất nhiều điểm tương đồng. Nhưng Stardew Valley game đ&atilde; mở rộng c&aacute;c t&iacute;nh năng, tăng cường sự tự do của người chơi để mang đến một trải nghiệm n&ocirc;ng trại say m&ecirc;, kh&oacute; l&ograve;ng dứt ra được.</p>\r\n<p>Bạn &ndash; nh&acirc;n vật ch&iacute;nh trong game, đang ch&aacute;n cuộc sống ồn &agrave;o nơi th&agrave;nh thị, được thừa hưởng một trang trại cũ của &ocirc;ng nội ở Stardew Valley. Với c&aacute;c n&ocirc;ng cụ thủ c&ocirc;ng v&agrave; một ch&uacute;t vốn, bạn sẽ ch&iacute;nh thức bắt đầu cuộc sống mới của m&igrave;nh ở thung lũng xinh đẹp n&agrave;y. Tựa game m&ocirc; phỏng n&ocirc;ng trại n&agrave;y c&oacute; gameplay v&ocirc; c&ugrave;ng s&ocirc;i nổi với v&ocirc; số hoạt động chăm s&oacute;c đồng &aacute;ng như cuốc đất, l&agrave;m cỏ, trồng c&acirc;y, chăn nu&ocirc;i gia s&uacute;c&hellip; v&agrave; giao lưu với những người h&agrave;ng x&oacute;m th&uacute; vị v&agrave; nhiều b&iacute; ẩn trong thung lũng Stardew.</p>\r\n<p>Kh&ocirc;ng quảng c&aacute;o ầm ĩ, kh&ocirc;ng cốt truyện phức tạp, kh&ocirc;ng đồ họa ho&agrave;nh tr&aacute;ng, Stardew Valley vẫn c&oacute; một sức h&uacute;t m&atilde;nh liệt khiến người chơi qu&ecirc;n ăn, qu&ecirc;n ngủ, đắm ch&igrave;m v&agrave;o thế giới của game để rồi khi dứt ra vẫn c&ograve;n cảm gi&aacute;c luyến tiếc!</p>\r\n<h2><strong>Grand Theft Auto Vice City</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/maxresdefault.jpg\" alt=\"Grand Theft Auto Vice City\" /></strong></p>\r\n<p>Grand Theft Auto: Vice City l&agrave; phần tiếp theo của huyền thoại GTA 3 . Lần n&agrave;y ch&uacute;ng ta đi từ Liberty City đến Vice City, một nơi được truyền cảm hứng r&otilde; r&agrave;ng từ Miami của những năm 1980 . Đối với những người h&acirc;m mộ c&aacute;c tựa game mang t&iacute;nh biểu tượng kh&aacute;c thuộc GTA Saga như GTA San Andreas, đ&oacute; l&agrave; một lựa chọn cổ điển.</p>\r\n<p>Ăn cắp xe hơi, tham gia v&agrave;o c&aacute;c cuộc tấn c&ocirc;ng v&agrave; bắn s&uacute;ng, v&agrave; tr&ecirc;n tất cả, tận dụng tối đa th&agrave;nh phố khổng lồ trải d&agrave;i, rực rỡ, trước mắt bạn.</p>\r\n<h2><strong>Bloons TD 6</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/Bloons-TD-6.jpg\" alt=\"Bloons TD 6\" /></strong></p>\r\n<p>Bloons TD 6 l&agrave; series game thủ th&agrave;nh v&ocirc; c&ugrave;ng hấp dẫn v&agrave; th&uacute; vị được sản xuất bởi Ninja Kiwi. Bloons TD 6 cho PC mang đến cho người chơi một giao diện mới hứa hẹn đem trở lại cơn sốt ng&agrave;y n&agrave;o tr&ecirc;n l&agrave;ng Game.</p>\r\n<p>Bloon TD 6 đưa game thủ đến phong c&aacute;ch thủ th&agrave;nh truyền thống nơi người chơi phải c&agrave;i cắm hệ thống trụ v&agrave; Hero với v&ocirc; v&agrave;n kỹ năng kh&aacute;c nhau. Bằng c&aacute;ch sắp đặt kh&eacute;o l&eacute;o v&agrave; n&acirc;ng cấp li&ecirc;n tục, game thủ sẽ phải l&agrave;m mọi gi&aacute; để ngăn kẻ th&ugrave; tr&agrave;n về như b&atilde;o lũ. Tiết tấu của Bloon TD 6 mang đ&uacute;ng phong c&aacute;ch nhanh gọn nhẹ nhưng cực g&acirc;y nghiện, vừa gi&uacute;p đẩy sức n&oacute;ng của gameplay l&ecirc;n cao vừa kh&ocirc;ng ti&ecirc;u tốn nhiều thời gian của người chơi.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, ngo&agrave;i lối chơi th&igrave; Bloon TD 6 c&ograve;n l&agrave; phi&ecirc;n bản đầu ti&ecirc;n được n&acirc;ng cấp l&ecirc;n đồ họa 3D, cho ph&eacute;p người chơi trải nghiệm những h&igrave;nh ảnh v&agrave; hiệu ứng mới đầy sắc m&agrave;u, cực vui nhộn.</p>\r\n<h2><strong>Feeding Frenzy 2</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/Feeding_Frenzy_2_titlescreen.jpg\" alt=\"Feeding Frezzy 2\" /></strong></p>\r\n<p>Feeding Frenzy 2 l&agrave; phi&ecirc;n bản tiếp theo của tr&ograve; chơi Feeding Frenzy - C&aacute; lớn nuốt c&aacute; b&eacute; nổi tiếng. Game cung cấp 60 cấp độ th&uacute; vị, ho&agrave;n to&agrave;n mới v&agrave; mang đậm t&iacute;nh giải tr&iacute;, ph&ugrave; hợp với mọi cấu h&igrave;nh m&aacute;y t&iacute;nh.</p>\r\n<p>C&aacute;ch chơi kh&aacute; đơn giản, giống với phi&ecirc;n bản Feeding Frenzy trước đ&oacute;, bạn chỉ cần sử dụng chuột để thao t&aacute;c điều khiển ch&uacute; c&aacute; của m&igrave;nh vượt qua c&aacute;c thử th&aacute;ch bằng c&aacute;ch ăn những con c&aacute; nhỏ hơn v&agrave; tr&aacute;nh c&aacute; lớn. C&agrave;ng về những cấp độ sau, con c&aacute; của bạn sẽ phải đối mặt với những kẻ th&ugrave; lớn v&agrave; hung dữ hơn như c&aacute; đuối gai, c&aacute; mập trắng. Những nhiệm vụ cũng kh&oacute; khăn hơn khi m&agrave; c&oacute; l&uacute;c bạn phải đuổi theo v&agrave; cắn v&agrave;o đu&ocirc;i con c&aacute; mập.</p>\r\n<h2><strong>Bejeweled&reg; 3</strong></h2>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/bejeweled-3-screenshot.jpg\" alt=\"Bejeweled&reg; 3\" /></p>\r\n<p>Bejeweled hay c&ograve;n được biết tới với t&ecirc;n gọi kh&aacute;c l&agrave; game xếp kim cương, một trong những tựa game kinh điển đ&atilde; trở th&agrave;nh m&oacute;n ăn tinh thần của rất nhiều thế hệ game thủ. V&agrave; sau một thời gian kh&aacute; d&agrave;i, phi&ecirc;n bản mới với t&ecirc;n gọi Bejeweled 3 tiếp tục mang đến những cuộc đua tr&iacute; tuệ gay cấn giữa người chơi với những vi&ecirc;n kim cương đầy hấp dẫn.</p>\r\n<p>Bejeweled&reg; 3 vẫn mang phong c&aacute;ch chơi quen thuộc của những phi&ecirc;n bản tiền nhiệm trước đ&oacute;. Đồng nghĩa với việc người chơi phải gh&eacute;p tối thiểu 3 vi&ecirc;n kim cương c&ugrave;ng m&agrave;u lại với nhau theo h&agrave;ng ngang, h&agrave;ng dọc hoặc h&igrave;nh L. B&ecirc;n cạnh đ&oacute;, để tăng phần hứng th&uacute; Bejeweled&reg; 3 c&ograve;n bổ sung t&iacute;nh năng Power - up, t&iacute;nh năng n&agrave;y đem lại cho bạn điểm số cao hơn gấp nhiều lần so với điểm số b&igrave;nh thường. Tuy nhi&ecirc;n để c&oacute; được người chơi phải ph&aacute; được 4-5 vi&ecirc;n kim cương c&ugrave;ng m&agrave;u tr&ecirc;n c&ugrave;ng h&agrave;ng hay cột. Đặc biệt người chơi c&ograve;n c&oacute; thể nhận được một vi&ecirc;n kim cương c&oacute; sức c&ocirc;ng ph&aacute; cực lớn c&oacute; thể nổ tung những vi&ecirc;n kim cương l&acirc;n cận.</p>\r\n<p>Tham gia Bejeweled&reg; 3, người c&oacute; thể trải nghiệm qua 4 m&agrave;n chơi kh&aacute;c nhau bao gồm: Classic, Zen, Lightning v&agrave; Ice Storm. Mỗi m&agrave;n chơi c&oacute; một đặc điểm v&agrave; sức thu h&uacute;t kh&aacute;c nhau. Tr&ograve; chơi tuy đơn giản nhưng dễ g&acirc;y nghiện v&agrave; cực nhẹ cho những chiếc m&aacute;y t&iacute;nh cấu h&igrave;nh thấp.</p>\r\n<h2><strong>Chicken Invaders 5</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137881/Originals/chicken-invaders-5-cluck-of-the-dark-side-2.jpg\" alt=\"Chicken Invader 5\" /></strong></p>\r\n<p>Chicken Invaders 5: Cluck of the Dark Side&nbsp;l&agrave; phi&ecirc;n bản mới trong series game bắn g&agrave; kinh điển của h&atilde;ng Interaction Studios. Căm hận trước sự &aacute;p bức của lo&agrave;i người, đội qu&acirc;n g&agrave; tiếp tục l&ecirc;n kế hoạch tấn c&ocirc;ng lần thứ 5 với hi vọng sẽ l&agrave;m cho tr&aacute;i đất đ&oacute;ng băng v&agrave; bị hủy diệt ho&agrave;n to&agrave;n.</p>\r\n<p>Nhiệm vụ của bạn trong game bắn g&agrave;&nbsp;Chicken Invader 5&nbsp;l&agrave; đ&aacute;nh lui đội qu&acirc;n g&agrave; đang c&oacute; &yacute; định x&acirc;m lược tr&aacute;i đất lần nữa. Phi&ecirc;n bản mới n&agrave;y c&oacute; nhiều thay đổi v&ocirc; c&ugrave;ng th&uacute; vị, từ chế độ chơi cho đến h&igrave;nh ảnh đo&agrave;n qu&acirc;n g&agrave;, những sinh vật mới, c&aacute;c loại vũ kh&iacute;, những h&agrave;nh tinh mới...Game thủ c&oacute; thể sử dụng chuột tr&aacute;i để bắn, chuột phải để ph&oacute;ng t&ecirc;n lửa, di chuyển chuột để điều khiển phi thuyền. C&aacute;ch thức chơi vẫn kh&ocirc;ng thay đổi nhưng kẻ th&ugrave; đ&atilde; được tăng cường gấp nhiều lần với những thứ vũ kh&iacute; m&agrave; ch&uacute;ng ta kh&ocirc;ng thể ngờ tới. Ngo&agrave;i ra game c&ograve;n đổi mới nhiều từ vũ kh&iacute; cho đến khả năng t&ugrave;y chỉnh chiến cơ v&agrave; c&aacute;c m&agrave;n chơi ho&agrave;n to&agrave;n mới.</p>', 1, 11, 1, '2021-08-15 03:15:42', '2021-08-15 03:15:42', NULL),
(21, 'Top 10 bộ phim nổi tiếng thế giới được trẻ em yêu thích', 'top-10-bo-phim-noi-tieng-the-gioi-duoc-tre-em-yeu-thich', 'public/uploads/post/1\\inside-out-nhung-manh-ghep-cam-xuc-2015.jpg', 'inside-out-nhung-manh-ghep-cam-xuc-2015.jpg', '<h2><strong>How to train your dragon - B&iacute; k&iacute;p luyện rồng (2010, 2014)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/How%20to%20train%20your%20dragon%20-%20B%C3%AD%20k%C3%ADp%20luy%E1%BB%87n%20r%E1%BB%93ng%20(2010%2C%202014).jpg\" alt=\"How to train your dragon - B&iacute; k&iacute;p luyện rồng (2010, 2014)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:</strong>&nbsp;Chris Sanders, Dean DeBlois</li>\r\n<li><strong>Diễn vi&ecirc;n:</strong>&nbsp;America ferrera, Christopher mint, Craig ferguson, Gerard Butler, Jay Baruchel, Jonah Hill</li>\r\n</ul>\r\n<p>Mặc d&ugrave; đều l&agrave; những &ldquo;đại gia&rdquo; tr&ecirc;n thị trường phim điện ảnh nhưng c&oacute; vẻ DreamWorks Animation c&oacute; đ&ocirc;i phần l&eacute;p vế trước Pixar v&agrave; Disney. Tuy nhi&ecirc;n, đ&oacute; chỉ l&agrave; trước khi c&oacute; sự ra đời của B&iacute; k&iacute;p luyện rồng. V&agrave;o năm 2010, B&iacute; k&iacute;p luyện rồng được c&ocirc;ng chiếu v&agrave; tạo n&ecirc;n tiếng vang lớn khi những gi&aacute; trị của bộ phim mang lại ngo&agrave;i những hiệu ứng kĩ xảo đẹp mắt c&ograve;n l&agrave; cậu chuyện khơi gợi bản năng y&ecirc;u thương động vật của trẻ em với t&igrave;nh bạn tuyệt đẹp của ch&uacute; b&eacute; người Viking t&ecirc;n Hiccup v&agrave; ch&uacute; rồng m&agrave; cậu đặt biệt danh l&agrave; Răng S&uacute;n.</p>\r\n<p>V&agrave; kh&ocirc;ng như những bộ phim trước đ&oacute; của DreamWorks khi chỉ tuyển trọn những ng&ocirc;i sao nổi tiếng để lồng tiếng c&aacute;c nh&acirc;n vật của m&igrave;nh, B&iacute; k&iacute;p luyện rồng đ&atilde; đi s&acirc;u l&agrave;m nổi bật nội dung của phim v&agrave; đi đến một thắng lợi tuyệt đối.</p>\r\n<h2><strong>Frozen - Nữ ho&agrave;ng băng gi&aacute; (2013)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/Frozen%20-%20N%E1%BB%AF%20ho%C3%A0ng%20b%C4%83ng%20gi%C3%A1%20(2013).jpg\" alt=\"Frozen - Nữ ho&agrave;ng băng gi&aacute; (2013)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:&nbsp;</strong>Chris Buck, Jennifer Lee</li>\r\n<li><strong>Diễn vi&ecirc;n:&nbsp;</strong>Kristen Bell, Josh Gad, Idina Menzel</li>\r\n</ul>\r\n<p>Được dựa tr&ecirc;n c&acirc;u chuyện cổ t&iacute;ch B&agrave; Ch&uacute;a tuyết (The Snow Queen) của nh&agrave; văn Đan Mạch Hans Christian Andersen được h&agrave;ng triệu đứa trẻ ở nhiều thế hệ y&ecirc;u mến trong nhiều năm qua, Frozen kể về h&agrave;nh tr&igrave;nh của n&agrave;ng c&ocirc;ng ch&uacute;a trẻ gan dạ Anna đi t&igrave;m người chị g&aacute;i của m&igrave;nh l&agrave; B&agrave; Ch&uacute;a Tuyết Elsa. Elsa l&agrave; người trị v&igrave; vương quốc Arendelle v&agrave; từ b&eacute; đ&atilde; c&oacute; quyền năng điều khiển gi&oacute;, băng tuyết nhưng lại mắc kẹt trong vương quốc m&ugrave;a đ&ocirc;ng vĩnh cửu v&igrave; một lời nguyền v&ocirc; h&igrave;nh. Sau khi t&igrave;nh cờ đ&oacute;ng băng cả vương quốc, Elsa thu m&igrave;nh lại, kh&ocirc;ng muốn tiếp x&uacute;c với bất kỳ ai v&agrave; muốn đắm ch&igrave;m m&atilde;i trong m&ugrave;a đ&ocirc;ng.</p>\r\n<p>Đồng h&agrave;nh c&ugrave;ng c&ocirc;ng ch&uacute;a Anna trong chuyến phi&ecirc;u lưu tới xứ sở m&ugrave;a đ&ocirc;ng l&agrave; anh ch&agrave;ng leo n&uacute;i Kristoff chuy&ecirc;n kinh doanh nước đ&aacute;, ch&uacute; tuần lộc Sven trung th&agrave;nh, vui nhộn v&agrave; người tuyết biết n&oacute;i Olaf. Tại miền đất c&oacute; kh&iacute; hậu khắc nghiệt với băng tuyết v&agrave; gi&aacute; lạnh bao phủ, cả nh&oacute;m vượt qua nhiều thử th&aacute;ch, chiến đấu với cả những con s&oacute;i hung dữ v&agrave; Người Tuyết khổng lồ để tới được vương quốc m&ugrave;a đ&ocirc;ng t&igrave;m kiếm Elsa.</p>\r\n<p>Nhiều nh&agrave; ph&ecirc; b&igrave;nh cho rằng đ&acirc;y l&agrave; bộ phim hoạt h&igrave;nh nhạc kịch hay nhất của Disney từ kỷ nguy&ecirc;n phục hưng của h&atilde;ng. V&igrave; vậy, cũng kh&ocirc;ng ngạc nhi&ecirc;n khi phim th&agrave;nh c&ocirc;ng ngoạn mục về thương mại khi thu về tr&ecirc;n 1,2 tỉ USD doanh thu ph&ograve;ng v&eacute; to&agrave;n cầu, trở th&agrave;nh phim hoạt h&igrave;nh c&oacute; doanh thu cao nhất hiện nay. B&ecirc;n cạnh đ&oacute;, bộ phim cũng được đ&aacute;nh gi&aacute; cao ở mặt nghệ thuật khi gi&agrave;nh được hai giải Oscar ở hạng mục Phim hoạt h&igrave;nh hay nhất v&agrave; Ca kh&uacute;c trong phim hay nhất (Let It Go), một giải Quả cầu v&agrave;ng cho Phim hoạt h&igrave;nh hay nhất, giải BAFTA c&ugrave;ng hạng mục v&agrave; nhiều giải thưởng danh gi&aacute; kh&aacute;c.</p>\r\n<h2><strong>Maleficent - Ti&ecirc;n hắc &aacute;m (2014)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/Maleficent%20-%20Ti%C3%AAn%20h%E1%BA%AFc%20%C3%A1m%20(2014).jpg\" alt=\"Maleficent - Ti&ecirc;n hắc &aacute;m (2014)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:</strong>&nbsp;Robert Stromberg</li>\r\n<li><strong>Diễn vi&ecirc;n:</strong>&nbsp;Angelina Jolie, Sharlto Copley, Elle Fanning, Brenton Thwaites, Sam Riley, Miranda Richardson</li>\r\n</ul>\r\n<p>Maleficent l&agrave; bộ phim thần ti&ecirc;n kỳ ảo, viễn tưởng của Mỹ năm 2014. Kịch bản phim được viết dựa tr&ecirc;n t&aacute;c phẩm nổi tiếng C&ocirc;ng Ch&uacute;a Ngủ Trong Rừng, chỉ kh&aacute;c trong phim g&oacute;c nh&igrave;n được khai th&aacute;c từ ph&iacute;a nh&acirc;n vật phản diện.</p>\r\n<p>Maleficent từng l&agrave; một n&agrave;ng ti&ecirc;n xinh đẹp ở vương Quốc ma thuật, c&ocirc; đem l&ograve;ng y&ecirc;u một ch&agrave;ng trai lo&agrave;i người nhưng đ&atilde; bị phản bội, anh ta đ&atilde; cắt đ&ocirc;i c&aacute;nh của c&ocirc; để đổi lấy ngai v&agrave;ng. Sau khi trở th&agrave;nh nạn nh&acirc;n của l&ograve;ng tham của đ&agrave;n &ocirc;ng v&agrave; được hướng dẫn bởi &yacute; ch&iacute; b&aacute;o th&ugrave; để bảo vệ v&ugrave;ng đất m&agrave; m&igrave;nh nắm giữ, Maleficent t&agrave;n nhẫn đặt lời nguyền l&ecirc;n C&ocirc;ng ch&uacute;a Aurora, con g&aacute;i duy nhất của&nbsp; Vua St&eacute;phane v&agrave; Nữ ho&agrave;ng Oriane. V&agrave;o sinh nhật 16 tuổi, n&agrave;ng C&ocirc;ng Ch&uacute;a sẽ tự đ&acirc;m v&agrave;o trục quay của một b&aacute;nh xe quay v&agrave; ngủ m&atilde;i m&atilde;i, Chỉ một nụ h&ocirc;n của t&igrave;nh y&ecirc;u ch&acirc;n th&agrave;nh mới c&oacute; thể cứu vớt c&ocirc;. Thế nhưng khi lời nguyền được k&iacute;ch hoạt, Maleficent bắt đầu hối hận v&agrave; những rủi ro xảy đến cho cả Vương Quốc. Liệu l&agrave;m thế n&agrave;o để Aurora c&oacute; thể tỉnh lại? Cuộc chiến của hai vương Quốc v&igrave; n&agrave;ng C&ocirc;ng Ch&uacute;a n&agrave;y sẽ đi về đ&acirc;u?</p>\r\n<p>Bộ phim thay đổi g&oacute;c nh&igrave;n mới, cho thấy được nỗi đau v&agrave; l&yacute; do của người phản diện Maleficent, qua đ&oacute; l&agrave;m cho c&acirc;u chuyện th&ecirc;m phần &yacute; nghĩa v&agrave; mở rộng. Ti&ecirc;n Hắc &Aacute;m c&oacute; kỹ thuật h&igrave;nh ảnh sinh động, sắc sảo, d&agrave;n diễn vi&ecirc;n xinh đẹp diễn rất tr&ograve;n vai, tổng thể cốt truyện nhẹ nh&agrave;ng v&agrave; rất th&iacute;ch hợp cho một lựa chọn phim hay.</p>\r\n<h2><strong>Big Hero 6 - Biệt Đội Big Hero 6 (2014)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/Big%20Hero%206%20-%20Bi%E1%BB%87t%20%C4%90%E1%BB%99i%20Big%20Hero%206%20(2014).jpg\" alt=\"Big Hero 6 - Biệt Đội Big Hero 6 (2014)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:</strong>&nbsp;Don Hall, Chris Williams</li>\r\n<li><strong>Diễn vi&ecirc;n:&nbsp;</strong>Ryan Potter, Scott Adsit, Jamie Chung, Damon Wayans, Jr., G&eacute;nesis Rodr&iacute;guez, T. J. Miller, Maya Rudolph</li>\r\n</ul>\r\n<p>Big Hero 6 l&agrave; một c&acirc;u chuyện d&agrave;nh cho mọi lứa tuổi v&agrave; tất cả mọi người đều c&oacute; thể cười thả ga khi thưởng thức. Nội dung phim hội đủ yếu tố t&acirc;m l&yacute;, h&agrave;nh động, phi&ecirc;u lưu, khoa học v&agrave; cả giới t&iacute;nh. Kh&ocirc;ng cần một triết l&yacute; qu&aacute; cao si&ecirc;u, Big Hero 6 mang đến cảm gi&aacute;c vui vẻ cho trẻ em, h&agrave;i hước cho người lớn v&agrave; thỏa m&atilde;n cho những kh&aacute;n giả lớn tuổi v&agrave; kh&oacute; t&iacute;nh nhất.</p>\r\n<p>Phim kể về Hiro Hamada l&agrave; một học sinh tốt nghiệp trung học 14 tuổi v&agrave; l&agrave; thi&ecirc;n t&agrave;i người m&aacute;y sống ở th&agrave;nh phố tương lai San Fransokyo, cậu được anh trai -Tadashi đưa anh đến ph&ograve;ng th&iacute; nghiệm nghi&ecirc;n cứu tại Viện C&ocirc;ng nghệ San Fransokyo, nơi Hiro gặp gỡ bạn b&egrave; của Tadashi, Go Go, Wasabi, Honey Lemon v&agrave; Fred. Hiro quyết định nộp đơn v&agrave;o trường đại học v&agrave; cũng từ đ&acirc;y, cậu c&ugrave;ng nh&oacute;m bạn bắt đầu chuỗi những ng&agrave;y th&aacute;ng cố gắng bảo vệ thế giới bằng c&aacute;ch tạo ra Big Hero 6.</p>\r\n<p>Big Hero 6 được c&ocirc;ng chiếu tại Li&ecirc;n hoan phim quốc tế Tokyo lần thứ 27 v&agrave;o ng&agrave;y 23 th&aacute;ng 10 năm 2014. Bộ phim đ&atilde; đạt được cả th&agrave;nh c&ocirc;ng quan trọng v&agrave; thương mại, thu về hơn 657,8 triệu đ&ocirc; la tr&ecirc;n to&agrave;n thế giới v&agrave; trở th&agrave;nh bộ phim hoạt h&igrave;nh c&oacute; doanh thu cao nhất tr&ecirc;n to&agrave;n thế giới 2014. N&oacute; đ&atilde; gi&agrave;nh giải Oscar cho Phim hoạt h&igrave;nh hay nhất v&agrave; h&agrave;ng loạt c&aacute;c giải thưởng danh gi&aacute; kh&aacute;c.</p>\r\n<h2><strong>Inside Out - Những mảnh gh&eacute;p cảm x&uacute;c (2015)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/Inside%20Out%20-%20Nh%E1%BB%AFng%20m%E1%BA%A3nh%20gh%C3%A9p%20c%E1%BA%A3m%20x%C3%BAc%20(2015).jpg\" alt=\"Inside Out - Những mảnh gh&eacute;p cảm x&uacute;c (2015)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:</strong>&nbsp;Pete Docter</li>\r\n<li><strong>Diễn vi&ecirc;n:</strong>&nbsp;Amy Poehler, Phyllis Smith, Lewis Black, Mindy Kaling, Bill Hader, Kaitlyn Dias</li>\r\n</ul>\r\n<p>&ldquo;Những mảnh gh&eacute;p cảm x&uacute;c&rdquo; l&agrave; bộ phim hoạt h&igrave;nh thiếu nhi, bối cảnh của phim chủ yếu l&agrave; b&ecirc;n trong n&atilde;o bộ của c&ocirc; b&eacute; Riley Anderson (Kaitlyn Dias thủ vai). Qu&aacute; tr&igrave;nh lớn l&ecirc;n l&agrave; một con đường gập ghềnh v&agrave; nhiều cảm x&uacute;c, với Riley cũng vậy. Riley buộc phải rời Minnesota y&ecirc;u dấu để chuyển đến San Francisco, v&igrave; cha c&ocirc; chuyển c&ocirc;ng t&aacute;c đến đ&acirc;y. Riley được những người bạn sống trong đại n&atilde;o của m&igrave;nh, bao gồm: Joy (Vui Vẻ), Anger (Giận Dữ), Disgust (Ch&aacute;n Gh&eacute;t), Fear (Sợ H&atilde;i) v&agrave; Sadness (Buồn B&atilde;) tư vấn cảm x&uacute;c cho c&ocirc; trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n<p>Khi Riley v&agrave; cảm x&uacute;c của c&ocirc; đấu tranh để th&iacute;ch nghi với cuộc sống mới ở San Francisco, t&igrave;nh trạng hỗn loạn xảy ra ở Trụ sở ch&iacute;nh. Cuốn chiến hỗn độn của c&aacute;c cảm x&uacute;c khiến c&ocirc; b&eacute; Riley hoạt b&aacute;t, đ&aacute;ng y&ecirc;u dần trở n&ecirc;n v&ocirc; cảm v&agrave; xa c&aacute;ch với bố mẹ. Nhận ra sự thay đổi ti&ecirc;u cực của Riley, từ đ&acirc;y những người bạn cảm x&uacute;c bắt đầu h&agrave;nh tr&igrave;nh t&igrave;m lại c&ocirc; b&eacute; hạnh ph&uacute;c, gần gũi bố mẹ ng&agrave;y xưa.</p>\r\n<p>Bộ phim được c&ocirc;ng chiếu lần đầu ti&ecirc;n v&agrave;o 18 th&aacute;ng 5 năm 2015 tại Li&ecirc;n hoan phim Cannes lần thứ 68, nhận được rất nhiều lời khen t&iacute;ch cực. V&agrave; nhận được rất nhiều giải thưởng, cũng như danh s&aacute;ch đề cử của c&aacute;c giải thưởng danh gi&aacute;. Ngo&agrave;i ra, n&oacute; c&ograve;n l&agrave; một th&agrave;nh c&ocirc;ng về mặt thương mại, khi thu về 818,6 triệu đ&ocirc; la so với 175 triệu đ&ocirc; la kinh ph&iacute;.</p>\r\n<h2><strong>The Jungle Book - Cậu b&eacute; rừng xanh (2016)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137783/Originals/The%20Jungle%20Book%20-%20C%E1%BA%ADu%20b%C3%A9%20r%E1%BB%ABng%20xanh%20(2016).jpg\" alt=\"The Jungle Book - Cậu b&eacute; rừng xanh (2016)\" /></strong></p>\r\n<ul>\r\n<li><strong>Đạo diễn:&nbsp;</strong>Jon Favreau</li>\r\n<li><strong>Diễn vi&ecirc;n:&nbsp;</strong>Bill Murray, Ben Kingsley, Idris Elba, Lupita Nyong&rsquo;o, Scarlett Johansson, Giancarlo Esposito, Christopher Walken, Neel Sethi</li>\r\n</ul>\r\n<p>&ldquo;Cậu b&eacute; rừng xanh&rdquo; l&agrave; h&agrave;nh tr&igrave;nh phi&ecirc;u lưu của Mowgli. Mowgli được nu&ocirc;i dưỡng bởi con s&oacute;i Raksha v&agrave; bầy đ&agrave;n của c&ocirc;, tại một khu rừng Ấn Độ kể từ khi anh được con b&aacute;o đen Bagheera đưa đến khi c&ograve;n b&eacute;. Bagheera huấn luyện Mowgli học c&aacute;ch của những con s&oacute;i, nhưng cậu b&eacute; phải đối mặt với những thử th&aacute;ch nhất định v&agrave; tụt lại ph&iacute;a sau anh chị em s&oacute;i của m&igrave;nh.</p>\r\n<p>B&ecirc;n cạnh đ&oacute; Akela &ndash; con s&oacute;i đầu đ&agrave;n- kh&ocirc;ng chấp nhận những c&aacute;ch thức kiếm sống của&nbsp; Mowgli, những phương ph&aacute;p mang t&iacute;nh bản năng của lo&agrave;i người. Cho đến khi, con hổ Shere Khan đến gần bầy s&oacute;i v&agrave; ph&aacute;t hiện m&ugrave;i hương của Mowgli. Shere Khan dường như căm th&ugrave; con người &ndash; những kẻ đ&atilde; để lại vết sẹo lớn cho n&oacute;, Khan đưa ra lời cảnh b&aacute;o rằng anh ta sẽ giết Mowgli v&agrave;o cuối đợt hạn h&aacute;n. M&ugrave;a kh&ocirc; sắp qua đi, Mowgli nghe theo lời khuy&ecirc;n của mọi người trong đ&agrave;n v&agrave; Bagheera đi đến một nơi an to&agrave;n. Anh bắt đầu một chuyến đi đầy gian nan v&agrave; th&uacute; vị.</p>\r\n<p>Bộ phim đ&atilde; trở th&agrave;nh một th&agrave;nh c&ocirc;ng lớn về mặt thương mại, khi thu về 966,6 triệu đ&ocirc; la tr&ecirc;n to&agrave;n thế giới. Lợi nhuận r&ograve;ng của bộ phim l&agrave; 258 triệu đ&ocirc; la, khi bao gồm tất cả c&aacute;c chi ph&iacute; v&agrave; doanh thu cho bộ phim, khiến n&oacute; trở th&agrave;nh bản ph&aacute;t h&agrave;nh c&oacute; lợi nhuận cao thứ s&aacute;u năm 2016.</p>', 1, 11, 1, '2021-08-15 03:16:17', '2021-08-15 03:16:17', NULL);
INSERT INTO `posts` (`id`, `title`, `slug`, `image_post`, `image_post_name`, `content`, `status`, `category_post_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(22, 'Top game cờ nhân phẩm đang được chơi nhiều nhất năm 2021', 'top-game-co-nhan-pham-dang-duoc-choi-nhieu-nhat-nam-2021', 'public/uploads/post/1\\637643899084750084_f3.jpg', '637643899084750084_f3.jpg', '<p>Game cờ nh&acirc;n phẩm (game Auto Chess hay game Battle Royale) c&oacute; thể hiểu theo một c&aacute;ch kh&aacute;c l&agrave; sự kết hợp của thể loại game chiến thuật v&agrave; game thẻ b&agrave;i. Người chơi sẽ kh&ocirc;ng trực tiếp điều khiển nh&acirc;n vật như những game nhập vai th&ocirc;ng thường m&agrave; họ sẽ mua tướng, sắp xếp đội h&igrave;nh v&agrave; dựa tr&ecirc;n t&iacute;nh tương hỗ, tương khắc lẫn nhau giữa c&aacute;c chất tướng m&agrave; đưa ra chiến thuật với từng đối thủ, trong từng trận đ&aacute;nh.</p>\r\n<p>Nguy&ecirc;n l&yacute; chiến trận của cờ nh&acirc;n phẩm cũng kh&aacute; đặc biệt, n&oacute; diễn ra cố định tr&ecirc;n một b&agrave;n cờ, mỗi qu&acirc;n sẽ ở trong một &ocirc;. C&oacute; thể chơi với nhiều chế độ kh&aacute;c nhau, nhưng mỗi người chơi sẽ c&oacute; trước một điểm số nhất định v&agrave; chiến thắng sẽ chỉ d&agrave;nh cho người cuối c&ugrave;ng c&ograve;n sống s&oacute;t.</p>\r\n<h2><strong>Đấu Trường Ch&acirc;n L&yacute; (PC)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/tai-dau-truong-chan-ly-mobile-1.jpg\" alt=\"Đấu trường ch&acirc;n l&yacute;\" /></strong></p>\r\n<p>Đứng đầu danh s&aacute;ch n&agrave;y chắc chắn phải n&oacute;i đến tựa game Đấu Trường Ch&acirc;n L&yacute;. Tr&ograve; chơi thuộc thể loại Auto Chess n&agrave;y được ph&aacute;t triển bởi Riot với bản tr&ecirc;n PC v&agrave; thu h&uacute;t đ&ocirc;ng đảo game thủ Việt từ những ng&agrave;y đầu ra mắt. Game c&ograve;n c&oacute; t&ecirc;n tiếng Anh l&agrave; Teamfight Tactics.</p>\r\n<p>Với Đấu Trường Ch&acirc;n L&yacute;, bạn sẽ đối đầu với 7 đối thủ c&ugrave;ng l&uacute;c, cần phải vận dụng sức mạnh tướng được mua bằng v&agrave;ng c&ugrave;ng với kĩ năng xếp đội h&igrave;nh để sống s&oacute;t đến cuối c&ugrave;ng. Trong mỗi v&aacute;n chơi, bạn sẽ d&ugrave;ng v&agrave;ng để chi&ecirc;u mộ những vị tướng từ nhiều hệ tộc về đội m&igrave;nh. Trong xuy&ecirc;n suốt trận đấu, đội h&igrave;nh của bạn sẽ dần trở n&ecirc;n mạnh mẽ hơn với những vị tướng c&ugrave;ng hệ tộc si&ecirc;u phẩm. Th&ecirc;m v&agrave;o đ&oacute; bạn cũng c&oacute; thể kết hợp ba vị tướng giống nhau để tạo n&ecirc;n một phi&ecirc;n bản si&ecirc;u cấp của vị tướng đ&oacute; v&agrave; kết hợp ba vị tướng si&ecirc;u cấp c&ugrave;ng loại để tạo n&ecirc;n một phi&ecirc;n bản tối thượng.</p>\r\n<p>C&aacute;c vị tướng trong Đấu Trường Ch&acirc;n L&yacute; cũng lấy từ tựa game nổi tiếng Li&ecirc;n Minh Huyền Thoại. Hiện bản mobile vẫn đang trong qu&aacute; tr&igrave;nh ph&aacute;t triển c&ugrave;ng với LMHT: Tốc Chiến, hứa hẹn sẽ sớm ra mắt game thủ Việt trong năm 2021.</p>\r\n<h2><strong>Auto Chess Mobile (iOS &amp; Android)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/Auto-chess-mobile-phong-vu-1.jpg\" alt=\"Auto Chess Mobile\" /></strong></p>\r\n<p>Auto Chess Mobile l&agrave; tr&ograve; chơi cờ nh&acirc;n phẩm hiện đang sở hữu nhiều lượt tải nhất tr&ecirc;n nền tảng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>. L&agrave; sự kết hợp ho&agrave;n hảo giữa thể loại chiến thuật theo lượt v&agrave; d&ograve;ng game đấu thẻ tướng, game đ&ograve;i hỏi người chơi phải t&iacute;nh to&aacute;n, suy nghĩ t&iacute;nh kĩ nước đi của m&igrave;nh trong từng v&ograve;ng đấu.</p>\r\n<p>Tất nhi&ecirc;n người chơi cũng cần tới kh&aacute; nhiều sự may mắn th&igrave; mới c&oacute; thể d&agrave;nh chiến thắng một v&aacute;n cờ trước 7 đối thủ kh&aacute;c. Được biết mỗi qu&acirc;n cờ trong game n&agrave;y l&agrave; một Hero n&ecirc;n ch&uacute;ng cũng sở hữu ch&iacute;nh x&aacute;c c&aacute;c bộ skill, chỉ số m&agrave; bạn thường thấy trong Dota 2. Bạn thậm ch&iacute; c&ograve;n c&oacute; thể trang bị item cho ch&uacute;ng th&ocirc;ng qua việc farm creep, tạo n&ecirc;n một tầng lớp tiến h&oacute;a chuy&ecirc;n s&acirc;u nữa cho Auto Chess Mobile.</p>\r\n<h2><strong>Dota Underlords (PC, iOS &amp; Android)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/dota-underlords.png\" alt=\"Dota Underlords\" /></strong></p>\r\n<p>Dota Underlords l&agrave; phi&ecirc;n bản Auto Chess - game cờ nh&acirc;n phẩm mới nhất m&agrave; &ocirc;ng lớn Valve giới thiệu tới người h&acirc;m mộ to&agrave;n cầu. Hiện Dota Underlords đ&atilde; c&oacute; phi&ecirc;n bản cho m&aacute;y t&iacute;nh, iOS v&agrave; Android tr&ecirc;n c&aacute;c cửa h&agrave;ng ứng dụng.</p>\r\n<p>Như c&aacute;c tựa game thể loại Auto Chess kh&aacute;c, trong game Dota Underlords, người chơi sẽ tuyển mộ qu&acirc;n đội v&agrave; ti&ecirc;u diệt kẻ th&ugrave; trong c&aacute;c trận đ&aacute;nh chiến thuật thuộc thế giới Dota. Mỗi anh h&ugrave;ng trong Dota Underlords đều c&oacute; những thế mạnh ri&ecirc;ng. Kết hợp sức mạnh của mỗi anh h&ugrave;ng để tạo n&ecirc;n li&ecirc;n minh bất khả chiến bại, từ đ&oacute; mở kh&oacute;a bonus v&agrave; chiếm được lợi thế nhiều hơn tr&ecirc;n chiến trường.</p>\r\n<h2><strong>Auto Chess War (iOS &amp; Android)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/maxresdefault.jpg\" alt=\"Auto Chess War\" /></strong></p>\r\n<p>Lấy bối cảnh trong một thế giới giả tưởng thời Trung Cổ nơi c&aacute;c chủng tộc như chằn tinh, y&ecirc;u tinh v&agrave; c&aacute;c sinh vật ma thuật kh&aacute;c sinh sống, Auto Chess War cũng l&agrave; một c&aacute;i t&ecirc;n đ&aacute;ng nhắc đến trong thị trường game cờ nh&acirc;n phẩm tr&ecirc;n thiết bị di động.</p>\r\n<p>Về cơ bản, bạn cần chiến đấu chống lại đối thủ trong 15 v&ograve;ng li&ecirc;n tiếp. Người chiến thắng trong mỗi v&ograve;ng sẽ kiếm được tiền thưởng v&agrave; thiết bị để gi&uacute;p đội của m&igrave;nh ng&agrave;y c&agrave;ng mạnh hơn. B&ecirc;n cạnh lối chơi quen thuộc của game cờ nh&acirc;n phẩm, Auto Chess War c&ograve;n cố gắng đưa đến những nội dung hấp dẫn kh&aacute;c như tập trung v&agrave;o mảng chơi offline hơn. Đ&oacute; c&oacute; thể l&agrave; chế độ Adventure (phi&ecirc;u lưu), ch&iacute;nh x&aacute;c hơn l&agrave; phần cốt truyện với rất nhiều chương cho bạn tự do chiến với v&ocirc; số đối thủ m&aacute;y kh&aacute;c nhau.</p>\r\n<p>B&ecirc;n cạnh đ&oacute; Chế độ thử th&aacute;ch v&ocirc; tận c&oacute; thể l&agrave; một c&aacute;ch tốt để thử nghiệm một số chiến lược kh&aacute;c nhau trong tr&ograve; chơi. Trong chế độ n&agrave;y game cũng kh&ocirc;ng giới hạn số v&agrave;ng bạn c&oacute; thể kiếm được, do đ&oacute; bạn c&oacute; thể l&ecirc;n đến 10 v&agrave;ng l&atilde;i mỗi trận để c&oacute; thể tạo ra đội h&igrave;nh si&ecirc;u to si&ecirc;u khổng lồ v&agrave; tiếp tục h&agrave;nh tr&igrave;nh chinh chiến trong game.</p>\r\n<h2><strong>Hearthstone Battlegrounds (PC, iOS &amp; Android)</strong></h2>\r\n<p><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/Battlegrounds-Update.jpg\" alt=\"Hearthstone Battlegrounds\" /></p>\r\n<p>Một c&aacute;i t&ecirc;n nữa bước v&agrave;o cuộc chơi cờ nh&acirc;n phẩm đầy s&ocirc;i động ch&iacute;nh l&agrave; Hearthstone &ndash; Con cưng một thời của đại gia Blizzard. Battlegrounds l&agrave; chế độ ho&agrave;n to&agrave;n mới trong Hearthstone. Bạn c&oacute; thể n&oacute;i n&oacute; giống như một game mới được t&iacute;ch hợp trong Hearthstone vậy. Hearthstone Battlegrounds được ph&aacute;t triển theo thể loại Auto Battler. C&aacute;ch chơi của game sẽ kh&ocirc;ng giống như hầu hết c&aacute;c game Auto Chess, v&agrave; vẫn đem lại cảm gi&aacute;c rất Hearthstone &ndash; đ&acirc;y c&oacute; thể l&agrave; lợi thể lớn của Battlegrounds.</p>\r\n<p>C&oacute; tổng cộng 24 hero được chọn với những Hero Power độc đ&aacute;o kh&aacute;c nhau. T&ugrave;y theo Hero Power n&agrave;y, người chơi sẽ c&oacute; chiến thuật xoay quanh l&iacute;nh cho ph&ugrave; hợp để gi&agrave;nh chiến thắng trong game. Mỗi trận Hearthstone Battlegrounds, c&aacute;c bạn cần phải lọt v&agrave;o top 3 mới được t&iacute;nh l&agrave; chiến thắng.</p>\r\n<h2><strong>Might &amp; Magic: Chess Royale (iOS &amp; Android)</strong></h2>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/filters:quality(90)/fptshop.com.vn/uploads/images/tin-tuc/137926/Originals/pasted%20image%200.png\" alt=\"Might &amp; Magic: Chess Royale\" /></strong></p>\r\n<p>Might And Magic Chess Royale cũng l&agrave; một game thuộc thể loại Auto Chess tr&ecirc;n nền tảng mobile c&oacute; hệ thống chiến đấu kh&ocirc;ng đ&acirc;u bằng với 100 người chơi mỗi trận đấu. Might And Magic Chess Royale c&oacute; hệ thống Heroes kh&aacute; đa dạng v&agrave; bạn sẽ phải mất h&agrave;ng giờ liền để l&agrave;m quen ch&uacute;ng.</p>\r\n<p>Tương tự như c&aacute;c game cờ nh&acirc;n phẩm kh&aacute;c, c&aacute;ch chơi game Might And Magic Chess Royale kh&aacute; đơn giản, bạn sẽ phải lựa chọn đơn vị mạnh nhất v&agrave; đặt ch&uacute;ng l&ecirc;n b&agrave;n cờ, tạo li&ecirc;n minh giữa c&aacute;c hệ tộc để đ&aacute;nh bại c&aacute;c người chơi kh&aacute;c. Ngo&agrave;i ra, bạn c&oacute; thể hỗ trợ đội qu&acirc;n của m&igrave;nh bằng c&aacute;ch sử dụng ph&eacute;p thuật, n&acirc;ng cấp hoặc hợp nhất c&aacute;c qu&acirc;n cờ giống nhau để c&oacute; qu&acirc;n cờ phi&ecirc;n bản mạnh hơn. B&ecirc;n cạnh c&aacute;c qu&acirc;n cờ, bạn c&ograve;n c&oacute; thể d&ugrave;ng v&agrave;ng để mua được những ph&eacute;p thuật đặc biệt nhằm hỗ trợ trong trận chiến song song với combo tộc hệ c&aacute;c nh&acirc;n vật, từ đ&oacute; tạo n&ecirc;n một kiểu chiến thuật đặc th&ugrave; của ri&ecirc;ng của m&igrave;nh.</p>', 1, 11, 1, '2021-08-15 03:18:40', '2021-08-15 03:18:40', NULL),
(23, 'Hướng dẫn cách thay đổi ngoại hình trong Play Together', 'huong-dan-cach-thay-doi-ngoai-hinh-trong-play-together', 'public/uploads/post/1\\cachthaydoingoaihinhtrongplaytogether2-_1366x768-300x200.jpg', 'cachthaydoingoaihinhtrongplaytogether2-_1366x768-300x200.jpg', '<h2>Play Together l&agrave; một tựa game kh&aacute; hay v&agrave; rất th&acirc;n thiện để bạn c&oacute; thể giao lưu với bạn b&egrave; trong m&ugrave;a dịch ngay tr&ecirc;n&nbsp;<a title=\"điện thoại\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"điện thoại\">điện thoại</a>. Ngo&agrave;i những t&iacute;nh năng như đi dạo trong th&agrave;nh phố hay c&acirc;u c&aacute; ra th&igrave; bạn c&ograve;n c&oacute; thể trang bị cho m&igrave;nh những bộ &lsquo;c&aacute;nh&rsquo; tuyệt đẹp. V&agrave; b&agrave;i viết n&agrave;y sẽ hướng dẫn bạn&nbsp;<a title=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" href=\"http://www.thegioididong.com/tin-tuc/cach-thay-doi-ngoai-hinh-trong-play-together-1375330\" target=\"_blank\" rel=\"noopener\" type=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\">c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together</a>.</h2>\r\n<h3><strong>C&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together</strong></h3>\r\n<p>Đầu ti&ecirc;n, nếu bạn chưa chơi Play Together bao giờ th&igrave; c&oacute; thể tải về từ AppStore hoặc Google Play, bạn c&oacute; thể tải nhanh từ đường link b&ecirc;n dưới.</p>\r\n<ul>\r\n<li><a title=\"Link tải Play Together cho iOS\" href=\"https://apps.apple.com/vn/app/play-together/id1402384531?l=vi\" target=\"_blank\" rel=\"nofollow noopener\" type=\"Link tải Play Together cho iOS\">Link tải Play Together cho iOS</a></li>\r\n<li><a title=\"Link tải Play Together cho Android\" href=\"https://play.google.com/store/apps/details?id=com.haegin.playtogether&amp;hl=vi&amp;gl=US\" target=\"_blank\" rel=\"nofollow noopener\" type=\"Link tải Play Together cho Android\">Link tải Play Together cho Android</a></li>\r\n</ul>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" src=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether3-_1366x768-800-resize.jpg\" alt=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether3-_1366x768-800-resize.jpg\" /></div>\r\n<p><strong>Bước 1:&nbsp;</strong>Bạn v&agrave;o game v&agrave; chọn v&agrave;o&nbsp;<strong>mục điện thoại</strong>&nbsp;ở ph&iacute;a b&ecirc;n cạnh phải của game.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" src=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether1-_1366x768-800-resize.jpg\" alt=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether1-_1366x768-800-resize.jpg\" /></div>\r\n<p><strong>Bước 2:</strong>&nbsp;Bạn chọn v&agrave;o mục&nbsp;<strong>Trang tr&iacute;.</strong></p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" src=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether6-_1920x1080-800-resize.jpg\" alt=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether6-_1920x1080-800-resize.jpg\" /></div>\r\n<p><strong>Bước 3:</strong>&nbsp;V&agrave; cuối c&ugrave;ng bạn sẽ chọn cho m&igrave;nh những bộ &lsquo;c&aacute;nh&rsquo; tuyệt đẹp nh&eacute;.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" src=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether5-_1920x1080-800-resize.jpg\" alt=\"c&aacute;ch thay đổi ngoại h&igrave;nh trong Play Together\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/15/1375330/cachthaydoingoaihinhtrongplaytogether5-_1920x1080-800-resize.jpg\" /></div>\r\n<p>Bạn c&oacute; chơi Play Together trong thời gian ở nh&agrave; kh&ocirc;ng? Nếu thấy b&agrave;i viết hữu &iacute;ch cho m&igrave;nh xin một like v&agrave; một share nh&eacute;. Rất cảm ơn c&aacute;c bạn đ&atilde; xem b&agrave;i viết.</p>', 1, 10, 1, '2021-08-15 03:22:21', '2021-08-15 03:23:27', NULL),
(24, 'Cách chơi game Võ Lâm Truyền Kỳ trên Zalo', 'cach-choi-game-vo-lam-truyen-ky-tren-zalo', 'public/uploads/post/1\\choi-vo-lam-truyen-ky-tren-zalo-21_800x450-300x200.jpg', 'choi-vo-lam-truyen-ky-tren-zalo-21_800x450-300x200.jpg', '<h2>Game nhập vai V&otilde; L&acirc;m Truyền Kỳ H5 đ&atilde; ch&iacute;nh thức xuất hiện tr&ecirc;n Zalo, h&atilde;y c&ugrave;ng nhau trải nghiệm thế giới v&otilde; l&acirc;m đầy m&agrave;u sắc v&agrave; hấp dẫn nh&eacute;. Sau đ&acirc;y l&agrave;&nbsp;<a title=\"c&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" href=\"https://www.thegioididong.com/tin-tuc/cach-choi-game-vo-lam-truyen-ky-tren-zalo-1375135\" target=\"_blank\" rel=\"noopener\" type=\"c&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\">c&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo</a>&nbsp;kh&ocirc;ng cần tải game về&nbsp;<a title=\"điện thoại\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"điện thoại\">điện thoại</a>, gi&uacute;p bạn tiết kiệm dung lượng m&aacute;y.</h2>\r\n<h3><strong>C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo si&ecirc;u dễ</strong></h3>\r\n<p><strong>Bước 1:&nbsp;</strong>Đ&acirc;y l&agrave;&nbsp;<strong>t&iacute;nh năng mới được ph&aacute;t h&agrave;nh</strong>, những phi&ecirc;n bản Zalo cũ c&oacute; thể sẽ kh&ocirc;ng c&oacute; hoặc kh&ocirc;ng sử dụng được. Ch&iacute;nh v&igrave; vậy bạn h&atilde;y tiến h&agrave;nh cập nhật phi&ecirc;n bản mới nhất của ứng dụng Zalo để trải nghiệm t&iacute;nh năng n&agrave;y ho&agrave;n chỉnh hơn nh&eacute;.</p>\r\n<ul>\r\n<li><a title=\"Zalo cho IOS\" href=\"https://apps.apple.com/vn/app/zalo/id579523206\" target=\"_blank\" rel=\"nofollow noopener\" type=\"Zalo cho IOS\">Zalo cho IOS</a></li>\r\n<li><a title=\"Zalo cho Android\" href=\"https://play.google.com/store/apps/details?id=com.zing.zalo&amp;hl=vi&amp;gl=US\" target=\"_blank\" rel=\"nofollow noopener\" type=\"Zalo cho Android\">Zalo cho Android</a></li>\r\n</ul>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" src=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/fcq-_2048x1356-800-resize.jpg\" alt=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/fcq-_2048x1356-800-resize.jpg\" /></div>\r\n<p><strong>Bước 2:</strong>&nbsp;<strong>Mở ứng dụng Zalo</strong>&nbsp;tr&ecirc;n smartphone của m&igrave;nh l&ecirc;n &gt; Chọn mục&nbsp;<strong>Kh&aacute;m ph&aacute;</strong>&nbsp;&gt; Chọn phần<strong>&nbsp;Game&nbsp;</strong>&nbsp;&gt; Lướt t&igrave;m game&nbsp;<strong>V&otilde; L&acirc;m Truyền Kỳ</strong>&nbsp;&gt; Chọn&nbsp;<strong>Chơi ngay.</strong></p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" src=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/add_1280x1280-800-resize.jpg\" alt=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/add_1280x1280-800-resize.jpg\" /></div>\r\n<p><strong>Bước 3:</strong>&nbsp;Tiến h&agrave;nh&nbsp;<strong>đăng nhập v&agrave;o Game</strong>, lựa chọn t&ecirc;n nh&acirc;n vật v&agrave; sever m&igrave;nh muốn v&agrave; bắt đầu trải nghiệm th&ocirc;i.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" src=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/nga_1280x1280-800-resize.jpg\" alt=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/nga_1280x1280-800-resize.jpg\" /></div>\r\n<p>Phi&ecirc;n bản n&agrave;y tương tự như phi&ecirc;n bản ứng dụng, c&oacute; đầy đủ c&aacute;c t&iacute;nh năng như phi&ecirc;n bản ứng dụng v&agrave; hoạt động b&igrave;nh thường tr&ecirc;n tất cả c&aacute;c d&ograve;ng điện thoại, c&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng trải nghiệm ngay.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" src=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/a_1280x1280-800-resize.jpg\" alt=\"C&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/14/1375135/a_1280x1280-800-resize.jpg\" /></div>\r\n<p>Chỉ với v&agrave;i bước đơn giản m&igrave;nh đ&atilde; hướng dẫn xong c&aacute;ch chơi game V&otilde; L&acirc;m Truyền Kỳ tr&ecirc;n Zalo rồi, từ nay bạn c&oacute; thể thoải m&aacute;i chơi game m&agrave; kh&ocirc;ng lo tốn dung lượng điện thoại nửa. Cảm ơn bạn đ&atilde; theo d&otilde;i b&agrave;i viết, nếu thấy hay h&atilde;y Like v&agrave; Share để ủng hộ m&igrave;nh nha.</p>', 1, 10, 1, '2021-08-15 03:22:55', '2021-08-15 03:22:55', NULL),
(25, 'Cách thay đổi âm lượng điện thoại tự động theo lịch', 'cach-thay-doi-am-luong-dien-thoai-tu-dong-theo-lich', 'public/uploads/post/1\\anhbia1_1280x720-300x200.jpg', 'anhbia1_1280x720-300x200.jpg', '<h2>Đ&atilde; bao giờ bạn gặp phải t&igrave;nh huống m&agrave; cần phải đặt&nbsp;<a title=\"dt\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"dt\">điện thoại</a>&nbsp;về chế độ im lặng ngay lập tức chưa? V&iacute; dụ như khi mang điện thoại đến lớp học, đến một cuộc họp,... Ngo&agrave;i việc bật t&iacute;nh năng Kh&ocirc;ng l&agrave;m phiền (Do Not Disturb) th&igrave; vẫn c&ograve;n một c&aacute;ch kh&aacute;c gi&uacute;p bạn hẹn lịch thay đổi &acirc;m lượng theo thiết lập c&oacute; sẵn. Sau đ&acirc;y, m&igrave;nh xin hướng dẫn c&aacute;c bạn&nbsp;<a title=\"Thay đổi &acirc;m lượng điện thoại tự động\" href=\"https://www.thegioididong.com/tin-tuc/cach-thay-doi-am-luong-dien-thoai-tu-dong-theo-lich-1375055\" target=\"_blank\" rel=\"noopener\" type=\"Thay đổi &acirc;m lượng điện thoại tự động\">c&aacute;ch thay đổi &acirc;m lượng điện thoại tự động theo lịch</a>&nbsp;cực đơn giản v&agrave; tiện lợi.</h2>\r\n<h3><strong>C&aacute;ch thay đổi &acirc;m lượng điện thoại tự động theo lịch</strong></h3>\r\n<p><strong>Bước 1:</strong>&nbsp;Tải về ứng dụng&nbsp;<a title=\"Sound Profile\" href=\"https://play.google.com/store/apps/details?id=Orion.Soft&amp;hl=en_US&amp;gl=US\" target=\"_blank\" rel=\"nofollow noopener\" type=\"Sound Profile\">Sound Profile</a>.</p>\r\n<p><strong>Bước 2:</strong>&nbsp;Mở ứng dụng v&agrave; cấp quyền truy cập cho ứng dụng.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại\" src=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc2_2560x2560-800-resize.jpg\" alt=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc2_2560x2560-800-resize.jpg\" /></div>\r\n<p><strong>Bước 3:</strong>&nbsp;Tại m&agrave;n h&igrave;nh giao diện ch&iacute;nh của ứng dụng, bạn c&oacute; thể thiết lập nhanh c&aacute;c trạng th&aacute;i &acirc;m thanh của điện thoại như&nbsp;<strong>b&igrave;nh thường, y&ecirc;n tĩnh, im lặng, ban đ&ecirc;m hoặc chỉ rung,...</strong></p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" src=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc3_2560x2560-800-resize.jpg\" alt=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc3_2560x2560-800-resize.jpg\" /></div>\r\n<p><strong>Bước 4:</strong>&nbsp;Chọn v&agrave;o&nbsp;<strong>Menu mở rộng</strong>&nbsp;v&agrave; chọn mục&nbsp;<strong>Scheduler</strong>&nbsp;để đặt lịch cho c&aacute;c thiết lập &acirc;m lượng.</p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" src=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc4_2560x2560-800-resize.jpg\" alt=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc4_2560x2560-800-resize.jpg\" /></div>\r\n<p><strong>Bước 5:</strong>&nbsp;Tại&nbsp;<strong>Scheduler</strong>, chọn&nbsp;<strong>My Scheduler</strong>&nbsp;để tạo lịch mới. Sau đ&oacute; bấm&nbsp;<strong>Add</strong>&nbsp;v&agrave;&nbsp;<strong>Save.</strong></p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" src=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc5_2560x2560-800-resize.jpg\" alt=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc5_2560x2560-800-resize.jpg\" /></div>\r\n<p>Ngo&agrave;i ra, khi chọn v&agrave;o mục&nbsp;<strong>Edit Profile</strong>, bạn c&oacute; thể thay đổi c&aacute;c thiết lập theo y&ecirc;u cầu của bạn như&nbsp;<strong>t&ecirc;n, mức &acirc;m lượng,...</strong></p>\r\n<div class=\"imgwrap\"><img class=\" lazyloaded\" title=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" src=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc6_2560x2560-800-resize.jpg\" alt=\"c&aacute;ch thay đổi &acirc;m lượng điện thoại theo lịch\" data-original=\"https://cdn.tgdd.vn/Files/2021/08/13/1375055/buoc6_2560x2560-800-resize.jpg\" /></div>', 1, 10, 1, '2021-08-15 03:24:31', '2021-08-28 04:26:49', NULL);

