--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `feature_image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` int(11) NOT NULL,
  `description` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1,
  `featured` tinyint(4) NOT NULL DEFAULT 0,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `category_product_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Samsung Galaxy A31', 'samsung-galaxy-a31', 'public/uploads/product/1\\sam-a31-den-1.jpg', 12000000, '<p><strong>Sở hữu&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-a31\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy A31</a>, bạn sẽ c&oacute; được trải nghiệm h&igrave;nh ảnh kh&ocirc;ng thua k&eacute;m&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>&nbsp;cao cấp n&agrave;o với m&agrave;n h&igrave;nh rộng 6.4 inch rực rỡ sắc n&eacute;t. Ngo&agrave;i ra, khả năng chụp ảnh chuy&ecirc;n nghiệp nhờ cụm bốn camera tr&ecirc;n mặt lưng cũng l&agrave; điểm cộng kh&ocirc;ng thể bỏ qua khi nhắc tới Galaxy A31.</strong></p>\r\n<p><img id=\"M&ocirc; tả sản phẩm Samsung Galaxy A31 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-1.jpg\" alt=\"M&ocirc; tả sản phẩm Samsung Galaxy A31 1\" /></p>\r\n<h3><strong>G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch</strong></h3>\r\n<p>Lần đầu cầm Galaxy A31 tr&ecirc;n tay, bạn sẽ lập tức bị thu h&uacute;t bởi m&agrave;n h&igrave;nh Infinity-U 6.4 inch của chiếc điện thoại n&agrave;y. C&aacute;ch thiết kế đưa camera selfie v&agrave;o kh&ocirc;ng gian hiển thị gi&uacute;p&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;r&uacute;t gọn viền bao quanh mặt trước v&agrave; tạo hiệu ứng m&agrave;n h&igrave;nh tr&agrave;n cạnh cuốn h&uacute;t. Tấm nền<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-super-amoled-la-gi-vi-sao-day-la-con-bai-chien-luoc-cua-samsung-57061\">&nbsp;Super AMOLED</a>&nbsp;độ ph&acirc;n giải Full HD+ của m&agrave;n h&igrave;nh Galaxy A31 sẽ đưa bạn v&agrave;o thế giới giải tr&iacute; hấp dẫn khi thưởng thức c&aacute;c bộ phim bom tấn, chơi những tựa game y&ecirc;u th&iacute;ch v&agrave; theo d&otilde;i sự kiện livestream.</p>\r\n<p><img id=\"G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-2.jpg\" alt=\"G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch \" /></p>\r\n<h3><strong>Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn</strong></h3>\r\n<p>Samsung Galaxy A31 sở hữu thiết kế mỏng 8.6mm cực k&igrave; thời trang, nhưng kh&ocirc;ng v&igrave; thế m&agrave; m&aacute;y c&oacute; dung lượng pin thấp. Tr&aacute;i lại, Galaxy A31 mang trong m&igrave;nh vi&ecirc;n pin lớn tới 5.000 mAh, c&oacute; thể đảm bảo trải nghiệm thoải m&aacute;i suốt ng&agrave;y d&agrave;i. Sản phẩm sẽ khiến bạn h&agrave;i l&ograve;ng khi cầm nắm v&agrave; thao t&aacute;c nhờ bộ khung vỏ được tạo d&aacute;ng kh&eacute;o l&eacute;o vừa vặn với l&ograve;ng b&agrave;n tay. Mặt lưng m&aacute;y c&oacute; hoạ tiết tạo khối h&igrave;nh học cắt xẻ độc đ&aacute;o v&agrave; được t&ocirc; điểm bởi những sắc m&agrave;u thời trang như xanh, đen v&agrave; trắng.</p>\r\n<p><img id=\"Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-3.jpg\" alt=\"Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn\" /></p>\r\n<h3><strong>Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera</strong></h3>\r\n<p>Cụm bốn camera ở ph&iacute;a sau Galaxy A31 được g&oacute;i gọn v&agrave;o trong module chữ nhật theo đ&uacute;ng xu hướng thiết kế chuẩn 2020. Trong đ&oacute;, camera ch&iacute;nh với độ ph&acirc;n giải 48MP sẽ đảm bảo h&igrave;nh ảnh chụp được lu&ocirc;n sắc n&eacute;t v&agrave; chi tiết, camera 8MP Ultra wide 123 độ mở ra g&oacute;c tr&ocirc;ng ảnh rộng r&atilde;i c&ograve;n camera Marco 5MP đem tới nhiều tinh chỉnh cần thiết khi chụp cận cảnh, đồng thời đảm bảo chủ thể khu&ocirc;n h&igrave;nh lu&ocirc;n nổi bật khi kết hợp với hiệu ứng Live Focus m&agrave; Depth camera 5MP mang lại.</p>\r\n<p><img id=\"Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-4.JPG\" alt=\"Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera\" /></p>\r\n<h3><strong>Bắt trọn mọi quang cảnh trong tầm mắt</strong></h3>\r\n<p>Sự hiện diện của camera Ultra wide kết hợp với camera ch&iacute;nh 48MP đem tới hai t&ugrave;y chọn g&oacute;c tr&ocirc;ng ảnh l&agrave; 80 độ v&agrave; 123 độ. Khi cần chụp ảnh phong cảnh hoặc chụp ảnh nh&oacute;m, bạn h&atilde;y k&iacute;ch hoạt g&oacute;c chụp cực rộng để c&oacute; được những khu&ocirc;n h&igrave;nh to&agrave;n cảnh như &yacute; muốn.</p>\r\n<p><img id=\"Bắt trọn mọi quang cảnh trong tầm mắt 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-5.JPG\" alt=\"Bắt trọn mọi quang cảnh trong tầm mắt 1\" /></p>\r\n<h3><strong>Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t</strong></h3>\r\n<p>Để ph&aacute;t huy tối đa năng lực của camera 20MP tr&ecirc;n mặt trước Galaxy A31, nh&agrave; sản xuất đ&atilde; t&iacute;ch hợp t&iacute;nh năng Live Focus hỗ trợ người d&ugrave;ng l&agrave;m mờ bối cảnh khi chụp selfie. Mọi bức ảnh chụp từ camera trước của Samsung Galaxy A31 đều hết sức sắc n&eacute;t với chủ thể khu&ocirc;n h&igrave;nh nổi bật nhờ sự can thiệp của hiệu ứng x&oacute;a ph&ocirc;ng.</p>\r\n<p><img id=\"Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-6.jpg\" alt=\"Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t 1\" /></p>\r\n<h3><strong>Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh</strong></h3>\r\n<p>Khi m&agrave; smartphone trở th&agrave;nh c&ocirc;ng cụ li&ecirc;n lạc, l&agrave;m việc, học tập v&agrave; giải tr&iacute; cần thiết cho đời sống thường ng&agrave;y của ch&uacute;ng ta th&igrave; pin trở th&agrave;nh yếu tố quan trọng khi c&acirc;n nhắc lựa chọn một chiếc điện thoại. Hiểu được điều đ&oacute;, Samsung t&iacute;ch hợp cho Galaxy A31 vi&ecirc;n pin dung lượng lớn 5.000 mAh, đủ khả năng cung ứng năng lượng cho m&agrave;n h&igrave;nh 6.4 inch hoạt động xuy&ecirc;n suốt một ng&agrave;y d&agrave;i trải nghiệm. Khi hết pin bất chợt, c&ocirc;ng nghệ sạc nhanh 15W sẽ gi&uacute;p bạn nạp lại pin cho m&aacute;y rất nhanh v&agrave; hiệu quả.</p>\r\n<p><img id=\"Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-7.JPG\" alt=\"Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh 1\" /></p>\r\n<h3><strong>Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả</strong></h3>\r\n<p>Chiếc điện thoại Samsung Galaxy A31 c&oacute; thể xử l&yacute; t&aacute;c vụ nhanh ch&oacute;ng nhờ chip xử l&yacute; Octa-core mạnh mẽ kết hợp với dung lượng RAM 6GB. Sản phẩm cho ph&eacute;p người d&ugrave;ng lưu trữ kho dữ liệu c&aacute; nh&acirc;n đồ sộ qua bộ nhớ trong 128GB. Nếu l&agrave; người c&oacute; th&oacute;i quen tải xuống phim ảnh v&agrave; file nhạc lưu sẵn trong m&aacute;y, bạn c&oacute; thể mở rộng kh&ocirc;ng gian lưu trữ qua thẻ nhớ ngo&agrave;i 512GB.</p>\r\n<p><img id=\"Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-8.JPG\" alt=\"Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả 1\" /></p>\r\n<h3><strong>Tối ưu h&oacute;a hiệu suất với Game Booster</strong></h3>\r\n<p>Kh&ocirc;ng chỉ ph&aacute;t huy tối đa sức mạnh của thiết bị khi chơi game, c&ocirc;ng nghệ Game Booster c&ograve;n hạn chế th&ocirc;ng b&aacute;o l&agrave;m ảnh hưởng đến người d&ugrave;ng trong những cuộc giao tranh căng thẳng. Giao diện của Game Booster được thiết kế rất trực quan, sinh động, cho ph&eacute;p ch&uacute;ng ta theo d&otilde;i được nhiệt độ của m&aacute;y khi chơi game c&ugrave;ng c&aacute;c th&ocirc;ng số cần thiết như bộ nhớ v&agrave; pin.</p>\r\n<p><img id=\"Tối ưu h&oacute;a hiệu suất với Game Booster 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-9.JPG\" alt=\"Tối ưu h&oacute;a hiệu suất với Game Booster 1\" /></p>\r\n<h3><strong>Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh</strong></h3>\r\n<p>Bạn c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m khi sử dụng Samsung Galaxy A31 bởi chiếc điện thoại n&agrave;y được trang bị cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh hiện đại &ndash; đ&acirc;y l&agrave; c&ocirc;ng nghệ bảo mật sinh trắc học hiện đại bậc nhất tr&ecirc;n smartphone ng&agrave;y nay. Th&ocirc;ng qua thao t&aacute;c chạm đơn giản v&agrave;o m&agrave;n h&igrave;nh, bạn sẽ l&agrave; người duy nhất mở kh&oacute;a v&agrave; đăng nhập v&agrave;o b&ecirc;n trong thiết bị.</p>\r\n<p><img id=\"Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh 33\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-10.JPG\" alt=\"Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh 3\" /></p>', 1, 1, 1, 11, '2021-08-05 00:28:29', '2021-08-14 02:31:15', NULL),
(3, 'Samsung Galaxy A12', 'samsung-galaxy-a12', 'public/uploads/product/1\\samsung-a12-xanh-1.jpg', 10000000, '<p><strong>Khả năng chụp ảnh ưu việt với 4 camera sau chất lượng, m&agrave;n h&igrave;nh lớn v&agrave; thời lượng pin thoải m&aacute;i cho cả ng&agrave;y l&agrave; những g&igrave; đang chờ đ&oacute;n bạn tr&ecirc;n&nbsp;<a title=\"Mua ngay điện thoại Samsung Galaxy A12 ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPTShop\" href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-a12\" type=\"Mua ngay điện thoại Samsung Galaxy A12 ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPTShop\">Samsung Galaxy A12</a>, chiếc điện thoại tuyệt vời d&agrave;nh cho giới trẻ.</strong></p>\r\n<p><strong><img class=\"Samsung Galaxy A12\" title=\"Samsung Galaxy A12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-1.jpg\" alt=\"Samsung Galaxy A12\" /></strong></p>\r\n<h3><strong>Thưởng thức h&igrave;nh ảnh cực đ&atilde; tr&ecirc;n m&agrave;n h&igrave;nh rộng</strong></h3>\r\n<p>M&agrave;n h&igrave;nh lớn tới 6,5 inch tr&agrave;n viền Infinity-V của Galaxy A12 sẽ mang đến cho bạn trải nghiệm nội dung tuyệt vời, d&ugrave; l&agrave; ở bất cứ ứng dụng n&agrave;o. Đọc b&aacute;o với nhiều th&ocirc;ng tin hơn; xem phim với m&agrave;n h&igrave;nh rộng hơn, hay chơi game với một chiến trường lớn hơn, m&agrave;n h&igrave;nh Galaxy A12 gi&uacute;p mọi thứ trở n&ecirc;n sống động, ch&acirc;n thực trong tầm mắt của bạn.</p>\r\n<p><img class=\"Samsung Galaxy A12 | M&agrave;n h&igrave;nh tr&agrave;n viền Infinity-V\" title=\"Samsung Galaxy A12 | M&agrave;n h&igrave;nh tr&agrave;n viền Infinity-V\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-2.jpg\" alt=\"Samsung Galaxy A12 | M&agrave;n h&igrave;nh tr&agrave;n viền Infinity-V\" /></p>\r\n<h3><strong>Kiểu d&aacute;ng hiện đại, m&agrave;u sắc trẻ trung</strong></h3>\r\n<p>Thiết kế của Galaxy A12 l&agrave; sự pha trộn giữa n&eacute;t hiện đại v&agrave; t&iacute;nh thực dụng. Bạn sẽ c&oacute; được một chiếc&nbsp;<a title=\"Tham khảo nhiều mẫu điện thoại ch&iacute;nh h&atilde;ng nhận nhiều ưu đ&atilde;i tại FPTShop\" href=\"https://fptshop.com.vn/dien-thoai/\" type=\"Tham khảo nhiều mẫu điện thoại ch&iacute;nh h&atilde;ng nhận nhiều ưu đ&atilde;i tại FPTShop\">điện thoại</a>&nbsp;kiểu d&aacute;ng đơn giản v&agrave; mặt lưng ho&agrave;n thiện dạng v&acirc;n nh&aacute;m, cầm nắm rất chắc chắn, đồng thời kh&ocirc;ng bị b&aacute;m bẩn, mồ h&ocirc;i hay dấu v&acirc;n tay. Cụm camera h&igrave;nh vu&ocirc;ng tinh tế ch&iacute;nh l&agrave; điểm nhấn trong thiết kế m&aacute;y. B&ecirc;n cạnh đ&oacute;,&nbsp;<a title=\"Tham khảo nhiều mẫu điện thoại Samsung ch&iacute;nh h&atilde;ng tại FPTShop\" href=\"https://fptshop.com.vn/dien-thoai/samsung\" type=\"Tham khảo nhiều mẫu điện thoại Samsung ch&iacute;nh h&atilde;ng tại FPTShop\">Samsung</a>&nbsp;Galaxy A12 c&ograve;n cho người d&ugrave;ng 3 sự lựa chọn m&agrave;u sắc đầy c&aacute; t&iacute;nh: Đen ph&aacute; c&aacute;ch, Trắng t&acirc;m điểm v&agrave; Xanh bứt ph&aacute;.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Thiết kế hiện đại\" title=\"Samsung Galaxy A12 | Thiết kế hiện đại\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-3.jpg\" alt=\"Samsung Galaxy A12 | Thiết kế hiện đại\" /></p>\r\n<h3><strong>Nhiếp ảnh chuy&ecirc;n nghiệp với bộ tứ camera 48MP</strong></h3>\r\n<p>Mang tr&ecirc;n m&igrave;nh tới 4 camera sau, Galaxy A12 sẵn s&agrave;ng c&ugrave;ng bạn ghi lại mọi khoảnh khắc đ&aacute;ng nhớ. Camera ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 48MP mang tới những bức h&igrave;nh sắc n&eacute;t d&ugrave; l&agrave; trong điều kiện ngược s&aacute;ng hay thiếu s&aacute;ng. H&igrave;nh ảnh gi&agrave;u chi tiết, m&agrave;u sắc sống động, độ tương phản cao sẽ gi&uacute;p bạn lưu lại cuộc sống th&uacute; vị qua những khung h&igrave;nh chất lượng.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Bộ 4 camera thời thượng\" title=\"Samsung Galaxy A12 | Bộ 4 camera thời thượng\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-4.jpg\" alt=\"Samsung Galaxy A12 | Bộ 4 camera thời thượng\" /></p>\r\n<h3><strong>Mở rộng khung cảnh bằng camera g&oacute;c si&ecirc;u rộng</strong></h3>\r\n<p>Nếu bạn cảm thấy khung h&igrave;nh của camera ch&iacute;nh chưa đủ để lưu giữ cảnh đẹp th&igrave; camera g&oacute;c si&ecirc;u rộng 5MP sẽ l&agrave;m c&ocirc;ng việc đ&oacute; một c&aacute;ch hiệu quả hơn. G&oacute;c si&ecirc;u rộng tới 123 độ sẽ cho những h&igrave;nh ảnh thu được như trong tầm mắt người, lưu lại khung cảnh rộng lớn rất dễ d&agrave;ng.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Với camera g&oacute;c si&ecirc;u rộng\" title=\"Samsung Galaxy A12 | Với camera g&oacute;c si&ecirc;u rộng\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-5.jpg\" alt=\"Samsung Galaxy A12 | Với camera g&oacute;c si&ecirc;u rộng\" /></p>\r\n<h3><strong>Kh&aacute;m ph&aacute; vẻ đẹp cận cảnh</strong></h3>\r\n<p>Camera Macro 2MP tr&ecirc;n Galaxy A12 c&oacute; thể chụp cận cảnh ở khoảng c&aacute;ch si&ecirc;u gần chỉ 3 &ndash; 5cm. Đồng nghĩa với việc bạn c&oacute; thể chụp những vật thể rất nhỏ như c&ocirc;n tr&ugrave;ng, nhụy hoa, đồng xu, &hellip; một c&aacute;ch rất chi tiết, tha hồ s&aacute;ng tạo từ những điều nhỏ b&eacute; nhất.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Camera macro\" title=\"Samsung Galaxy A12 | Camera macro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-6.jpg\" alt=\"Samsung Galaxy A12 | Camera macro\" /></p>\r\n<h3><strong>Chụp ảnh x&oacute;a ph&ocirc;ng ảo diệu</strong></h3>\r\n<p>Sự kết hợp của camera ch&iacute;nh 48MP v&agrave; camera x&oacute;a ph&ocirc;ng 2MP sẽ tạo n&ecirc;n những bức ảnh ch&acirc;n dung x&oacute;a ph&ocirc;ng bằng phần cứng, nổi bật chủ thể đồng thời l&agrave;m mờ hậu cảnh chuy&ecirc;n nghiệp. Bạn sẽ trở n&ecirc;n ấn tượng hơn trong khung h&igrave;nh nhờ hiệu ứng x&oacute;a ph&ocirc;ng như chụp bằng những m&aacute;y ảnh DSLR cao cấp.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Chụp ảnh x&oacute;a phong\" title=\"Samsung Galaxy A12 | Chụp ảnh x&oacute;a phong\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-7.jpg\" alt=\"Samsung Galaxy A12 | Chụp ảnh x&oacute;a phong\" /></p>\r\n<h3><strong>Thoải m&aacute;i trải nghiệm kh&ocirc;ng lo hết pin</strong></h3>\r\n<p>Samsung Galaxy A12 l&agrave; một trong những chiếc điện thoại c&oacute; pin tốt nhất tr&ecirc;n thị trường hiện nay. Vi&ecirc;n pin dung lượng l&ecirc;n tới 5000 mAh gi&uacute;p Galaxy A12 dễ d&agrave;ng đ&aacute;p ứng nhu cầu giải tr&iacute; của bạn trong suốt cả ng&agrave;y d&agrave;i. Hơn thế nữa, điện thoại c&ograve;n hỗ trợ c&ocirc;ng nghệ sạc nhanh 15W, nhanh ch&oacute;ng nạp đầy năng lượng, kh&ocirc;ng l&agrave;m gi&aacute;n đoạn trải nghiệm của bạn.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Dung lượng pin lớn\" title=\"Samsung Galaxy A12 | Dung lượng pin lớn\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-8.jpg\" alt=\"Samsung Galaxy A12 | Dung lượng pin lớn\" /></p>\r\n<h3><strong>Hiệu năng mượt m&agrave;</strong></h3>\r\n<p>Nhờ bộ vi xử l&yacute; 8 nh&acirc;n MediaTek MT6765, đi c&ugrave;ng 4GB v&agrave; bộ nhớ trong l&ecirc;n tới 128GB, Galaxy A12 c&oacute; thể chạy tốt mọi t&aacute;c vụ. Bạn c&oacute; thể v&agrave;o mạng x&atilde; hội, đọc b&aacute;o, xem video trực tuyến hay chơi game một c&aacute;ch mượt m&agrave;. Bộ nhớ lớn 128GB cho ph&eacute;p thoải m&aacute;i chụp ảnh, lưu dữ liệu, tải xuống c&aacute;c tệp tin m&agrave; kh&ocirc;ng lo bị hết dung lượng.</p>\r\n<p><img class=\"Samsung Galaxy A12 | Hiệu năng mượt m&agrave;\" title=\"Samsung Galaxy A12 | Hiệu năng mượt m&agrave;\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a21-9.jpg\" alt=\"Samsung Galaxy A12 | Hiệu năng mượt m&agrave;\" /></p>', 1, 1, 1, 11, '2021-08-05 00:30:23', '2021-08-25 07:33:13', NULL),
(5, 'Samsung Galaxy A51a', 'samsung-galaxy-a51a', 'public/uploads/product/1\\ss-a51-trang-1.jpg', 6000000, '<p><strong>Tự h&agrave;o l&agrave; smartphone đầu ti&ecirc;n tr&ecirc;n thế giới được t&iacute;ch hợp camera Macro hỗ trợ chụp ảnh cận cảnh,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-a51\">Galaxy A51</a>&nbsp;đem tới trải nghiệm chụp h&igrave;nh vượt ngo&agrave;i mong đợi với khả năng t&aacute;i tạo đến từng chi tiết nhỏ nhất. Ngo&agrave;i ra, sự ưu việt của m&agrave;n h&igrave;nh v&ocirc; cực kết hợp với cấu h&igrave;nh mạnh mẽ v&agrave;&nbsp;</strong><strong>vi&ecirc;n pin lớn</strong><strong>&nbsp;4.000&nbsp;</strong><strong>mAh sạc si&ecirc;u nhanh</strong><strong>&nbsp;khiến Galaxy A51 c&oacute; thể l&agrave;m vừa l&ograve;ng bất cứ ai ngay từ lần trải nghiệm đầu ti&ecirc;n.</strong></p>\r\n<p><img id=\"M&ocirc; tả sản phẩm Samsung Galaxy A51 112\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-3.jpg\" alt=\"M&ocirc; tả sản phẩm Samsung Galaxy A51 1\" /></p>\r\n<h3><strong>Khắc họa</strong><strong>&nbsp;từng đường n&eacute;t chi tiết&nbsp;</strong><strong>nhờ camera</strong><strong>&nbsp;Macro</strong></h3>\r\n<p>Mặt lưng của Galaxy A51 đ&aacute;nh dấu sự xuất hiện của 4 camera sau, trong đ&oacute; đ&aacute;ng ch&uacute; &yacute; nhất l&agrave; camera Macro hỗ trợ chụp ph&oacute;ng đại vật thể v&agrave; lấy n&eacute;t chủ thể ở phạm vi cực gần, nhờ vậy, mọi khu&ocirc;n h&igrave;nh bạn chụp từ thiết bị đều được t&aacute;i tạo ho&agrave;n hảo cả về m&agrave;u sắc, chi tiết đến từng đường n&eacute;t nhỏ nhất. Đ&acirc;y l&agrave; chiếc smartphone đầu ti&ecirc;n tr&ecirc;n thế giới sở hữu t&iacute;nh năng ưu việt n&agrave;y, bạn sẽ c&oacute; cảm nhận như đang cầm tr&ecirc;n tay một chiếc m&aacute;y ảnh chuy&ecirc;n nghiệp thực thụ khi sở hữu Samsung Galaxy A51.</p>\r\n<p><img id=\"Khắc họa từng đường n&eacute;t chi tiết nhờ camera Macro 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-0.jpg\" alt=\"Khắc họa từng đường n&eacute;t chi tiết nhờ camera Macro 1\" /></p>\r\n<h3><strong>Chiều l&ograve;ng mọi t&iacute;n đồ chụp ảnh với 4 camera sau</strong></h3>\r\n<p>Bốn camera của Galaxy A51 gồm camera ch&iacute;nh 48MP gi&uacute;p t&aacute;i hiện h&igrave;nh ảnh ch&acirc;n thực, camera Macro hỗ trợ chụp cận cảnh tối ưu, một camera g&oacute;c si&ecirc;u rộng 123 độ v&agrave; cuối c&ugrave;ng l&agrave; camera cảm biến độ s&acirc;u trường ảnh l&agrave;m nổi bật c&aacute;c đối tượng trong khu&ocirc;n h&igrave;nh.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;đ&atilde; kh&eacute;o l&eacute;o &aacute;p dụng c&ocirc;ng nghệ tr&iacute; tuệ nh&acirc;n tạo (AI) để kết hợp linh hoạt th&ocirc;ng tin ghi nhận từ bộ 4 camera n&agrave;y nhằm đưa ra h&igrave;nh ảnh sau c&ugrave;ng đạt chất lượng ưng &yacute;. Ngo&agrave;i ra, sự g&oacute;p mặt của AI c&ograve;n gi&uacute;p chiếc điện thoại Galaxy A51 nhận diện được 30 chủ thể v&agrave; dễ d&agrave;ng t&ugrave;y chỉnh th&ocirc;ng số để c&oacute; được kết quả tốt nhất.</p>\r\n<p><img id=\"Chiều l&ograve;ng mọi t&iacute;n đồ chụp ảnh với 4 camera sau 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-1.jpg\" alt=\"Chiều l&ograve;ng mọi t&iacute;n đồ chụp ảnh với 4 camera sau 1\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh v&ocirc; cực đột ph&aacute; mọi giới hạn</strong></h3>\r\n<p>Chẳng cần phải bỏ ra số tiền đắt đỏ để sở hữu một chiếc smartphone m&agrave;n h&igrave;nh v&ocirc; cực, Galaxy A51 sẽ khiến bạn kinh ngạc với chất lượng h&igrave;nh ảnh sắc n&eacute;t m&agrave; m&agrave;n h&igrave;nh Infinity-O 6.5 inch Full HD+ đem lại.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-super-amoled-la-gi-vi-sao-day-la-con-bai-chien-luoc-cua-samsung-57061\">C&ocirc;ng nghệ Super AMOLED</a>&nbsp;từ l&acirc;u đ&atilde; l&agrave;m n&ecirc;n t&ecirc;n tuổi của nh&agrave; sản xuất xứ H&agrave;n nhờ khả năng hiển thị rực rỡ sống động, t&aacute;i tạo h&igrave;nh ảnh c&oacute; chiều s&acirc;u m&agrave; vẫn tiết kiệm năng lượng hiệu quả. M&agrave;n h&igrave;nh Galaxy A51 được thiết kế tr&agrave;n s&aacute;t c&aacute;c m&eacute;p viền v&agrave; gần như chiếm trọn mặt trước, camera selfie được đưa v&agrave;o trong kh&ocirc;ng gian hiển thị theo đ&uacute;ng xu hướng đang được ưa chuộng nhất thị trường di động hiện nay.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh v&ocirc; cực đột ph&aacute; mọi giới hạn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-2.jpg\" alt=\"M&agrave;n h&igrave;nh v&ocirc; cực đột ph&aacute; mọi giới hạn 1\" /></p>\r\n<h3><strong>Ngỡ ng&agrave;ng trước thiết kế mỏng nhẹ tinh tế</strong></h3>\r\n<p>Từ l&acirc;u, những chiếc điện thoại Galaxy A của Samsung đ&atilde; nổi danh nhờ ngoại h&igrave;nh đẹp mắt v&agrave; Galaxy A51 cũng kh&ocirc;ng phải ngoại lệ. Trải nghiệm thực tế cho thấy A51 đem tới cảm gi&aacute;c cầm nắm hết sức tuyệt vời với th&acirc;n m&aacute;y mỏng 7.9 mm v&agrave; mặt lưng được vuốt cong hai m&eacute;p, tr&aacute;nh tạo n&ecirc;n cảm gi&aacute;c cấn tay khi sử dụng. Sự phối trộn m&agrave;u sắc tinh tế tr&ecirc;n mặt lưng kết hợp với họa tiết cắt kim cương gi&uacute;p ph&ocirc; diễn trọn vẹn vẻ đẹp thiết bị.</p>\r\n<p><img id=\"Ngỡ ng&agrave;ng trước thiết kế mỏng nhẹ tinh tế 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-5.jpg\" alt=\"Ngỡ ng&agrave;ng trước thiết kế mỏng nhẹ tinh tế 1\" /></p>\r\n<h3><strong>Tiết kiệm thời gian của bạn với c&ocirc;ng nghệ sạc si&ecirc;u tốc</strong></h3>\r\n<p>Cung cấp năng lượng hoạt động cho Galaxy A51 hoạt động l&agrave; vi&ecirc;n pin lớn tới 4.000 mAh chuẩn Li-Po. Tuy dung lượng pin lớn nhưng bạn sẽ kh&ocirc;ng cần phải chờ đợi l&acirc;u trong qu&aacute; tr&igrave;nh nạp năng lượng nhờ sự hỗ trợ của c&ocirc;ng nghệ sạc nhanh si&ecirc;u tốc 15W. Theo ước t&iacute;nh của nh&agrave; sản xuất, chỉ với 10 ph&uacute;t sạc, chiếc điện thoại Galaxy A51 sẽ c&oacute; đủ điện năng gọi điện li&ecirc;n tục trong 3 giờ, xem video trong 3 giờ hoặc nghe nhạc suốt 10 giờ. Đ&acirc;y l&agrave; lợi thế lớn gi&uacute;p n&acirc;ng cao trải nghiệm người d&ugrave;ng trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p><img id=\"Tiết kiệm thời gian của bạn với c&ocirc;ng nghệ sạc si&ecirc;u tốc 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-8.jpg\" alt=\"Tiết kiệm thời gian của bạn với c&ocirc;ng nghệ sạc si&ecirc;u tốc 1\" /></p>\r\n<h3><strong>Cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh tiện dụng</strong></h3>\r\n<p>Một điểm cộng kh&ocirc;ng thể bỏ qua của Samsung Galaxy A51 l&agrave; t&iacute;nh năng cảm biến v&acirc;n tay nh&uacute;ng dưới m&agrave;n h&igrave;nh. Chỉ với một lần chạm nhẹ, chiếc smartphone của bạn sẽ tự động nhận diện chủ nh&acirc;n v&agrave; mở kh&oacute;a v&agrave;o m&agrave;n h&igrave;nh ch&iacute;nh. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ cảm biến v&acirc;n tay c&ograve;n c&oacute; thể x&aacute;c thực danh t&iacute;nh v&agrave; gi&uacute;p bạn đăng nhập v&agrave;o c&aacute;c ứng dụng một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n<p><img id=\"Cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh tiện dụng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/02/Mo-ta-san-pham-samsung-galaxy-a51-4.jpg\" alt=\"Cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh tiện dụng 1\" /></p>\r\n<h3><strong>Chơi game thỏa th&iacute;ch nhờ hiệu năng mạnh mẽ</strong></h3>\r\n<p>L&agrave;m n&ecirc;n sức mạnh hiệu năng của Galaxy A51 l&agrave; bộ vi xử l&yacute; t&aacute;m nh&acirc;n Exynos 9611, kết hợp với bộ RAM 6GB c&ugrave;ng dung lượng lưu trữ 128GB. Sản phẩm c&oacute; thể xử l&yacute; những t&aacute;c vụ phức tạp v&agrave; c&aacute;c tựa game nặng một c&aacute;ch mượt m&agrave;. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ Neuro Game Booster cũng hỗ trợ chơi game với trải nghiệm tốt nhất để bạn c&oacute; được kh&ocirc;ng gian giải tr&iacute; ưng &yacute; với Galaxy A51.</p>', 1, 1, 1, 11, '2021-08-05 00:34:28', '2021-08-28 04:33:40', NULL),
(16, 'Samsung Galaxy A72', 'samsung-galaxy-a72', 'public/uploads/product/1\\samsung-a72-xanh-1.jpg', 15000000, '<p><strong>Pin dung lượng khủng 5000mAh, bộ nhớ trong l&ecirc;n tới 256GB, khả năng thu ph&oacute;ng h&igrave;nh ảnh tới 30x v&agrave; m&agrave;n h&igrave;nh tuyệt mỹ,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-a72\">Samsung Galaxy A72</a>&nbsp;đ&atilde; sẵn s&agrave;ng để đưa bạn tới những trải nghiệm kh&ocirc;ng giới hạn.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72.jpg\" alt=\"Samsung Galaxy A72\" /></strong></p>\r\n<h3><strong>Giải tr&iacute; đỉnh cao tr&ecirc;n m&agrave;n h&igrave;nh Super AMOLED cao cấp</strong></h3>\r\n<p>Tr&ecirc;n Galaxy A72, bạn sẽ được thưởng thức những h&igrave;nh ảnh ở một tầm cao mới với m&agrave;n h&igrave;nh lớn 6,7 inch v&ocirc; cực Infinity-O, tấm nền Super AMOLED cao cấp, độ s&aacute;ng l&ecirc;n tới 800 nits, độ ph&acirc;n giải Full HD+ sắc n&eacute;t. Hơn thế nữa, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n trang bị c&ocirc;ng nghệ Eye Comfort Shield 2 bảo vệ mắt v&agrave; đặc biệt l&agrave; c&ocirc;ng nghệ Real Smooth c&ugrave;ng tấm nền 90Hz để mọi thao t&aacute;c vuốt chạm đều mượt m&agrave; đ&aacute;ng kinh ngạc.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-1.jpg\" alt=\"m&agrave;n h&igrave;nh Samsung Galaxy A72\" /></p>\r\n<h3><strong>Thiết kế hiện đại, vẻ đẹp của sự đơn giản</strong></h3>\r\n<p>Samsung Galaxy A72 l&agrave; hội tụ của những n&eacute;t đẹp tinh tế. Mặt trước m&aacute;y l&agrave; m&agrave;n h&igrave;nh v&ocirc; cực Infinity-O viền si&ecirc;u mỏng; mặt sau l&agrave; lớp sơn nh&aacute;m sang trọng v&agrave; cụm camera đồng nhất, tạo n&ecirc;n d&aacute;ng vẻ hiện đại nhưng kh&ocirc;ng k&eacute;m phần thời trang. Sẽ c&oacute; 4 m&agrave;u sắc để bạn lựa chọn theo sở th&iacute;ch ri&ecirc;ng, bao gồm T&iacute;m thanh lịch, Đen bản lĩnh, Trắng tối giản v&agrave; Xanh năng động.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-2.jpg\" alt=\"thiết kế Samsung Galaxy A72\" /></p>\r\n<h3><strong>Camera si&ecirc;u zoom 30X</strong></h3>\r\n<p>Sự kết hợp của camera ch&iacute;nh 64MP v&agrave; camera Tele 8MP gi&uacute;p bạn c&oacute; thể chụp những bức ảnh si&ecirc;u zoom quang học 3x, ph&oacute;ng to gấp 3 lần h&igrave;nh ảnh m&agrave; vẫn giữ nguy&ecirc;n độ sắc n&eacute;t, chi tiết. Thậm ch&iacute; Galaxy A72 c&ograve;n c&oacute; t&iacute;nh năng thu ph&oacute;ng chuẩn kh&ocirc;ng gian 30x, đưa những vật thể ở khoảng c&aacute;ch cực xa đến gần ngay trước mắt. Bạn sẽ phải bất ngờ hơn nữa khi cả camera ch&iacute;nh v&agrave; camera Tele đều hỗ trợ t&iacute;nh năng chống rung quang học OIS, chụp ảnh sắc n&eacute;t, lấy n&eacute;t ch&iacute;nh x&aacute;c trong cả điều kiện thiếu s&aacute;ng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-3.jpg\" alt=\"camera Samsung Galaxy A72\" /></p>\r\n<h3><strong>Bộ tứ camera chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Ngo&agrave;i camera ch&iacute;nh 64MP v&agrave; camera Tele 8MP, tr&ecirc;n Galaxy A72 c&ograve;n c&oacute; camera g&oacute;c si&ecirc;u rộng 12MP v&agrave; camera macro chụp cận cảnh 5MP. Camera g&oacute;c si&ecirc;u rộng sẽ thu được những khung cảnh rộng lớn, h&ugrave;ng vĩ hơn, ph&ugrave; hợp khi chụp ảnh đ&ocirc;ng người hay chụp cảnh thi&ecirc;n nhi&ecirc;n. Trong khi đ&oacute; camera macro c&oacute; khả năng lấy n&eacute;t ở khoảng c&aacute;ch cực gần, để bạn kh&aacute;m ph&aacute; những vật thể nhỏ b&eacute; đầy th&uacute; vị.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-4.jpg\" alt=\"camera Samsung Galaxy A72\" /></p>\r\n<h3><strong>Chống nước chuẩn IP67</strong></h3>\r\n<p>Samsung Galaxy A72 kh&ocirc;ng chỉ thiết kế cứng c&aacute;p, bền vững m&agrave; c&ograve;n đạt chuẩn chống nước v&agrave; chống bụi IP67. Bạn c&oacute; thể ng&acirc;m nước ở độ s&acirc;u 1m trong tối đa 30 ph&uacute;t m&agrave; điện thoại vẫn sống khỏe. Những nỗi lo thường trực trong cuộc sống như v&ocirc; t&igrave;nh đổ nước, sử dụng điện thoại dưới trời mưa hay rơi&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;xuống hồ bơi sẽ kh&ocirc;ng thể l&agrave;m kh&oacute; được Galaxy A72.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-5.jpg\" alt=\"chống nước Samsung Galaxy A72\" /></p>\r\n<h3><strong>Thưởng thức &acirc;m thanh v&ograve;m sống động</strong></h3>\r\n<p>B&ecirc;n cạnh m&agrave;n h&igrave;nh đẹp m&atilde;n nh&atilde;n, bạn c&ograve;n được tận hưởng hệ thống &acirc;m thanh v&ograve;m ngay tr&ecirc;n loa ngo&agrave;i của Galaxy A72. Điện thoại c&oacute; 2 loa ở cả vị tr&iacute; tr&ecirc;n v&agrave; dưới, ph&aacute;t ra &acirc;m thanh nổi m&agrave; kh&ocirc;ng cần&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>. Mọi thứ sẽ trở n&ecirc;n sống động, hấp dẫn hơn bao giờ hết, đặc biệt l&agrave; khi xem phim hay chơi game. Galaxy A72 đ&iacute;ch thực l&agrave; một phương tiện giải tr&iacute; ho&agrave;n hảo.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-6.jpg\" alt=\"&acirc;m thanh Samsung Galaxy A72\" /></p>\r\n<h3><strong>Dung lượng pin khủng 5000 mAh</strong></h3>\r\n<p>D&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/galaxy-a-series\">Galaxy A</a>&nbsp;của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;lu&ocirc;n nổi tiếng với những chiếc điện thoại pin tốt bậc nhất thị trường. Ch&iacute;nh v&igrave; thế kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi Galaxy A72 c&oacute; thể sử dụng thoải m&aacute;i tới 2 ng&agrave;y chỉ sau một lần sạc đầy. Vi&ecirc;n pin dung lượng tới 5000mAh v&agrave; t&iacute;nh năng quản l&yacute; pin bằng AI k&eacute;o d&agrave;i thời lượng pin l&ecirc;n tối đa. Điện thoại c&ograve;n hỗ trợ sạc nhanh si&ecirc;u tốc 25W, nhanh ch&oacute;ng tiếp th&ecirc;m năng lượng cho những trải nghiệm bất tận.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-7.jpg\" alt=\"pin Samsung Galaxy A72\" /></p>\r\n<h3><strong>Cấu h&igrave;nh cực mạnh, bộ nhớ trong l&ecirc;n đến 256GB</strong></h3>\r\n<p>Hiệu năng Galaxy A72 đầy bứt ph&aacute; khi trang bị bộ vi xử l&yacute; Snapdragon 720G mạnh mẽ, 8GB RAM v&agrave; bộ nhớ trong l&ecirc;n tới 256GB. Kết hợp với m&agrave;n h&igrave;nh tần số qu&eacute;t 90Hz, bạn sẽ cảm nhận được sự mượt m&agrave; trong từng thao t&aacute;c, kể cả những ứng dụng nặng hay chơi c&aacute;c tựa game 3D đồ họa cao. Dung lượng bộ nhớ trong l&ecirc;n tới 256GB, tương đương cả với những&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">m&aacute;y t&iacute;nh x&aacute;ch tay</a>&nbsp;mang đến kh&ocirc;ng gian lưu trữ cực lớn, thoải m&aacute;i lưu trữ mọi thứ bạn muốn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-8.jpg\" alt=\"cấu h&igrave;nh Samsung Galaxy A72\" /></p>\r\n<h3><strong>Bảo mật chỉ với một c&uacute; chạm</strong></h3>\r\n<p>Điện thoại Samsung Galaxy A72 t&iacute;ch hợp cảm biến v&acirc;n tay ngay trong m&agrave;n h&igrave;nh, cho ph&eacute;p bạn mở kh&oacute;a m&aacute;y nhanh ch&oacute;ng chỉ sau một c&uacute; chạm. Hơn nữa, cảm biến v&acirc;n tay n&agrave;y c&ograve;n c&oacute; thể thay thế mật khẩu khi bạn đăng nhập v&agrave;o c&aacute;c t&agrave;i khoản, ứng dụng, kể cả những ứng dụng ng&acirc;n h&agrave;ng đ&ograve;i hỏi t&iacute;nh bảo mật cao. Đ&acirc;y l&agrave; phương thức bảo mật hết sức an to&agrave;n v&agrave; tiện lợi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-a72-9.jpg\" alt=\"bảo mật Samsung Galaxy A72\" /></p>', 1, 1, 1, 11, '2021-08-12 06:56:01', '2021-08-25 07:34:04', NULL),
(17, 'Samsung Galaxy Note 20', 'samsung-galaxy-note-20', 'public/uploads/product/1\\ss-note-20-xam-1.jpg', 22500000, '<p><strong>Chiếc Galaxy Note m&agrave;n h&igrave;nh lớn nhất, b&uacute;t S Pen th&ocirc;ng minh nhất v&agrave; những t&iacute;nh năng mạnh mẽ nhất đ&atilde; xuất hiện.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-note-20-ultra\">Samsung Galaxy Note 20 Ultra</a>&nbsp;sẽ định nghĩa lại&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>, thay đổi những trải nghiệm của bạn theo c&aacute;ch th&uacute; vị hơn bao giờ hết.</strong></p>\r\n<p><strong><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-1.jpg\" alt=\"Điện thoại Samsung Galaxy Note 20 Ultra\" /></strong></p>\r\n<h3><strong>Kiệt t&aacute;c c&ocirc;ng nghệ với m&agrave;u sắc huyền b&iacute;</strong></h3>\r\n<p>Samsung Galaxy Note 20 Ultra được chế t&aacute;c từ những vật liệu cao cấp h&agrave;ng đầu hiện nay, với sự tỉ mỉ v&agrave; chất lượng gia c&ocirc;ng thượng thừa, tạo n&ecirc;n chiếc điện thoại đẹp hơn những g&igrave; bạn c&oacute; thể tưởng tượng. Kh&ocirc;ng chỉ c&oacute; kiểu d&aacute;ng thanh lịch,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/giai-ma-nhung-cong-nghe-man-hinh-vo-cuc-moi-cua-samsung-77417\">m&agrave;n h&igrave;nh kh&ocirc;ng viền Infinity-O</a>&nbsp;quyến rũ, Galaxy Note20 Ultra c&ograve;n thể hiện sự cao cấp ở từng chi tiết nhỏ như c&aacute;c phần viền cạnh s&aacute;ng b&oacute;ng, họa tiết phay xước độc đ&aacute;o tr&ecirc;n khung m&aacute;y, mang đến niềm cảm hứng cho người d&ugrave;ng ở mọi g&oacute;c cạnh.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-2.jpg\" alt=\"thiết kế Samsung Galaxy Note 20 Ultra\" /></p>\r\n<p>B&ecirc;n cạnh phần th&acirc;n kim loại, chất liệu ch&iacute;nh l&agrave;m n&ecirc;n Note 20 Ultra l&agrave; k&iacute;nh cường lực<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/corning-trinh-lang-kinh-cuong-luc-gorilla-glass-victus-roi-tu-2-met-van-song-sot-123754\">&nbsp;Corning Gorilla Glass Victus</a>, c&oacute; khả năng chịu lực tốt nhất thế giới smartphone hiện nay. M&agrave;u sắc cũng l&agrave; điều bạn sẽ y&ecirc;u th&iacute;ch tr&ecirc;n Note 20 Ultra khi điện thoại c&oacute; 3 lựa chọn m&agrave;u l&agrave; Trắng huyền b&iacute;, Đen huyền b&iacute; v&agrave; m&agrave;u đặc biệt Đồng huyền b&iacute; chỉ c&oacute; tr&ecirc;n Galaxy Note 20 series.</p>\r\n<h3><strong>Hiển thị tuyệt đẹp, mượt m&agrave; đến kh&oacute; tin</strong></h3>\r\n<p>Note 20 Ultra chắc chắn l&agrave; mẫu smartphone c&oacute; m&agrave;n h&igrave;nh đẹp bậc nhất thế giới hiện nay. Trước mắt bạn l&agrave; một m&agrave;n h&igrave;nh cực lớn 6,9 inch tr&agrave;n cạnh Infinity-O, độ ph&acirc;n giải QHD+ si&ecirc;u n&eacute;t, hỗ trợ HDR10+ v&agrave; c&ocirc;ng nghệ m&agrave;n h&igrave;nh<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-samsung-dynamic-amoled-khong-chi-la-man-hinh-not-ruoi-no-con-co-nhieu-hon-the-80743\">&nbsp;Dynamic AMOLED 2X</a>. Mọi thứ đều hiển thị lớn, sống động, đẹp rực rỡ v&agrave; độ chi tiết ho&agrave;n hảo. Kh&ocirc;ng chỉ đẹp, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n c&oacute; độ mượt m&agrave; vượt trội với tần số qu&eacute;t 120Hz, cho những thao t&aacute;c vuốt chạm của bạn phản hồi ngay lập tức, đưa hiệu quả c&ocirc;ng việc v&agrave; giải tr&iacute; l&ecirc;n tối đa.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-3.jpg\" alt=\"m&agrave;n h&igrave;nh Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>B&uacute;t S Pen cải tiến mạnh mẽ tr&ecirc;n Note 20 Ultra</strong></h3>\r\n<p>Bạn sẽ được trải nghiệm c&acirc;y b&uacute;t cảm ứng cho n&eacute;t b&uacute;t tự nhi&ecirc;n, ch&acirc;n thực v&agrave; mượt m&agrave; nhất từ trước đến nay với S Pen mới tr&ecirc;n Galaxy Note 20 Ultra. Nhờ độ nhạy vượt trội v&agrave; m&agrave;n h&igrave;nh tần số qu&eacute;t 120Hz, thao t&aacute;c ghi ch&uacute; của S Pen gần như kh&ocirc;ng c&oacute; độ trễ, mọi thứ giống như bạn ghi ch&eacute;p tr&ecirc;n giấy. Lu&ocirc;n sẵn s&agrave;ng ghi ch&uacute; v&agrave; ghi ch&uacute; ở bất cứ đ&acirc;u, từ m&agrave;n h&igrave;nh kh&oacute;a cho đến ảnh chụp, S Pen mang đến cho bạn cuộc sống tiện lợi v&agrave; dễ d&agrave;ng hơn.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-4.jpg\" alt=\"b&uacute;t S Pen Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Ghi ch&uacute; đồng bộ v&agrave; liền mạch</strong></h3>\r\n<p>Với ứng dụng Samsung Notes tr&ecirc;n cả điện thoại, m&aacute;y t&iacute;nh bảng v&agrave; m&aacute;y t&iacute;nh, những ghi ch&uacute; của bạn c&oacute; thể được đồng bộ, chỉnh sửa dễ d&agrave;ng tr&ecirc;n mọi thiết bị. Ghi ch&uacute; nhanh ch&oacute;ng tr&ecirc;n điện thoại, sau đ&oacute; xem v&agrave; chỉnh sửa ở m&aacute;y t&iacute;nh bảng hoặc m&aacute;y t&iacute;nh để c&oacute; m&agrave;n h&igrave;nh lớn c&ugrave;ng nhiều c&ocirc;ng cụ hơn, Galaxy Note 20 Ultra gi&uacute;p mọi thứ liền mạch, ho&agrave;n hảo hơn bao giờ hết.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-5.jpg\" alt=\"ghi ch&uacute; Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Kh&aacute;m ph&aacute; sức mạnh h&agrave;ng đầu</strong></h3>\r\n<p>L&agrave; thiết bị cao cấp nhất thế giới smartphone hiện tại, kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi Samsung Galaxy Note 20 Ultra mang tr&ecirc;n m&igrave;nh cấu h&igrave;nh đ&aacute;ng kinh ngạc. Bạn sẽ c&oacute; bộ vi xử l&yacute;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/chip-exynos-990-cua-galaxy-note-20-se-co-hieu-nang-ngang-snapdragon-865-123912\">Exynos 990</a>&nbsp;sản xuất tr&ecirc;n tiến tr&igrave;nh 7nm+ ti&ecirc;n tiến nhất hiện nay; 8GB RAM v&agrave; 256GB bộ nhớ trong cho sức mạnh tương đương m&aacute;y t&iacute;nh để b&agrave;n.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;c&ograve;n trang bị hệ thống l&agrave;m m&aacute;t n&acirc;ng cao để lu&ocirc;n đảm bảo hiệu năng tốt nhất cho thiết bị. Kết hợp th&ecirc;m m&agrave;n h&igrave;nh 120Hz si&ecirc;u mượt, Note 20 Ultra sẽ ph&aacute;t huy hết sức mạnh từ những t&aacute;c vụ sử dụng thường ng&agrave;y cho đến những tựa game đồ họa nặng.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-6.jpg\" alt=\"cấu h&igrave;nh Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Tuyệt t&aacute;c camera 108MP của Note 20 Ultra</strong></h3>\r\n<p>Độ sắc n&eacute;t của những bức ảnh tr&ecirc;n Samsung Galaxy Note 20 Ultra đ&atilde; vượt xa mọi giới hạn với camera ch&iacute;nh độ ph&acirc;n giải l&ecirc;n tới 108MP hỗ trợ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-tinh-nang-chong-rung-quang-hoc-la-gi-63290\">chống rung quang học OIS</a>. Ngo&agrave;i ra Note 20 Ultra c&ograve;n c&oacute; camera 12MP Tele OIS, camera 12MP g&oacute;c si&ecirc;u rộng v&agrave; cảm biến lấy n&eacute;t Laser. Kết quả l&agrave; bạn sẽ c&oacute; những bức ảnh sắc n&eacute;t, ch&acirc;n thực như những g&igrave; mắt người chứng kiến. Ấn tượng hơn nữa, t&iacute;nh năng si&ecirc;u zoom cho ph&eacute;p ph&oacute;ng to ảnh tới 50 lần, đưa cảnh vật từ rất xa đến ngay trước mắt bạn một c&aacute;ch đầy kỳ diệu.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-7.jpg\" alt=\"camera Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Quay phim chuẩn điện ảnh</strong></h3>\r\n<p>Bạn đ&atilde; sẵn s&agrave;ng s&aacute;ng tạo những bộ phim chuy&ecirc;n nghiệp, chuẩn điện ảnh ngay tr&ecirc;n chiếc điện thoại Samsung Galaxy Note 20 Ultra của m&igrave;nh. Khả năng quay video độ ph&acirc;n giải l&ecirc;n tới 8K, sắc n&eacute;t gấp 4 lần chuẩn UltraHD 4K, mang đến những thước phim sắc n&eacute;t, chi tiết đến bất ngờ. C&aacute;c t&ugrave;y chọn tỉ lệ 16:9 v&agrave; 21:9 đa dạng tạo n&ecirc;n trải nghiệm điện ảnh đ&iacute;ch thực. Hơn nữa, chế độ Pro cho ph&eacute;p bạn điều chỉnh tốc độ thu ph&oacute;ng trong khi quay, tạo n&ecirc;n những g&oacute;c quay ấn tượng.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-8.jpg\" alt=\"quay video Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Hiệu ứng video cực đỉnh từ Note 20 Ultra</strong></h3>\r\n<p>Mức độ ho&agrave;n thiện video đ&atilde; l&ecirc;n một tầm cao mới ở Note 20 Ultra. Bạn c&oacute; thể quay phim 120fps, l&agrave;m chậm khung h&igrave;nh mượt m&agrave;; quay video x&oacute;a ph&ocirc;ng với c&aacute;c hiệu ứng đầy nghệ thuật. Tất nhi&ecirc;n kh&ocirc;ng thể kh&ocirc;ng nhắc đến khả năng quay video chống rung si&ecirc;u ổn định với t&iacute;nh năng Super Steady. Cảm biến camera cao cấp, c&ocirc;ng nghệ chống rung quang học OIS, ổn định h&igrave;nh ảnh AI n&acirc;ng cao gi&uacute;p những cảnh quay h&agrave;nh động nhanh trở n&ecirc;n mượt m&agrave; v&agrave; sắc n&eacute;t.</p>\r\n<h3><strong>Trải nghiệm trọn vẹn suốt cả ng&agrave;y</strong></h3>\r\n<p>Bạn ho&agrave;n to&agrave;n c&oacute; thể tin tưởng v&agrave;o thời lượng pin của Samsung Galaxy Note 20 Ultra. Vi&ecirc;n pin dung lượng cao 4500mAh được tối ưu th&ocirc;ng minh dựa tr&ecirc;n th&oacute;i quen sử dụng người d&ugrave;ng sẽ mang đến thời lượng pin thoải m&aacute;i suốt cả ng&agrave;y. Tha hồ l&agrave;m việc, giải tr&iacute; liền mạch, kh&ocirc;ng sợ gi&aacute;n đoạn tr&ecirc;n Note 20 Ultra.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-9.jpg\" alt=\"pin Samsung Galaxy Note 20 Ultra\" /></p>\r\n<h3><strong>Sạc nhanh si&ecirc;u tốc, tr&agrave;n đầy năng lượng</strong></h3>\r\n<p>Nhanh ch&oacute;ng sạc đầy v&agrave; tiếp tục trải nghiệm nhờ bộ sạc si&ecirc;u nhanh 25W c&oacute; sẵn trong hộp. Bạn sẽ chỉ mất 30 ph&uacute;t để sạc được 50% pin cho Samsung Galaxy Note 20 Ultra, gi&uacute;p chiếc điện thoại lu&ocirc;n tr&agrave;n đầy năng lượng.</p>\r\n<p><img src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-note20-ultra-10.jpg\" alt=\"sạc nhanh Samsung Galaxy Note 20 Ultra\" /></p>', 1, 1, 1, 11, '2021-08-12 07:01:03', '2021-09-01 07:43:55', NULL);
INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(18, 'Samsung Galaxy S21', 'samsung-galaxy-s21', 'public/uploads/product/1\\ss-s21-plus-bac-1.jpg', 8000000, '<p><strong>Kh&aacute;m ph&aacute; vẻ đẹp chuẩn mực, ho&agrave;n to&agrave;n kh&aacute;c biệt tr&ecirc;n Samsung Galaxy S21 Plus, nơi bạn sẽ được tận hưởng camera chuy&ecirc;n nghiệp nhất từ trước đến nay, bộ vi xử l&yacute; mạnh mẽ v&agrave; hỗ trợ kết nối mạng 5G si&ecirc;u tốc.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-1.jpg\" alt=\"Samsung Galaxy S21 Plus\" /></strong></p>\r\n<h3><strong>Tuyệt t&aacute;c đầy ph&aacute; c&aacute;ch</strong></h3>\r\n<p><strong>Samsung Galaxy S21 Plus</strong>&nbsp;tiếp tục l&agrave; một kiệt t&aacute;c về thiết kế với m&agrave;n h&igrave;nh viền si&ecirc;u mỏng tuyệt đẹp, khung kim loại nguy&ecirc;n khối cứng c&aacute;p v&agrave; mặt lưng k&iacute;nh mờ sang trọng. Kh&ocirc;ng chỉ đẹp, Galaxy S21&nbsp;Plus&nbsp;c&ograve;n thể hiện sự độc đ&aacute;o với cụm camera ph&aacute; c&aacute;ch, nổi bật tạo điểm nhấn tr&ecirc;n mặt lưng m&aacute;y. Trừ phi&ecirc;n bản m&agrave;u đen nguy&ecirc;n bản, c&aacute;c phi&ecirc;n bản m&agrave;u kh&aacute;c cụm camera đều được chế t&aacute;c m&agrave;u sắc tương phản, cho bạn một vẻ đẹp thời thượng, dẫn đầu xu hướng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-2.jpg\" alt=\"thiết kế Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Rực rỡ ở mọi g&oacute;c nh&igrave;n</strong></h3>\r\n<p>M&agrave;n h&igrave;nh của một thiết bị cao cấp đến từ&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;chưa bao giờ khiến bạn phải thất vọng. Samsung Galaxy S21&nbsp;Plus&nbsp;sở hữu m&agrave;n h&igrave;nh h&agrave;ng đầu thế giới smartphone với tấm nền 6,7 inch, thiết kế dạng v&ocirc; cực Infinity-O quyến rũ, hỗ trợ HDR10+ v&agrave; c&ocirc;ng nghệ Dynamic AMOLED 2X cao cấp.</p>\r\n<p>Bạn sẽ được chi&ecirc;m ngưỡng những m&agrave;u sắc sống động, độ tương phản cao v&agrave; ch&acirc;n thực đến từng chi tiết. Chưa hết, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n hỗ trợ tần số qu&eacute;t 120Hz si&ecirc;u mượt, phản hồi v&agrave; chuyển cảnh nhanh đến kh&ocirc;ng ngờ, mang đến trải nghiệm ho&agrave;n to&agrave;n kh&aacute;c biệt.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-3.jpg\" alt=\"m&agrave;n h&igrave;nh Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Bền bỉ qua thời gian</strong></h3>\r\n<p>Được chế t&aacute;c từ những vật liệu cao cấp,&nbsp;<strong>Galaxy S21&nbsp;Plus</strong>&nbsp;hết sức bền vững. Đặc biệt, m&agrave;n h&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;được bảo vệ từ k&iacute;nh cường lực Gorilla Glass Victus, loại k&iacute;nh cường lực cứng c&aacute;p nhất từ trước đến nay, hạn chế tối đa hiện tượng trầy xước v&agrave; hư hỏng. Tất nhi&ecirc;n Samsung Galaxy S21&nbsp;Plus&nbsp;cũng c&oacute; khả năng chống nước v&agrave; chống bụi IP68, gần như miễn nhiễm mọi nguy cơ từ nước trong cuộc sống thường ng&agrave;y.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-4.jpg\" alt=\"bền bỉ Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Bộ ba camera 64MP đẳng cấp</strong></h3>\r\n<p>Sở hữu tới 3 camera sau cao cấp, bao gồm camera g&oacute;c rộng 12MP, hỗ trợ lấy n&eacute;t k&eacute;p Dual Pixel; camera g&oacute;c si&ecirc;u rộng 12MP v&agrave; camera Tele 64MP, Samsung Galaxy S21&nbsp;Plus&nbsp;gi&uacute;p ai cũng c&oacute; thể trở th&agrave;nh nhiếp ảnh gia khi dễ d&agrave;ng ghi lại những bức ảnh sắc n&eacute;t. Khả năng thu ph&oacute;ng chuy&ecirc;n nghiệp khi bạn kh&ocirc;ng cần di chuyển c&oacute; thể đưa chủ thể từ xa đến gần, ph&oacute;ng to khung h&igrave;nh sang g&oacute;c si&ecirc;u rộng; dễ d&agrave;ng zoom lai 10x, zoom kh&ocirc;ng gian 30x để chụp được những vật thể cực xa một c&aacute;ch r&otilde; n&eacute;t.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-5.jpg\" alt=\"camera Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Kiến tạo tuyệt phẩm trong đ&ecirc;m tối</strong></h3>\r\n<p>Nhờ những cảm biến chất lượng h&agrave;ng đầu, khẩu độ lớn, c&ocirc;ng nghệ lấy n&eacute;t theo pha k&eacute;p, chống rung quang học OIS,&nbsp;<em>Samsung Galaxy S21 Plus</em>&nbsp;đ&iacute;ch thực l&agrave; một chiếc điện thoại chuy&ecirc;n chụp đ&ecirc;m. D&ugrave; trong m&ocirc;i trường thiếu s&aacute;ng, mọi chi tiết cũng đều được khắc họa ch&acirc;n thực, m&agrave;u sắc sống động, &aacute;nh s&aacute;ng tự nhi&ecirc;n, cho bạn những bức ảnh tuyệt đẹp.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-6.jpg\" alt=\"chụp đ&ecirc;m Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Ảnh ch&acirc;n dung nghệ thuật</strong></h3>\r\n<p>Ảnh ch&acirc;n dung l&agrave; một t&iacute;nh năng kh&ocirc;ng mới, nhưng t&iacute;nh năng n&agrave;y đ&atilde; được n&acirc;ng tầm mạnh mẽ tr&ecirc;n Samsung Galaxy S21 Plus. Ngo&agrave;i việc chủ thể được t&aacute;ch biệt ho&agrave;n hảo với phần ph&ocirc;ng nền l&agrave;m mờ tự nhi&ecirc;n, bạn c&ograve;n c&oacute; thể th&ecirc;m rất nhiều hiệu ứng Studio chuy&ecirc;n nghiệp. Kết hợp th&ecirc;m độ chi tiết đỉnh cao từ những camera h&agrave;ng đầu, bạn sẽ dễ d&agrave;ng c&oacute; được bức ảnh ch&acirc;n dung đầy t&iacute;nh nghệ thuật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-s21-plus-7.jpg\" alt=\"ảnh ch&acirc;n dung Samsung Galaxy S21 Plus\" /></p>\r\n<h3><strong>Quay video chuẩn điện ảnh</strong></h3>\r\n<p><strong>S21&nbsp;Plus</strong>&nbsp;c&oacute; thể quay video độ ph&acirc;n giải 8K, cao nhất tr&ecirc;n thế giới smartphone từ trước đến nay. Những đoạn video n&agrave;y c&oacute; thể ph&aacute;t tr&ecirc;n TV 8K m&agrave;n h&igrave;nh lớn hay thậm ch&iacute; l&agrave; tr&ecirc;n m&agrave;n h&igrave;nh khổng lồ của rạp chiếu phim m&agrave; vẫn v&ocirc; c&ugrave;ng sắc n&eacute;t. Ấn tượng hơn nữa, bạn c&ograve;n c&oacute; thể tr&iacute;ch xuất ảnh tĩnh trong đoạn phim để c&oacute; được những khoảnh khắc đ&aacute;ng nhớ.</p>\r\n<h3><strong>Chống rung mượt m&agrave;</strong></h3>\r\n<p>Samsung Galaxy S21 Plus c&ograve;n c&oacute; t&iacute;nh năng si&ecirc;u chống rung, gi&uacute;p quay video mượt m&agrave; d&ugrave; l&agrave; đang di chuyển ở tốc độ cao hay quay c&aacute;c cảnh h&agrave;nh động. Chế độ quay phim si&ecirc;u chống rung kết hợp giữa t&iacute;nh năng chống rung AI, tốc độ khung h&igrave;nh chuẩn 60 fps, thậm ch&iacute; c&ograve;n quay cực mượt ở trong điều kiện thiếu s&aacute;ng.</p>', 1, 1, 1, 11, '2021-08-12 07:02:22', '2021-08-25 07:32:07', NULL),
(19, 'MacBook Pro 13\" 2020 M1', 'macbook-pro-13-2020-m1', 'public/uploads/product/1\\mbp-2020-m1-gray-1.jpg', 34900000, '<p><strong>Tốc độ v&agrave; sức mạnh ho&agrave;n hảo trong một th&acirc;n m&aacute;y nhỏ gọn.&nbsp;<a title=\"MacBook Pro M1\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/macbook-pro-13-2020-touch-bar-m1-256gb\" type=\"MacBook Pro M1\">MacBook Pro M1</a>&nbsp;13 inch 2020 Touch Bar mới với bộ vi xử l&yacute;&nbsp;<a title=\"Apple M1\" href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-chip-apple-m1-bo-vi-xu-ly-dinh-cao-moi-danh-cho-mac-128095\" type=\"Apple M1\">Apple M1</a>&nbsp;cho bạn hiệu suất v&agrave; thời lượng pin tuyệt vời nhất từ trước đến nay, mang đến hiệu năng chuy&ecirc;n nghiệp để ho&agrave;n th&agrave;nh những c&ocirc;ng việc chuy&ecirc;n nghiệp.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-1.jpg\" alt=\"MacBook Pro 13 2020 Touch Bar M1\" /></strong></p>\r\n<h3><strong>Con chip nhỏ, đột ph&aacute; lớn</strong></h3>\r\n<p>Vũ kh&iacute; b&iacute; mật của MacBook Pro 13 2020 đến từ Apple M1, bộ vi xử l&yacute; đầu ti&ecirc;n được ch&iacute;nh Apple chế tạo cho m&aacute;y Mac. Với 16 tỷ b&oacute;ng b&aacute;n dẫn, Apple M1 t&iacute;ch hợp tất cả từ CPU, GPU, Neural Engine, I/O, bộ xử l&yacute; h&igrave;nh ảnh v&agrave; hơn thế nữa v&agrave;o một con chip nhỏ b&eacute;. Bạn sẽ được tận hưởng những điều đ&aacute;ng kinh ngạc chưa từng thấy ở MacBook Pro 13.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-16.jpg\" alt=\"MacBook Pro 13 2020 Touch Bar M1 vi xử l&yacute;\" /></p>\r\n<h3><strong>Tốc độ nhanh vượt trội</strong></h3>\r\n<p>Bộ vi xử l&yacute; M1 gi&uacute;p MacBook Pro 13 2020 nhanh v&agrave; mạnh một c&aacute;ch đ&aacute;ng kinh ngạc. Bộ vi xử l&yacute; 8 l&otilde;i mạnh mẽ c&oacute; thể giải quyết c&aacute;c t&aacute;c vụ nặng, c&aacute;c quy tr&igrave;nh phức tạp với tốc độ CPU nhanh hơn tới 2,8 lần thế hệ trước. 4 l&otilde;i hiệu năng cao của Apple M1 l&agrave; 4 l&otilde;i CPU nhanh nhất thế giới đối với d&ograve;ng chip sử dụng năng lượng thấp. Trong khi đ&oacute; 4 l&otilde;i c&ograve;n lại gi&uacute;p bạn xử l&yacute; c&aacute;c t&aacute;c vụ th&ocirc;ng thường h&agrave;ng ng&agrave;y chỉ với một phần mười năng lượng, qua đ&oacute; mang đến thời lượng pin m&agrave; ai cũng phải bất ngờ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-5.jpg\" alt=\"tốc độ MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>Đồ họa t&iacute;ch hợp nhanh nhất thế giới</strong></h3>\r\n<p>GPU 8 nh&acirc;n b&ecirc;n trong Apple M1 l&agrave; bộ xử l&yacute; đồ họa ti&ecirc;n tiến nhất m&agrave; Apple từng tạo ra. Bạn sẽ c&oacute; hiệu suất đồ họa nhanh gấp 5 lần thế hệ trước v&agrave; l&agrave; đồ họa t&iacute;ch hợp nhanh nhất thế giới laptop hiện nay. Bạn c&oacute; thể ph&aacute;t video 8K, xử l&yacute; c&aacute;c t&aacute;c vụ đồ họa 3D, thiết kế những bối cảnh phức tạp v&agrave; thậm ch&iacute; l&agrave; b&ugrave;ng nổ với những tựa game đồ họa m&atilde;n nh&atilde;n tr&ecirc;n chiếc MacBook Pro 13 2020 nhỏ b&eacute; nhưng đầy chuy&ecirc;n nghiệp.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-17.jpg\" alt=\"đồ họa MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>L&agrave;m việc nhanh hơn nhờ tr&iacute; tuệ AI</strong></h3>\r\n<p>Apple M1 t&iacute;ch hợp nh&acirc;n Neural Engine 16 l&otilde;i, tối ưu hệ thống tr&iacute; tuệ nh&acirc;n tạo Machine Learning. Tốc độ xử l&yacute; sẽ nhanh hơn nhờ khả năng tự động h&oacute;a cho c&aacute;c hoạt động ph&acirc;n t&iacute;ch video, nhận dạng giọng n&oacute;i, xử l&yacute; h&igrave;nh ảnh v&agrave; hơn thế nữa. So với thế hệ trước, Machine Learning nhanh hơn tới 11 lần, để bạn ho&agrave;n th&agrave;nh c&ocirc;ng việc trong chớp mắt.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-22.jpg\" alt=\"AI MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>L&agrave;m m&aacute;t tức th&igrave;, duy tr&igrave; hiệu năng</strong></h3>\r\n<p>MacBook Pro 13 2020 M1 trang bị quạt tản nhiệt ti&ecirc;n tiến, nhanh ch&oacute;ng l&agrave;m m&aacute;t, lưu th&ocirc;ng luồng kh&iacute; để bạn lu&ocirc;n c&oacute; được hiệu năng ổn định trong thời gian d&agrave;i. Sẵn s&agrave;ng cho những c&ocirc;ng việc cần hoạt động li&ecirc;n tục, những t&aacute;c vụ đ&ograve;i hỏi khả năng xử l&yacute; cao m&agrave; m&aacute;y vẫn hết sức mượt m&agrave;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-21.jpg\" alt=\"l&agrave;m m&aacute;t MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>Thời lượng pin kh&oacute; tin 20 tiếng</strong></h3>\r\n<p>Với bộ vi xử l&yacute; Apple M1, Apple đ&atilde; tạo ra chiếc&nbsp;<a title=\"MacBook ch&iacute;nh h&atilde;ng\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/apple-macbook\" type=\"MacBook ch&iacute;nh h&atilde;ng\">MacBook</a>&nbsp;c&oacute; thời lượng pin l&acirc;u nhất từ trước đến nay. MacBook Pro 13 2020 M1 c&oacute; thể xem video li&ecirc;n tục trong 20 giờ hay duyệt web qua Wi-Fi trong 17 giờ đồng hồ. Thời lượng pin n&agrave;y l&agrave; qu&aacute; đủ để hoạt động l&agrave;m việc, giải tr&iacute; của bạn kh&ocirc;ng bao giờ bị gi&aacute;n đoạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-8.jpg\" alt=\"pin MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh Retina đỉnh cao</strong></h3>\r\n<p>Vẻ đẹp của m&agrave;n h&igrave;nh Retina tr&ecirc;n MacBook Pro 13 2020 M1 thật kh&oacute; c&oacute; thể để diễn tả th&agrave;nh lời. Từ văn bản sắc n&eacute;t cho đến h&igrave;nh ảnh r&otilde; r&agrave;ng, độ chi tiết ho&agrave;n hảo v&agrave; sự ch&acirc;n thực đ&aacute;ng kinh ngạc. Đ&egrave;n nền LED mang tới m&agrave;u đen s&acirc;u v&agrave; &aacute;nh s&aacute;ng trắng. Trong khi đ&oacute; c&ocirc;ng nghệ m&agrave;u P3 cho dải m&agrave;u rộng hơn tới 25% so với sRGB. Hơn nữa, MacBook Pro 13 2020 M1 c&ograve;n c&oacute; c&ocirc;ng nghệ True Tone, điều chỉnh m&agrave;u sắc theo m&ocirc;i trường v&agrave; &aacute;nh s&aacute;ng xung quanh để bạn lu&ocirc;n c&oacute; được trải nghiệm xem dễ chịu nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-2.jpg\" alt=\"m&agrave;n h&igrave;nh MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>N&acirc;ng cao chất lượng gọi FaceTime</strong></h3>\r\n<p>Con chip M1 cũng g&oacute;p phần tăng cường chất lượng cuộc gọi FaceTime HD nhờ bộ xử l&yacute; t&iacute;n hiệu h&igrave;nh ảnh mới. Chất lượng h&igrave;nh ảnh được cải thiện r&otilde; rệt, đặc biệt l&agrave; trong c&aacute;c điều kiện &aacute;nh s&aacute;ng yếu hay ch&ecirc;nh lệch &aacute;nh s&aacute;ng mạnh. Kết hợp với 3 micro chất lượng ph&ograve;ng thu, giảm thiểu tạp &acirc;m đ&aacute;ng kể, bạn sẽ c&oacute; những cuộc gọi FaceTime HD r&otilde; r&agrave;ng v&agrave; r&otilde; n&eacute;t. Thoải m&aacute;i sắp xếp c&ocirc;ng việc v&agrave; l&agrave;m việc ở bất cứ đ&acirc;u qua c&aacute;c cuộc họp video trực tuyến chất lượng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-3.jpg\" alt=\"FaceTime MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>B&agrave;n ph&iacute;m Magic Keyboard, Touch Bar v&agrave; Touch ID</strong></h3>\r\n<p>Tr&ecirc;n MacBook Pro 13 2020 M1, b&agrave;n ph&iacute;m Magic Keyboard mới với cơ chế cắt k&eacute;o tinh chỉnh mang lại trải nghiệm g&otilde; ph&iacute;m nhanh, nhạy v&agrave; y&ecirc;n tĩnh. Ngo&agrave;i ra bạn c&ograve;n nhận được sự hỗ trợ của thanh cảm ứng Touch Bar, lu&ocirc;n hiểu bạn cần g&igrave; v&agrave; th&ecirc;m v&agrave;o những thao t&aacute;c l&agrave;m việc nhanh ch&oacute;ng ở đ&uacute;ng nơi bạn cần. Cuối c&ugrave;ng l&agrave; cảm biến v&acirc;n tay Touch ID, thay thế thao t&aacute;c nhập mật khẩu chỉ bằng một c&uacute; chạm. Tất cả đều hướng tới sự nhanh ch&oacute;ng, tiện lợi nhất cho bạn khi l&agrave;m việc với MacBook Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-4.jpg\" alt=\"b&agrave;n ph&iacute;m MacBook Pro 13 2020 Touch Bar M1\" /></p>\r\n<h3><strong>Kết nối nhanh ch&oacute;ng</strong></h3>\r\n<p>MacBook Pro 13 2020 Touch Bar M1 t&iacute;ch hợp những kết nối nhanh h&agrave;ng đầu thế giới hiện nay, d&ugrave; l&agrave; kết nối kh&ocirc;ng d&acirc;y hay c&oacute; d&acirc;y. Thế hệ Wi-Fi 6 vừa nhanh hơn, vừa cho chất lượng s&oacute;ng khỏe hơn, kể cả khi bạn truy cập mạng ở nơi c&ocirc;ng cộng c&oacute; nhiều người d&ugrave;ng c&ugrave;ng l&uacute;c. Trong khi đ&oacute; cổng Thunderbolt 4 kh&ocirc;ng chỉ c&oacute; tốc độ cao m&agrave; c&ograve;n hết sức đa năng. Bạn c&oacute; thể d&ugrave;ng cổng kết nối n&agrave;y để sạc, xuất h&igrave;nh độ ph&acirc;n giải l&ecirc;n tới 6K, truyền dữ liệu si&ecirc;u tốc 40Gb/s v&agrave; kết nối với nhiều thiết bị ngoại vi kh&aacute;c.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-13-m1-20.jpg\" alt=\"kết nối MacBook Pro 13 2020 Touch Bar M1\" /></p>', 1, 1, 1, 15, '2021-08-15 22:59:10', '2021-08-15 22:59:10', NULL),
(20, 'MacBook Air 13\" 2020 M1', 'macbook-air-13-2020-m1', 'public/uploads/product/1\\mba-2020-gray-1.jpg', 28900000, '<p><strong>Chiếc&nbsp;<a title=\"MacBook Air\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/macbook-air\" type=\"MacBook Air\">MacBook Air</a>&nbsp;c&oacute; hiệu năng đột ph&aacute; nhất từ trước đến nay đ&atilde; xuất hiện. Bộ vi xử l&yacute;&nbsp;<a title=\"Apple M1\" href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-chip-apple-m1-bo-vi-xu-ly-dinh-cao-moi-danh-cho-mac-128095\" type=\"Apple M1\">Apple M1</a>&nbsp;ho&agrave;n to&agrave;n mới đưa sức mạnh của&nbsp;<a title=\"MacBook Air M1\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/macbook-air-13-2020-m1-256gb\" type=\"MacBook Air M1\">MacBook Air M1</a>&nbsp;13 inch vượt xa khỏi mong đợi người d&ugrave;ng, c&oacute; thể chạy được những t&aacute;c vụ nặng v&agrave; thời lượng pin đ&aacute;ng kinh ngạc.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-1.jpg\" alt=\"MacBook Air 13 2020 M1\" /></strong></p>\r\n<h3><strong>Sức mạnh chưa từng thấy tr&ecirc;n d&ograve;ng MacBook Air</strong></h3>\r\n<p>Lần đầu ti&ecirc;n Apple sử dụng con chip do ch&iacute;nh m&igrave;nh sản xuất cho d&ograve;ng m&aacute;y&nbsp;<a title=\"Macbook ch&iacute;nh h&atilde;ng\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/apple-macbook\" type=\"Macbook ch&iacute;nh h&atilde;ng\">Macbook</a>. Bộ vi xử l&yacute; Apple M1 với 16 tỉ b&oacute;ng b&aacute;n dẫn, bao gồm 8 nh&acirc;n cực mạnh, trong đ&oacute; c&oacute; 4 nh&acirc;n hiệu năng cao v&agrave; 4 nh&acirc;n tiết kiệm điện, mang đến cho MacBook Air tốc độ xử l&yacute; tuyệt vời, đồng thời k&eacute;o d&agrave;i thời lượng pin. Nhờ vậy MacBook Air 13 2020 M1 c&oacute; hiệu suất nhanh gấp 3,5 lần thế hệ cũ, cho bạn l&agrave;m việc với cả những c&ocirc;ng việc chuy&ecirc;n nghiệp, những t&aacute;c vụ nặng m&agrave; kh&ocirc;ng thể chạy được tr&ecirc;n MacBook Air trước đ&acirc;y.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-17.jpg\" alt=\"cấu h&igrave;nh MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Khả năng xử l&yacute; đồ họa đỉnh cao</strong></h3>\r\n<p>MacBook Air 13 M1 c&oacute; khả năng đồ họa kh&oacute; tin tr&ecirc;n một chiếc laptop si&ecirc;u nhỏ gọn. GPU t&iacute;ch hợp tr&ecirc;n Apple M1 c&oacute; tới 8 nh&acirc;n v&agrave; l&agrave; GPU t&iacute;ch hợp mạnh nhất thế giới laptop hiện nay. So với thế hệ trước, MacBook Air 13 2020 M1 c&oacute; khả năng xử l&yacute; đồ họa mạnh gấp 5 lần. Giờ đ&acirc;y ngay tr&ecirc;n chiếc MacBook Air cực kỳ di động, bạn đ&atilde; c&oacute; thể xem v&agrave; chỉnh sửa video 4K mượt m&agrave;, thậm ch&iacute; l&agrave; chơi game cũng như chạy c&aacute;c t&aacute;c vụ đồ họa chuy&ecirc;n s&acirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-10.jpg\" alt=\"đồ họa MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Thời lượng pin tới 18 tiếng</strong></h3>\r\n<p>MacBook Air trước đ&acirc;y đ&atilde; được đ&aacute;nh gi&aacute; cao về thời lượng pin, nhưng với MacBook Air 13 2020 M1, bạn c&ograve;n l&agrave;m được nhiều điều hơn thế. Chiếc MacBook Air mới nhất c&oacute; thể lướt web li&ecirc;n tục trong 15 tiếng v&agrave; xem video li&ecirc;n tục trong 18 tiếng. Sẽ thật dễ d&agrave;ng để bạn l&agrave;m việc hay giải tr&iacute; cả ng&agrave;y m&agrave; kh&ocirc;ng cần quan t&acirc;m đến nguồn điện với MacBook Air M1.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-11.jpg\" alt=\"pin MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Hoạt động &ecirc;m &aacute;i</strong></h3>\r\n<p>Kh&ocirc;ng cần đến quạt tản nhiệt, MacBook Air 13 2020 M1 vẫn c&oacute; thể tản nhiệt hiệu quả bởi một bộ tản nhiệt với những l&aacute; nh&ocirc;m. Nhờ vậy, chiếc&nbsp;<a title=\"Laptop ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPT Shop\" href=\"https://fptshop.com.vn/may-tinh-xach-tay\" type=\"Laptop ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPT Shop\">laptop</a>&nbsp;của bạn c&oacute; thể hoạt động &ecirc;m &aacute;i, ho&agrave;n to&agrave;n y&ecirc;n tĩnh d&ugrave; l&agrave; trong đ&ecirc;m tối.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-3.jpg\" alt=\"tản nhiệt MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Tận hưởng h&igrave;nh ảnh đẹp tuyệt mỹ tr&ecirc;n m&agrave;n h&igrave;nh Retina</strong></h3>\r\n<p>Trước mắt bạn l&agrave; m&agrave;n h&igrave;nh chuẩn Retina 13,3 inch, độ ph&acirc;n giải 2560 x 1600 pixels si&ecirc;u n&eacute;t của MacBook Air 13 2020. Tất cả đều hiển thị ho&agrave;n hảo, sắc n&eacute;t, ch&acirc;n thực v&agrave; m&agrave;u sắc ch&iacute;nh x&aacute;c gần như tuyệt đối. M&agrave;n h&igrave;nh MacBook Air hỗ trợ c&ocirc;ng nghệ True Tone v&agrave; dải m&agrave;u rộng P3 nhiều hơn tới 25% m&agrave;u sắc so với chuẩn sRGB th&ocirc;ng thường. D&ugrave; l&agrave; giải tr&iacute; hay l&agrave;m đồ họa chuy&ecirc;n nghiệp, m&agrave;n h&igrave;nh MacBook Air cũng cho bạn c&aacute;i nh&igrave;n trung thực, ch&iacute;nh x&aacute;c v&agrave; hấp dẫn nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-16.jpg\" alt=\"m&agrave;n h&igrave;nh MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Tăng cường chất lượng gọi video</strong></h3>\r\n<p>Cả &acirc;m thanh v&agrave; h&igrave;nh ảnh trong c&aacute;c cuộc gọi FaceTime đều được cải thiện tr&ecirc;n MacBook Air 13 2020 M1. Con chip xử l&yacute; h&igrave;nh ảnh của Apple M1 gi&uacute;p chất lượng h&igrave;nh ảnh tốt hơn bao giờ hết, r&otilde; n&eacute;t kể cả trong điều kiện thiếu s&aacute;ng. Hệ thống 3 micro tập trung v&agrave;o giọng n&oacute;i của bạn, đ&agrave;m thoại to, r&otilde; r&agrave;ng d&ugrave; trong m&ocirc;i trường ồn &agrave;o.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-20.jpg\" alt=\"gọi video MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>B&agrave;n ph&iacute;m Magic Keyboard, trải nghiệm g&otilde; ph&iacute;m tốt nhất</strong></h3>\r\n<p>Đ&uacute;ng như t&ecirc;n gọi, b&agrave;n ph&iacute;m Magic Keyboard của MacBook Air 13 2020 M1 quả l&agrave; mang đến những điều kỳ diệu. Cơ chế b&agrave;n ph&iacute;m cắt k&eacute;o, c&aacute;c ph&iacute;m tắt hữu &iacute;ch, biểu tượng cảm x&uacute;c tiện lợi v&agrave; đ&egrave;n nền c&oacute; cảm biến &aacute;nh s&aacute;ng, tất cả kết hợp lại tạo n&ecirc;n&nbsp;<a title=\"B&agrave;n ph&iacute;m laptop, b&agrave;n ph&iacute;m m&aacute;y t&iacute;nh gi&aacute; rẻ\" href=\"https://fptshop.com.vn/phu-kien/ban-phim\" type=\"B&agrave;n ph&iacute;m laptop, b&agrave;n ph&iacute;m m&aacute;y t&iacute;nh gi&aacute; rẻ\">b&agrave;n ph&iacute;m laptop</a>&nbsp;ho&agrave;n hảo bậc nhất hiện nay.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-21.jpg\" alt=\"b&agrave;n ph&iacute;m MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Cảm biến v&acirc;n tay Touch ID một chạm</strong></h3>\r\n<p>Kh&ocirc;ng cần phải mất thời gian nhập mật khẩu, tr&ecirc;n MacBook Air 13 2020 M1, cảm biến v&acirc;n tay Touch ID sẽ đăng nhập mọi thứ chỉ với một c&uacute; chạm. Mở kh&oacute;a m&aacute;y t&iacute;nh hay mở kh&oacute;a t&agrave;i liệu bảo mật, truy cập c&agrave;i đặt hệ thống, bạn đều c&oacute; thể sử dụng v&acirc;n tay một c&aacute;ch nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-5.jpg\" alt=\"v&acirc;n tay MacBook Air 13 2020 M1\" /></p>\r\n<h3><strong>Kết nối mạnh mẽ v&agrave; ti&ecirc;n tiến h&agrave;ng đầu</strong></h3>\r\n<p>D&ugrave; l&agrave; kết nối kh&ocirc;ng d&acirc;y hay c&oacute; d&acirc;y, MacBook Air 13 2020 M1 cũng đều sở hữu những c&ocirc;ng nghệ h&agrave;ng đầu hiện nay. Thế hệ Wi-Fi 6 cho tốc độ nhanh, chất lượng kết nối ổn định, phạm vi phủ s&oacute;ng rộng để bạn vẫn c&oacute; Wi-Fi ổn định ở những nơi c&ocirc;ng cộng. Trong khi đ&oacute; cổng Thunderbolt 4 l&agrave; cổng kết nối đa năng v&agrave; mạnh mẽ nhất, khi c&oacute; thể truyền dữ liệu, sạc hay xuất h&igrave;nh để sử dụng nhiều m&agrave;n h&igrave;nh c&ugrave;ng l&uacute;c.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-air-m1-4.jpg\" alt=\"kết nối MacBook Air 13 2020 M1\" /></p>', 1, 0, 1, 15, '2021-08-15 23:01:29', '2021-08-15 23:01:29', NULL),
(21, 'Asus Zenbook UX425EA KI439T', 'asus-zenbook-ux425ea-ki439t', 'public/uploads/product/1\\asus-zenbook-ux425ja-xam-1.jpg', 30699000, '<p><strong>Khi vẻ đẹp vượt thời gian, t&iacute;nh di động ho&agrave;n hảo v&agrave; sức mạnh hiệu năng đỉnh cao kết hợp lại với nhau, đ&oacute; ch&iacute;nh l&agrave; l&uacute;c&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-zenbook-ux425ea-ki439t-i7-1165g7\">Asus ZenBook UX425EA KI439T</a>&nbsp;xuất hiện, một si&ecirc;u phẩm laptop đ&iacute;ch thực d&agrave;nh cho c&ocirc;ng việc.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-19.jpg\" alt=\"Asus ZenBook UX425EA KI439T\" /></strong></p>\r\n<h3><strong>Đỉnh cao của sức mạnh, c&ugrave;ng bạn đi tới th&agrave;nh c&ocirc;ng</strong></h3>\r\n<p>Asus ZenBook UX425EA KI439T l&agrave; phi&ecirc;n bản c&oacute; cấu h&igrave;nh mạnh nhất của d&ograve;ng ZenBook 14 khi trang bị bộ vi xử l&yacute;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/intel-core-i7\"><strong>Intel Core i7</strong></a>&nbsp;1165G7. Đ&acirc;y l&agrave; con chip đầu bảng của thế hệ Intel thứ 11, với 4 nh&acirc;n 8 luồng v&agrave; tốc độ tối đa l&ecirc;n tới 4.70GHz, cực kỳ mạnh mẽ, xử l&yacute; chuy&ecirc;n nghiệp mọi t&aacute;c vụ. Ngo&agrave;i ra GPU đồ họa cũng được n&acirc;ng cấp l&ecirc;n Intel Iris Xe Graphics, cho hiệu năng đồ họa vượt trội. B&ecirc;n cạnh đ&oacute; ZenBook 14 c&ograve;n c&oacute; sẵn&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/8-gb\"><strong>8GB RAM</strong></a>&nbsp;LPDDR4X 4266 MHz v&agrave; ổ cứng&nbsp;<strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/ssd-512-gb\">SSD 512GB</a></strong>&nbsp;PCIe 3.0 si&ecirc;u tốc. Khả năng xử l&yacute; cực nhanh mọi t&aacute;c vụ gi&uacute;p c&ocirc;ng việc của bạn diễn ra một c&aacute;ch ho&agrave;n hảo.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-13.jpg\" alt=\"cấu h&igrave;nh Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Si&ecirc;u mỏng nhẹ, người bạn đồng h&agrave;nh l&yacute; tưởng cho mỗi chuyến đi</strong></h3>\r\n<p>Asus ZenBook 14 UX425EA g&acirc;y ấn tượng mạnh nhờ kiểu d&aacute;ng thanh mảnh đầy thời trang. Trọng lượng chỉ 1,13kg v&agrave; độ mỏng 14,3mm gi&uacute;p m&aacute;y c&oacute; khả năng cơ động tuyệt vời, lu&ocirc;n đồng h&agrave;nh c&ugrave;ng bạn đi bất cứ đ&acirc;u. ZenBook 14 UX425EA cũng l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/khoang-14-inch\">laptop 14 inch</a>&nbsp;mỏng nhất thế giới sở hữu đầy đủ cổng kết nối, kể cả cổng HDMI k&iacute;ch thước ti&ecirc;u chuẩn v&agrave; USB Type-A, mang đến những kết nối linh hoạt đầy tiện lợi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-1.jpg\" alt=\"thiết kế Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Thiết kế của tương lai, tỏa s&aacute;ng qua năm th&aacute;ng</strong></h3>\r\n<p>Phi&ecirc;n bản ZenBook 14 UX425EA trong b&agrave;i viết c&oacute; m&agrave;u x&aacute;m th&ocirc;ng, thể hiện sự th&acirc;m trầm, hiện đại. Kiểu d&aacute;ng cao cấp v&agrave; ho&agrave;n thiện từ kim loại nguy&ecirc;n khối, c&ugrave;ng m&agrave;n h&igrave;nh viền si&ecirc;u mỏng tạo n&ecirc;n một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>&nbsp;đẹp ho&agrave;n mỹ. Asus ZenBook 14 UX425EA mang tới đẳng cấp ri&ecirc;ng biệt cho người d&ugrave;ng, khẳng định gi&aacute; trị của sự tr&iacute; tuệ qua một thiết bị c&ocirc;ng nghệ đỉnh cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-6.jpg\" alt=\"thiết kế Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Tất cả nội dung trở n&ecirc;n sống động hơn tr&ecirc;n m&agrave;n h&igrave;nh tuyệt mỹ</strong></h3>\r\n<p>Vẻ đẹp của m&agrave;n h&igrave;nh ZenBook 14 UX425EA kh&oacute; c&oacute; thể diễn tả hết bằng lời m&agrave; bạn cần phải tự cảm nhận mới thấy được đầy đủ sự hấp dẫn. Một m&agrave;n h&igrave;nh 14 inch độ ph&acirc;n giải Full HD sắc n&eacute;t, độ s&aacute;ng tới 400 nits được bao phủ bởi 4 cạnh viền si&ecirc;u mỏng tạo hiệu ứng tr&agrave;n viền tuyệt đẹp. Tỷ lệ m&agrave;n h&igrave;nh chiếm tới 90% th&acirc;n m&aacute;y, đồng nghĩa với việc h&igrave;nh ảnh như nổi l&ecirc;n trước mắt bạn. Những trải nghiệm như xem ảnh, xem phim tr&ecirc;n Asus ZenBook 14 UX425EA sẽ tuyệt vời hơn bao giờ hết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-3.jpg\" alt=\"m&agrave;n h&igrave;nh Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Kh&ocirc;ng gian giải tr&iacute; cho ri&ecirc;ng bạn</strong></h3>\r\n<p>Hệ thống &acirc;m thanh của ZenBook 14 UX425EA được chứng nhận bởi c&aacute;c chuy&ecirc;n gia &acirc;m thanh đến từ h&atilde;ng Harman Kardon danh tiếng. Bạn sẽ được đắm ch&igrave;m trong &acirc;m thanh lớn, trong trẻo, &acirc;m trầm mạnh mẽ, tha hồ tận hưởng những bộ phim y&ecirc;u th&iacute;ch hoặc tham gia những cuộc gọi video chất lượng cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-17.jpg\" alt=\"&acirc;m thanh Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>L&agrave;m việc, giải tr&iacute; ở bất cứ đ&acirc;u</strong></h3>\r\n<p>T&iacute;nh cơ động tuyệt vời của ZenBook 14 UX425EA được thể hiện ở thời lượng pin tối đa l&ecirc;n tới 22 tiếng sau một lần sạc đầy. Bạn c&oacute; thể l&agrave;m việc mọi l&uacute;c mọi nơi m&agrave; kh&ocirc;ng cần quan t&acirc;m đến nguồn sạc. Ngo&agrave;i ra m&aacute;y c&ograve;n hỗ trợ t&iacute;nh năng sạc nhanh, cho ph&eacute;p sạc đầy 60% pin chỉ trong v&ograve;ng 49 ph&uacute;t, đảm bảo nguồn năng lượng dồi d&agrave;o.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-21.jpg\" alt=\"di động Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Kết nối Thunderbolt 4 cao cấp</strong></h3>\r\n<p>ZenBook 14 UX425EA đ&atilde; t&aacute;i định nghĩa về kết nối cho&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/mong-nhe\">d&ograve;ng laptop si&ecirc;u mỏng</a>. M&aacute;y c&oacute; đầy đủ những kết nối h&agrave;ng đầu, bao gồm đầu đọc thẻ MicroSD, cổng USB Type-A 3.2 Gen 1, cổng HDMI v&agrave; đặc biệt l&agrave; 2 cổng Thunderbolt 4 USB-C. Cổng Thunderbolt 4 mang đến sự linh hoạt v&agrave; khả năng kết nối vượt trội, bạn c&oacute; thể sử dụng l&agrave;m cổng sạc; kết nối với m&agrave;n h&igrave;nh 4K UHD v&agrave; tốc độ truyền dữ liệu l&ecirc;n tới 40Gbps. Chưa hết, bạn c&ograve;n được tặng k&egrave;m cổng chuyển cho mạng LAN v&agrave; tai nghe khi mua laptop, tạo n&ecirc;n chiếc laptop mỏng nhất c&oacute; khả năng kết nối to&agrave;n diện.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-12.jpg\" alt=\"kết nối Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Bản lề ErgoLift th&ocirc;ng minh</strong></h3>\r\n<p>Bản lề ErgoLift l&agrave; sự s&aacute;ng tạo th&ocirc;ng minh của&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus\">laptop Asus</a>&nbsp;trong những năm gần đ&acirc;y. Khi mở m&aacute;y ra, bản lề sẽ n&acirc;ng laptop l&ecirc;n một g&oacute;c nhỏ khoảng 3 độ. Chỉ cần một g&oacute;c nghi&ecirc;ng nhỏ n&agrave;y cũng gi&uacute;p bạn c&oacute; vị tr&iacute; g&otilde; ph&iacute;m tốt hơn; khả năng l&agrave;m m&aacute;t th&ocirc;ng tho&aacute;ng; chất lượng &acirc;m thanh được tăng cường v&agrave; c&ograve;n thu hẹp một phần viền m&agrave;n h&igrave;nh cạnh dưới, tạo n&ecirc;n sự c&acirc;n đối trong trải nghiệm.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-12.jpg\" alt=\"bản lề Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Cho những cuộc họp trực tuyến của bạn c&oacute; chất lượng tốt nhất</strong></h3>\r\n<p>Những cuộc họp video đang dần phổ biến hơn trong m&ocirc;i trường l&agrave;m việc hiện đại. Ch&iacute;nh v&igrave; thế ASUS ZenBook 14 UX425EA trang bị t&iacute;nh năng khử ồn AI để người d&ugrave;ng c&oacute; trải nghiệm gọi video tốt nhất. C&ocirc;ng nghệ AI sẽ t&aacute;ch tiếng ồn kh&ocirc;ng mong muốn từ ngoại cảnh ra khỏi giọng n&oacute;i con người. T&iacute;nh năng ClearVoice Mic v&agrave; ClearVoice Speaker gi&uacute;p &acirc;m lượng của cả người n&oacute;i v&agrave; người nghe đều được đảm bảo r&otilde; r&agrave;ng, trong trẻo. Bạn sẽ c&oacute; được trải nghiệm họp trực tuyến kh&aacute;c biệt với c&ocirc;ng nghệ chồng ồn AI của Asus.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-14.jpg\" alt=\"webcam Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>B&agrave;n ph&iacute;m, Touchpad đầy s&aacute;ng tạo</strong></h3>\r\n<p>Asus ZenBook 14 UX425EA được t&iacute;nh to&aacute;n v&agrave; thiết kế để mang đến trải nghiệm tốt nhất cho người d&ugrave;ng. Laptop c&oacute; b&agrave;n ph&iacute;m tr&agrave;n viền, tạo n&ecirc;n ph&iacute;m bấm lớn, h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u cho cảm gi&aacute;c g&otilde; ph&iacute;m thoải m&aacute;i; phần Touchpad được l&agrave;m rộng, thao t&aacute;c thoải m&aacute;i v&agrave; đặc biệt c&oacute; thể k&iacute;ch hoạt b&agrave;n ph&iacute;m số ngay tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/huong-dan-kich-hoat-touchpad-cho-laptop-61318\">Touchpad</a>&nbsp;đầy s&aacute;ng tạo. Chưa hết, ZenBook 14 UX425EA c&ograve;n trang bị camera hồng ngoại, cho ph&eacute;p bạn đăng nhập bằng khu&ocirc;n mặt v&agrave; hoạt động tốt kể cả trong điều kiện &aacute;nh s&aacute;ng yếu.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-15.jpg\" alt=\"b&agrave;n ph&iacute;m Asus ZenBook UX425EA KI439T\" /></p>\r\n<h3><strong>Độ bền chuẩn qu&acirc;n đội</strong></h3>\r\n<p>Kh&ocirc;ng chỉ đẹp m&agrave; độ bền của Asus ZenBook 14 UX425EA cũng rất đ&aacute;ng kinh ngạc. ZenBook 14 thậm ch&iacute; c&ograve;n đạt chuẩn qu&acirc;n đội Mỹ MIL-STD-810G. M&aacute;y đ&atilde; trải qua rất nhiều thử nghiệm trong m&ocirc;i trường khắc nghiệt, bao gồm cả độ cao; thả rơi; rung lắc; nhiệt độ cao thấp, &hellip; để c&oacute; được độ bền cứng c&aacute;p, hoạt động ổn định trong thời gian d&agrave;i.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-zenbook-ux425-fpt-7.jpg\" alt=\"độ bền Asus ZenBook UX425EA KI439T\" /></p>', 1, 0, 1, 16, '2021-08-15 23:20:24', '2021-08-15 23:31:31', NULL),
(22, 'Asus TUF Gaming FX506LH HN002T', 'asus-tuf-gaming-fx506lh-hn002t', 'public/uploads/product/1\\asus-tuf-fx506-xam-1.jpg', 20999000, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-tuf-gaming-fx506lh-hn002t-i5-10300h\">Asus TUF Gaming FX506LH HN002T</a>&nbsp;thuộc d&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-tuf-gaming\">laptop chơi game Asus TUF</a>, cho bạn sự mạnh mẽ, bền bỉ kh&ocirc;ng chỉ thiết kế bề ngo&agrave;i m&agrave; cả hiệu năng b&ecirc;n trong. Với Asus TUF Gaming FX506, bạn sẽ l&agrave; một chiến binh thực sự trong tr&ograve; chơi y&ecirc;u th&iacute;ch.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-9.jpg\" alt=\"Asus TUF Gaming FX506LH HN002T\" /></strong></p>\r\n<h3><strong>Kiểu d&aacute;ng gaming nhưng kh&ocirc;ng k&eacute;m phần sang trọng</strong></h3>\r\n<p>Thiết kế của Asus TUF FX506LH HN002T nh&igrave;n qua c&oacute; vẻ đơn giản nhưng lại đậm c&aacute; t&iacute;nh v&agrave; ẩn chứa nhiều điều bất ngờ. Bạn sẽ c&oacute; một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?nhu-cau=gaming-do-hoa\">laptop gaming</a>&nbsp;gọn g&agrave;ng với viền m&agrave;n h&igrave;nh mỏng v&agrave; trọng lượng chỉ 2.3 kg. To&agrave;n th&acirc;n m&aacute;y l&agrave; những đường n&eacute;t tinh tế như họa tiết tổ ong ở khung m&aacute;y độc đ&aacute;o, g&oacute;p phần tản nhiệt tốt hơn; c&aacute;c đường kẻ sọc đối lập đầy thanh tho&aacute;t v&agrave; đặc biệt l&agrave; thiết kế Fortress Gray với m&agrave;u x&aacute;m cực sang trọng tr&ecirc;n mặt lưng, c&ugrave;ng logo TUF gaming đẳng cấp. Tất cả tạo n&ecirc;n một chiếc laptop chuẩn game thủ, c&aacute; t&iacute;nh v&agrave; mạnh mẽ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-2.jpg\" alt=\"thiết kế Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Cấu h&igrave;nh ho&agrave;n hảo trong tầm gi&aacute;</strong></h3>\r\n<p>Asus TUF FX506LH HN002T mang tr&ecirc;n m&igrave;nh sức mạnh của bộ vi xử l&yacute; Intel thế hệ thứ 10.&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?cpu=intel-core-i5\"><strong>Con chip Intel Core i5</strong></a>&nbsp;10300H với 4 l&otilde;i 8 luồng v&agrave; tốc độ tối đa l&ecirc;n tới 4.50GHz cho hiệu năng đ&aacute;ng tin cậy, d&ugrave; l&agrave; bạn chơi game, ph&aacute;t trực tiếp hay l&agrave;m việc. Đi k&egrave;m với đ&oacute; l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?o-cung=ssd-512-gb\"><strong>512GB SSD</strong></a>&nbsp;NVMe PCIe, 8GB RAM DDR4 v&agrave; khả năng n&acirc;ng cấp dễ d&agrave;ng, để bạn c&oacute; được hiệu năng đ&uacute;ng như &yacute; muốn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-13.jpg\" alt=\"cấu h&igrave;nh Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>N&acirc;ng tầm đồ họa, chơi tốt mọi tựa game phổ biến</strong></h3>\r\n<p>Card đồ họa Asus TUF FX506LH HN002T sử dụng l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?card-do-hoa=nvidia-geforce-series\">NVIDIA GeForce</a>&nbsp;GTX 1650. Đ&acirc;y l&agrave; card đồ họa với kiến tr&uacute;c Turing danh tiếng, gi&uacute;p bạn c&oacute; thể chơi tốt mọi game eSports v&agrave; sẵn s&agrave;ng cho c&aacute;c tựa game bom tấn AAA. C&ocirc;ng nghệ d&ograve; tia theo thời gian thực trong game,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cong-nghe-ai-la-gi-tim-hieu-ve-cong-nghe-ai-63382\">tr&iacute; tuệ nh&acirc;n tạo AI</a>&nbsp;sẽ mang đến cho bạn trải nghiệm game cực đỉnh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-11.jpg\" alt=\"đồ họa Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Hoạt động l&acirc;u hơn</strong></h3>\r\n<p>Asus TUF FX506 F15 d&ugrave; c&oacute; thiết kế nhỏ gọn hơn, nhưng lại mang tr&ecirc;n m&igrave;nh vi&ecirc;n pin dung lượng lớn hơn, l&ecirc;n tới 90Wh. Bạn c&oacute; thể xem video li&ecirc;n tục trong tới 12,5 giờ hoặc duyệt web trong 8,4 giờ. Giờ đ&acirc;y chiếc laptop chơi game của bạn đ&atilde; c&oacute; thể l&agrave;m việc v&agrave; giải tr&iacute; ở bất cứ đ&acirc;u, trong thời gian d&agrave;i m&agrave; kh&ocirc;ng cần đến nguồn điện.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-14.jpg\" alt=\"pin Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Mở rộng kh&ocirc;ng gian hiển thị, tần số qu&eacute;t 144Hz si&ecirc;u mượt</strong></h3>\r\n<p>M&agrave;n h&igrave;nh Asus TUF FX506LH HN002T được chế t&aacute;c viền mỏng tối đa, mang đến những h&igrave;nh ảnh tuyệt đẹp tr&ecirc;n tấm nền 15,6 inch,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/nhung-dieu-can-biet-ve-do-phan-giai-full-hd-131916\">độ ph&acirc;n giải Full HD</a>. Đặc biệt, đ&acirc;y c&ograve;n l&agrave; m&agrave;n h&igrave;nh c&oacute; tốc độ khung h&igrave;nh vượt trội 144Hz. C&aacute;c tựa game sẽ được t&aacute;i hiện một c&aacute;ch mượt m&agrave;, tốc độ khung h&igrave;nh nhanh v&agrave; giảm thiểu hiện tượng x&eacute; h&igrave;nh. Hơn thế nữa, bạn c&ograve;n c&oacute; thể kết nối với hai m&agrave;n h&igrave;nh b&ecirc;n ngo&agrave;i c&ugrave;ng l&uacute;c, bao gồm một m&agrave;n h&igrave;nh để l&agrave;m việc qua cổng USB 3.2 gen 2 Type-C hỗ trợ DisplayPort 1.4 v&agrave; một m&agrave;n h&igrave;nh lớn để chơi game, xem phim 4K qua cổng HDMI 2.0b.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-16.jpg\" alt=\"m&agrave;n h&igrave;nh Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>B&agrave;n ph&iacute;m cho bạn lợi thế khi chơi game</strong></h3>\r\n<p>Asus TUF FX506LH HN002T trang bị b&agrave;n ph&iacute;m tương tự m&aacute;y t&iacute;nh để b&agrave;n, tốt nhất cho hoạt động chơi game. Tất nhi&ecirc;n bạn sẽ c&oacute; đ&egrave;n nền RGB đồng bộ v&agrave; nhiều chế độ kh&aacute;c nhau để tha hồ thể hiện kỹ năng một c&aacute;ch &ldquo;cool ngầu&rdquo; hơn. C&ocirc;ng nghệ Overstroke k&iacute;ch hoạt vị tr&iacute; nhận lệnh cao hơn tr&ecirc;n ph&iacute;m bấm, mang tới tốc độ phản hồi nhanh hơn. Tất nhi&ecirc;n kh&ocirc;ng thể kh&ocirc;ng nhắc đến độ bền của một sản phẩm TUF khi b&agrave;n ph&iacute;m Asus TUF Gaming F15 c&oacute; tuổi thọ tới 20 triệu lần nhấn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-6.jpg\" alt=\"b&agrave;n ph&iacute;m Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Hệ thống &acirc;m thanh v&ograve;m ho&agrave;nh tr&aacute;ng</strong></h3>\r\n<p>Với việc sở hữu hai loa n&acirc;ng cấp v&agrave; 4 &acirc;m thanh đầu ra, Asus TUF FX506LH HN002T cho &acirc;m lượng lớn gấp 1,8 lần v&agrave; &acirc;m bass trầm hơn 2,7 lần so với thế hệ trước. Bạn sẽ được trải nghiệm &acirc;m thanh v&ograve;m ảo 7.1 nhờ c&ocirc;ng nghệ DTS:X, mang đến chất lượng &acirc;m thanh chuẩn ph&ograve;ng thu khi sử dụng tai nghe Stereo. Trong những trận chiến game hiện đại, &acirc;m thanh cũng đ&oacute;ng vai tr&ograve; quan trọng, v&igrave; thế &acirc;m thanh chất lượng của Asus TUF F15 cũng l&agrave; một vũ kh&iacute; d&agrave;nh cho ri&ecirc;ng bạn. Tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>&nbsp;c&ograve;n c&oacute; 8 chế độ &acirc;m thanh kh&aacute;c nhau để bạn c&oacute; được trải nghiệm nghe đ&uacute;ng như &yacute; th&iacute;ch với mỗi hoạt động nghe nhạc, xem phim hay chơi game.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-18.jpg\" alt=\"&acirc;m thanh Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Giải ph&aacute;p tản nhiệt to&agrave;n diện</strong></h3>\r\n<p>Hệ thống tản nhiệt cao cấp bao gồm nhiều ống tản nhiệt v&agrave; 3 ống dẫn nhiệt gi&uacute;p Asus TUF F15 c&oacute; khả năng tản nhiệt nhanh ch&oacute;ng. Thiết kế l&agrave;m m&aacute;t tự l&agrave;m sạch th&ocirc;ng minh đảm bảo hệ thống tản nhiệt hoạt động hiệu quả v&agrave; l&acirc;u d&agrave;i. Nhờ vậy bạn lu&ocirc;n c&oacute; được hiệu năng ổn định tr&ecirc;n Asus TUF FX506LH HN002T để tự tin giải tr&iacute; với những trận chiến Game hấp dẫn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-19.jpg\" alt=\"tản nhiệt Asus TUF Gaming FX506LH HN002T\" /></p>\r\n<h3><strong>Độ bền danh tiếng của chuẩn TUF</strong></h3>\r\n<p>Những chiếc laptop TUF Gaming lu&ocirc;n nổi tiếng với độ bền xuất sắc. Asus TUF FX506LH-HN002T đ&atilde; vượt qua b&agrave;i thử nghiệm độ bền chuẩn qu&acirc;n đội MIL-STD-810H. C&aacute;c thử nghiệm như thả rơi, chống rung, nhiệt độ cao, nhiệt độ thấp hay độ ẩm đều đ&atilde; được vượt qua tr&ecirc;n chiếc TUF Gaming F15, để khi đến tay người ti&ecirc;u d&ugrave;ng, đ&oacute; sẽ l&agrave; một chiếc laptop gaming v&ocirc; c&ugrave;ng bền vững.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/asus-tuf-fx-506-4.jpg\" alt=\"độ bền Asus TUF Gaming FX506LH HN002T\" /></p>', 1, 1, 1, 16, '2021-08-15 23:23:44', '2021-08-17 01:34:22', NULL);
INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(23, 'Dell Gaming G3 15 3500B', 'dell-gaming-g3-15-3500b', 'public/uploads/product/1\\dell-g3500-den-1.jpg', 31999000, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/dell-g3-15-3500b-i7-10750h\">Dell G3 15 3500B</a>&nbsp;l&agrave; phi&ecirc;n bản mạnh nhất của d&ograve;ng laptop chơi game Dell G3 15 2020. Với&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?cpu=intel-core-i7\">bộ vi xử l&yacute; Intel Core i7</a>&nbsp;10750H v&agrave; card rời GTX 1660 Ti, Dell G3 15 3500B c&oacute; sức mạnh đột ph&aacute; để mang đến trải nghiệm chơi game đỉnh cao.</strong></p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-7.jpg\" alt=\"Dell G3 15 3500B\" /></p>\r\n<h3><strong>Mạnh mẽ vượt trội với Intel Core i7 thế hệ thứ 10</strong></h3>\r\n<p>Thỏa sức &ldquo;c&acirc;n&rdquo; mọi tựa game với sức mạnh đ&aacute;ng kinh ngạc của Dell G3 15 3500B. Bộ vi xử l&yacute; Intel Core i7 10750H với 6 l&otilde;i 12 luồng v&agrave; tốc độ tối đa l&ecirc;n tới 5.00 GHz sẽ gi&uacute;p cho trải nghiệm game của bạn kh&ocirc;ng bị gi&aacute;n đoạn v&igrave; những hiện tượng giật lag. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể livestream, xem video v&agrave; l&agrave;m mọi c&ocirc;ng việc kh&aacute;c tr&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?nhu-cau=gaming-do-hoa\">laptop chơi game</a>&nbsp;của m&igrave;nh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-5.jpg\" alt=\"cấu h&igrave;nh Dell G3 15 3500B\" /></p>\r\n<h3><strong>Card rời GTX 1660 Ti, livestream chơi game thoải m&aacute;i</strong></h3>\r\n<p>Phi&ecirc;n bản Dell G3 15 3500B mang tr&ecirc;n m&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?card-do-hoa=nvidia-geforce-series\">card đồ họa rời NVIDIA GeForce</a>&nbsp;GTX 1660 Ti 6GB GDDR6 với bộ vi xử l&yacute; đa luồng mạnh mẽ v&agrave; kiến tr&uacute;c Turing đ&atilde; từng đạt giải thưởng, cho khả năng xử l&yacute; đồ họa xuất sắc. Sức mạnh của GTX 1660 Ti tương đương với GTX 1070, chơi tốt cả những tựa game bom tấn AAA. Hơn nữa, GTX 1660 Ti c&ograve;n tối ưu cho hoạt động vừa chơi game vừa livestram đang l&agrave; xu hướng hiện nay.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-15.jpg\" alt=\"card rời Dell G3 15 3500B\" /></p>\r\n<h3><strong>16GB RAM v&agrave; 512GB SSD</strong></h3>\r\n<p>Trang bị sẵn&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?ram=16-gb\"><strong>16GB RAM</strong></a>&nbsp;DDR4 2933MHz hiệu năng cao v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?o-cung=ssd-512-gb\"><strong>ổ cứng SSD 512GB</strong></a>, Dell G3 15 c&oacute; tốc độ cực nhanh trong suốt cả qu&aacute; tr&igrave;nh sử dụng. Từ khởi động m&aacute;y, mở ứng dụng, tải tệp tin, sao ch&eacute;p dữ liệu cho đến v&agrave;o game, tất cả đều diễn ra rất nhanh ch&oacute;ng, kh&ocirc;ng để bạn phải chờ đợi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-2.jpg\" alt=\"RAM Dell G3 15 3500B\" /></p>\r\n<h3><strong>K&iacute;ch hoạt sức mạnh tối đa chỉ sau một lần nhấn</strong></h3>\r\n<p>Chơi những game nặng lu&ocirc;n đ&ograve;i hỏi hệ thống phải hoạt động ở c&ocirc;ng suất tối đa. Với Dell G3 15, đơn giản bạn chỉ cần k&iacute;ch hoạt chế độ hiệu năng cao bằng tổ hợp ph&iacute;m Fn+G. T&iacute;nh năng Game Shift sẽ ngay lập tức tối ưu h&oacute;a hiệu năng, tối đa tốc độ quạt để bộ xử l&yacute; hoạt động mạnh hơn, m&aacute;y m&aacute;t mẻ hơn. Sau khi chơi game, bạn c&oacute; thể nhấn tổ hợp ph&iacute;m tr&ecirc;n một lần nữa để laptop trở về trạng th&aacute;i mặc định, l&agrave;m những t&aacute;c vụ th&ocirc;ng thường.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-8.jpg\" alt=\"hiệu năng Dell G3 15 3500B\" /></p>\r\n<h3><strong>Lu&ocirc;n lu&ocirc;n m&aacute;t mẻ</strong></h3>\r\n<p>Dell G3 15 được trang bị hệ thống l&agrave;m m&aacute;t bằng quạt k&eacute;p, gi&uacute;p nhanh ch&oacute;ng tản nhiệt ra ngo&agrave;i, phần cứng lu&ocirc;n lu&ocirc;n m&aacute;t mẻ v&agrave; đạt được hiệu suất tốt nhất.</p>\r\n<h3><strong>Quản l&yacute; v&agrave; thiết lập hiệu năng đơn giản</strong></h3>\r\n<p>Tr&ecirc;n Dell G3 15 c&oacute; một phần mềm mang t&ecirc;n Alienware Command Center. Đ&acirc;y l&agrave; nơi bạn c&oacute; thể tinh chỉnh tất cả c&agrave;i đặt hệ thống cho từng tựa game, quản l&yacute; cả phần cứng v&agrave; phần mềm để thiết lập c&aacute;c chế độ hiệu năng theo &yacute; muốn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-13.jpg\" alt=\"hiệu năng Dell G3 15 3500B\" /></p>\r\n<h3><strong>Hiển thị sắc n&eacute;t, tần số qu&eacute;t 120Hz si&ecirc;u mượt</strong></h3>\r\n<p>Phần viền m&agrave;n h&igrave;nh Dell G3 15 được l&agrave;m rất mỏng, đặc biệt l&agrave; ở hai cạnh b&ecirc;n để bạn c&oacute; được trải nghiệm h&igrave;nh ảnh tốt hơn. Chất lượng từ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tu-van/man-hinh-ips-la-gi-34814\">m&agrave;n h&igrave;nh IPS Full HD</a>&nbsp;gi&uacute;p mọi thứ trở n&ecirc;n sống động, ch&acirc;n thực, hiển thị xuất sắc những tựa game c&oacute; đồ họa cao. Hơn nữa, m&agrave;n h&igrave;nh Dell G3 15 3500B c&ograve;n c&oacute; tần số qu&eacute;t 120Hz, hiển thị mượt m&agrave; hơn, th&iacute;ch hợp cho những tựa game fps.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-6.jpg\" alt=\"m&agrave;n h&igrave;nh Dell G3 15 3500B\" /></p>\r\n<h3><strong>Trải nghiệm &acirc;m thanh nổi 3D ngay tr&ecirc;n loa ngo&agrave;i</strong></h3>\r\n<p>Dải loa k&eacute;p với &acirc;m thanh 3D nahimic d&agrave;nh cho game thủ sẽ gi&uacute;p cho những giờ ph&uacute;t chơi game của bạn trở n&ecirc;n tuyệt vời. Từng tiếng động, nhạc nền đều được t&aacute;i hiện r&otilde; n&eacute;t để bạn thực sự nhập t&acirc;m v&agrave;o tr&ograve; chơi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-9.jpg\" alt=\"&acirc;m thanh Dell G3 15 3500B\" /></p>\r\n<h3><strong>Gọn nhẹ bền bỉ đậm chất ri&ecirc;ng</strong></h3>\r\n<p>Dell G3 15 c&oacute; thiết kế đầy phong c&aacute;ch, thể hiện chất ri&ecirc;ng của game thủ nhưng vẫn mang hơi hướng hiện đại, bền bỉ của thương hiệu Dell. Những đường cắt t&aacute;o bạo, phi&ecirc;n bản đen tuyền c&aacute; t&iacute;nh c&ugrave;ng những dải m&agrave;u xanh tạo điểm nhấn, Dell G3 15 cho bạn th&ecirc;m niềm cảm hứng trong mỗi trận chiến game. Hơn nữa với thiết kế gọn g&agrave;ng v&agrave; trọng lượng chỉ 2.34 kg, bạn c&oacute; thể dễ d&agrave;ng mang laptop đi khắp mọi nơi để tận hưởng đam m&ecirc;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/dell-g3-15-17.jpg\" alt=\"thiết kế Dell G3 15 3500B\" /></p>', 1, 0, 1, 17, '2021-08-15 23:39:44', '2021-08-15 23:39:44', NULL),
(24, 'Dell Gaming G7 15 7500', 'dell-gaming-g7-15-7500', 'public/uploads/product/1\\dell-gaming-g7-15-den-1.jpg', 38990000, '<p><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/dell-gaming-g7-15-7500-i7-10750h\"><strong>Dell Gaming G7 15 7500</strong></a><strong>&nbsp;l&agrave; mẫu laptop gaming cao cấp c&oacute; thiết kế si&ecirc;u mỏng, tinh tế đến từng đường n&eacute;t. B&ecirc;n trong m&aacute;y l&agrave; bộ vi xử l&yacute; Intel Core i7 thế hệ thứ 10 v&agrave; card đồ họa rời GTX 1660 Ti, cho bạn hiệu năng cực đỉnh trong mọi tựa game.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-15.jpg\" alt=\"Dell Gaming G7 15 7500\" /></strong></p>\r\n<h3><strong>Chiếc laptop chơi game si&ecirc;u mỏng đầy ấn tượng</strong></h3>\r\n<p>Dell Gaming G7 15 mới l&agrave; một trong những chiếc laptop gaming mỏng nhất hiện nay. Với độ mỏng chỉ 18,3mm v&agrave; sự trau chuốt, tỉ mỉ trong từng chi tiết, m&aacute;y mang đến ấn tượng mạnh cho người d&ugrave;ng. Kiểu d&aacute;ng hiện đại với những đường cắt phẳng, dứt kho&aacute;t kết hợp c&ugrave;ng m&agrave;u đen Minaral mạnh mẽ v&agrave; logo Dell b&oacute;ng bẩy tạo n&ecirc;n một sản phẩm thực sự cao cấp. Chưa hết, hệ thống đ&egrave;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/ban-phim-led-rgb-la-gi-nen-mua-ban-phim-led-rgb-nao-134653\">LED RGB tr&ecirc;n b&agrave;n ph&iacute;m</a>&nbsp;v&agrave; đ&egrave;n LED khung gầm độc đ&aacute;o gi&uacute;p chiếc m&aacute;y t&iacute;nh của bạn tỏa s&aacute;ng lung linh đầy phong c&aacute;ch.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-11.jpg\" alt=\"thiết kế Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>Intel Core i7 thế hệ thứ 10, sức mạnh h&agrave;ng đầu cho mọi t&aacute;c vụ</strong></h3>\r\n<p>Mang tr&ecirc;n m&igrave;nh bộ vi xử l&yacute; Intel Core i7 10750H, Dell Gaming G7 15 7500 tự tin ho&agrave;n th&agrave;nh xuất sắc mọi t&aacute;c vụ. Con chip với 6 nh&acirc;n 12 luồng, tốc độ tối đa l&ecirc;n tới 5.00GHz, TDP 45W c&oacute; thể đ&aacute;p ứng tốt từ những tựa game bom tấn AAA cho đến ph&aacute;t trực tiếp, chỉnh sửa video hay những phần mềm nặng. Ngo&agrave;i ra bạn c&ograve;n c&oacute; sẵn&nbsp;<strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/8-gb\">8GB RAM</a></strong>,&nbsp;<strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/ssd-512-gb\">512GB SSD</a></strong>&nbsp;c&ugrave;ng khả năng n&acirc;ng cấp dễ d&agrave;ng để c&oacute; được trải nghiệm nhanh ch&oacute;ng v&agrave; mượt m&agrave; nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-13.jpg\" alt=\"cấu h&igrave;nh Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>Chơi game đỉnh cao với card rời GTX 1660 Ti</strong></h3>\r\n<p>Card đồ họa NVIDIA GeForce GTX 1660 Ti 6GB c&oacute; mặt tr&ecirc;n Dell Gaming G7 15 7500 lu&ocirc;n được game thủ y&ecirc;u th&iacute;ch nhờ sức mạnh v&agrave; khả năng tương th&iacute;ch game tuyệt vời. Dựa tr&ecirc;n kiến tr&uacute;c Turing ti&ecirc;n tiến, GTX 1660 Ti cho sức mạnh tương đương GTX 1070, với t&iacute;nh năng thực thi đồng thời c&aacute;c ph&eacute;p to&aacute;n dấu phẩy động v&agrave; số nguy&ecirc;n, c&ocirc;ng nghệ đổ b&oacute;ng th&iacute;ch ứng, kiến tr&uacute;c bộ nhớ hợp nhất mới v&agrave; bộ nhớ đệm gấp đ&ocirc;i, bạn sẽ c&oacute; hiệu năng chơi game đ&aacute;ng kinh ngạc. Trải nghiệm game tốc độ cao hơn, đồ họa đẹp hơn, h&igrave;nh ảnh mượt m&agrave; hơn với GTX 1660 Ti.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-5.jpg\" alt=\"card rời Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>L&agrave;m m&aacute;t hiệu quả</strong></h3>\r\n<p>Trong một th&acirc;n m&aacute;y mỏng, Dell Gaming G7 15 vẫn mang đến hệ thống l&agrave;m m&aacute;t chuy&ecirc;n dụng với quạt k&eacute;p hoạt động hiệu quả v&agrave; y&ecirc;n tĩnh. Nhiệt sẽ được đẩy ra ngo&agrave;i nhanh ch&oacute;ng, đảm bảo nhiệt độ m&aacute;t mẻ cho hệ thống, kể cả khi bạn chơi những game nặng trong thời gian d&agrave;i.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-12.jpg\" alt=\"l&agrave;m m&aacute;t Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>C&ocirc;ng nghệ Game Shift</strong></h3>\r\n<p>Những tựa game nặng lu&ocirc;n đ&ograve;i hỏi cả hệ thống hoạt động nhịp nh&agrave;ng ở mức hiệu năng cao. C&ocirc;ng nghệ Game Shift tr&ecirc;n Dell Gaming G7 15 7500 cho ph&eacute;p bạn chỉ một lần nhấn n&uacute;t, trung t&acirc;m điều khiển Alienware Command Center sẽ ngay lập tức k&iacute;ch hoạt bộ vi xử l&yacute;, card m&agrave;n h&igrave;nh, quạt tản nhiệt hoạt động ở c&ocirc;ng suất cao, gi&uacute;p bạn c&oacute; hiệu năng đạt đỉnh ngay lập tức.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-3.jpg\" alt=\"Game Shift Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>Tận hưởng thế giới game ch&acirc;n thực</strong></h3>\r\n<p><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/tren-15-inch\">M&agrave;n h&igrave;nh 15,6 inch</a>&nbsp;viền si&ecirc;u mỏng 3 cạnh của Dell Gaming G7 15 7500 gi&uacute;p thế giới game hiện l&ecirc;n ch&acirc;n thực như ở ngay trước mắt bạn. Từ những hiệu ứng ch&aacute;y nổ cho đến nền trời, cảnh vật đều hết sức sống động. Hơn nữa, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n c&oacute; tần số qu&eacute;t cao 144Hz, gi&uacute;p bạn chơi game ở&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/fps-la-gi-thong-so-fps-tot-nhat-khi-choi-game-la-bao-nhieu-131770\">mức fps cao</a>, mượt m&agrave; hơn bao giờ hết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-1.jpg\" alt=\"chơi game Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>&Acirc;m thanh nahimic 3D mạnh mẽ</strong></h3>\r\n<p>Sở hữu cụm loa k&eacute;p v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/for-gamers/tim-hieu-ve-nahimic3-cong-nghe-am-thanh-cuc-dinh-tren-laptop-choi-game-msi-gf63-72485\">c&ocirc;ng nghệ &acirc;m thanh nổi nahimic 3D</a>, bạn sẽ tha hồ tận hưởng từng giai điệu &acirc;m thanh trong tr&ograve; chơi y&ecirc;u th&iacute;ch. Thậm ch&iacute; Dell Gaming G7 c&ograve;n c&oacute; c&ocirc;ng nghệ &acirc;m thanh radar để định vị &acirc;m thanh ph&aacute;t ra trong tựa game dễ d&agrave;ng hơn, từ đ&oacute; bạn c&oacute; thể thao t&aacute;c nhanh ch&oacute;ng v&agrave; đưa ra quyết định ch&iacute;nh x&aacute;c. Kể cả những tiếng động nhỏ như tiếng bước ch&acirc;n hay hơi thở của kẻ th&ugrave; cũng được t&aacute;i hiện một c&aacute;ch sống động, cho bạn đắm ch&igrave;m v&agrave;o thế giới game thực sự.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-2.jpg\" alt=\"&acirc;m thanh Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>Kết nối chuy&ecirc;n dụng cho game thủ</strong></h3>\r\n<p>Kh&ocirc;ng chỉ c&oacute; đầy đủ cổng kết nối cần thiết, tr&ecirc;n Dell Gaming G7 15 7500 c&ograve;n tập hợp những kết nối ti&ecirc;n tiến bậc nhất chuy&ecirc;n dụng cho game thủ. Bạn sẽ c&oacute; cổng kết nối đa năng Thunderbolt 3 USB 3.2 Gen 2 Type-C; cổng xuất h&igrave;nh MiniDP 1.4; cổng xuất h&igrave;nh HDMI 2.0; cổng mạng si&ecirc;u tốc RJ-45 Killer Networks E2500V2 Gigabit Ethernet. Đ&acirc;y đều l&agrave; những cổng kết nối hiếm thấy ở&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>&nbsp;th&ocirc;ng thường, cho bạn khả năng xuất h&igrave;nh đa năng, kết nối to&agrave;n diện v&agrave; tốc độ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-10.jpg\" alt=\"kết nối Dell Gaming G7 15 7500\" /></p>\r\n<h3><strong>Trung t&acirc;m kiểm so&aacute;t tr&ograve; chơi Alienware Command Center</strong></h3>\r\n<p>Alienware Command Cnter l&agrave; hệ thống phần mềm được thiết kế đặc biệt cho những laptop chơi game của Dell, gi&uacute;p bạn c&oacute; trải nghiệm game tốt hơn, t&iacute;nh tương th&iacute;ch cao v&agrave; mượt m&agrave; hơn. Bạn c&oacute; thể t&ugrave;y chỉnh&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tu-van/tim-hieu-phan-cung-co-ban-tren-may-tinh-cho-nguoi-khong-chuyen-20972\">phần cứng</a>&nbsp;v&agrave; phần mềm, thiết lập phần cứng cấu h&igrave;nh cho mỗi tr&ograve; chơi với t&iacute;nh c&aacute; nh&acirc;n h&oacute;a cực cao. Giao diện cũng rất dễ sử dụng để bạn lu&ocirc;n c&oacute; được hiệu năng trong game xuất sắc nhất, đồng thời sử dụng t&agrave;i nguy&ecirc;n hợp l&yacute; nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Dell-Gaming-G7-15-7500-6.jpg\" alt=\"kiểm so&aacute;t Dell Gaming G7 15 7500\" /></p>', 1, 1, 1, 17, '2021-08-15 23:42:24', '2021-08-17 01:34:19', NULL),
(25, 'Acer Aspire Gaming A715 42G R4ST', 'acer-aspire-gaming-a715-42g-r4st', 'public/uploads/product/1\\acer-aspire-gaming-a715-42g-den-1.jpg', 19000000, '<p><strong>Acer Aspire 7 A715 42G R4ST l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay?nhu-cau=gaming-do-hoa\">laptop gaming</a>&nbsp;ti&ecirc;n phong với bộ vi xử l&yacute; AMD 5000 series thế hệ mới nhất, mang đến cho bạn hiệu năng đột ph&aacute; trong một mức gi&aacute; b&igrave;nh d&acirc;n.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-a715-5000-3.jpg\" alt=\"Acer Aspire 7 A715 42G R4ST\" /></strong></p>\r\n<h3><strong>Hiệu năng tốt nhất trong tầm gi&aacute; nhờ AMD Ryzen 5000 series</strong></h3>\r\n<p>Sở hữu&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/acer-aspire-gaming-a715-42g-r4st-r5-5500u\">Acer Aspire Gaming A715 42G R4ST</a>&nbsp;đồng nghĩa với việc bạn l&agrave; một trong những game thủ đầu ti&ecirc;n trải nghiệm sức mạnh của d&ograve;ng chip AMD Ryzen 5000 series. Con chip&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/amd-ryzen-5\"><strong>AMD Ryzen 5</strong></a>&nbsp;5500U c&oacute; mặt tr&ecirc;n Acer Aspire Gaming A715 42G R4ST c&oacute; tới 6 nh&acirc;n 12 luồng cực mạnh, tốc độ tối đa 4.0GHz v&agrave; kh&ocirc;ng thể kh&ocirc;ng nhắc đến tiến tr&igrave;nh 7nm. Bạn sẽ c&oacute; hiệu năng đơn luồng v&agrave; đa luồng kh&ocirc;ng chỉ mạnh m&agrave; c&ograve;n hoạt động ổn định, đảm bảo mức xung nhịp cao trong suốt qu&aacute; tr&igrave;nh chơi game, để bạn y&ecirc;n t&acirc;m giải ph&oacute;ng những kỹ năng tốt nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-a715-5000-1.jpg\" alt=\"chip Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Card đồ họa rời GTX 1650</strong></h3>\r\n<p>Chịu tr&aacute;ch nhiệm cho xử l&yacute; đồ họa tr&ecirc;n Acer Aspire Gaming A715 42G R4ST l&agrave; card đồ họa rời GTX 1650. Đ&acirc;y l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/card-do-hoa-la-gi-nguyen-ly-va-nhiem-vu-cua-card-man-hinh-roi-82498\">card đồ họa</a>&nbsp;sản xuất bởi kiến tr&uacute;c Turing danh tiếng từ NVIDIA đ&atilde; d&agrave;nh được rất nhiều giải thưởng. Bạn c&oacute; thể chơi tốt c&aacute;c tựa game eSports với tốc độ khung h&igrave;nh mượt m&agrave;, hiệu ứng đồ họa ch&acirc;n thực.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-a715-5000-2.jpg\" alt=\"card rời Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>8GB RAM v&agrave; 256GB SSD</strong></h3>\r\n<p>Acer Aspire 7 A715 trang bị sẵn<strong>&nbsp;</strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/8-gb\"><strong>RAM 8GB</strong></a>&nbsp;DDR4 v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/ssd-256-gb\"><strong>256GB ổ cứng M.2 PCIe SSD</strong></a>&nbsp;si&ecirc;u tốc. Đ&acirc;y l&agrave; những linh kiện gi&uacute;p laptop c&oacute; tốc độ khởi động m&aacute;y, mở ứng dụng, tải game, chạy đa nhiệm cực kỳ mượt m&agrave; v&agrave; nhanh ch&oacute;ng. Bạn sẽ kh&ocirc;ng cần phải chờ đợi v&agrave; lu&ocirc;n tr&agrave;n đầy cảm hứng khi sử dụng laptop Acer Aspire 7 A715.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-18.jpg\" alt=\"RAM SSD Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Thể hiện phong c&aacute;ch chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Kh&ocirc;ng cần phải qu&aacute; cầu kỳ, thiết kế Acer Aspire 7 A715 hướng tới việc phục vụ cho nhu cầu sử dụng v&agrave; di chuyển của người d&ugrave;ng nhiều hơn.&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">Laptop</a>&nbsp;c&oacute; kết cấu cứng c&aacute;p, kiểu d&aacute;ng phẳng tối giản, bản lề cho g&oacute;c mở ho&agrave;n to&agrave;n tới 180 độ, để bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về độ bền. M&aacute;y c&oacute; sẵn đ&egrave;n nền b&agrave;n ph&iacute;m v&agrave; b&agrave;n ph&iacute;m full size, kh&ocirc;ng chỉ đ&aacute;p ứng tốt nhu cầu chơi game m&agrave; c&ocirc;ng việc, học tập cũng rất tiện dụng. Đặc biệt, với trọng lượng chỉ 2,1kg, rất nhẹ đối với một chiếc laptop gaming, bạn c&oacute; thể dễ d&agrave;ng lu&ocirc;n mang theo Acer Aspire 7 A715 b&ecirc;n m&igrave;nh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-20.jpg\" alt=\"thiết kế Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Chơi game b&ugrave;ng nổ tr&ecirc;n m&agrave;n h&igrave;nh tr&agrave;n viền</strong></h3>\r\n<p>Cho trải nghiệm xem nhiều hơn tr&ecirc;n một chiếc laptop nhỏ gọn hơn. D&ugrave; c&oacute; thiết kế nhỏ gọn nhưng Acer Aspire 7 A715 vẫn sở hữu&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/tren-15-inch\">m&agrave;n h&igrave;nh lớn 15,6 inch</a>, độ ph&acirc;n giải Full HD,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tu-van/man-hinh-ips-la-gi-34814\">tấm nền IPS</a>&nbsp;tuyệt đẹp. B&iacute; mật nằm ở phần viền m&agrave;n h&igrave;nh si&ecirc;u mỏng, gi&uacute;p m&agrave;n h&igrave;nh chiếm tới 81,61% th&acirc;n m&aacute;y. Ngo&agrave;i ra, c&ocirc;ng nghệ m&agrave;u sắc th&ocirc;ng minh từ Acer cũng sẽ mang đến h&igrave;nh ảnh sống động hơn, đặc biệt l&agrave; với những t&aacute;c vụ như giải tr&iacute; hay chơi game.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-16.jpg\" alt=\"m&agrave;n h&igrave;nh Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Khả năng tản nhiệt nhanh ch&oacute;ng</strong></h3>\r\n<p>Acer Aspire 7 A715 sử dụng hệ thống hai quạt tản nhiệt, 3 ống đồng fullsize v&agrave; heatsink size lớn. Nhờ vậy bạn sẽ c&oacute; hiệu quả tản nhiệt mạnh mẽ, tối ưu hiệu năng d&ugrave; chơi game trong thời gian d&agrave;i. Laptop tr&aacute;nh được t&igrave;nh trạng qu&aacute; nhiệt ở c&aacute;c linh kiện ch&iacute;nh v&agrave; đảm bảo hiệu năng ổn định trong suốt qu&aacute; tr&igrave;nh chơi game.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-10.jpg\" alt=\"tản nhiệt Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Đầy đủ những kết nối mới nhất</strong></h3>\r\n<p>Tr&ecirc;n Acer Aspire 7 A715 c&oacute; đầy đủ những kết nối phục vụ c&ocirc;ng việc v&agrave; hoạt động chơi game của bạn. Ch&uacute;ng ta sẽ c&oacute; 2 cổng USB 3.2; 1 cổng USB Type-C; 1 cổng USB 2.0; tai nghe;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/nhung-dieu-can-biet-ve-chuan-ket-noi-hinh-anh-am-thanh-hdmi-122520\">cổng HDMI</a>&nbsp;v&agrave; cổng mạng Ethernet. Những kết nối ti&ecirc;n tiến v&agrave; tốc độ nhất hiện nay gi&uacute;p bạn thỏa sức kết nối với bất kỳ thiết bị ngoại vi n&agrave;o.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-19.jpg\" alt=\"kết nối Acer Aspire 7 A715 42G R4ST\" /></p>\r\n<h3><strong>Mạng Wi-Fi 6 ti&ecirc;n tiến</strong></h3>\r\n<p>Tốc độ mạng kh&ocirc;ng d&acirc;y tr&ecirc;n Acer Aspire 7 A715 được n&acirc;ng tầm nhờ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-wi-fi-6-cong-nghe-mang-wi-fi-tien-tien-sap-duoc-su-dung-rong-rai-79110\">kết nối Wi-Fi 6</a>&nbsp;(802.11ax) băng tần k&eacute;p mới nhất, kh&ocirc;ng chỉ tăng tốc độ kết nối l&ecirc;n 3 lần m&agrave; c&ograve;n cho chất lượng mạng ổn định hơn tới 75% so với chuẩn Wi-Fi 5 (802.11ac) hiện tại. Bạn c&oacute; thể chơi game ở bất cứ đ&acirc;u v&agrave; chơi qua mạng Wi-Fi với chất lượng kết nối ổn định.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-aspire-7-17.jpg\" alt=\"Wi-Fi Acer Aspire 7 A715 42G R4ST\" /></p>', 1, 0, 1, 18, '2021-08-15 23:45:42', '2021-08-15 23:45:42', NULL),
(26, 'Acer Nitro Gaming AN515 57 51G6', 'acer-nitro-gaming-an515-57-51g6', 'public/uploads/product/1\\acer-nitro-gaming-an515-57-den-1.jpg', 25999000, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/acer-nitro-gaming-an515-57-51g6-i5-11400h\">Acer Nitro Gaming AN515 57 51G6</a>&nbsp;</strong><strong>đ&aacute;p ứng ho&agrave;n hảo mọi y&ecirc;u cầu của game thủ trong tầm gi&aacute; rất tốt, từ thiết kế bền bỉ, b&agrave;n ph&iacute;m RGB cực chất cho đến cấu h&igrave;nh l&yacute; tưởng với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/intel-gen-11th-buoc-nhay-ve-hieu-nang-do-hoa-cho-laptop-the-he-moi-127394\">bộ vi xử l&yacute; Intel thế hệ thứ 11</a>&nbsp;v&agrave; card đồ họa RTX 30 series mới nhất.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-20.jpg\" alt=\"Acer Nitro Gaming AN515 57 51G6\" /></strong></p>\r\n<h3><strong>Thiết kế hiện đại, gọn nhẹ cho những game thủ năng động</strong></h3>\r\n<p>Phi&ecirc;n bản mới của Acer Nitro 5 tiếp tục đi theo ng&ocirc;n ngữ thiết kế gaming ấn tượng với m&agrave;u đen chủ đạo v&agrave; những chi tiết m&agrave;u đỏ tạo điểm nhấn. Kiểu d&aacute;ng hầm hố v&agrave; g&oacute;c cạnh cho bạn th&ecirc;m phần cảm hứng khi chơi game. Ốp bản lề m&agrave;u đỏ nổi bật tr&ecirc;n t&ocirc;ng m&agrave;u đen sang trọng c&ugrave;ng 2 đường dập nổi 2 b&ecirc;n tạo cho Acer Nitro 5 AN515 57 sự kh&aacute;c biệt v&agrave; đậm chất game thủ. Phần viền m&agrave;n h&igrave;nh mỏng chỉ 7,02mm gi&uacute;p&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>&nbsp;th&ecirc;m phần nhỏ gọn, dễ d&agrave;ng để bạn chơi game ở bất cứ đ&acirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-15.jpg\" alt=\"thiết kế Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>L&agrave;m hết sức, chơi hết m&igrave;nh</strong></h3>\r\n<p>Acer Nitro 5 AN515 57 51G6 sở hữu bộ vi xử l&yacute; m&agrave; chắc chắn game thủ n&agrave;o cũng rất th&iacute;ch th&uacute;. Con chip&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/intel-core-i5\">Intel Core i5 11400H</a>&nbsp;l&agrave; mẫu CPU mới nhất của thế hệ thứ 11, d&ugrave; l&agrave; i5 nhưng c&oacute; tới 6 nh&acirc;n 12 luồng, tốc độ tối đa 4.50GHz, bộ nhớ đệm 12MB, cho sức mạnh kh&ocirc;ng thua k&eacute;m g&igrave; những&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/intel-core-i7\">CPU Core i7</a>&nbsp;trước đ&acirc;y. Bộ vi xử l&yacute; cực mạnh gi&uacute;p Nitro 5 AN515 57 kh&ocirc;ng chỉ chơi game tốt m&agrave; c&ograve;n c&oacute; thể chạy mượt những ứng dụng c&ocirc;ng việc nặng hay render video nhanh ch&oacute;ng. Bạn c&oacute; thể l&agrave;m mọi việc một c&aacute;ch hiệu quả cao nhất tr&ecirc;n chiếc laptop gaming của m&igrave;nh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-23.jpg\" alt=\"CPU Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>Hiệu suất chơi game đột ph&aacute; với card đồ họa RTX 3050</strong></h3>\r\n<p>Nitro Gaming AN515 57 51G6 mang tr&ecirc;n m&igrave;nh d&ograve;ng card đồ họa mới nhất RTX 30 series. Đ&acirc;y l&agrave; d&ograve;ng GPU cho hiệu suất ho&agrave;n hảo, kể cả hoạt động chơi game lẫn đồ họa s&aacute;ng tạo. GPU NVIDIA GeForce RTX 3050 sử dụng kiến tr&uacute;c Ampere mới nhất, với c&aacute;c l&otilde;i Ray Tracing mới, l&otilde;i Tensor v&agrave; c&aacute;c bộ đa xử l&yacute; ph&aacute;t trực tuyến, mang đến bước nhảy vọt về hiệu năng, h&igrave;nh ảnh ch&acirc;n thực đ&aacute;ng kinh ngạc. Bạn c&oacute; thể chơi tốt hầu hết mọi game ở thiết lập Full HD, từ những game eSports cho đến c&aacute;c game bom tấn AAA.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-19.jpg\" alt=\"GPU Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>Chiến game si&ecirc;u mượt tr&ecirc;n m&agrave;n h&igrave;nh 144Hz</strong></h3>\r\n<p>Kh&ocirc;ng chỉ cấu h&igrave;nh, m&agrave;n h&igrave;nh với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/top-man-hinh-144hz-dang-mua-nhat-trong-nam-2021-nay-132182\">tần số qu&eacute;t l&ecirc;n đến 144Hz</a>&nbsp;cũng l&agrave; một &ldquo;vũ kh&iacute;&rdquo; lợi hại kh&aacute;c của Acer Nitro 5 AN515 57. Với m&agrave;n h&igrave;nh n&agrave;y, bạn sẽ thấy r&otilde; từng chuyển động của kẻ th&ugrave;, kh&ocirc;ng bỏ qua bất cứ khoảnh khắc n&agrave;o trong những tựa game fps diễn biến nhanh. Chất lượng hiển thị cũng hết sức tuyệt vời nhờ tấm nền 15,6 inch Full HD IPS, cho bạn những giờ ph&uacute;t giải tr&iacute; hấp dẫn. Viền m&agrave;n h&igrave;nh giờ đ&acirc;y đ&atilde; được thu hẹp chỉ c&ograve;n 7.02mm, tăng tỉ lệ m&agrave;n h&igrave;nh tr&ecirc;n th&acirc;n m&aacute;y 80%, mang đến h&igrave;nh ảnh sống động như thật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-17.jpg\" alt=\"m&agrave;n h&igrave;nh Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>N&acirc;ng tầm chất lượng &acirc;m thanh</strong></h3>\r\n<p>Cho những trận chiến th&ecirc;m phần hấp dẫn nhờ hệ thống &acirc;m thanh chuẩn gaming. Phần mềm &acirc;m thanh DTS: X Ultra t&aacute;i tạo &acirc;m thanh 3D v&agrave; 6 chế độ t&ugrave;y chỉnh &acirc;m thanh trong phần mềm Acer True Harmony sẽ gi&uacute;p cho &acirc;m thanh sống động, diễn biến ch&iacute;nh x&aacute;c t&ugrave;y thuộc v&agrave;o thể loại game bạn chơi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-6.jpg\" alt=\"&acirc;m thanh Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>Tăng cường hiệu quả tản nhiệt</strong></h3>\r\n<p>Acer Nitro 5 AN515 57 sử dụng hệ thống tản nhiệt mới với 2 quạt v&agrave; c&ocirc;ng nghệ Acer CoolBoost gi&uacute;p tăng 10% tốc độ quạt, cho hiệu quả cao hơn v&agrave; giảm nhiệt độ CPU lẫn GPU xuống 9%. Khả năng lưu th&ocirc;ng kh&iacute; cũng tốt hơn 25% nhờ 4 khe h&uacute;t gi&oacute; tho&aacute;t nhiệt th&ocirc;ng minh. Khả năng tản nhiệt tốt gi&uacute;p bạn c&oacute; thể y&ecirc;n t&acirc;m chơi game, l&agrave;m việc ổn định trong thời gian d&agrave;i. Những th&ocirc;ng số về nhiệt độ v&agrave; t&iacute;nh năng điều chỉnh tốc độ quạt c&oacute; thể thực hiện dễ d&agrave;ng qua phần mềm Nitro Sense.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-22.jpg\" alt=\"tản nhiệt Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>T&ugrave;y chỉnh hiệu năng theo c&aacute;ch của bạn</strong></h3>\r\n<p>Bạn sẽ ho&agrave;n to&agrave;n l&agrave;m chủ những điều m&igrave;nh muốn tr&ecirc;n Acer Nitro 5 AN515. Tất cả đều sẵn s&agrave;ng sau ph&iacute;m bấm duy nhất k&iacute;ch hoạt Nitro Sense, đ&acirc;y l&agrave; phần mềm độc quyền c&oacute; thể điều chỉnh chế độ quạt, hiệu chỉnh m&agrave;u b&agrave;n ph&iacute;m RGB v&agrave; những tinh chỉnh hữu &iacute;ch kh&aacute;c.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-7.jpg\" alt=\"hiệu năng Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>Chơi game online đường truyền ổn định</strong></h3>\r\n<p>Kết nối mạng chưa bao giờ nhanh đến thế với Nitro 5. Chuẩn Wi-Fi 6 mới nhất v&agrave; kết nối mạng d&acirc;y Killer Ethernet E2600 gi&uacute;p đường truyền Internet lu&ocirc;n ổn định, để bạn y&ecirc;n t&acirc;m tập trung v&agrave;o cuộc chiến ở những tựa game online. Laptop cũng c&oacute; đầy đủ những cổng kết nối kh&aacute;c như 3 cổng USB 3.2 Type-A, 1 cổng USB Type-C, 1 cổng HDMI v&agrave; cổng tai nghe 3.5mm.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-fpt-16.jpg\" alt=\"mạng LAN Acer Nitro Gaming AN515 57 51G6\" /></p>\r\n<h3><strong>Th&ecirc;m niềm cảm hứng với b&agrave;n ph&iacute;m RGB tuyệt đẹp</strong></h3>\r\n<p>Chơi game th&ecirc;m phần hiệu quả v&agrave; phấn kh&iacute;ch nhờ hệ thống đ&egrave;n LED RGB 4 v&ugrave;ng tr&ecirc;n Acer Nitro 5. B&agrave;n ph&iacute;m tuyệt đẹp, phản hồi nhạy b&eacute;n, tối ưu cho game thủ v&agrave; c&aacute;c thiết lập m&agrave;u sắc, hiệu ứng hấp dẫn gi&uacute;p cho những tựa game của bạn th&ecirc;m phần th&uacute; vị. Đồng thời ngay cả khi giải tr&iacute; bằng c&aacute;c t&aacute;c vụ như nghe nhạc hay xem phim, b&agrave;n ph&iacute;m RGB cũng tạo th&ecirc;m nhịp điệu cho ri&ecirc;ng bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/acer-nitro-5-2021-11.jpg\" alt=\"b&agrave;n ph&iacute;m Acer Nitro Gaming AN515 57 51G6\" /></p>', 1, 1, 1, 18, '2021-08-15 23:54:39', '2021-08-17 01:34:13', NULL),
(27, 'Xiaomi POCO X3 Pro NFC', 'xiaomi-poco-x3-pro-nfc', 'public/uploads/product/1\\poco-x3-pro-den-1.jpg', 7300000, '<p><strong><a href=\"https://fptshop.com.vn/dien-thoai/xiaomi-poco-x3-pro\">Xiaomi POCO X3 Pro NFC</a>&nbsp;l&agrave; tập hợp của mọi t&iacute;nh năng m&agrave; một game thủ cần tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>. M&agrave;n h&igrave;nh si&ecirc;u mượt, dung lượng pin khủng, sạc si&ecirc;u nhanh, &acirc;m thanh chất lượng ph&ograve;ng thu v&agrave; một cấu h&igrave;nh đ&aacute;ng kinh ngạc, c&oacute; qu&aacute; nhiều điều đang chờ đ&oacute;n bạn.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-1.jpg\" alt=\"Xiaomi POCO X3 Pro NFC\" /></strong></p>\r\n<h3><strong>Sức mạnh dẫn đầu</strong></h3>\r\n<p>Trong mức gi&aacute; tầm trung nhưng cấu h&igrave;nh m&agrave; Xiaomi POCO X3 Pro NFC trang bị lại l&agrave; bộ vi xử l&yacute; d&agrave;nh cho những smartphone cao cấp của năm 2021. Bộ vi xử l&yacute; Qualcomm Snapdragon 860 mạnh mẽ h&agrave;ng đầu hiện nay mang đến tốc độ tuyệt hảo, kể cả trong c&aacute;c tựa game đồ họa cao. Thậm ch&iacute; POCO X3 Pro c&ograve;n t&iacute;ch hợp c&ocirc;ng nghệ tản nhiệt chất lỏng, đảm bảo m&aacute;y m&aacute;t v&agrave; hiệu năng ổn định trong suốt qu&aacute; tr&igrave;nh chơi game. H&atilde;y tự tin v&agrave;o kỹ năng của bạn, Xiaomi POCO X3 Pro NFC sẽ gi&uacute;p bạn l&agrave;m chủ t&igrave;nh h&igrave;nh trong mọi cuộc chơi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-2.jpg\" alt=\"cấu h&igrave;nh Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Thiết kế d&agrave;nh cho tốc độ</strong></h3>\r\n<p>Tốc độ của Xiaomi POCO X3 Pro NFC kh&ocirc;ng chỉ dừng lại ở cấu h&igrave;nh, m&agrave;n h&igrave;nh với tần số qu&eacute;t 120Hz cho trải nghiệm mượt m&agrave; gấp đ&ocirc;i so với m&agrave;n h&igrave;nh 60Hz tr&ecirc;n c&aacute;c smartphone th&ocirc;ng thường. Tần số lấy mẫu cảm ứng l&ecirc;n tới 240Hz gi&uacute;p bạn v&ocirc; c&ugrave;ng c&oacute; lợi khi chơi game, nơi mọi thao t&aacute;c đều được phản hồi ngay lập tức, mang đến lợi thế lớn trước c&aacute;c đối thủ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-3.jpg\" alt=\"m&agrave;n h&igrave;nh Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Đậm phong c&aacute;ch game thủ</strong></h3>\r\n<p>Xiaomi POCO X3 Pro c&oacute; kiểu d&aacute;ng bắt mắt với mặt lưng chia l&agrave;m 3 phần r&otilde; rệt, đẹp b&oacute;ng bẩy v&agrave; c&aacute; t&iacute;nh. Chất liệu cải tiến chống b&aacute;m v&acirc;n tay gi&uacute;p điện thoại của bạn lu&ocirc;n đẹp như mới. POCO X3 Pro cũng hết sức bền vững khi được chế t&aacute;c từ k&iacute;nh cường lực Corning Gorilla Glass 6, chống xước v&agrave; chống va đập hiệu quả h&agrave;ng đầu hiện nay.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-4.jpg\" alt=\"game thủ Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Dung lượng pin si&ecirc;u khủng, chơi game kh&ocirc;ng giới hạn</strong></h3>\r\n<p>Vi&ecirc;n pin dung lượng l&ecirc;n tới 5160mAh l&agrave; &ldquo;vũ kh&iacute; b&iacute; mật&rdquo; tiếp theo m&agrave; POCO X3 Pro NFC d&agrave;nh cho c&aacute;c game thủ. Với dung lượng cực &ldquo;khủng&rdquo;, Xiaomi POCO X3 Pro c&oacute; thể chơi game li&ecirc;n tục trong 11 giờ, ph&aacute;t video li&ecirc;n tục trong 18 giờ hoặc ph&aacute;t nhạc trong 110 giờ. Kể cả khi hết pin, bạn cũng kh&ocirc;ng phải chờ đợi l&acirc;u khi t&iacute;nh năng sạc nhanh 33W gi&uacute;p điện thoại chỉ mất 30 ph&uacute;t để sạc được 59%. Đừng để hết pin hay sạc chậm l&agrave;m gi&aacute;n đoạn niềm vui chơi game của bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-5.jpg\" alt=\"pin Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Phản hồi rung th&ocirc;ng minh</strong></h3>\r\n<p>Với c&aacute;c game thủ, những phản hồi rung ch&acirc;n thực sẽ gi&uacute;p n&acirc;ng tầm trải nghiệm game. Tr&ecirc;n Xiaomi POCO X3 Pro NFC c&oacute; động cơ tuyến t&iacute;nh trục Z phản hồi sống động tương t&aacute;c đầu ng&oacute;n tay v&agrave; m&agrave;n h&igrave;nh. Giờ đ&acirc;y bạn sẽ c&oacute; trải nghiệm chơi game tr&ecirc;n điện thoại ho&agrave;n to&agrave;n mới, ch&acirc;n thực v&agrave; đầy cảm x&uacute;c tương tự như chơi tr&ecirc;n Console.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-6.jpg\" alt=\"rung Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Loa k&eacute;p mạnh mẽ, ph&aacute;t &acirc;m thanh chuẩn ph&ograve;ng thu</strong></h3>\r\n<p>Hệ thống loa k&eacute;p &acirc;m thanh chất lượng cao Hi-Res Audio của chiếc điện thoại&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi\">Xiaomi</a>&nbsp;n&agrave;y&nbsp;gi&uacute;p bạn đắm ch&igrave;m trong thế giới game như thể bạn đang ở b&ecirc;n trong m&agrave;n h&igrave;nh. Từng tiếng động, chi tiết nhỏ trong tựa game đều được ph&aacute;t ra sắc n&eacute;t, trung thực, mang đến th&ecirc;m nhiều cảm hứng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-7.jpg\" alt=\"loa k&eacute;p Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Niềm vui chụp ảnh nh&acirc;n 4 với 4 camera AI</strong></h3>\r\n<p>Trang bị tới 4 camera sau, Xiaomi POCO X3 Pro NFC cho bạn ghi lại cả thế giới theo c&aacute;ch sống động nhất. Camera ch&iacute;nh 48MP chụp đ&ecirc;m tuyệt đỉnh; camera g&oacute;c si&ecirc;u rộng 8MP lưu giữ lại khung h&igrave;nh rộng gấp đ&ocirc;i; camera đo độ s&acirc;u 2MP hỗ trợ chụp ảnh x&oacute;a ph&ocirc;ng ch&iacute;nh x&aacute;c v&agrave; camera macro 2MP cho g&oacute;c nh&igrave;n si&ecirc;u cận ho&agrave;n to&agrave;n mới. Sự kết hợp giữa 4 camera gi&uacute;p bạn c&oacute; được khả năng chụp ảnh to&agrave;n diện nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-8.jpg\" alt=\"chụp ảnh Xiaomi POCO X3 Pro NFC\" /></p>\r\n<h3><strong>Chống nước IP53</strong></h3>\r\n<p>Bạn c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m với những mối lo về chất lỏng tr&ecirc;n Xiaomi POCO X3 Pro NFC nhờ khả năng chống nước IP53. V&ocirc; t&igrave;nh đổ caf&eacute; hay sử dụng điện thoại dưới trời mưa cũng kh&ocirc;ng l&agrave;m kh&oacute; được chiếc điện thoại bền bỉ của bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/poco-x3-pro-9.jpg\" alt=\"chống nước Xiaomi POCO X3 Pro NFC\" /></p>', 1, 0, 1, 13, '2021-08-16 06:54:41', '2021-08-16 06:54:41', NULL),
(28, 'Xiaomi Redmi Note 10', 'xiaomi-redmi-note-10', 'public/uploads/product/1\\xiaomi-redmi-note-10-xanh.jpg', 5240000, '<p><strong>Nh&agrave; v&ocirc; địch mới của&nbsp;<a title=\"điện thoại th&ocirc;ng minh\" href=\"https://fptshop.com.vn/dien-thoai\" type=\"điện thoại th&ocirc;ng minh\">smartphone</a>&nbsp;gi&aacute; rẻ đ&atilde; xuất hiện,&nbsp;<a title=\"Redmi Note 10\" href=\"https://fptshop.com.vn/dien-thoai/xiaomi-redmi-note-10\" type=\"Redmi Note 10\">Xiaomi Redmi Note 10</a>&nbsp;ch&iacute;nh l&agrave; sự lựa chọn ho&agrave;n hảo với sức mạnh v&agrave; tốc độ cực đỉnh, 4 camera 48MP, m&agrave;n h&igrave;nh AMOLED c&ugrave;ng thời lượng pin bền bỉ đ&aacute;ng kinh ngạc.</strong></p>\r\n<p><strong><img class=\"Đ&aacute;nh gi&aacute; chi tiết Xiaomi Redmi Note 10\" title=\"Đ&aacute;nh gi&aacute; chi tiết Xiaomi Redmi Note 10\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10.jpg\" alt=\"Đ&aacute;nh gi&aacute; chi tiết Xiaomi Redmi Note 10\" /></strong></p>\r\n<h3><strong>Trải nghiệm kh&aacute;c biệt tr&ecirc;n m&agrave;n h&igrave;nh Super AMOLED</strong></h3>\r\n<p>Trong khi hầu hết smartphone gi&aacute; rẻ kh&aacute;c đều sử dụng m&agrave;n h&igrave;nh LCD th&igrave; Redmi Note 10 lại trang bị m&agrave;n h&igrave;nh tấm nền Super AMOLED cao cấp. Với m&agrave;n h&igrave;nh lớn 6,43 inch độ ph&acirc;n giải Full HD+ c&ocirc;ng nghệ Super AMOLED, Redmi Note 10 cho khả năng hiển thị xuất sắc với m&agrave;u sắc sống động, h&igrave;nh ảnh mượt m&agrave;, độ tương phản cao. Thiết kế camera trước nằm gọn g&agrave;ng trong tấm k&iacute;nh m&agrave;n h&igrave;nh mang đến trải nghiệm m&atilde;n nh&atilde;n v&agrave; tập trung hơn.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | M&agrave;n h&igrave;nh Super AMOLED\" title=\"Xiaomi Redmi Note 10 | M&agrave;n h&igrave;nh Super AMOLED\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-1.jpg\" alt=\"Xiaomi Redmi Note 10 | M&agrave;n h&igrave;nh Super AMOLED\" /></p>\r\n<h3><strong>Bộ tứ camera 48MP, đầy đủ t&iacute;nh năng chụp h&igrave;nh d&agrave;nh cho bạn</strong></h3>\r\n<p>Với 4 camera sau, bạn sẽ được tha hồ thể hiện khả năng nhiếp ảnh với đầy đủ t&iacute;nh năng tr&ecirc;n Redmi Note 10. Camera ch&iacute;nh c&oacute; độ ph&acirc;n giải cao 48MP, hỗ trợ t&iacute;nh năng chụp đ&ecirc;m Night Mode, dễ d&agrave;ng chụp được những bức ảnh sắc n&eacute;t cả ng&agrave;y lẫn đ&ecirc;m. Ngo&agrave;i ra ch&uacute;ng ta c&ograve;n c&oacute; camera 8MP g&oacute;c si&ecirc;u rộng, camera 2MP hỗ trợ chụp ảnh x&oacute;a ph&ocirc;ng v&agrave; camera 2MP chụp macro si&ecirc;u cận cảnh. Trong mọi trường hợp, Redmi Note 10 đều mang tới những bức ảnh ấn tượng v&agrave; đầy s&aacute;ng tạo.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Bộ tứ camera 48MP\" title=\"Xiaomi Redmi Note 10 | Bộ tứ camera 48MP\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-2.jpg\" alt=\"Xiaomi Redmi Note 10 | Bộ tứ camera 48MP\" /></p>\r\n<h3><strong>Camera trước 13MP trong m&agrave;n h&igrave;nh</strong></h3>\r\n<p>Để người d&ugrave;ng c&oacute; được trải nghiệm xem tốt nhất, camera selfie của Redmi Note 10 được đặt ở vị tr&iacute; tinh tế nằm ngay trong tấm k&iacute;nh m&agrave;n h&igrave;nh, đồng thời rất nhỏ gọn với k&iacute;ch thước chỉ 3,54mm. Tuy nhi&ecirc;n chất lượng của camera n&agrave;y vẫn được đảm bảo với độ ph&acirc;n giải cao 13MP, hỗ trợ l&agrave;m đẹp khu&ocirc;n mặt bằng AI, HDR tự động, cho bạn những bức ảnh &ldquo;tự sướng&rdquo; tuyệt đẹp.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Camera trước 13MP trong m&agrave;n h&igrave;nh\" title=\"Xiaomi Redmi Note 10 | Camera trước 13MP trong m&agrave;n h&igrave;nh\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-3.jpg\" alt=\"Xiaomi Redmi Note 10 | Camera trước 13MP trong m&agrave;n h&igrave;nh\" /></p>\r\n<h3><strong>Thiết kế đến từ đam m&ecirc;</strong></h3>\r\n<p>Sự t&acirc;m huyết của đội ngũ thiết kế&nbsp;<a title=\"smartphone xiaomi\" href=\"https://fptshop.com.vn/dien-thoai/xiaomi\" type=\"smartphone xiaomi\">Xiaomi</a>&nbsp;đ&atilde; tạo ra chiếc điện thoại c&oacute; kiểu d&aacute;ng cao cấp, vừa vặn, hiện đại với c&aacute;ch bố tr&iacute; hợp l&yacute;, tinh tế ở mọi đường n&eacute;t. Redmi Note 10 ho&agrave;n to&agrave;n kh&aacute;c biệt khi đặt cạnh c&aacute;c đối thủ c&ugrave;ng tầm gi&aacute;. Từ m&agrave;n h&igrave;nh tr&agrave;n viền, cụm camera ph&aacute; c&aacute;ch cho đến những đường cong ho&agrave;n hảo v&agrave; lớp sơn mềm mại ở mặt lưng, Redmi Note 10 thực sự đẹp cuốn h&uacute;t, ph&ugrave; hợp với mọi người d&ugrave;ng.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Thiết kế cao cấp\" title=\"Xiaomi Redmi Note 10 | Thiết kế cao cấp\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-4.jpg\" alt=\"Xiaomi Redmi Note 10 | Thiết kế cao cấp\" /></p>\r\n<h3><strong>Sức mạnh dẫn đầu cuộc chơi</strong></h3>\r\n<p>Một trong những l&yacute; do lớn để người d&ugrave;ng lựa chọn điện thoại Xiaomi ch&iacute;nh l&agrave; cấu h&igrave;nh. Redmi Note 10 tiếp tục l&agrave; chiếc điện thoại c&oacute; cấu h&igrave;nh mạnh nhất ph&acirc;n kh&uacute;c nhờ bộ vi xử l&yacute; Snapdragon 678. Đ&acirc;y l&agrave; con chip mới, sản xuất tr&ecirc;n tiến tr&igrave;nh 11nm, bao gồm 8 nh&acirc;n Kryo 460 cực mạnh, GPU đồ họa Adreno 612 v&agrave; chip xử l&yacute; h&igrave;nh ảnh Spectra 250L. Phi&ecirc;n bản trong b&agrave;i c&oacute; 6GB RAM v&agrave; 128GB bộ nhớ trong, kết hợp th&ecirc;m bộ vi xử l&yacute; mạnh mẽ sẽ cho bạn hiệu năng v&ocirc; c&ugrave;ng mượt m&agrave; ở mọi t&aacute;c vụ, d&ugrave; l&agrave; những tựa game nặng hiện nay.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Cấu h&igrave;nh mạnh mẽ\" title=\"Xiaomi Redmi Note 10 | Cấu h&igrave;nh mạnh mẽ\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-5.jpg\" alt=\"Xiaomi Redmi Note 10 | Cấu h&igrave;nh mạnh mẽ\" /></p>\r\n<h3><strong>Dung lượng pin khủng, sạc si&ecirc;u nhanh</strong></h3>\r\n<p>D&ugrave; l&agrave; một chiếc điện thoại tương đối mỏng nhẹ, nhưng Redmi Note 10 lại mang tr&ecirc;n m&igrave;nh vi&ecirc;n pin dung lượng l&ecirc;n tới 5000 mAh, cho ph&eacute;p bạn sử dụng với cường độ cao trọn vẹn 1 ng&agrave;y. Hơn nữa, chiếc điện thoại n&agrave;y c&ograve;n hỗ trợ sạc nhanh c&ocirc;ng suất 33W, chỉ mất 30 ph&uacute;t để sạc đầy 67% pin.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Dung lượng pin lớn\" title=\"Xiaomi Redmi Note 10 | Dung lượng pin lớn\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-6.jpg\" alt=\"Xiaomi Redmi Note 10 | Dung lượng pin lớn\" /></p>\r\n<h3><strong>Tận hưởng loa k&eacute;p sống động</strong></h3>\r\n<p>Redmi Note 10 l&agrave; chiếc điện thoại hiếm hoi đầu tư cho chất lượng &acirc;m thanh ở loa ngo&agrave;i. Cụm loa k&eacute;p Stereo v&agrave; &acirc;m thanh Hi-Res cao cấp cho bạn trải nghiệm &acirc;m thanh lớn, chi tiết đến từng sắc &acirc;m. Những giờ ph&uacute;t giải tr&iacute; với c&aacute;c hoạt động như xem phim, chơi game sẽ trở n&ecirc;n sống động v&agrave; hấp dẫn hơn rất nhiều.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Tận hưởng loa k&eacute;p sống động\" title=\"Xiaomi Redmi Note 10 | Tận hưởng loa k&eacute;p sống động\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-7.jpg\" alt=\"Xiaomi Redmi Note 10 | Tận hưởng loa k&eacute;p sống động\" /></p>\r\n<h3><strong>Cảm biến v&acirc;n tay th&ocirc;ng minh</strong></h3>\r\n<p>Vị tr&iacute; đặt cảm biến v&acirc;n tay của Redmi Note 10 hết sức th&ocirc;ng minh khi nằm ở cạnh b&ecirc;n của m&aacute;y, đảm bảo sự thanh tho&aacute;t trong thiết kế. Tất nhi&ecirc;n độ nhạy cảm biến n&agrave;y l&agrave; kh&ocirc;ng phải b&agrave;n c&atilde;i khi chỉ cần một c&uacute; chạm nhẹ, điện thoại sẽ mở kh&oacute;a ngay lập tức.</p>\r\n<p><img class=\"Xiaomi Redmi Note 10 | Cảm biến v&acirc;n tay th&ocirc;ng minh\" title=\"Xiaomi Redmi Note 10 | Cảm biến v&acirc;n tay th&ocirc;ng minh\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-redmi-note-10-8.jpg\" alt=\"Xiaomi Redmi Note 10 | Cảm biến v&acirc;n tay th&ocirc;ng minh\" /></p>', 1, 0, 1, 13, '2021-08-16 06:56:02', '2021-08-16 06:56:02', NULL);
INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(29, 'iPhone 12 Pro Max', 'iphone-12-pro-max', 'public/uploads/product/1\\ip-12-pro-max-bac-1.jpg', 30999000, '<p><strong>&ldquo;Tr&ugrave;m cuối&rdquo; của d&ograve;ng iPhone 12 đ&atilde; xuất hiện.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro-max\">iPhone 12 Pro Max</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh lớn nhất từ trước đến nay, mang tr&ecirc;n m&igrave;nh bộ vi xử l&yacute; mạnh nhất, camera&nbsp;</strong><strong>đẳng cấp pro c&ugrave;ng kết nối 5G si&ecirc;u tốc, cho bạn những trải nghiệm tuyệt vời chưa từng c&oacute;.</strong></p>\r\n<p><strong><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-3.JPG\" alt=\"iPhone 12 Pro Max\" /></strong></p>\r\n<h3><strong>Chiếc iPhone m&agrave;n h&igrave;nh lớn nhất</strong></h3>\r\n<p>iPhone 12 Pro Max sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước kh&oacute; tin, l&ecirc;n tới 6,7 inch, cho bạn trải nghiệm h&igrave;nh ảnh cực đ&atilde;. Chất lượng đến từ một m&agrave;n h&igrave;nh c&ocirc;ng nghệ Super Retina XDR, tấm nền OLED cao cấp v&agrave; độ ph&acirc;n giải si&ecirc;u cao thực sự qu&aacute; tuyệt vời.</p>\r\n<p>Mọi thứ đều trở n&ecirc;n sống động tr&ecirc;n&nbsp;<strong>iPhone 12 Pro Max</strong>, đặc biệt khi bạn xem ảnh, xem phim hay chơi game. C&ograve;n bất ngờ hơn khi d&ugrave; m&agrave;n h&igrave;nh lớn hơn, nhưng iPhone 12 Pro Max vẫn gọn g&agrave;ng như thế hệ trước nhờ viền m&agrave;n h&igrave;nh được tối ưu si&ecirc;u mỏng đầy tinh tế.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-2.JPG\" alt=\"m&agrave;n h&igrave;nh iPhone 12 Pro Max\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh si&ecirc;u bền, giảm nguy cơ trầy xước rơi vỡ</strong></h3>\r\n<p>Kh&ocirc;ng chỉ c&oacute; một m&agrave;n h&igrave;nh đẹp xuất sắc, m&agrave;n h&igrave;nh iPhone 12 Pro Max&nbsp;c&ograve;n được bảo vệ bởi c&ocirc;ng nghệ phủ gốm Ceramic Shield. C&ocirc;ng nghệ n&agrave;y sử dụng c&aacute;c tinh thể gốm nano cứng hơn cả kim loại để tấm k&iacute;nh c&oacute; khả năng chống xước cao hơn v&agrave; bền hơn tới 4 lần nếu chẳng may điện thoại bị rơi. Đ&acirc;y cũng l&agrave; lớp k&iacute;nh m&agrave;n h&igrave;nh cứng nhất thế giới smartphone hiện nay, cho bạn cảm gi&aacute;c y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-1.JPG\" alt=\"thiết kế iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Chế t&aacute;c từ th&eacute;p kh&ocirc;ng gỉ cao cấp</strong></h3>\r\n<p>Được l&agrave;m từ những vật liệu cao cấp h&agrave;ng đầu v&agrave; khả năng ho&agrave;n thiện tinh xảo, iPhone 12 Pro Max khiến tất cả đều phải ngước nh&igrave;n. Ấn tượng nhất chắc chắn l&agrave; phần khung viền l&agrave;m từ th&eacute;p kh&ocirc;ng gỉ, được l&agrave;m phẳng hiện đại v&agrave; c&aacute;c đường cắt kim cương s&aacute;ng b&oacute;ng sang trọng.</p>\r\n<p>Phần viền m&agrave;n h&igrave;nh v&agrave; th&acirc;n m&aacute;y đều được l&agrave;m si&ecirc;u mỏng, tạo n&ecirc;n sự thanh lịch. Ngo&agrave;i ra bạn c&ograve;n c&oacute; tới 4 m&agrave;u sắc để lựa chọn l&agrave; Bạc, X&aacute;m than ch&igrave;, V&agrave;ng v&agrave; Xanh đại dương, tất cả đều rất đẳng cấp. B&ecirc;n cạnh đ&oacute;,&nbsp;iPhone 12 Pro Max sẽ c&oacute; 3 phi&ecirc;n bản dung lượng với c&aacute;c t&ugrave;y chọn m&agrave;u sắc kể tr&ecirc;n&nbsp;cho người d&ugrave;ng lựa chọn gồm:&nbsp;<strong>iPhone 12 Pro Max&nbsp;128GB</strong>,&nbsp;<strong>iPhone 12 Pro Max&nbsp;256GB</strong>&nbsp;v&agrave;&nbsp;<strong>iPhone 12 Pro Max&nbsp;512GB</strong>.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-4.JPG\" alt=\"th&eacute;p kh&ocirc;ng gỉ iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Khả năng chống nước ho&agrave;n hảo</strong></h3>\r\n<p>iPhone 12 Pro Max c&oacute; thể ng&acirc;m nước ở độ s&acirc;u tối đa l&ecirc;n tới 6m trong khoảng thời gian 30 ph&uacute;t. Trang bị chuẩn chống nước IP68 tốt nhất thế giới smartphone hiện nay, iPhone 12 Pro Max ho&agrave;n to&agrave;n kh&ocirc;ng bị nguy hại khi đối mặt với những nguy cơ từ nước trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-8.JPG\" alt=\"chống nước iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Tốc độ nhanh kh&ocirc;ng tưởng với mạng 5G</strong></h3>\r\n<p>iPhone 12 Pro Max trang bị băng tần 5G si&ecirc;u nhanh, gi&uacute;p trải nghiệm Internet của bạn l&ecirc;n một tầm cao mới. Bất cứ điều g&igrave; bạn l&agrave;m tr&ecirc;n mạng Internet đều diễn ra nhanh ch&oacute;ng. Xem phim trực tuyến chất lượng cao, livestream, chơi game online, tải t&agrave;i liệu, tất cả đều được xử l&yacute; trong chớp mắt với kết nối 5G.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-6.JPG\" alt=\"mạng 5G iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Chiếc điện thoại mạnh mẽ nhất thế giới</strong></h3>\r\n<p><a title=\"Apple A14 Bionic\" href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-chip-apple-a14-trai-tim-se-lam-nen-suc-manh-cho-iphone-12-121314\" type=\"Apple A14 Bionic\">Apple A14 Bionic</a>, bộ vi xử l&yacute; điện thoại mạnh nhất thế giới đ&atilde; c&oacute; mặt tr&ecirc;n iPhone 12 Pro Max. Đ&acirc;y l&agrave; con chip đầu ti&ecirc;n được sản xuất tr&ecirc;n tiến tr&igrave;nh 5nm, c&oacute; số lượng b&oacute;ng b&aacute;n dẫn nhiều hơn tới 40% so với thế hệ trước, kh&ocirc;ng chỉ mang đến hiệu năng cực đỉnh m&agrave; c&ograve;n c&oacute; thời lượng pin tuyệt vời.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, Apple A14 Bionic c&ograve;n t&iacute;ch hợp chip xử l&yacute; h&igrave;nh ảnh ISP mới, n&acirc;ng cao khả năng chụp ảnh v&agrave; quay video. Số nh&acirc;n Neural Engine cũng được tăng gấp đ&ocirc;i, từ 8 l&ecirc;n 16 để việc vận dụng AI c&oacute; hiệu suất cao hơn bao giờ hết.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-7.JPG\" alt=\"cấu h&igrave;nh iPhone 12 Pro Max\" /></p>\r\n<h3><strong>M&aacute;y qu&eacute;t LiDAR chuy&ecirc;n nghiệp</strong></h3>\r\n<p>NASA đang sử dụng c&ocirc;ng nghệ LiDAR cho lần tiếp theo đặt ch&acirc;n l&ecirc;n sao Hỏa. Bạn c&oacute; thể trải nghiệm c&ocirc;ng nghệ ti&ecirc;n tiến n&agrave;y qua m&aacute;y qu&eacute;t LiDAR ngay tr&ecirc;n ch&iacute;nh iPhone 12 Pro Max.</p>\r\n<p>LiDAR c&oacute; khả năng dựng lại vật thể bằng c&aacute;ch đo thời gian &aacute;nh s&aacute;ng phản xạ đến vật thể, với độ ch&iacute;nh x&aacute;c tới từng nano gi&acirc;y. LiDAR sẽ qu&eacute;t c&aacute;c bề mặt trong kh&ocirc;ng gian để ph&acirc;n t&iacute;ch v&agrave; tạo cảnh dựa tr&ecirc;n ứng dụng AR. Đ&acirc;y l&agrave; ng&agrave;nh c&ocirc;ng nghệ c&oacute; t&iacute;nh ứng dụng rất cao trong tương lai gần.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-6.JPG\" alt=\"LiDAR iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Zoom quang học 2,5x, chụp sắc n&eacute;t ở cự ly xa</strong></h3>\r\n<p>Với ống k&iacute;nh Tele mới ti&ecirc;u cự 65mm, iPhone 12 Pro Max c&oacute; khả năng zoom quang học 2,5x, ph&oacute;ng to h&igrave;nh ảnh tới 2,5 lần m&agrave; chất lượng kh&ocirc;ng hề thay đổi. Nghĩa l&agrave; bạn c&oacute; thể chụp những vật thể ở khoảng c&aacute;ch xa với khả năng thu ph&oacute;ng v&agrave; độ n&eacute;t đ&aacute;ng kinh ngạc.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-14(1).JPG\" alt=\"zoom quang iPhone 12 Pro Max\" /></p>\r\n<h3><strong>T&iacute;nh năng chống rung quang học mới</strong></h3>\r\n<p>Camera ch&iacute;nh tr&ecirc;n&nbsp;<em>iPhone 12 Pro Max</em>&nbsp;l&agrave; camera smartphone đầu ti&ecirc;n c&oacute; t&iacute;nh năng chống rung quang học thay đổi cảm biến (sensor-shift OIS). T&iacute;nh năng n&agrave;y mang tới hiệu quả chống rung gấp 5 lần so với OIS th&ocirc;ng thường.</p>\r\n<p>Kết hợp th&ecirc;m khẩu độ lớn f/1.6 v&agrave; k&iacute;ch thước điểm ảnh l&ecirc;n tới 1,7um, camera iPhone 12 Pro Max đ&iacute;ch thực l&agrave; một ngọn hải đăng trong đ&ecirc;m. C&aacute;c bức ảnh chụp đ&ecirc;m sẽ s&aacute;ng r&otilde;, sắc n&eacute;t v&agrave; m&agrave;u sắc v&ocirc; c&ugrave;ng ch&iacute;nh x&aacute;c. Tha hồ để bạn tự do s&aacute;ng tạo, ghi lại những khoảnh khắc đ&aacute;ng nhớ trong đ&ecirc;m tối.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-19.JPG\" alt=\"OIS iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Dựng phim chuy&ecirc;n nghiệp, quay video 4K HDR Dolby Vision</strong></h3>\r\n<p>Đến với thế giới m&agrave;u sắc bằng những đoạn phim 4K HDR Dolby Vision ch&acirc;n thực một c&aacute;ch đ&aacute;ng kinh ngạc tr&ecirc;n iPhone 12 Pro Max. Những video giờ đ&acirc;y sẽ c&oacute; m&agrave;u sắc sống động chưa từng thấy với 700 triệu m&agrave;u, gấp 60 lần so với video ti&ecirc;u chuẩn.</p>\r\n<p>Bạn sẽ thấy r&otilde; sự kh&aacute;c biệt khi thưởng thức video Dolby Vision tr&ecirc;n TV 4K HDR. iPhone 12 Pro Max l&agrave; chiếc điện thoại đầu ti&ecirc;n c&oacute; thể quay video Dolby Vision, thậm ch&iacute; bạn c&ograve;n c&oacute; những c&ocirc;ng cụ để chỉnh sửa, bi&ecirc;n tập video trước khi xuất bản. Tất cả c&aacute;c c&ocirc;ng việc của một studio chuy&ecirc;n nghiệp g&oacute;i gọn trong chiếc điện thoại đầy quyền lực tr&ecirc;n tay.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-15.JPG\" alt=\"4K HDR iPhone 12 Pro Max\" /></p>\r\n<h3><strong>Chụp ảnh v&agrave; quay video thiếu s&aacute;ng ho&agrave;n hảo</strong></h3>\r\n<p>Nếu như ở những chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;kh&aacute;c, thiếu s&aacute;ng lu&ocirc;n l&agrave; một điều kiện kh&oacute; khăn cho camera, th&igrave; với iPhone 12 Pro Max, m&agrave;n đ&ecirc;m chỉ khiến cho những bức ảnh v&agrave; video của bạn lung linh hơn. Bạn c&oacute; thể chụp ảnh r&otilde; n&eacute;t, chụp ch&acirc;n dung đ&ecirc;m v&agrave; quay video với độ s&aacute;ng tốt hơn tới 87%.</p>\r\n<p>Ngay cả những v&ugrave;ng tối cũng c&oacute; độ chi tiết rất cao tr&ecirc;n&nbsp;<strong>iPhone 12 Pro Max</strong>. Cảm biến camera h&agrave;ng đầu, chip xử l&yacute; h&igrave;nh ảnh ISP mới tr&ecirc;n Apple A14 Bionic, m&aacute;y qu&eacute;t LiDAR tạo n&ecirc;n sự kết hợp ho&agrave;n hảo khi chụp ảnh, quay video v&agrave;o ban đ&ecirc;m.</p>\r\n<p><img title=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-13.JPG\" alt=\"chụp thiếu s&aacute;ng iPhone 12 Pro Max\" /></p>', 1, 0, 1, 12, '2021-08-16 07:07:12', '2021-08-16 07:07:12', NULL),
(30, 'iPhone 11 Pro', 'iphone-11-pro', 'public/uploads/product/1\\11-pro-den.jpg', 29000000, '<p><strong>Phi&ecirc;n bản bộ nhớ lớn nhất<a href=\"https://fptshop.com.vn/dien-thoai/iphone-11-pro-512gb\">&nbsp;iPhone 11 Pro 512GB</a>&nbsp;cho bạn kh&ocirc;ng gian lưu trữ thoải m&aacute;i, tha hồ chụp ảnh v&agrave; quay video bằng hệ thống 3 camera sau si&ecirc;u đỉnh, thời lượng pin d&agrave;i v&ocirc; tận c&ugrave;ng sức mạnh khổng lồ từ bộ vi xử l&yacute; Apple A13 Bionic.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-14.jpg\" alt=\"\" /></strong></p>\r\n<h3><strong>Sức mạnh đẳng cấp Pro</strong></h3>\r\n<p>Bộ vi xử l&yacute;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/con-chip-apple-a13-bionic-tren-iphone-11-manh-co-nao-101678\">&nbsp;A13 Bionic</a>&nbsp;tr&ecirc;n iPhone 11 Pro 512GB mang đến hiệu năng ở một đẳng cấp kh&aacute;c so với c&aacute;c&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>&nbsp;hiện nay. Con chip si&ecirc;u mạnh mẽ cả về CPU v&agrave; GPU c&ograve;n được hỗ trợ bởi tr&iacute; tuệ nh&acirc;n tạo, tự động học hỏi để mang đến trải nghiệm mượt m&agrave; hiếm c&oacute;. Mọi t&aacute;c vụ d&ugrave; phức tạp đến mấy cũng đều được xử l&yacute; nhanh ch&oacute;ng, tất cả c&aacute;c tựa game nặng đều được thể hiện xuất sắc. Sức mạnh đ&aacute;ng kinh ngạc của iPhone 11 Pro khiến cho mọi việc đều trở n&ecirc;n dễ d&agrave;ng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-10.jpg\" alt=\"iPhone Pro hiệu năng\" /></p>\r\n<h3><strong>Thời lượng pin kh&oacute; tin</strong></h3>\r\n<p>Bạn sẽ bất ngờ về thời lượng pin của iPhone 11 Pro. Chiếc<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">&nbsp;iPhone</a>&nbsp;mới c&oacute; thời gian sử dụng l&acirc;u hơn&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-xs-64gb\">iPhone Xs</a>&nbsp;trước đ&acirc;y tới 4 giờ đồng hồ. Đ&acirc;y l&agrave; kết quả của cả hệ thống vận h&agrave;nh, bao gồm pin, chip, m&agrave;n h&igrave;nh, phần mềm được cải tiến. Tr&ecirc;n iPhone 11 Pro bạn c&oacute; thể sử dụng thoải m&aacute;i kh&ocirc;ng lo hết pin, đồng thời sạc cực nhanh bằng củ sạc 18W đi k&egrave;m. Chỉ cần 30 ph&uacute;t sạc cho cả ng&agrave;y d&agrave;i năng động, lu&ocirc;n lu&ocirc;n sẵn s&agrave;ng v&agrave; kh&ocirc;ng phải chờ đợi với iPhone 11 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-11.jpg\" alt=\"iPhone Pro pin\" /></p>\r\n<h3><strong>Thiết kế mặt lưng nh&aacute;m cao cấp</strong></h3>\r\n<p>Vẫn được ho&agrave;n thiện từ khung th&eacute;p kh&ocirc;ng gỉ cao cấp v&agrave; hai mặt k&iacute;nh cường lực b&oacute;ng bẩy, nhưng giờ đ&acirc;y lớp sơn nh&aacute;m ở mặt lưng mang đến diện mạo ho&agrave;n to&agrave;n mới cho iPhone 11 Pro. Mặt lưng sẽ lu&ocirc;n s&aacute;ng đẹp, mịn m&agrave;ng m&agrave; kh&ocirc;ng bị b&aacute;m bẩn, mồ h&ocirc;i hay dấu v&acirc;n tay. Chiếc iPhone nhỏ gọn, cho cảm gi&aacute;c cầm nắm ho&agrave;n hảo nhưng vẫn c&oacute; m&agrave;n h&igrave;nh đủ lớn để trải nghiệm tốt mọi thứ. Bạn sẽ c&oacute; 4 lựa chọn m&agrave;u sắc sang trọng, bao gồm V&agrave;ng, X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; m&agrave;u mới Xanh Midnigh đặc biệt.</p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-1.jpg\" alt=\"iPhone Pro\" /></strong></p>\r\n<h3><strong>Chất liệu si&ecirc;u cứng, ho&agrave;n thiện chắc chắn, chống nước chuẩn IP68</strong></h3>\r\n<p>Những chất liệu tạo n&ecirc;n iPhone 11 Pro bao gồm th&eacute;p kh&ocirc;ng gỉ v&agrave; k&iacute;nh cường lực cứng nhất thế giới smartphone. Ch&iacute;nh v&igrave; vậy iPhone 11 Pro v&ocirc; c&ugrave;ng chắc chắn, sẵn s&agrave;ng đương đầu mọi thử th&aacute;ch. Khả năng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/chuan-ip68-la-gi-khac-biet-gi-so-voi-ip67-57823\">chống nước chuẩn IP68</a>&nbsp;cho ph&eacute;p bạn ng&acirc;m nước dưới độ s&acirc;u 4m trong thời gian l&ecirc;n tới 30 ph&uacute;t, đồng nghĩa với việc iPhone 11 miễn nhiễm với hầu hết nguy cơ từ chất lỏng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-12.jpg\" alt=\"iPhone Pro thiết kế\" /></p>\r\n<h3><strong>Hiển thị Pro với m&agrave;n h&igrave;nh Super Retina XDR</strong></h3>\r\n<p>iPhone 11 Pro sở hữu m&agrave;n h&igrave;nh 5,8 inch<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi-1/super-retina-xdr-la-gi-cong-nghe-man-hinh-moi-tren-iphone-11-101690\">&nbsp;Super Retina XDR</a>&nbsp;cực chất. M&agrave;n h&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-oled-la-gi-cong-nghe-man-hinh-oled-uu-va-nhuoc-diem-57402\">c&ocirc;ng nghệ OLED&nbsp;</a>mới cho m&agrave;u sắc ch&acirc;n thực, g&oacute;c nh&igrave;n ho&agrave;n hảo v&agrave; độ s&aacute;ng đ&aacute;ng kinh ngạc. Độ s&aacute;ng m&agrave;n h&igrave;nh iPhone 11 Pro đạt tới 800 nits khi hiển thị ngo&agrave;i trời v&agrave; thậm ch&iacute; đạt con số 1200 nits khi xem những nội dung c&oacute; dải nhạy s&aacute;ng rộng. Ngo&agrave;i việc hiển thị xuất sắc, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n tiết kiệm điện hơn 15%, g&oacute;p phần n&acirc;ng cao thời lượng pin của iPhone 11 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-pro-15.jpg\" alt=\"\" /></p>\r\n<h3><strong>3 camera sau chuy&ecirc;n nghiệp</strong></h3>\r\n<p>iPhone 11 Pro l&agrave; chiếc iPhone đầu ti&ecirc;n trang bị hệ thống 3 camera sau chuy&ecirc;n nghiệp. Chụp cảnh rộng gấp 4 lần; h&igrave;nh ảnh tuyệt đẹp trong điều kiện &aacute;nh s&aacute;ng yếu; quay video chất lượng cao nhất thế giới smartphone v&agrave; tr&ecirc;n hết khả năng chỉnh sửa với những c&ocirc;ng cụ chuy&ecirc;n nghiệp ngay tr&ecirc;n chiếc điện thoại của bạn. iPhone 11 Pro kh&ocirc;ng chỉ l&agrave; m&aacute;y ảnh v&agrave; m&aacute;y quay video ho&agrave;n hảo, đ&acirc;y c&ograve;n l&agrave; phương tiện để bạn hậu kỳ, chỉnh sửa v&agrave; xuất bản.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-2.jpg\" alt=\"camera iPhone Pro\" /></p>\r\n<h3><strong>Chụp gần, chụp g&oacute;c rộng v&agrave; g&oacute;c si&ecirc;u rộng</strong></h3>\r\n<p>Chỉ tr&ecirc;n một chiếc điện thoại, bạn c&oacute; thể chụp gần với camera tele, chụp th&ocirc;ng thường với camera g&oacute;c rộng v&agrave; chụp những bức ảnh ho&agrave;nh tr&aacute;ng với camera g&oacute;c si&ecirc;u rộng. iPhone 11 Pro c&oacute; khả năng zoom quang 4x v&agrave; thu được khung cảnh lớn gấp 4 lần đầy ấn tượng. Sẽ c&oacute; những l&uacute;c bạn muốn lưu giữ những cảnh đẹp ở nơi m&igrave;nh đ&atilde; đi, iPhone 11 Pro sẽ gi&uacute;p bạn bằng c&aacute;c bức ảnh sống động nhất với camera g&oacute;c si&ecirc;u rộng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-3.jpg\" alt=\"g&oacute;c si&ecirc;u rộng iPhone Pro\" /></p>\r\n<h3><strong>Chế độ chụp đ&ecirc;m mới Night Mode, giải ph&aacute;p ho&agrave;n hảo cho chụp ảnh thiếu s&aacute;ng</strong></h3>\r\n<p>Những &aacute;nh đ&egrave;n trong đ&ecirc;m lu&ocirc;n mang đến rất nhiều cảm hứng nhưng lại thật kh&oacute; để chụp lại bằng camera. iPhone 11 Pro dễ d&agrave;ng ghi lại những khoảnh khắc của bạn trong điều kiện thiếu s&aacute;ng nhờ chế độ chụp đ&ecirc;m Night Mode. Sự kết hợp giữa cảm biến camera mới, phần mềm th&ocirc;ng minh v&agrave; khả năng xử l&yacute; tuyệt vời của con chip A13 Bionic mang lại những bức ảnh thiếu s&aacute;ng chưa từng c&oacute; tr&ecirc;n iPhone. Chế độ Night Mode c&ograve;n hoạt động rất th&ocirc;ng minh khi tự động nhận diện điều kiện thiếu s&aacute;ng để k&iacute;ch hoạt, cho bức ảnh ch&acirc;n thực nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-4.jpg\" alt=\"iPhone Pro thiếu s&aacute;ng\" /></p>\r\n<h3><strong>N&acirc;ng tầm ảnh chụp ch&acirc;n dung x&oacute;a ph&ocirc;ng</strong></h3>\r\n<p>Việc sử dụng tới 3 camera gi&uacute;p khả năng chụp ảnh ch&acirc;n dung của iPhone 11 Pro trở n&ecirc;n chuy&ecirc;n nghiệp hơn bao giờ hết. Bạn sẽ c&oacute; th&ecirc;m những hiệu ứng &aacute;nh s&aacute;ng đơn sắc chuẩn studio.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-ve-cong-nghe-chup-xoa-phong-la-gi-63770\">Chế độ x&oacute;a ph&ocirc;ng</a>&nbsp;mới cũng cho ph&eacute;p bạn điều chỉnh cường độ &aacute;nh s&aacute;ng, nhờ vậy chủ thể sẽ xuất hiện đẹp lung linh, đậm phong c&aacute;ch v&agrave; c&aacute; t&iacute;nh kh&aacute;c biệt. B&ecirc;n cạnh đ&oacute;, x&oacute;a ph&ocirc;ng tr&ecirc;n iPhone 11 Pro c&ograve;n th&ocirc;ng minh hơn khi x&oacute;a ph&ocirc;ng được cả đối tượng l&agrave; vật thể, hoặc x&oacute;a ph&ocirc;ng 2 người trong c&ugrave;ng một khung h&igrave;nh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-5.jpg\" alt=\"iPhone Pro x&oacute;a ph&ocirc;ng\" /></p>\r\n<h3><strong>Smart HDR, giải ph&aacute;p cho những bức ảnh ch&acirc;n thực v&agrave; sống động</strong></h3>\r\n<p>Smart HDR sẽ gi&uacute;p cho những bức ảnh của bạn kh&ocirc;ng chỉ đẹp m&agrave; c&ograve;n hết sức ch&acirc;n thực v&agrave; sống động. C&aacute;c thuật to&aacute;n ti&ecirc;n tiến c&ugrave;ng khả năng tự học hỏi Machine Learning được &aacute;p dụng để l&agrave;m nổi bật &aacute;nh s&aacute;ng, hiệu ứng b&oacute;ng đổ v&agrave; từng chi tiết nhỏ trong bức ảnh. iPhone 11 Pro c&oacute; thể tinh chỉnh ri&ecirc;ng biệt chủ thể v&agrave; cảnh nền, t&iacute;nh năng vốn chỉ c&oacute; ở một số m&aacute;y ảnh DSLR cao cấp. Độ tương phản, m&agrave;u sắc, c&acirc;n bằng trắng trong ảnh trở n&ecirc;n ho&agrave;n hảo hơn bao giờ hết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-6.jpg\" alt=\"iPhone Pro smart hdr\" /></p>\r\n<h3><strong>Quay video chất lượng, chỉnh sửa video dễ d&agrave;ng</strong></h3>\r\n<p>iPhone 11 Pro xứng đ&aacute;ng l&agrave; chiếc điện thoại c&oacute; khả năng quay video h&agrave;ng đầu thế giới hiện nay. Bộ vi xử l&yacute; mạnh mẽ c&ugrave;ng cụm camera cao cấp gi&uacute;p iPhone 11 Pro c&oacute; thể quay video 4K 60fps si&ecirc;u n&eacute;t với dải nhạy s&aacute;ng cực rộng v&agrave; chống rung chuy&ecirc;n nghiệp. Thậm ch&iacute; với camera g&oacute;c rộng, bạn c&oacute; thể s&aacute;ng tạo hơn khi li&ecirc;n tục hay đổi g&oacute;c nh&igrave;n, tất cả đều diễn ra một c&aacute;ch trơn tru v&agrave; đẹp mắt. Hơn nữa, khi bạn zoom h&igrave;nh ảnh v&agrave;o người đang n&oacute;i chuyện th&igrave; &acirc;m lượng của giọng n&oacute;i cũng được tăng l&ecirc;n theo, giống như phim truyền h&igrave;nh vậy. Ngo&agrave;i ra, mọi thứ đều dễ d&agrave;ng chỉnh sửa v&agrave; hậu kỳ nhanh ch&oacute;ng dựa tr&ecirc;n c&ocirc;ng cụ phần mềm c&oacute; sẵn của iPhone 11 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-7.jpg\" alt=\"iPhone Pro video\" /></p>\r\n<h3><strong>Chụp ảnh selfie tuyệt đẹp bằng camera trước 12MP</strong></h3>\r\n<p>Camera trước iPhone 11 Pro được n&acirc;ng cấp l&ecirc;n độ ph&acirc;n giải 12MP, mang đến những bức ảnh sắc n&eacute;t, chất lượng hơn. Apple c&ograve;n th&ecirc;m t&iacute;nh năng quay video chuyển động si&ecirc;u chậm ở 120 fps cho camera selfie. Những đoạn video chuyển động si&ecirc;u chậm quay bằng camera trước được gọi l&agrave; slofie, cho bạn lưu lại những khoảnh khắc theo c&aacute;ch ấn tượng. Camera n&agrave;y cũng c&oacute; g&oacute;c rộng hơn để kh&ocirc;ng ai bị bỏ qua trong khung h&igrave;nh. Tha hồ selfie v&agrave; slofie c&ugrave;ng bạn b&egrave; một c&aacute;ch vui vẻ tr&ecirc;n iPhone 11 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-8.jpg\" alt=\"iPhone Pro camera selfie\" /></p>\r\n<h3><strong>Face ID thế hệ mới, nhanh ch&oacute;ng v&agrave; bảo mật</strong></h3>\r\n<p>Face ID của iPhone 11 Pro nhanh hơn 30%, nhận diện được xa hơn, nhiều g&oacute;c độ hơn v&agrave; hiệu quả hơn. Đơn giản chỉ cần nhấc iPhone l&ecirc;n, m&aacute;y sẽ mở kh&oacute;a ngay lập tức một c&aacute;ch tự nhi&ecirc;n. Ngo&agrave;i ra, mọi dữ liệu tr&ecirc;n iPhone đều được bảo mật an to&agrave;n. Kể cả dữ liệu khu&ocirc;n mặt của bạn cũng kh&ocirc;ng hề được sao lưu ở iCloud hay bất cứ nơi n&agrave;o kh&aacute;c. Th&ocirc;ng tin bản đồ v&agrave; iMessage cũng được m&atilde; h&oacute;a để tr&aacute;nh bị hacker th&acirc;m nhập.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro.jpg\" alt=\"thiết kế iPhone Pro\" /></p>', 1, 0, 1, 12, '2021-08-16 07:08:38', '2021-08-28 04:31:11', NULL),
(31, 'Samsung Galaxy Watch 4 40mm', 'samsung-galaxy-watch-4-40mm', 'public/uploads/product/1\\samsung-galaxy-watch-4-den-small-1.jpg', 6490000, '<p><strong>Kh&ocirc;ng chỉ l&agrave; chiếc đồng hồ th&ocirc;ng minh đầy đủ cả t&iacute;nh năng c&ocirc;ng nghệ v&agrave; vẻ đẹp thời trang,&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/dong-ho-samsung-galaxy-watch-4\">Samsung Galaxy Watch 4</a>&nbsp;c&ograve;n l&agrave; một người bạn đồng h&agrave;nh lu&ocirc;n lu&ocirc;n thấu hiểu sức khỏe v&agrave; những g&igrave; bạn cần, c&ugrave;ng bạn ho&agrave;n thiện bản th&acirc;n mỗi ng&agrave;y.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-17.jpg\" alt=\"Samsung Galaxy Watch 4\" /></strong></p>\r\n<h3><strong>Thấu hiểu cơ thể của bạn</strong></h3>\r\n<p>Galaxy Watch 4 c&oacute; khả năng ph&acirc;n t&iacute;ch th&agrave;nh phần cơ thể khi cung cấp th&ocirc;ng tin về tỷ lệ mỡ, cơ xương, lượng nước trong cơ thể v&agrave; nhiều th&ocirc;ng tin quan trọng kh&aacute;c. Cảm biến Samsung BioActive mới c&oacute; thể đo c&aacute;c th&agrave;nh phần trong cơ thể bạn theo thời gian thực, gi&uacute;p bạn lu&ocirc;n lu&ocirc;n kiểm so&aacute;t v&agrave; biết cần phải tập luyện, bổ sung những g&igrave; để c&oacute; được sức khỏe v&agrave; h&igrave;nh thể như &yacute;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-11.jpg\" alt=\"cơ thể Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>C&ugrave;ng tập luyện với bạn b&egrave;</strong></h3>\r\n<p>T&iacute;nh năng Thử th&aacute;ch nh&oacute;m tr&ecirc;n Galaxy Watch 4 sẽ duy tr&igrave; cảm hứng tập luyện của bạn bằng c&aacute;c cuộc thi đầy hấp dẫn. Bạn c&oacute; thể so s&aacute;nh với bạn b&egrave; xem ai đốt ch&aacute;y nhiều calo hơn, ai đi bộ nhiều hơn trong một ng&agrave;y. Sẽ c&oacute; những huy hiệu v&agrave; bảng xếp hạng để tạo động lực cho những người chăm chỉ tập luyện.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-1.jpg\" alt=\"cuộc thi Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>Tập luyện chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Trang bị bộ vi xử l&yacute; v&agrave; những cảm biến ti&ecirc;n tiến nhất hiện nay, bạn c&oacute; thể dễ d&agrave;ng theo d&otilde;i th&agrave;nh quả tập luyện của m&igrave;nh tr&ecirc;n Samsung Galaxy Watch 4.</p>\r\n<p>Đồng hồ c&oacute; thể đếm bước ch&acirc;n, lượng calo đốt ch&aacute;y, h&agrave;nh tr&igrave;nh di chuyển, nhận diện hoạt động luyện tập với hơn 90 chế độ tập luyện kh&aacute;c nhau. D&ugrave; bạn chơi bất cứ m&ocirc;n thể dục thể thao n&agrave;o, Galaxy Watch 4 vẫn lu&ocirc;n l&agrave; người bạn đồng h&agrave;nh l&yacute; tưởng c&ugrave;ng bạn chinh phục mục ti&ecirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-10.jpg\" alt=\"tập luyện Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>Cải thiện t&igrave;nh trạng giấc ngủ to&agrave;n diện</strong></h3>\r\n<p>Kh&ocirc;ng chỉ tập luyện, nghỉ ngơi cũng đ&oacute;ng vai tr&ograve; quan trọng trong sức khỏe mỗi người. Galaxy Watch 4 sẽ theo d&otilde;i, ph&acirc;n t&iacute;ch v&agrave; đ&aacute;nh gi&aacute; chất lượng giấc ngủ của bạn một c&aacute;ch ch&iacute;nh x&aacute;c qua việc đo&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/spo2-la-gi-133577\">SpO2</a>, ph&aacute;t hiện tiếng ng&aacute;y trong khi ngủ.</p>\r\n<p><a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;đ&atilde; hợp t&aacute;c với tổ chức chuy&ecirc;n về giấc ngủ National Sleep Foudation để mang tới cho bạn những th&ocirc;ng tin chuy&ecirc;n s&acirc;u về giấc ngủ, qua đ&oacute; cải thiện giấc ngủ của bạn theo phương ph&aacute;p khoa học.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-2.jpg\" alt=\"giấc ngủ Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>Thiết kế năng động, trẻ trung</strong></h3>\r\n<p>Đồng hồ Samsung Galaxy Watch 4 c&oacute; ng&ocirc;n ngữ thiết kế tối giản với sự liền mạch hiện đại. Phần khung được l&agrave;m từ hợp kim nh&ocirc;m Armor Aluminum cao cấp, sang trọng v&agrave; si&ecirc;u cứng c&aacute;p. Kiểu d&aacute;ng đơn giản, trẻ trung, nhiều m&agrave;u sắc v&agrave; lựa chọn d&acirc;y đeo đa dạng gi&uacute;p chiếc&nbsp;<a href=\"https://fptshop.com.vn/smartwatch\">đồng hồ th&ocirc;ng minh</a>&nbsp;n&agrave;y&nbsp;l&agrave; một m&oacute;n&nbsp;<a href=\"https://fptshop.com.vn/phu-kien\">phụ kiện</a>&nbsp;vừa thời trang, vừa đầy đủ t&iacute;nh năng c&ocirc;ng nghệ tr&ecirc;n cổ tay bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-16.jpg\" alt=\"thiết kế Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>Thể hiện c&aacute; t&iacute;nh mỗi ng&agrave;y</strong></h3>\r\n<p>Bạn sẽ c&oacute; cả kho mặt đồng hồ đa dạng tr&ecirc;n Galaxy Watch 4. Từ những mặt đồng hồ cổ điển cho đến hiện đại, vui nhộn v&agrave; nhiều phong c&aacute;ch kh&aacute;c nhau. H&atilde;y thay đổi mặt đồng hồ thường xuy&ecirc;n để mỗi ng&agrave;y của bạn đều c&oacute; những niềm vui mới.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-3.jpg\" alt=\"mặt đồng hồ Samsung Galaxy Watch 4\" /></p>\r\n<h3><strong>Hệ điều h&agrave;nh Wear OS cải tiến</strong></h3>\r\n<p>Galaxy Watch 4 l&agrave; chiếc smartwatch đầu ti&ecirc;n chạy hệ điều h&agrave;nh Wear OS cải tiến, cho khả năng hoạt động mượt m&agrave;, đầy đủ ứng dụng về &acirc;m nhạc, sức khỏe, tập luyện, giải tr&iacute;. Hệ điều h&agrave;nh Wear OS mới cũng gi&uacute;p bạn trải nghiệm liền mạch hơn với c&aacute;c thiết bị Samsung Galaxy như điều khiển&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe-samsung-galaxy-buds2\">tai nghe Galaxy Buds 2</a>&nbsp;hay mở ứng dụng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;ngay tr&ecirc;n đồng hồ một c&aacute;ch tiện dụng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/samsung-galaxy-watch4-8.jpg\" alt=\"hệ điều h&agrave;nh Samsung Galaxy Watch 4\" /></p>', 1, 1, 1, 21, '2021-09-05 04:25:26', '2021-09-05 04:25:26', NULL),
(32, 'Galaxy Watch Active 2 Under Armour 44mm', 'galaxy-watch-active-2-under-armour-44mm', 'public/uploads/product/1\\galaxy-watch-active-2-44mm-nhom-den-1.jpg', 3990000, '<p><strong>L&agrave; sự hợp t&aacute;c của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">Samsung</a>&nbsp;v&agrave; Under Amour,&nbsp;<a href=\"https://fptshop.com.vn/smartwatch/galaxy-watch-active-2-under-armour-44mm-nhom\">Galaxy Watch Active 2 Under Armour</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/smartwatch\">đồng hồ th&ocirc;ng minh</a>&nbsp;d&agrave;nh ri&ecirc;ng cho những người th&iacute;ch chạy bộ. Khả năng kết nối, những t&iacute;nh năng hỗ trợ chạy bộ tối ưu sẽ đưa thể lực, giới hạn của bản th&acirc;n bạn l&ecirc;n một tầm cao mới.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-11.jpg\" alt=\"Galaxy Watch Active 2 Under Armour\" /></strong></p>\r\n<h3><strong>Huấn luyện vi&ecirc;n chạy bộ theo thời gian thực</strong></h3>\r\n<p>Galaxy Watch Active 2 Under Armour mang đến cho người d&ugrave;ng h&igrave;nh thức huấn luyện theo thời gian thực. Thay v&igrave; chỉ đưa ra c&aacute;c th&ocirc;ng số, dữ liệu từ đồng hồ sẽ được ph&acirc;n t&iacute;ch v&agrave; phản hồi trực tiếp bằng c&aacute;c t&iacute;n hiệu &acirc;m thanh khi tập để bạn c&oacute; được qu&aacute; tr&igrave;nh tập luyện chuy&ecirc;n nghiệp hơn. Trong bộ m&ocirc;n chạy bộ, những sải bước ch&acirc;n, tốc độ của bạn sẽ được tập trung để c&oacute; được kết quả tốt hơn từng ng&agrave;y.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-14.jpg\" alt=\"huấn luyện vi&ecirc;n Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>Ph&acirc;n t&iacute;ch chuy&ecirc;n s&acirc;u trong tập luyện</strong></h3>\r\n<p>Chạy bộ kh&ocirc;ng phải l&agrave; m&ocirc;n thể thao đơn giản như bạn nghĩ. Để chạy bộ đ&uacute;ng c&aacute;ch, bạn cần phải tập luyện một c&aacute;ch khoa học. Galaxy Watch Active 2 Under Armour sẽ đặt c&aacute;c mục ti&ecirc;u cho ri&ecirc;ng bạn về tốc độ, khoảng c&aacute;ch v&agrave; thời lượng. Đồng hồ sẽ th&ocirc;ng b&aacute;o qua &acirc;m thanh hoặc rung trong qu&aacute; tr&igrave;nh chạy để bạn điều chỉnh kịp thời. Đồng thời sau khi tập, bạn sẽ nhận được c&aacute;c ph&acirc;n t&iacute;ch chuy&ecirc;n s&acirc;u hơn dựa tr&ecirc;n kết quả v&agrave; c&aacute;c mẹo huấn luyện đầy chuy&ecirc;n nghiệp.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-12.jpg\" alt=\"chạy bộ Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>Ứng dụng theo d&otilde;i chạy bộ chuy&ecirc;n nghiệp MapMyRun</strong></h3>\r\n<p>Phi&ecirc;n bản Galaxy Watch Active 2 Under Armour sẽ đi k&egrave;m 6 th&aacute;ng sử dụng miễn ph&iacute; với tư c&aacute;ch l&agrave; th&agrave;nh vi&ecirc;n cao cấp của ứng dụng chạy bộ MapMyRun. Trong ứng dụng n&agrave;y sẽ c&oacute; c&aacute;c kế hoạch đ&agrave;o tạo chuy&ecirc;n nghiệp, đưa ra những tiến độ, b&agrave;i tập để bạn tiến bộ hơn mỗi ng&agrave;y. Ngo&agrave;i ra những loại gi&agrave;y chạy bộ chuy&ecirc;n dụng cũng c&oacute; thể kết nối với MapMyRun v&agrave; th&ecirc;m sự kết hợp của Galaxy Watch Active 2 phi&ecirc;n bản Amour, mỗi buổi chạy bộ của bạn sẽ trở th&agrave;nh một buổi đ&agrave;o tạo c&aacute; nh&acirc;n thực thụ. Kỹ thuật, tốc độ, sức bền của bạn li&ecirc;n tục được cải thiện để đạt được hiệu quả tối ưu từ bộ m&ocirc;n chạy bộ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-13.jpg\" alt=\"map my run Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>Thiết kế thời trang, cảm gi&aacute;c đeo thoải m&aacute;i</strong></h3>\r\n<p>L&agrave; một chiếc đồng hồ hỗ trợ tập luyện chuy&ecirc;n nghiệp, Galaxy Watch Active 2 Under Armour c&oacute; th&acirc;n m&aacute;y l&agrave;m từ nh&ocirc;m nhẹ, kiểu d&aacute;ng thể thao với mặt đồng hồ Super AMOLED Always On đẹp rực rỡ. Phi&ecirc;n bản 44mm cho m&agrave;n h&igrave;nh lớn, cung cấp th&ocirc;ng tin r&otilde; r&agrave;ng hơn. D&acirc;y đeo m&agrave;u đen mạnh mẽ được l&agrave;m từ FKM c&oacute; thiết kế tho&aacute;ng kh&iacute;, cho cảm gi&aacute;c đeo thoải m&aacute;i, tho&aacute;t mồ h&ocirc;i nhanh, ho&agrave;n to&agrave;n vừa vặn tr&ecirc;n tay bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-9.jpg\" alt=\"thiết kế Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>V&ograve;ng xoay cảm ứng th&ocirc;ng minh</strong></h3>\r\n<p>B&ecirc;n cạnh mặt đồng hồ cảm ứng, Galaxy Watch Active 2 Under Armour c&ograve;n c&oacute; v&ograve;ng xoay cảm ứng th&ocirc;ng minh, để bạn tận dụng tối đa kh&ocirc;ng gian m&agrave;n h&igrave;nh v&agrave; thao t&aacute;c mượt m&agrave; ở phần v&ograve;ng cảm ứng xung quanh. Thao t&aacute;c n&agrave;y gi&uacute;p trải nghiệm đồng hồ của bạn dễ d&agrave;ng v&agrave; trực quan hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-6.jpg\" alt=\"v&ograve;ng xoay Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>Thay đổi mặt đồng hồ dễ d&agrave;ng</strong></h3>\r\n<p>Galaxy Watch Active 2 Under Armour c&oacute; rất nhiều mặt đồng hồ để bạn lựa chọn, đặc biệt l&agrave; những mặt đồng hồ độc quyền chỉ c&oacute; ở phi&ecirc;n bản Under Armour. Để hoạt động chạy bộ diễn ra bất cứ l&uacute;c n&agrave;o, bạn chỉ cần một c&uacute; chạm ngay tr&ecirc;n mặt đồng hồ để truy cập ứng dụng MapMyRun.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-7.jpg\" alt=\"mặt đồng h&ocirc; Galaxy Watch Active 2 Under Armour \" /></p>\r\n<h3><strong>Độ bền chuẩn qu&acirc;n đội</strong></h3>\r\n<p><a href=\"https://fptshop.com.vn/smartwatch/samsung\">Samsung Galaxy Watch</a>&nbsp;Active 2 Under Armour l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dong-ho\">đồng hồ</a>&nbsp;v&ocirc; c&ugrave;ng bền vững, c&oacute; thể b&ecirc;n bạn đi khắp mu&ocirc;n nơi. Chuẩn chống bụi v&agrave; nước IP68, 5ATM c&ugrave;ng độ bền chuẩn qu&acirc;n sự gi&uacute;p đồng hồ vượt qua loạt điều kiện khắc nghiệt, để lu&ocirc;n c&ugrave;ng bạn chạy bộ bất kể thời tiết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-1.jpg\" alt=\"độ bền Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>C&aacute;c cảm biến ch&iacute;nh x&aacute;c</strong></h3>\r\n<p>Galaxy Watch Active 2 Under Armour trang bị đầy đủ những cảm biến h&agrave;ng đầu để chăm s&oacute;c sức khỏe người d&ugrave;ng. Đồng hồ sẽ tự động đo mức độ stress, đưa ra th&ocirc;ng b&aacute;o v&agrave; gợi &yacute; c&aacute;c b&agrave;i tập h&iacute;t thở để bạn nhanh ch&oacute;ng ổn định lại tinh thần. B&ecirc;n cạnh đ&oacute;, một cảm biến nhịp tim mới 8 photodiodes sẽ đo được ch&iacute;nh x&aacute;c nhịp tim, theo d&otilde;i sức khỏe của bạn dựa tr&ecirc;n biểu đồ nhịp tim v&agrave; đưa ra cảnh b&aacute;o kịp thời để bạn điều chỉnh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Galaxy-Watch-Active-2-Under-Armour-10.jpg\" alt=\"cảm biến Galaxy Watch Active 2 Under Armour\" /></p>\r\n<h3><strong>Loạt t&iacute;nh năng th&ocirc;ng minh</strong></h3>\r\n<p>Đồng hồ Galaxy Watch Active 2 Under Armour đ&oacute;ng vai người trợ l&yacute; đ&aacute;ng tin cậy của bạn khi c&oacute; thể thực hiện rất nhiều t&iacute;nh năng th&ocirc;ng minh. Điều chỉnh m&aacute;y ảnh để tự chụp với nh&oacute;m bạn tiện lợi; tự động dịch một ng&ocirc;n ngữ; theo d&otilde;i mạng x&atilde; hội; b&aacute;o thức v&agrave; rất nhiều ứng dụng tiện &iacute;ch đang chờ bạn kh&aacute;m ph&aacute;.</p>', 1, 0, 1, 21, '2021-09-05 04:26:36', '2021-09-05 04:26:36', NULL),
(33, 'Masstel Watch Dream Action', 'masstel-watch-dream-action', 'public/uploads/product/1\\masstel-watch-dream-action-hong-1.jpg', 790000, '<p><strong>Một chiếc&nbsp;<a title=\"Tham khảo nhiều mẫu đồng hồ th&ocirc;ng minh ch&iacute;nh h&atilde;ng, nhận nhiều qu&agrave; hấp dẫn tại FPTShop\" href=\"https://fptshop.com.vn/smartwatch\" type=\"Tham khảo nhiều mẫu đồng hồ th&ocirc;ng minh ch&iacute;nh h&atilde;ng, nhận nhiều qu&agrave; hấp dẫn tại FPTShop\">đồng hồ th&ocirc;ng minh</a>&nbsp;thời trang, c&oacute; khả năng chống nước, theo d&otilde;i sức khỏe, đo nhịp tim trong tầm gi&aacute; rẻ đến bất ngờ.&nbsp;<a title=\"Mua ngay đồng hồ Masstel Watch Dream Action ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại FPTShop\" href=\"https://fptshop.com.vn/smartwatch/masstel-watch-dream-action\" type=\"Mua ngay đồng hồ Masstel Watch Dream Action ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại FPTShop\">Masstel Dream Action</a>&nbsp;sẽ gi&uacute;p bạn biến giấc mơ trở th&agrave;nh hiện thực.</strong></p>\r\n<p><strong><img class=\"Đ&aacute;nh gi&aacute; Masstel Dream Action\" title=\"Đ&aacute;nh gi&aacute; Masstel Dream Action\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-1.jpg\" alt=\"Đ&aacute;nh gi&aacute; Masstel Dream Action\" /></strong></p>\r\n<h3><strong>Thiết kế si&ecirc;u mỏng thời thượng</strong></h3>\r\n<p>Masstel Dream Action c&oacute; thiết kế mặt tr&ograve;n v&agrave; d&acirc;y đeo khỏe khoắn, ph&ugrave; hợp cho mọi đối tượng người d&ugrave;ng. Phần khung vỏ l&agrave;m từ chất liệu Aluminium cao cấp, chống trầy xước v&agrave; va đập. Kiểu d&aacute;ng mỏng, thanh lịch v&agrave; mặt&nbsp;<a title=\"Xem nhiều mẫu đồng hồ ch&iacute;nh h&atilde;ng, hỗ trợ trả g&oacute;p 0% tại FPTShop\" href=\"https://fptshop.com.vn/dong-ho\" type=\"Xem nhiều mẫu đồng hồ ch&iacute;nh h&atilde;ng, hỗ trợ trả g&oacute;p 0% tại FPTShop\">đồng hồ</a>&nbsp;40,5mm l&yacute; tưởng cho cổ tay người Việt. Tuy nhi&ecirc;n ấn tượng nhất phải kể đến m&agrave;n h&igrave;nh 1,2 inch AMOLED cao cấp, được l&agrave;m cong 3D thời thượng. Khả năng hiển thị xuất sắc kể cả dưới trời nắng cho bạn cảm gi&aacute;c thật sự h&agrave;i l&ograve;ng khi sử dụng.</p>\r\n<p><img class=\"Masstel Dream Action | Thiết kế si&ecirc;u mỏng thời thượng\" title=\"Masstel Dream Action | Thiết kế si&ecirc;u mỏng thời thượng\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-2.jpg\" alt=\"Masstel Dream Action | Thiết kế si&ecirc;u mỏng thời thượng\" /></p>\r\n<h3><strong>Người bạn đồng h&agrave;nh khi tập luyện</strong></h3>\r\n<p><a title=\"Tham khảo nhiều mẫu đồng hồ Masstel ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại FPTShop\" href=\"https://fptshop.com.vn/smartwatch/masstel\" type=\"Tham khảo nhiều mẫu đồng hồ Masstel ch&iacute;nh h&atilde;ng, gi&aacute; rẻ tại FPTShop\">Đồng hồ Masstel</a>&nbsp;n&agrave;y&nbsp;t&iacute;ch hợp cảm biến Bosch, gi&uacute;p đo ch&iacute;nh x&aacute;c c&aacute;c chỉ số trong tập luyện. Đồng hồ c&oacute; thể nhận diện được hầu hết c&aacute;c chương tr&igrave;nh tập luyện phổ biến như chạy bộ ngo&agrave;i trời, chạy với m&aacute;y tập, đi bộ, đạp xe, leo n&uacute;i, bơi lội, yoga, &hellip; Dream Action sẽ đếm v&agrave; ghi lại ch&iacute;nh x&aacute;c số bước ch&acirc;n, lượng calo đốt trong tập luyện để bạn c&oacute; thể theo d&otilde;i, tập luyện hiệu quả hơn.</p>\r\n<p><img class=\"Masstel Dream Action | T&iacute;ch hợp cảm biến Bosch\" title=\"Masstel Dream Action | T&iacute;ch hợp cảm biến Bosch\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-3.jpg\" alt=\"Masstel Dream Action | T&iacute;ch hợp cảm biến Bosch\" /></p>\r\n<h3><strong>Cảm biến nhịp tim ch&iacute;nh x&aacute;c</strong></h3>\r\n<p>Bạn sẽ biết r&otilde; chế độ tập luyện v&agrave; t&igrave;nh trạng sức khỏe của m&igrave;nh hơn nhờ cảm biến nhịp tim Pixart 8002 tr&ecirc;n đồng hồ th&ocirc;ng minh Masstel Dream Action. Cảm biến n&agrave;y sẽ ti&ecirc;u thụ điện năng thấp hơn nhưng lại cho kết quả ch&iacute;nh x&aacute;c hơn, th&ocirc;ng b&aacute;o ch&iacute;nh x&aacute;c nhịp tim của bạn khi ngủ, l&agrave;m việc hay tập luyện.</p>\r\n<p><img class=\"Masstel Dream Action | Cảm biến nhịp tim ch&iacute;nh x&aacute;c\" title=\"Masstel Dream Action | Cảm biến nhịp tim ch&iacute;nh x&aacute;c\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-4.jpg\" alt=\"Masstel Dream Action | Cảm biến nhịp tim ch&iacute;nh x&aacute;c\" /></p>\r\n<h3><strong>Chuẩn chống nước IP68</strong></h3>\r\n<p>Tha hồ bơi lội, tham gia những hoạt động dưới nước hay kh&ocirc;ng sợ ra mồ h&ocirc;i khi tập luyện. Đơn giản v&igrave; Masstel Dream Action c&oacute; khả năng&nbsp;<a title=\"Chuẩn IP68 l&agrave; g&igrave;? Kh&aacute;c biệt g&igrave; so với IP67?\" href=\"https://fptshop.com.vn/tin-tuc/danh-gia/chuan-ip68-la-gi-khac-biet-gi-so-voi-ip67-57823\" type=\"Chuẩn IP68 l&agrave; g&igrave;? Kh&aacute;c biệt g&igrave; so với IP67?\">chống nước chuẩn IP68</a>, chống mọi rủi ro từ nước. Thậm ch&iacute; bạn c&ograve;n c&oacute; thể lặn ở độ s&acirc;u 3m m&agrave; đồng hồ vẫn &ldquo;sống khỏe&rdquo;.</p>\r\n<p><img class=\"Masstel Dream Action | Chuẩn chống nước IP68\" title=\"Masstel Dream Action | Chuẩn chống nước IP68\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-5.jpg\" alt=\"Masstel Dream Action | Chuẩn chống nước IP68\" /></p>\r\n<h3><strong>Kh&ocirc;ng bỏ lỡ bất kỳ th&ocirc;ng b&aacute;o n&agrave;o</strong></h3>\r\n<p>Chỉ cần đồng bộ với&nbsp;<a title=\"Tham khảo nhiều mẫu điện thoại tại FPTShop để nhận nhiều ưu đ&atilde;i, hỗ trợ trả g&oacute;p 0% tại FPTShop\" href=\"https://fptshop.com.vn/dien-thoai\" type=\"Tham khảo nhiều mẫu điện thoại tại FPTShop để nhận nhiều ưu đ&atilde;i, hỗ trợ trả g&oacute;p 0% tại FPTShop\">smartphone</a>, Dream Action sẽ l&agrave; nơi cung cấp cho bạn mọi th&ocirc;ng b&aacute;o từ tin nhắn, cuộc gọi cho đến c&aacute;c ứng dụng như FaceBook, Whatsapp, Twitter, &hellip; Tập trung l&agrave;m việc v&agrave; kh&ocirc;ng bỏ lỡ bất cứ th&ocirc;ng b&aacute;o n&agrave;o khi mọi thứ đều c&oacute; mặt ngay tr&ecirc;n cổ tay bạn.</p>\r\n<p><img class=\"Masstel Dream Action | Đồng bộ với Smartphone\" title=\"Masstel Dream Action | Đồng bộ với Smartphone\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-6.jpg\" alt=\"Masstel Dream Action | Đồng bộ với Smartphone\" /></p>\r\n<h3><strong>La b&agrave;n, b&aacute;o thức, lịch, thời tiết v&agrave; nhiều t&iacute;nh năng kh&aacute;c</strong></h3>\r\n<p>L&agrave; một chiếc đồng hồ th&ocirc;ng minh, Masstel Dream Action t&iacute;ch hợp rất nhiều ứng dụng cần thiết như la b&agrave;n, b&aacute;o thức, lịch, thời tiết, &hellip; Bạn sẽ dễ d&agrave;ng theo d&otilde;i đầy đủ những th&ocirc;ng tin để cuộc sống trở n&ecirc;n dễ d&agrave;ng v&agrave; vui vẻ hơn.</p>\r\n<p><img class=\"Masstel Dream Action | T&iacute;ch hợp rất nhiều ứng dụng cần thiết\" title=\"Masstel Dream Action | T&iacute;ch hợp rất nhiều ứng dụng cần thiết\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-7.jpg\" alt=\"Masstel Dream Action | T&iacute;ch hợp rất nhiều ứng dụng cần thiết\" /></p>\r\n<h3><strong>Thời lượng pin cực d&agrave;i</strong></h3>\r\n<p>Dung lượng pin 180mAh tr&ecirc;n Masstel Dream Action cho ph&eacute;p bạn sử dụng trong khoảng thời gian từ 5 &ndash; 7 ng&agrave;y t&ugrave;y theo c&aacute;c t&aacute;c vụ m&agrave; bạn sử dụng. Thời lượng pin d&agrave;i gi&uacute;p bạn tha hồ trải nghiệm, kh&aacute;m ph&aacute; m&agrave; kh&ocirc;ng lo hết năng lượng.</p>\r\n<p><img class=\"Masstel Dream Action | Pin l&ecirc;n đến 7 ng&agrave;y\" title=\"Masstel Dream Action | Pin l&ecirc;n đến 7 ng&agrave;y\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-8.jpg\" alt=\"Masstel Dream Action | Pin l&ecirc;n đến 7 ng&agrave;y\" /></p>\r\n<p>&nbsp;</p>\r\n<h3><strong>Giao diện th&acirc;n thiện, kết nối cả Android v&agrave; iOS</strong></h3>\r\n<p>Masstel Dream Action c&oacute; giao diện t&iacute;ch hợp tiếng Việt, rất dễ d&agrave;ng l&agrave;m quen v&agrave; nhanh ch&oacute;ng sử dụng thuần thục. Bạn c&ograve;n c&oacute; rất nhiều mặt đồng hồ để thay đổi thường xuy&ecirc;n. Hơn nữa, Masstel Dream Action c&ograve;n c&oacute; thể kết nối với cả điện thoại Android cũng như&nbsp;<a title=\"Tham khảo nhiều mẫu iPhone mới nhất tại FPTShop hỗ trợ trả g&oacute;p 0%\" href=\"https://fptshop.com.vn/apple/iphone\" type=\"Tham khảo nhiều mẫu iPhone mới nhất tại FPTShop hỗ trợ trả g&oacute;p 0%\">iPhone</a>&nbsp;qua ứng dụng Dream Connect, tạo n&ecirc;n sự đồng bộ, liền lạc trong trải nghiệm.</p>\r\n<p><img class=\"Masstel Dream Action | Giao diện th&acirc;n thiện\" title=\"Masstel Dream Action | Giao diện th&acirc;n thiện\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-dream-action-9.JPG\" alt=\"Masstel Dream Action | Giao diện th&acirc;n thiện\" /></p>', 1, 1, 1, 20, '2021-09-05 04:32:28', '2021-09-05 04:32:28', NULL);
INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(34, 'Đồng hồ định vị trẻ em Super Hero 4G', 'dong-ho-dinh-vi-tre-em-super-hero-4g', 'public/uploads/product/1\\super hero 4g - pink 2.jpg', 2100000, '<p><strong>Chiếc&nbsp;<a class=\"Tham khảo nhiều mẫu smartphone ch&iacute;nh h&atilde;ng, nhận nhiều ưu đ&atilde;i tại FPTShop\" title=\"Smartwatch\" href=\"https://fptshop.com.vn/smartwatch\" type=\"Smartwatch\" charset=\"Tham khảo nhiều mẫu smartphone ch&iacute;nh h&atilde;ng, nhận nhiều ưu đ&atilde;i tại FPTShop\">đồng hồ th&ocirc;ng minh</a>&nbsp;tr&ecirc;n tay b&eacute; y&ecirc;u giờ đ&acirc;y c&ograve;n trở n&ecirc;n hữu &iacute;ch hơn bao giờ hết. B&ecirc;n cạnh kiểm so&aacute;t định vị, bạn c&ograve;n c&oacute; thể gọi video call bằng 4G cho con mọi l&uacute;c mọi nơi v&agrave; loạt t&iacute;nh năng đầy th&uacute; vị với&nbsp;</strong><strong><a title=\"Đồng hồ định vị Masstel Super HERO 4G\" href=\"https://fptshop.com.vn/smartwatch/dong-ho-dinh-vi-tre-em-super-hero-4g\" type=\"Đồng hồ định vị Masstel Super HERO 4G\">Masstel Super HERO 4G</a>.</strong></p>\r\n<p><strong><img class=\"Đ&aacute;nh gi&aacute; Masstel Super HERO 4G\" title=\"Đ&aacute;nh gi&aacute; Masstel Super HERO 4G\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-4.jpg\" alt=\"Đ&aacute;nh gi&aacute; Masstel Super HERO 4G\" /></strong></p>\r\n<h3><strong>Thiết kế dễ thương, m&agrave;n h&igrave;nh sắc n&eacute;t</strong></h3>\r\n<p>Chắc chắn c&aacute;c b&eacute; đều y&ecirc;u th&iacute;ch thiết kế dễ thương của đồng hồ Masstel Super HERO 4G. Kiểu d&aacute;ng khỏe khoắn, m&agrave;u sắc sặc sỡ gi&uacute;p c&aacute;c b&eacute; nhanh ch&oacute;ng bị hấp dẫn. Bạn sẽ c&oacute; hai lựa chọn m&agrave;u l&agrave; hồng cho b&eacute; g&aacute;i v&agrave; xanh cho b&eacute; trai. Ngo&agrave;i ra, m&agrave;n h&igrave;nh chiếc&nbsp;<a title=\"Đồng hồ Masstel\" href=\"https://fptshop.com.vn/smartwatch/masstel\" type=\"Đồng hồ Masstel\">Masstel</a>&nbsp;n&agrave;y&nbsp;c&ograve;n c&oacute; k&iacute;ch thước lớn 1,54 inch, hiển thị sắc n&eacute;t với độ ph&acirc;n giải 240 x 240 pixels, cảm ứng mượt m&agrave;. Đ&acirc;y vừa l&agrave; chiếc đồng hồ th&ocirc;ng minh nhiều t&iacute;nh năng, vừa l&agrave; một m&oacute;n đồ chơi m&agrave; c&aacute;c b&eacute; rất y&ecirc;u th&iacute;ch.</p>\r\n<p><img class=\"Masstel Super HERO 4G | Thiết kế dễ thương, m&agrave;n h&igrave;nh sắc n&eacute;t\" title=\"Masstel Super HERO 4G | Thiết kế dễ thương, m&agrave;n h&igrave;nh sắc n&eacute;t\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-1.jpg\" alt=\"Masstel Super HERO 4G | Thiết kế dễ thương, m&agrave;n h&igrave;nh sắc n&eacute;t\" /></p>\r\n<h3><strong>Khả năng chống nước ho&agrave;n hảo</strong></h3>\r\n<p>Độ bền của Masstel Super HERO 4G l&agrave; điều m&agrave; bạn kh&ocirc;ng phải lo lắng khi đồng hồ c&oacute; khả năng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/chuan-ip68-la-gi-khac-biet-gi-so-voi-ip67-57823\">chống nước chuẩn IP68</a>, c&oacute; thể tồn tại trong 30 ph&uacute;t ở độ s&acirc;u 1,5m. Con trẻ thoải m&aacute;i với những tr&ograve; chơi hiếu động m&agrave; kh&ocirc;ng lo đồng hồ hỏng nếu lỡ bị v&agrave;o nước.</p>\r\n<p><img class=\"Masstel Super HERO 4G | Khả năng chống nước ho&agrave;n hảo\" title=\"Masstel Super HERO 4G | Khả năng chống nước ho&agrave;n hảo\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-6.jpg\" alt=\"Masstel Super HERO 4G | Khả năng chống nước ho&agrave;n hảo\" /></p>\r\n<h3><strong>V&agrave;o mạng si&ecirc;u tốc mọi l&uacute;c mọi nơi bằng 4G</strong></h3>\r\n<p>Đ&uacute;ng như t&ecirc;n gọi, đồng hồ Super HERO 4G t&iacute;ch hợp c&ocirc;ng nghệ 4G, gi&uacute;p v&agrave;o mạng mọi l&uacute;c mọi nơi, định vị ch&iacute;nh x&aacute;c hơn v&agrave; đặc biệt l&agrave; gọi video chất lượng cao. Nhờ camera độ ph&acirc;n giải cao 5MP, cha mẹ c&oacute; thể gọi video cho con trẻ bất cứ l&uacute;c n&agrave;o để gần gũi con hơn v&agrave; cũng ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m khi con ra ngo&agrave;i.</p>\r\n<p><img class=\"Masstel Super HERO 4G | V&agrave;o mạng si&ecirc;u tốc mọi l&uacute;c mọi nơi bằng 4G\" title=\"Masstel Super HERO 4G | V&agrave;o mạng si&ecirc;u tốc mọi l&uacute;c mọi nơi bằng 4G\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-8.jpg\" alt=\"Masstel Super HERO 4G | V&agrave;o mạng si&ecirc;u tốc mọi l&uacute;c mọi nơi bằng 4G\" /></p>\r\n<h3><strong>Hệ thống định vị ch&iacute;nh x&aacute;c</strong></h3>\r\n<p>Định vị l&agrave; t&iacute;nh năng ch&iacute;nh tr&ecirc;n đồng hồ th&ocirc;ng minh trẻ em. Masstel Super HERO 4G c&oacute; hệ thống định vị với đầy đủ c&aacute;c chế độ: LBS, Wifi,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thong-tin-tham-khao/phan-biet-giua-he-thong-dinh-vi-glonass-va-gps-20847\">GPS</a>, AGPS gi&uacute;p bố mẹ y&ecirc;n t&acirc;m tr&ocirc;ng con từ xa, biết được b&eacute; đang ở đ&acirc;u, lịch tr&igrave;nh di chuyển của b&eacute; trong ng&agrave;y. Bạn cũng c&oacute; thể thiết lập h&agrave;ng r&agrave;o điện tử để đồng hồ đưa ra cảnh b&aacute;o khi b&eacute; đi v&agrave;o địa điểm lạ.</p>\r\n<p><img class=\"Masstel Super HERO 4G | Hệ thống định vị ch&iacute;nh x&aacute;c\" title=\"Masstel Super HERO 4G | Hệ thống định vị ch&iacute;nh x&aacute;c\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-11.jpg\" alt=\"Masstel Super HERO 4G | Hệ thống định vị ch&iacute;nh x&aacute;c\" /></p>\r\n<h3><strong>Ứng dụng hữu &iacute;ch d&agrave;nh cho trẻ</strong></h3>\r\n<p>Tr&ecirc;n đồng hồ Masstel Super HERO 4G c&oacute; đầy đủ những ứng dụng học m&agrave; chơi, chơi m&agrave; học d&agrave;nh cho b&eacute;. Bạn c&oacute; thể tải về c&aacute;c ứng dụng tr&ograve; chơi c&acirc;u đố tr&iacute; tuệ, kho 1000 video kiến thức v&agrave; giải tr&iacute;, ứng dụng kể chuyện qua Wifi/4G. B&eacute; c&oacute; thể giải tr&iacute; l&agrave;nh mạnh, đồng thời ph&aacute;t triển tư duy với những ứng dụng hữu &iacute;ch.</p>\r\n<p><img class=\"Masstel Super HERO 4G | Ứng dụng hữu &iacute;ch d&agrave;nh cho trẻ\" title=\"Masstel Super HERO 4G | Ứng dụng hữu &iacute;ch d&agrave;nh cho trẻ\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-10.jpg\" alt=\"Masstel Super HERO 4G | Ứng dụng hữu &iacute;ch d&agrave;nh cho trẻ\" /></p>\r\n<h3><strong>Bảo mật an to&agrave;n</strong></h3>\r\n<p>Bảo mật th&ocirc;ng tin cũng l&agrave; một vấn đề kh&ocirc;ng k&eacute;m phần quan trọng trong thời buổi hiện nay. To&agrave;n bộ dữ liệu, th&ocirc;ng tin c&aacute; nh&acirc;n của b&eacute; v&agrave; gia đ&igrave;nh lưu tr&ecirc;n&nbsp;<a title=\"Đồng hồ\" href=\"https://fptshop.com.vn/dong-ho\" type=\"Đồng hồ\">đồng hồ</a>&nbsp;đều được m&atilde; h&oacute;a bảo mật an to&agrave;n. Bố mẹ c&ograve;n c&oacute; thể điều khiển chức năng kh&oacute;a b&agrave;n ph&iacute;m v&agrave; tắt nguồn tr&ecirc;n đồng hồ. Masstel Super HERO 4G cũng c&oacute; t&iacute;nh năng b&aacute;o mất m&aacute;y để định vị t&igrave;m lại đồng hồ dễ d&agrave;ng nếu bị thất lạc.</p>\r\n<p><img class=\"Masstel Super HERO 4G | Bảo mật an to&agrave;n\" title=\"Masstel Super HERO 4G | Bảo mật an to&agrave;n\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/masstel-super-hero-4g-9.jpg\" alt=\"Masstel Super HERO 4G | Bảo mật an to&agrave;n\" /></p>', 1, 0, 1, 20, '2021-09-05 04:33:50', '2021-09-05 04:33:50', NULL),
(35, 'Đồng hồ thông minh Garmin Forerunner 245', 'dong-ho-thong-minh-garmin-forerunner-245', 'public/uploads/product/1\\forerunner-245-music-black-red-1.jpg', 7990000, '<p><strong>Ngo&agrave;i những t&iacute;nh năng chăm s&oacute;c sức khỏe v&agrave; hỗ trợ tập luyện, Garmin Forerunner 245 phi&ecirc;n bản Music c&ograve;n c&oacute; thể điều khiển ph&aacute;t nhạc, hỗ trợ người d&ugrave;ng nghe nhạc mọi l&uacute;c mọi nơi m&agrave; kh&ocirc;ng cần phải chạm v&agrave;o smartphone. Sản phẩm c&oacute; thời lượng pin 7 ng&agrave;y, m&agrave;n h&igrave;nh chống xước v&agrave; thiết kế thể thao nhỏ gọn.</strong></p>\r\n<p><strong><img id=\"Garmin Forerunner 245 Music 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-1.jpg\" alt=\"Garmin Forerunner 245 Music 1\" /></strong></p>\r\n<h3><strong>Phong c&aacute;ch thể thao với d&acirc;y silicone</strong></h3>\r\n<p>Garmin Forerunner 245 Music được trang bị m&agrave;n h&igrave;nh 1.2 inch độ ph&acirc;n giải 240 x 240 pixels. Trải nghiệm thực tế cho thấy sản phẩm c&oacute; khả năng hiển thị h&igrave;nh ảnh sắc n&eacute;t với hệ thống th&ocirc;ng tin r&otilde; r&agrave;ng. Phần quai đeo c&oacute; nhiều nấc t&ugrave;y chỉnh để ph&ugrave; hợp với nhiều người sử dụng, đem lại cảm gi&aacute;c &ecirc;m &aacute;i khi đeo đồng thời thoải m&aacute;i khi chơi thể thao v&agrave; tập thể dục.</p>\r\n<p><img id=\"Phong c&aacute;ch thể thao với d&acirc;y silicone 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-6.jpg\" alt=\"Phong c&aacute;ch thể thao với d&acirc;y silicone 1\" /></p>\r\n<h3><strong>Tương t&aacute;c linh hoạt với điện thoại của bạn</strong></h3>\r\n<p>Về khả năng kết nối v&agrave; tương t&aacute;c, Forerunner 245 Music c&oacute; thể gh&eacute;p nối với smartphone thuộc cả hai nền tảng l&agrave; iOS v&agrave; Android. Qua đ&oacute;, bạn sẽ dễ d&agrave;ng hơn trong c&aacute;c t&aacute;c vụ như theo d&otilde;i sức khỏe, từ chối cuộc gọi, kiểm tra SMS hoặc kiểm tra th&ocirc;ng b&aacute;o từ Zalo, Facebook.</p>\r\n<p><img id=\"Tương t&aacute;c linh hoạt với điện thoại của bạn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-3.jpg\" alt=\"Tương t&aacute;c linh hoạt với điện thoại của bạn 1\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh sắc sảo với k&iacute;nh cường lực chống xước</strong></h3>\r\n<p>Một trong những c&ocirc;ng nghệ điểm nhấn của đồng hồ Forerunner 245 Music l&agrave; m&agrave;n h&igrave;nh MiP c&oacute; khả năng hiển thị sắc sảo v&agrave; r&otilde; r&agrave;ng ngay cả dưới &aacute;nh s&aacute;ng mặt trời nhờ việc thu nhận th&ocirc;ng tin trong mỗi điểm ảnh. Khi bạn theo d&otilde;i th&ocirc;ng tin tr&ecirc;n mặt đồng hồ trong điều kiện ngo&agrave;i trời, sản phẩm vẫn cho ph&eacute;p quan s&aacute;t r&otilde; r&agrave;ng. Mặt k&iacute;nh cường lực chống xước sẽ đảm bảo loại trừ những trầy xước kh&ocirc;ng đ&aacute;ng c&oacute; khi bạn vận động mạnh.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh sắc sảo với k&iacute;nh cường lực chống xước 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-8.jpg\" alt=\"M&agrave;n h&igrave;nh sắc sảo với k&iacute;nh cường lực chống xước 1\" /></p>\r\n<h3><strong>Kho giao diện đầy c&aacute; t&iacute;nh tr&ecirc;n Garmin Connect</strong></h3>\r\n<p>Nhằm đảm bảo trải nghiệm mới mẻ trong qu&aacute; tr&igrave;nh sử dụng Garmin Forerunner 245 Music, nh&agrave; sản xuất trang bị sẵn cho người d&ugrave;ng ba mặt giao diện kh&aacute;c nhau. Trong đ&oacute; bao gồm th&ocirc;ng tin về&nbsp;thời gian, nhịp tim, thời gian vận động v&agrave; phần trăm pin c&ograve;n lại. Trong trường hợp bạn đ&atilde; ch&aacute;n c&aacute;c mặt đồng hồ n&agrave;y, bạn ho&agrave;n to&agrave;n c&oacute; thể truy cập v&agrave;o kho ứng dụng Garmin Connect tr&ecirc;n hai hệ điều h&agrave;nh l&agrave; iOS v&agrave; Android để tải về mặt đồng hồ mới.</p>\r\n<p><img id=\"Kho giao diện đầy c&aacute; t&iacute;nh tr&ecirc;n Garmin Connect 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-2.jpg\" alt=\"Kho giao diện đầy c&aacute; t&iacute;nh tr&ecirc;n Garmin Connect 1\" /></p>\r\n<h3><strong>Trải nghiệm pin trong một tuần</strong></h3>\r\n<p>Sau khi được sạc đầy, Forerunner 245 Music c&oacute; thể vận h&agrave;nh trong một ng&agrave;y nếu bạn sử dụng c&aacute;c chức năng như nghe nhạc v&agrave; k&iacute;ch hoạt GPS. Trong trường hợp bạn sử dụng sản phẩm như một smartwatch, thiết bị c&oacute; thể vận h&agrave;nh bền bỉ trong một tuần &ndash; th&ocirc;ng số rất ấn tượng khi so s&aacute;nh với tất cả c&aacute;c d&ograve;ng smartwatch tr&ecirc;n thị trường.</p>\r\n<p><img id=\"Trải nghiệm pin trong một tuần 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-7.jpg\" alt=\"Trải nghiệm pin trong một tuần 1\" /></p>\r\n<h3><strong>Cảm biến sức khỏe lu&ocirc;n theo d&otilde;i bạn</strong></h3>\r\n<p>Với cảm biến sức khỏe được nghi&ecirc;n cứu bởi đội ngũ Garmin, chiếc đồng hồ c&oacute; thể d&otilde;i theo nhịp tim, gi&aacute;m s&aacute;t giấc ngủ v&agrave; &aacute;p dụng thuật to&aacute;n đo đạc để đưa ra chỉ số VO2Max. Nhờ đ&oacute;, bạn c&oacute; thể nắm được chi tiết t&igrave;nh trạng sức khỏe của bản th&acirc;n v&agrave; c&oacute; c&aacute;i nh&igrave;n tổng qu&aacute;t hơn về t&igrave;nh trạng của ch&iacute;nh m&igrave;nh, đồng thời đưa ra phương &aacute;n tập luyện để cơ thể khỏe mạnh hơn nữa.</p>\r\n<p><img id=\"Cảm biến sức khỏe lu&ocirc;n theo d&otilde;i bạn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-5.jpg\" alt=\"Cảm biến sức khỏe lu&ocirc;n theo d&otilde;i bạn 1\" /></p>\r\n<h3><strong>N&acirc;ng cao hiệu năng tập thể thao của bạn</strong></h3>\r\n<p>Tuy được chế tạo d&agrave;nh cho những người y&ecirc;u th&iacute;ch trải nghiệm tập luyện qua việc chạy bộ, nhưng thực tế mẫu đồng hồ Garmin Forerunner 245 Music c&ograve;n hỗ trợ t&agrave;i liệu hướng dẫn chi tiết để bạn thực hiện c&aacute;c m&ocirc;n thể thao kh&aacute;c như yoga, chạy, tập cơ, xe đạp trong nh&agrave;, bơi lội. Mọi chỉ số thống k&ecirc; sẽ được hiển thị trực tiếp để bạn theo d&otilde;i dễ d&agrave;ng hơn.</p>\r\n<p><img id=\"N&acirc;ng cao hiệu năng tập thể thao của bạn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Garmin-forerunner-245-music-4.jpg\" alt=\"N&acirc;ng cao hiệu năng tập thể thao của bạn 1\" /></p>\r\n<h3><strong>&Acirc;m nhạc mọi nơi mọi l&uacute;c</strong></h3>\r\n<p>Với khả năng chứa đựng 500 b&agrave;i h&aacute;t trong bộ nhớ, Garmin Forerunner 245 Music cho ph&eacute;p người d&ugrave;ng chuyển nhạc từ laptop sang đồng hồ để nghe bất cứ khi n&agrave;o m&igrave;nh th&iacute;ch. Nhờ đ&oacute;, bạn sẽ khơi dậy niềm hứng khởi tập luyện thể thao để buổi tập của m&igrave;nh lu&ocirc;n h&agrave;o hứng nhất, tr&agrave;n đầy năng lượng nhất.</p>', 1, 0, 1, 19, '2021-09-05 04:39:30', '2021-09-05 04:39:30', NULL),
(36, 'Đồng hồ thông minh Garmin Instinct', 'dong-ho-thong-minh-garmin-instinct', 'public/uploads/product/1\\garmin-instinct-xam-1.jpg', 6100000, '<p><strong>Garmin Instinct l&agrave; chiếc đồng hồ được thiết kế v&agrave; sản xuất theo chuẩn qu&acirc;n sự Hoa Kỳ MIL-STD810G, đảm bảo khả năng chống va đập, chống v&agrave;o nước v&agrave; chịu nhiệt rất tốt. Đ&acirc;y l&agrave; thiết bị rất ph&ugrave; hợp cho những ai y&ecirc;u thể thao v&agrave; quan t&acirc;m đến việc chăm s&oacute;c sức khỏe bản th&acirc;n, đo đạc nhịp tim cũng như sự căng thẳng của bạn.</strong></p>\r\n<p><img id=\"Garmin Instinct 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-1.jpg\" alt=\"Garmin Instinct 1\" /></p>\r\n<h3><strong>Thiết kế phong c&aacute;ch đậm chất thể thao</strong></h3>\r\n<p>Phong c&aacute;ch thể thao với những đường n&eacute;t khỏe khoắn được thể hiện rất r&otilde; tr&ecirc;n thiết kế của Garmin Instinct. Sản phẩm sử dụng mặt đồng hồ tr&ograve;n, c&aacute;c chi tiết đều c&oacute; n&eacute;t cắt xẻ dứt kho&aacute;t, hệ thống ph&iacute;m bấm được tạo h&igrave;nh mạnh mẽ, thuận tiện để thao t&aacute;c mọi nơi mọi l&uacute;c. Cơ chế quai đeo của Instinct c&oacute; rất nhiều khoen c&agrave;i để bạn đeo dễ d&agrave;ng v&agrave; ph&ugrave; hợp với nhiều k&iacute;ch cỡ cổ tay.</p>\r\n<p><img id=\"Thiết kế phong c&aacute;ch đậm chất thể thao 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-2.jpg\" alt=\"Thiết kế phong c&aacute;ch đậm chất thể thao 1\" /></p>\r\n<h3><strong>Độ bền bỉ tuyệt vời của Garmin Instinct</strong></h3>\r\n<p>Nếu bạn đang t&igrave;m kiếm một mẫu đồng hồ th&ocirc;ng minh thật bền bỉ để sử dụng trong nhiều năm th&igrave; Garmin Instinct l&agrave; sự lựa chọn rất l&yacute; tưởng. Sản phẩm được gia cố bằng vỏ cốt sợi polyme nhằm tăng cường độ chống chịu khi va đập. Với khả năng chống nước đạt chuẩn 10ATM, Instinct c&oacute; thể &ldquo;sống tốt&rdquo; trong hầu hết điều kiện khắc nghiệt.</p>\r\n<p><img id=\"Độ bền bỉ tuyệt vời của Garmin Instinct 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-3.jpg\" alt=\"Độ bền bỉ tuyệt vời của Garmin Instinct 1\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị r&otilde; dưới &aacute;nh s&aacute;ng mặt trời</strong></h3>\r\n<p>Garmin trang bị cho Instinct m&agrave;n h&igrave;nh độ ph&acirc;n giải 240 x 240 pixels, m&agrave;n h&igrave;nh n&agrave;y được t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ MIP (bộ nhớ tr&ecirc;n mỗi điểm ảnh), nhờ đ&oacute; gia tăng khả năng chống ch&oacute;i dưới &aacute;nh s&aacute;ng mặt trời, gi&uacute;p người d&ugrave;ng quan s&aacute;t r&otilde; r&agrave;ng mọi th&ocirc;ng tin hiển thị. Lớp k&iacute;nh cường lực h&oacute;a học chống xước rất tốt v&agrave; chịu được những va đập bất ngờ c&oacute; thể xảy ra trong qu&aacute; tr&igrave;nh bạn sử dụng đồng hồ.</p>\r\n<p><img id=\"Định vị la b&agrave;n 3 trục v&agrave; kh&iacute; &aacute;p kế đo độ cao 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-5.jpg\" alt=\"Định vị la b&agrave;n 3 trục v&agrave; kh&iacute; &aacute;p kế đo độ cao 1\" /></p>\r\n<h3><strong>Định vị la b&agrave;n 3 trục v&agrave; kh&iacute; &aacute;p kế đo độ cao</strong></h3>\r\n<p>Nổi danh l&agrave; thương hiệu c&oacute; tiếng trong ng&agrave;nh c&ocirc;ng nghiệp thiết bị định vị, Garmin đem tới cho chiếc đồng hồ Instinct c&ocirc;ng nghệ la b&agrave;n 3 trục v&agrave; kh&iacute; &aacute;p kế đo độ cao được t&iacute;ch hợp với hệ thống vệ tinh điều hướng to&agrave;n cầu bao gồm GPS, GLONASS v&agrave; Galileo. Nhờ vậy, sản phẩm sẽ định vị ch&iacute;nh x&aacute;c mọi nơi mọi l&uacute;c ngay cả trong những m&ocirc;i trường khắc nghiệt nhất.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh hiển thị r&otilde; dưới &aacute;nh s&aacute;ng mặt trời 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-4.jpg\" alt=\"M&agrave;n h&igrave;nh hiển thị r&otilde; dưới &aacute;nh s&aacute;ng mặt trời 1\" /></p>\r\n<h3><strong>Theo d&otilde;i nhịp tim, cảnh b&aacute;o độ căng thẳng</strong></h3>\r\n<p>Garmin Instinct kết hợp thuật to&aacute;n c&ugrave;ng cảm biến theo d&otilde;i nhịp tim đặt ở mặt dưới để gi&aacute;m s&aacute;t chặt chẽ những biến động của nhịp tim bạn, từ đ&oacute; sẽ thấu hiểu t&igrave;nh h&igrave;nh sức khỏe của bạn hơn. Sự biến đổi của nhịp tim n&oacute;i l&ecirc;n trạng th&aacute;i tinh thần hiện tại, gi&uacute;p bạn thấy được m&igrave;nh c&oacute; stress hay kh&ocirc;ng để điều chỉnh t&acirc;m l&yacute; qua những phương ph&aacute;p đơn giản như điều h&ograve;a nhịp thở.</p>\r\n<p><img id=\"Đồng bộ dữ liệu v&agrave; kết nối với Garmin Connect 99\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-7.jpg\" alt=\"Đồng bộ dữ liệu v&agrave; kết nối với Garmin Connect 9\" /></p>\r\n<h3><strong>Đồng bộ dữ liệu v&agrave; kết nối với Garmin Connect</strong></h3>\r\n<p>Garmin Connect l&agrave; một kho ứng dụng trực tuyến được nh&agrave; sản xuất Hoa Kỳ x&acirc;y dựng để đồng bộ h&oacute;a th&ocirc;ng tin từ qu&aacute; tr&igrave;nh tập luyện của người d&ugrave;ng l&ecirc;n, đồng thời hỗ trợ bạn chia sẻ th&agrave;nh t&iacute;ch luyện tập với cộng đồng sử dụng Garmin, từ đ&oacute; th&uacute;c đẩy tinh thần thể thao v&agrave; khơi dậy sự h&agrave;o hứng trong l&ograve;ng người d&ugrave;ng.</p>\r\n<p><img id=\"Theo d&otilde;i nhịp tim, cảnh b&aacute;o độ căng thẳng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-6.jpg\" alt=\"Theo d&otilde;i nhịp tim, cảnh b&aacute;o độ căng thẳng 1\" /></p>\r\n<h3><strong>Vận h&agrave;nh 14 ng&agrave;y ở chế độ smartwatch</strong></h3>\r\n<p>Về thời lượng sử dụng pin, Garmin Instinct được tối ưu thời gian sử dụng rất tốt, sản phẩm c&oacute; thể hoạt động li&ecirc;n tục trong hai tuần nếu đặt ở chế độ smartwatch. Trong trường hợp bạn bật định vị GPS, con số n&agrave;y sẽ dừng lại ở 14 giờ do cơ chế x&aacute;c định vị tr&iacute; kh&aacute; ngốn năng lượng. X&eacute;t tr&ecirc;n thị trường đồng hồ th&ocirc;ng minh hiện tại, thời lượng pin của Instinct thuộc top đầu khi đặt cạnh c&aacute;c đối thủ trực tiếp.</p>\r\n<p><img id=\"Vận h&agrave;nh 14 ng&agrave;y ở chế độ smartwatch 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/03/08/Garmin-instinct-8.jpg\" alt=\"Vận h&agrave;nh 14 ng&agrave;y ở chế độ smartwatch 1\" /></p>', 1, 0, 1, 19, '2021-09-05 04:40:22', '2021-09-05 04:40:22', NULL);

